(window.webpackJsonp=window.webpackJsonp||[]).push([[617],{972:function(n,h,t){"use strict";t.r(h);var c=t(44),g=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1532-lac-gia-đai-truong-lao"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1532-lac-gia-đai-truong-lao"}},[n._v("#")]),n._v(" Chương 1532: Lạc gia đại trưởng lão")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1532: Lạc gia đại trưởng lão"),t("br"),t("br"),n._v("Thân là Độc điện cao tầng nhất, đặc biệt là Thẩm Khuyết, vì tìm kiếm tổ sư lưu lại truyền thế độc điển, du lịch đại lục, gặp qua không biết bao nhiêu mạnh mẽ danh sư, thậm chí cùng trong đó thái thượng trưởng lão cũng giao thủ qua... Nhưng từ chưa nghe nói qua, chỉ nhìn một cái, liền có thể đem tất cả mọi người tình huống biết không sai chút nào, an bài chiến thuật, không có cái gì vấn đề!"),t("br"),t("br"),n._v("Đây cũng không phải là nhãn lực, mà là thần tiên quỷ mị!"),t("br"),t("br"),n._v("“Ừm! Hiện tại không cần tiến công, cái này có mấy đạo chân khí, các ngươi lặng lẽ ẩn giấu ở bốn phía, không nên để cho người phát hiện, làm xong, liền trở lại ta trước mặt ngồi xuống, cái gì đều không cần quản...”"),t("br"),t("br"),n._v("Trương Huyền truyền âm, ngay sau đó cong ngón búng ra."),t("br"),t("br"),n._v("Ba đại cao thủ mỗi người đồng thời tiếp thụ lấy mấy đạo chân khí."),t("br"),t("br"),n._v("Những này chân khí, linh khí đầy đủ, đường đường chính chính, không có chút nào kịch độc cùng những lực lượng khác, Lâm Giang Hải đám người tuy là không hiểu vì sao muốn làm như vậy, còn là theo lời đem chân khí bố trí đi."),t("br"),t("br"),n._v("Độc sư cường đại nhất thủ đoạn chính là hạ độc để cho người ta không có chút nào phát giác, sắp đặt loại này không có bất kỳ cái gì thuộc tính chân khí, càng là cực kỳ đơn giản, rất nhanh liền bố trí xong, lần nữa trở lại Trương Huyền trước mặt, dựa theo phân phó của hắn, không tiếp tục để ý trên không nhìn chằm chằm tất cả danh sư, mà là khoanh chân ngồi xuống."),t("br"),t("br"),n._v("Rất nhanh trên không tất cả danh sư, tiêu trừ thể nội độc khí, từng cái nhìn lại, mày nhăn lại."),t("br"),t("br"),n._v("Nói thật, dựa theo tình huống vừa rồi, mấy người kia là có cơ hội chạy trốn, giờ phút này không những không trốn, ngược lại đồng thời ngồi dưới đất, muốn làm gì?"),t("br"),t("br"),n._v("“Cố làm ra vẻ huyền bí, mới vừa rồi là chúng ta không sẵn sàng, lần này chỉ cần chuẩn bị kỹ càng, nhất định có thể đem toàn bộ chém giết!”"),t("br"),t("br"),n._v("Khúc trưởng lão khuôn mặt tái mét."),t("br"),t("br"),n._v("“Không sai, ba cái độc sư mà thôi, nếu như ngay cả cái này đều không giải quyết được, còn có cái gì tư cách, tọa trấn thiên hạ, đối kháng Dị Linh tộc nhân?”"),t("br"),t("br"),n._v("Một vị danh sư nhịn không được hét lớn."),t("br"),t("br"),n._v("Nói thật, đối với Danh Sư đường tổng bộ tới nói, Độc điện tuy là quỷ dị, lại cũng chỉ là giới tiển chi nhanh, không quan trọng gì, vốn cho rằng chỉ cần bọn họ ra tay, có thể dễ như trở bàn tay, nhẹ nhõm đem ba người nghiền ép, nằm mơ đều không có nghĩ đến, đối phương không biết dùng cái gì quỷ dị phương pháp, một chiêu giao thủ, ngược lại làm cho hắn bị thiệt lớn."),t("br"),t("br"),n._v("“Bọn họ lợi hại nhất chẳng qua dùng độc, ta phụ tu là cửu tinh y sư, bình thường cũng luyện chế ra không ít thuốc giải độc vật, ta vì chư vị mở tiên phong!”"),t("br"),t("br"),n._v("Quát lạnh một tiếng, một vị danh sư đi ra, lật bàn tay một cái, một cái giải độc đan để vào cổ họng, cũng không cần Nhậm Thanh Viễn đồng ý, nhanh chân hướng Trương Huyền đám người bay tới, người trên không trung, một chưởng vỗ rơi mà xuống."),t("br"),t("br"),n._v("Lực lượng của hắn như là ngọn núi to lớn, hạ xuống từ trên trời, vừa muốn rơi vào đám người đỉnh đầu, đột nhiên chạm đến vừa rồi mấy người sắp đặt ở chung quanh chân khí."),t("br"),t("br"),n._v("Đạo chân khí này theo chân khí của hắn lập tức đi ngược dòng nước, thời gian nháy mắt liền chui vào thân thể."),t("br"),t("br"),n._v("Nếu như là kịch độc lời nói, hắn hộ thể chân khí, có thể tự động che đậy, đem hắn ngăn cản ra ngoài, coi như đi vào trong cơ thể, giải độc đan cũng có thể trực tiếp đưa đến tác dụng... Có thể cỗ này chân khí, không có bất kỳ cái gì thuộc tính, hơn nữa tinh thuần vô cùng, vừa tiến vào kinh mạch, lập tức chìm vào nội tạng."),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Biến sắc, vị danh sư này công kích còn không có hạ xuống, liền tán loạn ra, cả người “Phù phù” một chút, từ không trung rơi xuống, trên đầu mồ hôi lạnh chi mạo."),t("br"),t("br"),n._v("“Đây là...”"),t("br"),t("br"),n._v("Khúc trưởng lão biến sắc, vội vàng đi tới trước mặt: “Bạch trưởng lão trúng độc...”"),t("br"),t("br"),n._v("đăng nhậphttp://ngantruyen.com để đọc truyện"),t("br"),n._v("Loại tình huống này, trước hắn tại Băng Nguyên cung thời điểm, gặp được, vị này Trương Huyền, chính là ngụy trang Dương sư, để hắn trúng độc, mất đi sức chiến đấu, nếu không... Cũng không thể nào để cho hắn làm xằng làm bậy, đem một tòa truyền thừa vạn năm cung điện, triệt để hủy hoại."),t("br"),t("br"),n._v("“Trúng độc?”"),t("br"),t("br"),n._v("Đám người vội vàng nhìn tới, quả nhiên thấy vị này Bạch trưởng lão không ngừng run rẩy, dường như thừa nhận to lớn thống khổ, trước đó giải độc đan các loại, không có một chút tác dụng nào."),t("br"),t("br"),n._v("“Đây rốt cuộc cái gì độc?”"),t("br"),t("br"),n._v("Nhậm Thanh Viễn nhịn không được ngạc nhiên."),t("br"),t("br"),n._v("Bạch trưởng lão y thuật, hắn biết hết sức rõ ràng, cửu tinh y sư, lại nuốt vào giải độc đan, đều trúng độc... Đến cùng loại nào kịch độc, cường đại như vậy?"),t("br"),n._v(" “Ta cũng không biết, cùng bình thường chân khí giống nhau như đúc, nhưng đi vào trong cơ thể, liền sẽ biến thành độc dịch, để cho người ta khó lòng phòng bị...”"),t("br"),t("br"),n._v("Hàm răng cắn chặt, Bạch trưởng lão một bên run rẩy, một bên giải thích."),t("br"),t("br"),n._v("“Cùng bình thường chân khí đồng dạng, chẳng lẽ là nhất niệm là độc? Cái này...”"),t("br"),t("br"),n._v("Nhậm Thanh Viễn ngạc nhiên, lập tức quay đầu dặn dò: “Mọi người không nên tùy tiện tấn công, chờ nghĩ ra đối sách động thủ lần nữa!”"),t("br"),t("br"),n._v("Nhất niệm là độc, là một loại lợi hại kịch độc, bình thường chỉ là vật phẩm bình thường, làm cho không người nào có thể phát giác, một khi dùng độc người vận dụng ý niệm, sẽ lập tức biến thành kịch độc, khó lòng phòng bị."),t("br"),t("br"),n._v("Mấy người kia bốn phía bố trí lợi hại như vậy kịch độc, nếu như bọn họ tùy tiện xông tới lời nói, rất dễ dàng xuất hiện biến cố, cần trước thời hạn nghĩ ra đối sách mới được."),t("br"),t("br"),n._v("“Loại độc này tuy là lợi hại, nhưng cũng rất dễ hóa giải, ta trước đó trải qua, ta giúp ngươi giải quyết...”"),t("br"),t("br"),n._v("Khúc trưởng lão ngón tay một điểm, một đạo chân khí đi vào Bạch trưởng lão trong cơ thể."),t("br"),t("br"),n._v("Tại Băng Nguyên cung hắn từng trúng qua loại độc này, biết phương pháp giải quyết, lần nữa gặp được, mặc dù biết thống khổ, nhưng không đả thương được căn bản."),t("br"),t("br"),n._v("Quả nhiên, dựa vào sự giúp đỡ của hắn, không đến ba phút, kịch độc liền giải quyết triệt để, có điều, tất cả danh sư, cũng hai mặt nhìn nhau, trong lúc nhất thời không dám tiếp tục hướng phía trước."),t("br"),t("br"),n._v("Tuy là hai người đem độc kết, nhưng thần không biết quỷ không hay ở giữa độc, đổi lại ai, đều sẽ cảm thấy sợ hãi, không dám tùy tiện đụng chạm."),t("br"),t("br"),n._v("“Tìm được trước bọn họ sắp đặt kịch độc địa phương, cũng làm rõ đây rốt cuộc là cái gì độc dược, nếu không, chỉ có thể làm nhìn lấy, không có bất kỳ biện pháp nào!”"),t("br"),t("br"),n._v("“Yên tâm, chỉ cần bao vây chung quanh, bọn họ cũng trốn không thoát, độc cũng có thời gian hạn định tính, ta không tin, có thể một mực duy trì loại uy lực này...”"),t("br"),t("br"),n._v("Đám người nhìn nhau, không ai tấn công nhưng cũng không rời đi."),t("br"),t("br"),n._v("“Trương sư... Thế mà cũng là dùng độc mọi người?”"),t("br"),t("br"),n._v("Nhìn thấy hơn mười vị cửu tinh danh sư, tính cả một vị quyền điện chủ, đều bó tay toàn tập, Lâm Giang Hải đám người lần nữa nhìn về phía thanh niên trước mắt, tràn đầy bội phục cùng ngạc nhiên."),t("br"),t("br"),n._v("Bọn họ đều là dùng độc đại chuyên gia, biết loại này “Nhất niệm là độc” kinh khủng, đối phương tiện tay bắn ra bình thường chân khí, có loại hiệu quả này, nằm mơ cũng không dám nghĩ."),t("br"),t("br"),n._v("Trước đó, vẫn cảm thấy vị này Trương sư, chỉ là đối điện chủ có chút ân tình, không đành lòng ân nhân gặp nạn, giờ phút này mới hiểu được, trước mắt vị này đối độc lý giải, vượt xa quá bọn họ, thậm chí so với điện chủ, đều chỉ mạnh không yếu!"),t("br"),t("br"),n._v("Khó trách xưng hô lão sư... Là thật truyền thụ nàng dùng độc chi pháp!"),t("br"),t("br"),n._v("“Chỉ chờ tới lúc điện chủ trở về, chắc hẳn bọn họ cũng không dám công kích...”"),t("br"),t("br"),n._v("Thấy nhiều danh sư tuy là tức giận, lại không xông lên, Lâm Giang Hải đám người nhịn không được thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Chỉ cần điện chủ đi tới, hẳn là có thể hóa giải hiện tại nguy cơ."),t("br"),t("br"),n._v("“Đây là thế nào? Vị nào là Trương Huyền? Ta cũng muốn nhìn một chút, đến cùng loại nào bộ dáng? Lại là loại nào ba đầu sáu tay!”"),t("br"),t("br"),n._v("Ngay tại bốn phía nằm ở yên tĩnh thời điểm, một cái thanh âm nhàn nhạt vang lên, ngay sau đó một bóng người chậm rãi xuất hiện."),t("br"),t("br"),n._v("Là cái lão giả râu bạc trắng."),t("br"),t("br"),n._v("“Là Lạc gia đại trưởng lão!”"),t("br"),t("br"),n._v("Trong đám người không biết ai nhận ra được."),t("br"),t("br"),n._v("“Ha ha, Lạc Khinh Trần ngươi tới được vừa vặn...”"),t("br"),t("br"),n._v("Nhìn người nọ, Nhậm Thanh Viễn con mắt lập tức sáng lên."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1540"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1542"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);