(window.webpackJsonp=window.webpackJsonp||[]).push([[621],{975:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1536-đo-nhi-lo-xung-tro-ve"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1536-đo-nhi-lo-xung-tro-ve"}},[n._v("#")]),n._v(" Chương 1536: Đồ nhi Lộ Xung trở về!")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1536: Đồ nhi Lộ Xung trở về!"),t("br"),t("br"),n._v("“Đây là... Vu hồn?”"),t("br"),t("br"),n._v("“Vu hồn sư... Không phải là bị diệt đi vài vạn năm sao? Làm sao vẫn tồn tại?”"),t("br"),t("br"),n._v("“Đầu này vu hồn nói... Đối phó lão sư cùng sư muội... Chẳng lẽ, hắn cũng là Trương Huyền học sinh?”"),t("br"),t("br"),n._v("“Không chỉ dạy ra độc sư, còn dạy ra một đầu vu hồn?”"),t("br"),t("br"),n._v("Tất cả mọi người đều biến sắc, một đám thiên băng địa hãm đều mặt không đổi sắc cửu tinh danh sư, giờ phút này tất cả đều giống giống như gặp quỷ."),t("br"),t("br"),n._v("Dạy ra Tiên Thiên độc thể Độc điện điện chủ, thì cũng thôi đi, khổng lồ như vậy vu hồn, vậy mà cũng tự xưng học sinh của hắn... Thật hay giả?"),t("br"),t("br"),n._v("Tuy là vu hồn sư truyền thừa đoạn tuyệt vài vạn năm, nhưng vừa ra tay liền đường chủ lệnh đều có thể bắt lại, ép Nhậm Thanh Viễn quyền đường chủ không cách nào ra tay... Thực lực mạnh yếu, tất cả mọi người vẫn là có thể nhìn ra được."),t("br"),t("br"),n._v("Dạy ra học sinh, còn mạnh hơn chính mình... Mấu chốt, thoáng cái xuất hiện hai cái!"),t("br"),t("br"),n._v("“Buông ra cho ta!”"),t("br"),t("br"),n._v("Cũng không nghĩ tới, một đầu vu hồn bất ngờ xuất hiện, còn thừa dịp hắn không sẵn sàng, cướp đi đường chủ lệnh, Nhậm Thanh Viễn biến sắc, vội vàng bắt đến."),t("br"),t("br"),n._v("Đường chủ lệnh, đại biểu Danh Sư đường uy nghiêm cùng địa vị, không thể sai sót!"),t("br"),t("br"),n._v("Tê lạp!"),t("br"),t("br"),n._v("Không khí bị xé nứt, bốn phía xuất hiện vặn vẹo."),t("br"),t("br"),n._v("Có thể trở thành quyền điện chủ, Nhậm Thanh Viễn thực lực, so với Trương Vô Ngân cường đại không ít, toàn lực ra tay, đám người chỉ cảm thấy, bốn phía thanh âm, ánh sáng đều bị tước đoạt đồng dạng, trước mắt chỉ còn lại có đen kịt một mảnh, phảng phất thiên địa đều trong nháy mắt bị xé nứt."),t("br"),t("br"),n._v("Đương nhiên, cũng không phải là chân chính xé rách thiên địa, mà là một loại cảm quan bên trên ảo giác, một chiêu này, đã có thể ảnh hưởng đến nguyên thần cùng linh hồn, làm cho không người nào có thể phân biệt ra được thật giả, còn là giả tạo."),t("br"),t("br"),n._v("“Thực lực rất mạnh, nhưng mà muốn bắt ta, còn có chút không đủ...”"),t("br"),t("br"),n._v("Trên không vu hồn, nhẹ nhàng cười một tiếng, thân ảnh chậm rãi trở thành nhạt, thời gian nháy mắt liền biến mất trên không trung, phảng phất chưa hề xuất hiện qua đồng dạng."),t("br"),t("br"),n._v("“Là thất truyền !” Lạc Khinh Trần con ngươi co rút lại."),t("br"),t("br"),n._v("“Vu Hồn Xuyên Toa?” Trương Vô Ngân không hiểu."),t("br"),t("br"),n._v("“Vu hồn vô hình vô chất, chỉ cần ý niệm đầy đủ, hoàn toàn có thể phá vỡ không gian ràng buộc, tiến hành xuyên qua, vật chất không cách nào ngăn cản... Tuy là không thể cùng truyền tống trận đồng dạng cự ly xa truyền tống, nhưng cự ly ngắn nhảy, cũng chiếm cứ ưu thế cực lớn!”"),t("br"),t("br"),n._v("Lạc Khinh Trần giải thích."),t("br"),t("br"),n._v("Nhờ vào huyết mạch, hắn đối không gian nắm giữ so các vị ở tại đây đều cường đại hơn không ít, đối loại này xuyên qua độ khó biết rất rõ ràng."),t("br"),t("br"),n._v("Thân thể xuyên qua, cần kinh nghiệm đủ loại không gian phong bạo, khó mà chống lại, vu hồn liền sẽ dễ dàng không ít, có thể đột phá không gian ràng buộc... Nhậm Thanh Viễn thực lực mặc dù cực mạnh, nhưng muốn bắt lại, chỉ sợ cũng không dễ dàng như vậy."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Lời còn chưa dứt, vừa mới biến mất vu hồn, đã xuất hiện tại một cái cửu tinh danh sư trước mặt."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Vừa xuất hiện, một cỗ đặc thù lực lượng, liền bao phủ mà ra, thẳng tắp đối vị này cửu tinh danh sư đâm đến."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Còn không có kịp phản ứng, vị danh sư này liền bị đánh trúng, bay ngược mà ra, va sụp vô số kiến trúc."),t("br"),t("br"),n._v("May mắn Nhậm Thanh Viễn đám người tới trước đó, biết sẽ gây ra cực lớn động tĩnh, để địa phương Danh Sư đường sơ tán rồi người bình thường, nếu không, không biết phải có bao nhiêu vô tội sinh mệnh chết tại đây thoáng cái va chạm bên trong."),t("br"),t("br"),n._v("“Ngươi... Chuyển đi sức mạnh của ta...”"),t("br"),t("br"),n._v("Vội vàng dừng tay, Nhậm Thanh Viễn trong mắt lửa giận cuồn cuộn."),t("br"),t("br"),n._v("Dùng nhãn lực của hắn, có thể nhẹ nhõm nhìn ra, đối phương tiến hành Vu Hồn Xuyên Toa thời điểm, đem hắn công kích một phần lực lượng, cũng cuốn đi, lúc này mới một lần đem đồng bạn của mình, tại chỗ đánh thành trọng thương."),t("br"),t("br"),n._v("“Không sai, tới đi, tiếp tục tiến công, ta nhìn các ngươi cửu tinh danh sư, còn lại rất nhiều, có thể từng cái đánh bại...”"),t("br"),t("br"),n._v("Khóe miệng nâng lên, vu hồn cười nhạt một tiếng."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),n._v("Nắm đấm nắm “Kẽo kẹt! Kẽo kẹt!” Vang vọng, Nhậm Thanh Viễn sắp nổ tung."),t("br"),t("br"),n._v("Đối phương có thể xuyên qua, công kích của hắn, liền không có bất kỳ chỗ dùng nào, một khi lần nữa chuyển dời đến cái khác danh sư trên người, chẳng phải tương đương, bản thân đem người một nhà đả thương?"),t("br"),t("br"),n._v("“Có thể tiến hành Vu Hồn Xuyên Toa vu hồn sư, thấp nhất đều đạt đến cửu tinh, vu hồn truyền thừa không phải đoạn tuyệt sao? Ngươi đến cùng là ai? Từ đâu tới...”"),t("br"),t("br"),n._v("Cũng nhịn không được nữa, lớn tiếng hỏi."),t("br"),t("br"),n._v("Nắm giữ loại thực lực này, tuyệt không phải một làn liền thành, làm sao Danh Sư đường, chưa từng nghe từng tới bất cứ tin tức gì?"),t("br"),t("br"),n._v("“Vu hồn là bị Danh Sư đường chỗ tiêu diệt, nhưng truyền thừa cũng không triệt để đoạn tuyệt... Được rồi, ngươi còn tấn công không tấn công, không tấn công, ta muốn nghỉ ngơi...”"),t("br"),t("br"),n._v("Nhàn nhạt nhìn qua, vu hồn trong mắt mang theo trêu tức."),t("br"),t("br"),n._v("“Đem đường chủ lệnh giao ra, nếu không, Danh Sư đường có thể diệt đi ngươi vu hồn sư một lần, cũng có thể diệt lần thứ hai...”"),t("br"),t("br"),n._v("Nhậm Thanh Viễn quát tháo, bàn chân bỗng nhiên trên mặt đất đạp mạnh."),t("br"),t("br"),n._v("Tê lạp!"),t("br"),t("br"),n._v("Không gian bốn phía giống như là đóng băng đồng dạng, muốn xé rách, gần như không có khả năng làm được."),t("br"),t("br"),n._v("Võ kỹ, đóng băng không gian!"),t("br"),t("br"),n._v("Có thể đem không gian ba động triệt để cầm cố lại, truyền tống trận pháp đều sẽ mất đi hiệu lực!"),t("br"),t("br"),n._v("Vừa rồi cố ý lấy cớ hỏi thăm, trên thực tế lại tại tích trữ lực lượng, thi triển ra chiêu này."),t("br"),t("br"),n._v("“Ta nhìn ngươi còn thế nào chạy trốn!”"),t("br"),t("br"),n._v("Đông cứng không gian, thở phào nhẹ nhõm, Nhậm Thanh Viễn lông mày nâng lên, cổ tay khẽ đảo, một thanh trường kiếm xuất hiện tại lòng bàn tay, chỉ vào không trung, thẳng tắp đối trước mắt vu hồn liền đâm tới."),t("br"),t("br"),n._v("Kiếm này mang theo nồng đậm sát khí, còn chưa tới đến trước mặt liền tựa như biến thành thực chất, liền xem như vu hồn, chính diện đụng tới, cũng khó có thể chạy trốn."),t("br"),t("br"),n._v("Nhìn thấy kiếm này uy lực, biết không tránh né được, vu hồn cũng không có bối rối, ngược lại nhẹ nhàng cười một tiếng, to lớn thân hình đột nhiên thu nhỏ, trong nháy mắt trở nên cùng lệnh bài đồng dạng lớn, trực tiếp trốn ở đường chủ lệnh đằng sau."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Không nghĩ tới cái tên này, lại còn có thể thu nhỏ, tiếp tục nữa, kiếm này tất nhiên sẽ đâm vào đường chủ lệnh bên trên, Nhậm Thanh Viễn vội vàng trên trường kiếm giương cao, thu về lực lượng."),t("br"),t("br"),n._v("Bởi vì chân khí thu quá nhanh, ngực mơ hồ cảm giác đau đớn, cũng nhịn không được nữa, một ngụm máu tươi phun tới."),t("br"),t("br"),n._v("“Ha ha, ngươi có thể tiếp tục động thủ, dù sao cái lệnh bài này tại đây, ngươi cũng không giết được ta!”"),t("br"),t("br"),n._v("Gặp hắn thu về lực lượng, vu hồn nhẹ nhàng cười một tiếng, lần nữa biến trở về trước đó bộ dáng."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Nhậm Thanh Viễn híp mắt lại, ngực không ngừng xúi giục, dường như cả người đều nhanh muốn bùng nổ."),t("br"),t("br"),n._v("Một cái vạn năm không dám đi ra độc sư, một cái không biết bao lâu trước cũng đã biến mất chức nghiệp... Vậy mà ép hắn có lửa không phát ra được, đổi lại trước đó có người cùng hắn nói như vậy, đều chắc chắn sẽ không tin tưởng."),t("br"),t("br"),n._v("“Đường chủ lệnh, nắm giữ các đời quyền đường chủ ý niệm, ngươi tuy là dùng vu hồn cướp đi, ngăn cách ý niệm của ta quan sát, nhưng mà vĩnh viễn không cách nào khống chế, thậm chí, còn muốn thời khắc đề phòng bị phản phệ bị thương!”"),t("br"),t("br"),n._v("Hít sâu một hơi, đem phẫn nộ trong lòng áp chế lại, Nhậm Thanh Viễn lần nữa nhìn qua: “Nếu như ta không có đoán sai, hiện tại ngươi cứ lấy lấy đường chủ lệnh, nhưng linh hồn cùng tiếp xúc bàn ủi đồng dạng nóng hổi đi!”"),t("br"),t("br"),n._v("Đường chủ lệnh, đại biểu Danh Sư đường tôn nghiêm, nếu là người người đều có thể cướp đi, cũng không có cái gì ý nghĩa."),t("br"),t("br"),n._v("Đối phương thoạt nhìn cầm đi đồ vật, chính mình cũng không có cách nào cướp về, nhưng trên thực tế, chỉ sợ đang thừa nhận các đời tiên tổ ý niệm công kích, không cách nào tự kềm chế."),t("br"),t("br"),n._v("“Thứ này quả thực có chút phỏng tay...”"),t("br"),t("br"),n._v("Vu hồn cười cười: “Bất quá, hiện tại còn không thể trả lại ngươi!”"),t("br"),t("br"),n._v("Nói xong, không tiếp tục để ý đối phương, thân thể nhoáng một cái, rơi trên mặt đất, đầu gối mềm nhũn quỳ rạp xuống đất."),t("br"),t("br"),n._v("“Đồ nhi Lộ Xung... Trở về!”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1544"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1546"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);