(window.webpackJsonp=window.webpackJsonp||[]).push([[623],{978:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1538-danh-su-đuong-chay"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1538-danh-su-đuong-chay"}},[n._v("#")]),n._v(" Chương 1538: Danh Sư đường chạy")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1538: Danh Sư đường chạy"),t("br"),t("br"),n._v("Nếu là đường chủ lệnh, nhất định nắm giữ hiệu lệnh tất cả danh sư năng lực, thật có thể luyện hóa, có lẽ liền có thể giải quyết trước mặt khốn khó, không cần lưỡng bại câu thương."),t("br"),t("br"),n._v("“Thiếu hụt!”"),t("br"),t("br"),n._v("Nắm chặt lệnh bài, Trương Huyền tinh thần khẽ động."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Một quyển sách xuất hiện tại trong óc."),t("br"),t("br"),n._v("“Danh Sư đường đường chủ lệnh, Cổ Thánh chí bảo, dùng Khổng sư ba giọt Thánh huyết, dung luyện mà thành, đối linh hồn nắm giữ cực mạnh lực công kích... Thiếu hụt: Không cách nào điều tra!”"),t("br"),t("br"),n._v("“Không cách nào điều tra? Cái này sao có thể?”"),t("br"),t("br"),n._v("Trương Huyền nhịn không được ngẩn ngơ."),t("br"),t("br"),n._v("Cùng nhau đi tới, vũ khí gặp qua không biết bao nhiêu, đủ loại kiểu dáng, muôn hình muôn vẻ, bất kể lợi hại hơn nữa đại sư luyện chế, đều sẽ có thiếu hụt, tại thiên đạo thư viện trước mặt, không có chút nào bí mật đáng nói, trước mắt cái này, thế mà không cách nào điều tra..."),t("br"),t("br"),n._v("Chuyện gì xảy ra?"),t("br"),t("br"),n._v("“Chẳng lẽ Khổng sư cũng là Diễn Hư thể chất?”"),t("br"),t("br"),n._v("Trong lòng hơi động."),t("br"),t("br"),n._v("Diễn Hư thể chất, có thể để cho thiên cơ sư không cách nào điều tra, thậm chí thư viện đều khó mà thẩm tra, tựa như hắn, thư viện có thể nhìn thấu những người khác thiếu hụt, tìm ra sơ hở, nhưng mà chính hắn, thậm chí liên quan tới chính mình sự tình, liền không có quá tác dụng lớn chỗ."),t("br"),t("br"),n._v("Bởi vậy, nói thư viện, điều tra không ra hắn tình huống, cũng không thành vấn đề."),t("br"),t("br"),n._v("Cái đường chủ này lệnh, là từ Khổng sư ba giọt huyết dịch dung luyện mà thành, đồng dạng điều tra không ra, có thể hay không bày tỏ, người sau, cũng giống như mình, đồng dạng là Diễn Hư thể chất?"),t("br"),t("br"),n._v("“Có lẽ vậy...”"),t("br"),t("br"),n._v("Trầm tư một chút, ngay sau đó biết."),t("br"),t("br"),n._v("Liền Trương gia vị kia tiểu thiên tài, Lạc gia tiểu công chúa đều là, Khổng sư loại này đại nhân vật, là loại thể chất này cũng liền nằm trong dự liệu."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, mới có thể tung hoành thiên địa, Dị Linh tộc nhân tiêu hao vô số tâm huyết, đều bắt không được, giết không chết, cuối cùng khai sáng ra nhân loại thịnh thế."),t("br"),t("br"),n._v("“Không biết thiếu hụt... Làm sao luyện hóa?”"),t("br"),t("br"),n._v("Nhíu nhíu mày."),t("br"),t("br"),n._v("Trước kia luyện hóa vũ khí, đều là tìm được trước thiếu sót, lại thêm dùng lợi dụng, nhẹ nhõm luyện hóa, nếu không, dựa theo bình thường thủ đoạn, bình thường Thánh cấp vũ khí, cần một, hai năm rèn luyện, về thời gian đầu tiên liền tiêu hao không nổi."),t("br"),t("br"),n._v("“Thử xem huyết dịch!”"),t("br"),t("br"),n._v("Ngón tay một điểm, một giọt máu, từ đầu ngón tay bay ra rơi vào trên lệnh bài."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Lệnh bài giống như là nhẵn bóng lá sen, huyết dịch ở phía trên trượt, không có bất kỳ cái gì hấp thu bộ dáng, nhấp nhô vài vòng, “Lạch cạch!” Một tiếng, rơi trên mặt đất."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trương Huyền lông mày nhảy một cái."),t("br"),t("br"),n._v("Bình thường vũ khí, chỉ cần huyết dịch dính lên, liền sẽ lập tức hấp thu, đem bản thân một ý niệm hòa vào trong đó, nhưng cái này, rõ ràng không phải như vậy, còn lần đầu tiên nhìn thấy đối huyết dịch, không có chút nào hấp thu đồ vật."),t("br"),t("br"),n._v("“Cùng ta rất thân cận, nhưng không hấp thu huyết dịch, cái này... Làm sao luyện hóa?”"),t("br"),t("br"),n._v("Cái lệnh bài này nắm bắt tới tay tâm, cảm giác một hồi thân thiết, còn tưởng rằng luyện hóa mười phần đơn giản, làm sao cũng không nghĩ đến, so những binh khí khác càng khó..."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Đang tràn đầy xoắn xuýt, lần nữa nghe được trên không nổ vang, ngẩng đầu nhìn lại, chỉ thấy bốn phía trận pháp đã triệt để bố trí xong, Nhậm Thanh Viễn đám người, từng cái lạnh lùng nhìn qua, giống như là nhìn lấy dê đợi làm thịt."),t("br"),t("br"),n._v("“Được rồi, không có thời gian luyện hóa, trước thu lại, quay đầu nghiên cứu...”"),t("br"),t("br"),n._v("Cổ tay khẽ đảo tướng lệnh bài thu vào nhẫn."),t("br"),t("br"),n._v("Đối mặt hắn thu lấy, cái này viên đường chủ lệnh cũng không phản kháng, mười phần ngoan ngoãn."),t("br"),t("br"),n._v("Loại cảm giác này, liền giống như kiếp trước điểu ti ưa thích nữ thần đồng dạng, có thể hẹn, có thể nói chuyện phiếm, nhưng muốn tiến thêm một bước thời điểm, nhưng vô tình bị đẩy ra, ngươi cho rằng là tình yêu, trên thực tế... Chỉ là cái lốp xe dự phòng!"),t("br"),n._v(" Cái này viên đường chủ lệnh hiện tại chính là như vậy, sờ tới sờ lui mười phần thuận theo, thu đến trong giới chỉ cũng không phản kháng, có thể nghĩ luyện hóa, cũng rất khó xử đến..."),t("br"),t("br"),n._v("“Trương Huyền, nếu như còn nhớ tới trước kia thân phận là danh sư, xin đem đường chủ lệnh giao ra! Nếu không, Độc điện cùng vu hồn sư, cũng sẽ ở ngày hôm nay trở thành lịch sử...”"),t("br"),t("br"),n._v("Nhậm Thanh Viễn lạnh lùng nhìn qua."),t("br"),t("br"),n._v("Trước đó, hắn còn muốn lấy, có thể hay không đem vị này bắt sống, khống chế lại, bây giờ đối phương đem đường chủ lệnh đều cướp đi, thậm chí còn nhận được trong giới chỉ, đã không còn khả năng cứu vãn."),t("br"),t("br"),n._v("Trước mặt mọi người cướp đi đường chủ lệnh, tương đương với bên ngoài hao tổn Danh Sư đường tôn nghiêm, lại thêm hắn hai cái học sinh, một vị khống chế Độc điện, một vị là vu hồn đại năng... Không xử lý, đã khó mà khống chế."),t("br"),t("br"),n._v("“Trở thành lịch sử? Vậy liền nhìn một chút các ngươi có bao nhiêu mạng người tới lấp!”"),t("br"),t("br"),n._v("Một tiếng hừ nhẹ, Ngụy Như Yên bàn tay lăng không một trảo, một đoàn khí độc, lần nữa phân tán bốn phía ra, đem Trương Huyền đám người bao phủ, như là một cái lồng ánh sáng."),t("br"),t("br"),n._v("Bất kể chân khí còn là lực lượng, chỉ cần đi vào lồng ánh sáng, liền sẽ chạm phải khí độc, cửu tinh danh sư đều khó mà may mắn thoát khỏi."),t("br"),t("br"),n._v("“Không biết sống chết! Mọi người nghe lệnh, vận chuyển Khôi Tinh trận!”"),t("br"),t("br"),n._v("Cười lạnh, Nhậm Thanh Viễn trường kiếm trong tay lần nữa nâng lên."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Chung quanh cửu tinh danh sư, lập tức chuyển động thân hình, thời gian nháy mắt, vô số lực lượng mượn nhờ bốn phía kiến trúc cùng trận pháp, hội tụ vào một chỗ, tập trung ở mũi kiếm của hắn phía trên, mang theo tựa như như lôi đình mạnh mẽ khí tức."),t("br"),t("br"),n._v("“Phá!”"),t("br"),t("br"),n._v("Lực lượng tập trung, kiếm mang bắn ra, như là một đạo dải lụa màu vàng óng, phá không ép xuống mà đến, không gian nứt ra một đạo thô to vết rách, chắc chắn khí độc, tới đụng một cái, không có kiên trì quá lâu, liền xé rách giấy đồng dạng, chia hai đoạn."),t("br"),t("br"),n._v("“Hừ!”"),t("br"),t("br"),n._v("Một tiếng gầm thét, Lộ Xung lần nữa biến thành mấy chục mét lớn nhỏ, to lớn hồn thể, bỗng nhiên xuyên qua, nứt ra không gian, giống như là bị may vá quần áo đồng dạng, từ hồn tuyến dẫn dắt, lần nữa khép lại."),t("br"),t("br"),n._v("“Mượn dùng hồn lực, đem nứt ra không gian may vá... Không hổ là hồn sư, quả nhiên có chút thủ đoạn, có điều, ta nhìn ngươi có thể kiên trì bao lâu!”"),t("br"),t("br"),n._v("Cười lạnh, Nhậm Thanh Viễn trường kiếm trong tay tiếp tục ép xuống."),t("br"),t("br"),n._v("Tê lạp!"),t("br"),t("br"),n._v("Kiếm mang như điện, mang theo không thể ngăn cản lực lượng."),t("br"),t("br"),n._v("Phốc!"),t("br"),t("br"),n._v("Lộ Xung hơi đỏ mặt, thân thể kìm lòng không được rơi xuống dưới, tựa hồ có chút không kiên trì nổi."),t("br"),t("br"),n._v("Thực lực của hắn mặc dù không yếu, nhưng mười mấy vị cửu tinh danh sư phối hợp đại trận, lại thêm Nhậm Thanh Viễn tự thân ra tay, căn bản không chống lại được."),t("br"),t("br"),n._v("Khí độc bị xé nứt, Ngụy Như Yên vẻ mặt cũng biến thành phấn trắng, trên đầu mồ hôi phả ra."),t("br"),t("br"),n._v("“Lão sư, chúng ta sắp không kiên trì nổi...”"),t("br"),t("br"),n._v("Cũng nhịn không được nữa, nhìn lại."),t("br"),t("br"),n._v("“Ừm...”"),t("br"),t("br"),n._v("Trương Huyền gật gật đầu."),t("br"),t("br"),n._v("Hai cái đệ tử ngăn cản công kích, hắn cũng không có nhàn rỗi, thừa dịp hiện tại công phu, đem trận pháp nhìn kỹ một lần, tìm ra không ít thiếu hụt, chỉ bất quá, những này thiếu hụt, cách nơi này đều có chút khoảng cách, dùng hắn tình huống hiện tại, căn bản đủ không đến."),t("br"),t("br"),n._v("“Ta nói cho các ngươi biết một chỗ, hai người các ngươi ai nghĩ biện pháp lao ra, phá đi, liền có thể giải quyết trận pháp này...”"),t("br"),t("br"),n._v("Dừng lại một chút, Trương Huyền truyền âm dặn dò, lời nói mới nói một nửa, liền thấy trên không kiếm mang đột nhiên “Tạch tạch!” Một tiếng tựa như bẻ gãy đồng dạng, vốn đằng đằng sát khí Nhậm Thanh Viễn, khí tức cứng lại, suýt chút nữa từ không trung rơi xuống, hơi đỏ mặt, một ngụm máu tươi phun ra."),t("br"),t("br"),n._v("“Làm sao vậy?”"),t("br"),t("br"),n._v("Ổn định khí tức, Nhậm Thanh Viễn xoay người hét lớn."),t("br"),t("br"),n._v("Ngay tại vừa rồi, trận pháp đột nhiên xuất hiện biến cố, nếu không phải hắn thực lực mạnh mẽ, chỉ sợ lần này, đã bị đánh chết tươi."),t("br"),t("br"),n._v("“Bẩm báo quyền đường chủ, không biết chuyện gì xảy ra... Trận pháp hạch tâm Danh Sư đường, đột nhiên... Đột nhiên chạy!”"),t("br"),t("br"),n._v("Một cái danh sư bay tới, bờ môi run rẩy."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1546"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1548"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);