(window.webpackJsonp=window.webpackJsonp||[]).push([[626],{981:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-146-thuong-y"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-146-thuong-y"}},[n._v("#")]),n._v(" Chương 146: Thương ý")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v(" Tiểu trong đình, Vương Sùng, Âu Dương Thành đối diện chè chén."),t("br"),t("br"),n._v(" “Ngươi nói vị này Trương đan sư, là Luận đan thông qua sát hạch?” "),t("br"),t("br"),n._v(" Vương Sùng đến bây giờ còn có chút không thể tin được."),t("br"),t("br"),n._v(" Luận đan hầu như là danh sư độc quyền, cao to bên trên tồn tại, một cái không đủ hai mươi thanh niên thành công thông qua, coi như là chính tai nghe, đều tràn đầy không thể tin được."),t("br"),t("br"),n._v(" “Đúng đấy, Trương đan sư đối với đan dược nắm giữ vẫn còn trên ta, chân chính anh hùng xuất thiếu niên!” Âu Dương Thành cảm khái."),t("br"),t("br"),n._v(" “Đan dược bên trên lợi hại, đối với thương pháp nhận thức thì có chút quá nông cạn, thương pháp không phải chơi nhà chòi, cũng không phải tùy tiện xem một cái liền có thể học được, nếu như dễ dàng như vậy, như thế nào sẽ làm ta chìm đắm cả đời.” "),t("br"),t("br"),n._v(" Trên mặt lộ ra tự kiêu, Vương Sùng trong mắt mang theo sự tự tin mạnh mẽ."),t("br"),t("br"),n._v(" “Đúng đấy, thương là hết thảy trong binh khí khó nhất, muốn có chỗ chiến tích nói nghe thì dễ.” "),t("br"),t("br"),n._v(" Rất nhiều vũ khí, thương là khó nhất luyện tập một loại, không ít người tu luyện tràn đầy tự tin học tập, đến cuối cùng, tất cả đều nửa bình ầm, vẽ hổ không được phản giống như khuyển."),t("br"),t("br"),n._v(" Cảm khái một tiếng, Âu Dương Thành đột nhiên nhớ tới cái gì, nhìn sang: “Ngươi thư phòng không có không truyền bên ngoài thương pháp bí tịch đi!” "),t("br"),t("br"),n._v(" Trương Huyền là hắn mang tới, vạn nhất lão hữu bị vướng bởi mặt mũi, tiết lộ cái gì không muốn truyền ra ngoài bí bản lĩnh, thật liền thật không tiện."),t("br"),t("br"),n._v(" “Không có, ta tự nghĩ ra thương pháp cùng gia truyền bí tịch, đều thuộc làu trong lòng, miệng tai truyền thụ, không ghi chép văn tự, người khác coi như muốn học cũng học không tới!” "),t("br"),t("br"),n._v(" Vương Sùng cười cợt: “Trong thư phòng, đều là chút thương pháp cơ sở cùng nhập môn, đối với vừa mới tiếp xúc thương pháp người, có giúp đỡ rất lớn, không tính là gì, để vị này Trương đan sư xem một cái cũng tốt, xem xong thì nên biết thương pháp độ khó, quên trước suy nghĩ ấu trí!” "),t("br"),t("br"),n._v(" Chưa bao giờ tiếp xúc qua thương pháp, liền muốn học tập làm sao dạy người, đùa gì thế!"),t("br"),t("br"),n._v(" Thương pháp nếu như đơn giản như vậy, hắn lại làm sao có khả năng nổi danh như vậy, thậm chí Thẩm Truy bệ hạ đều chính miệng tán dương?"),t("br"),t("br"),n._v(" Thương pháp đại sư, không phải là không duyên cớ chiếm được tên gọi."),t("br"),t("br"),n._v(" Điểm ấy, hắn có niềm tin tuyệt đối cùng dựa dẫm."),t("br"),t("br"),t("span",[t("br"),n._v("http://ngantruyen.com/"),t("br")]),n._v(" “Này ngược lại là...” Âu Dương Thành gật đầu."),t("br"),t("br"),n._v(" Bất luận một cái nào binh khí, một bộ võ kỹ, đều không phải ung dung luyện thành, đều cần ngày tiếp nối đêm luyện tập cùng lặp đi lặp lại đánh bóng, đơn giản có thể học được, cao thủ cũng sẽ không đáng giá."),t("br"),t("br"),n._v(" “Vị này Trương đan sư, vẫn là tuổi trẻ, không biết mức độ, một lúc nữa, xem xong thư tịch, ta triển lộ một bộ thương pháp, để hắn biết khó mà lui, liền hẳn là sẽ không suy nghĩ lung tung!” "),t("br"),t("br"),n._v(" Biết Trương Huyền luyện đan bên trên nắm giữ thiên phú kinh người, Vương Sùng tuy đối với hắn việc làm có chút bất mãn, lại cũng sẽ không xem thường."),t("br"),t("br"),n._v(" “Tốt, lời nói như vậy chắc hẳn sẽ hết hy vọng, chuyên tâm luyện đan, ta còn muốn chúng ta luyện đan sư công hội, ra một vị nhị tinh luyện đan sư đây...” "),t("br"),t("br"),n._v(" Âu Dương Thành cười cợt, chính muốn nói tiếp, liền gặp trước mắt Vương Sùng đột nhiên thân thể cứng đờ, tiếp theo liên tục run rẩy."),t("br"),t("br"),n._v(" “Leng keng!” "),t("br"),t("br"),n._v(" Chén rượu trong tay ngã xuống đất biến thành bụi phấn cũng không biết."),t("br"),t("br"),n._v(" “Vương Sùng...” "),t("br"),t("br"),n._v(" Nhìn thấy một vị thực lực tại Thiên Huyền thành đều có tên tuổi thương pháp đại sư, một hồi biến thành như vậy, Âu Dương Thành sợ hết hồn."),t("br"),t("br"),n._v(" Không nghe nói vị lão hữu này thân thể có bệnh a!"),t("br"),t("br"),n._v(" Làm sao nói nói, lại đột nhiên giật?"),t("br"),t("br"),n._v(" “Là thương ý, thương ý!” "),t("br"),t("br"),n._v(" Đang suy nghĩ dùng đan dược gì cứu người, liền gặp lão hữu khôi phục như cũ, hai mắt tỏa ánh sáng, như là nhìn thấy ngàn tỉ bảo tàng."),t("br"),t("br"),n._v(" “Thương ý? Cái kia là cái gì?” "),t("br"),t("br"),n._v(" Thấy hắn không có chuyện gì, Âu Dương Thành thở phào nhẹ nhõm, tràn đầy kỳ quái nhìn sang."),t("br"),t("br"),n._v(" Rốt cuộc là thứ gì, có thể làm cho luôn luôn bình tĩnh, vững như Thái sơn lão hữu biến thành như vậy?"),t("br"),t("br"),n._v(" “Tu luyện kiếm pháp, đạt đến cảnh giới cao thâm, có thể hình thành kiếm ý, thương cũng như thế, có thể ngưng tụ thương ý cao thủ, mới thật sự là thương pháp đại sư. Những năm này ta ít giao du với bên ngoài, chính là muốn mài giũa nội tâm, tranh thủ sớm ngày đạt đến cảnh giới này, đáng tiếc vẫn không tìm được điểm đột phá... Phủ đệ khi nào đến rồi một vị cao thủ, hoặc là có người so với ta sớm đột phá? Qua xem một chút!” "),t("br"),t("br"),n._v(" Đột nhiên đứng dậy, Vương Sùng vội vàng hướng cảm nhận được thương ý phương hướng đi đến."),t("br"),t("br"),n._v(" Ngưng tụ thương ý, trường thương chỗ đến, đều là thương pháp."),t("br"),n._v("  Hắn vẫn làm cái mục tiêu này mà phấn đấu, nhưng thủy chung không tìm được lối vào, nằm mơ đều không nghĩ tới, bản thân trong sân xuất hiện, đến cùng là ai?"),t("br"),t("br"),n._v(" Hai người không có dừng lại, vội vã hướng vừa nãy thương ý phóng thích địa phương đi tới, đúng dịp thấy Trương Huyền theo thư phòng chậm rãi đi ra."),t("br"),t("br"),n._v(" “Người đâu?” Còn chưa tới đến, Vương Sùng liền gấp gáp hỏi."),t("br"),t("br"),n._v(" “Người? Người nào?” Trương Huyền không biết đối phương có ý gì."),t("br"),t("br"),n._v(" Hắn vẫn đọc sách, sau đó lại tu luyện Thiên Đạo thương pháp, căn bản không chú ý bên ngoài, làm sao? Đến kẻ địch rồi?"),t("br"),t("br"),n._v(" “Ta cảm nhận được một đạo kinh người thương ý, ở ngay gần, sao không có ai vậy?” "),t("br"),t("br"),n._v(" Thấy hắn không biết, Vương Sùng vội vàng tìm kiếm khắp nơi, bất quá xung quanh trống không, nào có nửa bóng người."),t("br"),t("br"),n._v(" “Có thể hay không là ngươi nghĩ từ lâu thành bệnh, cảm ứng sai rồi?” Âu Dương Thành nhìn sang."),t("br"),t("br"),n._v(" Bản thân vị lão hữu này, mỗi ngày nghĩ thương ý, có thể hay không thời gian dài xuất hiện ảo giác?"),t("br"),t("br"),n._v(" Bằng không, bản thân làm sao không cảm ứng được cái gì?"),t("br"),t("br"),n._v(" “Ta khoảng cách thương ý chỉ thiếu chút nữa ngăn cách, mỗi ngày đều sáng nhớ chiều mong, vừa nãy tuyệt đối có một đạo thương ý xông thẳng bầu trời, không thể có sai lầm!” "),t("br"),t("br"),n._v(" Vương Sùng đầy mặt kiên định."),t("br"),t("br"),n._v(" Hắn mỗi ngày nghiên cứu thương pháp, từ lâu nhập ma thành si, nếu như lại đem thương ý lầm, thật liền mất mặt."),t("br"),t("br"),n._v(" “Dừng lại, dừng lại, các ngươi nói thương ý là cái gì?” "),t("br"),t("br"),n._v(" Xem hai người thảo luận kịch liệt, Trương Huyền cũng không nhịn được nữa."),t("br"),t("br"),n._v(" Hắn xem thư tịch, đều là đơn giản nhất cơ sở, thương ý đã dính đến tầng thứ càng cao hơn, cùng không có ghi chép, vì lẽ đó, cũng không biết."),t("br"),t("br"),n._v(" “Thương ý là đúng thương pháp lĩnh ngộ được cực hạn, mới có thể cảm ngộ đến đặc thù ý cảnh...” "),t("br"),t("br"),n._v(" Vương Sùng giải thích một câu, tiếp theo lắc lắc đầu: “Ngươi chưa bao giờ học được thương pháp, nói rồi cũng không hiểu, giải thích nhiều hơn nữa cũng là uổng phí...” "),t("br"),t("br"),n._v(" Mặc dù đối phương không nói tỉ mỉ, nhưng Trương Huyền xem như là làm rõ xảy ra chuyện gì: “Ngươi ở đây tìm, lẽ nào vừa nãy có người ở đây phóng thích thương ý?” "),t("br"),t("br"),n._v(" “Ta cũng không xác định, vừa nãy mơ hồ cảm ứng được, ngay ở chung quanh đây...” Vương Sùng gật đầu."),t("br"),t("br"),n._v(" Cái kia cỗ thương ý, xuất hiện thời gian rất ngắn, lóe lên liền qua, mặc dù là hắn, cũng chỉ là có cái cảm giác mơ hồ, không cách nào xác định xác thực vị trí, bằng không, khẳng định trực tiếp đi tìm đi tới, cần gì tại đây băn khoăn."),t("br"),t("br"),n._v(" “Đúng rồi, ngươi liền ở ngay đây, lẽ ra có thể rõ ràng xác định vị trí!” "),t("br"),t("br"),n._v(" Nhớ tới cái gì, Vương Sùng hai mắt tỏa ánh sáng nhìn sang."),t("br"),t("br"),n._v(" Bản thân cách khá xa, không cảm ứng được vị trí cụ thể, trước mắt cái này Trương đan sư không giống a, hắn liền ở ngay đây, nhất định có thể tìm tới."),t("br"),t("br"),n._v(" Một khi xác định địa phương, đến cùng là người trong nhà vẫn là bên ngoài đến, liền có thể rõ ràng."),t("br"),t("br"),n._v(" Phải biết, làm thương pháp thế gia, cả gia tộc, người người luyện tập thương, thậm chí hộ vệ đều sẽ, vạn nhất ai ngẫu nhiên lĩnh ngộ thương ý, ngậm miệng không nói mà nói, còn không biết, tìm tới xác thực vị trí liền đơn giản."),t("br"),t("br"),n._v(" “Có thể... Ta không biết thương ý là cái gì?” Trương Huyền chần chờ một chút."),t("br"),t("br"),n._v(" “Cái này đơn giản, ta tuy rằng còn không lĩnh ngộ thương ý, nhưng cũng có thể mô phỏng theo cái bảy, tám phần, chính là loại khí tức này!” "),t("br"),t("br"),n._v(" Vương Sùng thân thể hơi động, một luồng ác liệt khí tức phả vào mặt, như là có thể mang bất luận là đồ vật gì đều đâm thủng."),t("br"),t("br"),n._v(" “Đây chính là thương ý?” Trương Huyền tràn đầy quái lạ."),t("br"),t("br"),n._v(" “Không sai, chính là loại khí tức này, ngươi biết là nơi nào xuất hiện sao?” Giải trừ khí tức, Vương Sùng há mồm thở dốc, trên đầu liên tục đổ mồ hôi, không nhịn được hỏi."),t("br"),t("br"),n._v(" Tuy chỉ mô phỏng theo một hồi, mạnh mẽ tiêu hao, vẫn như cũ để hắn có chút hư thoát."),t("br"),t("br"),n._v(" “Cái này...” Trương Huyền có chút thật không tiện: “Cái này... Ngươi nói, có phải là cái này?” "),t("br"),t("br"),n._v(" Tiếng nói kết thúc, cả người hắn như là đã biến thành một thanh trường thương, tỏa ra sóng to gió lớn khí tức, khác nào bất cứ lúc nào cũng sẽ đâm thủng bầu trời."),t("br"),t("br"),n._v(" Vương Sùng vừa nãy thương ý cùng với so sánh, quả thực chính là chó đuôi cỏ cùng đại thụ chọc trời chênh lệch."),t("br"),t("br"),n._v(" Không thể giống nhau."),t("br"),t("br"),n._v(" (Ngày hôm nay hừng đông lên giá, sẽ có chương 10 đại bạo phát, cầu đặt mua, vé tháng, đề cử đủ loại giúp đỡ!) "),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-154"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-156"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);