(window.webpackJsonp=window.webpackJsonp||[]).push([[627],{982:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1541-lao-su-xin-đe-hoc-sinh-cho-nguoi-trut-gian"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1541-lao-su-xin-đe-hoc-sinh-cho-nguoi-trut-gian"}},[n._v("#")]),n._v(" Chương 1541: Lão sư, xin để học sinh cho người trút giận")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1541: Lão sư, còn xin để học sinh cho ngươi trút giận!"),t("br"),t("br"),n._v("“Chiến tử, ngươi có ý tứ gì?”"),t("br"),t("br"),n._v("Nhận ra đối phương là ai, Nhậm Thanh Viễn da mặt kìm lòng không được nhảy lên, cố nén ngực lửa giận: “Ngươi phải nhớ kỹ, tất cả mọi thứ ở hiện tại, đều là ai cho, chỉ cần bằng lòng, Danh Sư đường có thể tùy thời tước đoạt đi thuộc về ngươi tất cả!”"),t("br"),t("br"),n._v("“Tước đoạt tất cả?”"),t("br"),t("br"),n._v("Xoay người, Trịnh Dương lắc đầu, chẳng muốn nói nhiều: “Ngươi vui vẻ liền tốt!”"),t("br"),t("br"),n._v("Nói xong, dưới chân nhẹ nhàng đạp mạnh, thẳng tắp hướng Trương Huyền phương hướng bay đi."),t("br"),t("br"),n._v("“Trịnh Dương chiến tử, ngươi đây là thái độ gì?”"),t("br"),t("br"),n._v("Thân thể nhoáng một cái, Khúc trưởng lão ngăn tại phía trước, lửa giận cháy hừng hực, dường như sắp nổ tung."),t("br"),t("br"),n._v("Trước đó mặc dù trúng độc, nhưng thân là cửu tinh danh sư, giải độc linh dược không ít, lại thêm thực lực mạnh mẽ, khôi phục một hồi, đã hành động tự nhiên, không có gì đáng ngại."),t("br"),t("br"),n._v("“Tránh ra!” Trịnh Dương mí mắt vừa nhấc."),t("br"),t("br"),n._v("“Làm càn, đây là cùng cửu tinh danh sư nói chuyện thái độ?”"),t("br"),t("br"),n._v("Khúc trưởng lão lập tức nổ tung, lửa giận vọt tới trên mặt."),t("br"),t("br"),n._v("Vị này Trương Huyền học sinh, một cái so một cái chảnh, Độc điện, hồn sư, khải linh sư công hội hội trưởng, chúng ta không quản được, nhưng Chiến Sư đường lệ thuộc Danh Sư đường, thuộc về thuộc hạ đơn vị, ngươi còn dám hung hăng, quả thực tự tìm cái chết!"),t("br"),t("br"),n._v("“Vậy ngươi muốn cái gì thái độ? Quỳ xuống à?”"),t("br"),t("br"),n._v("Trịnh Dương nhìn qua."),t("br"),t("br"),n._v("“Quỳ xuống khỏi cần, chỉ là để ngươi biết, ngươi có thể có thực lực bây giờ, đều là Danh Sư đường công lao, phải ân báo đáp, không cần thiết tự hủy tương lai...”"),t("br"),t("br"),n._v("Khúc trưởng lão hét lớn, thanh âm còn không có kết thúc, đột nhiên toàn thân lông tơ đột nhiên nổ lên, ngay sau đó nhìn thấy một chuôi trường thương thẳng tắp đâm tới, lăng lệ khí tức, tựa hồ muốn hắn nguyên thần đều quấy thành bụi phấn."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Không dám dừng lại nghỉ, lật bàn tay một cái, trước mặt một thanh trường kiếm xuất hiện, lập tức đón lấy mũi thương, “Đinh!” Một tiếng, cảm thấy một đạo lực lượng hùng hồn dọc theo cánh tay xông tới mà tới, tựa hồ muốn toàn thân hắn kinh mạch đều chấn vỡ."),t("br"),t("br"),n._v("Phốc!"),t("br"),t("br"),n._v("Trường kiếm rời tay, một ngụm máu tươi phun ra, Khúc trưởng lão trong nháy mắt bị chấn bay ngược mà ra, người còn tại trên không, lần nữa nhìn thấy trường thương cán thương, đánh tới."),t("br"),t("br"),n._v("Dọa đến thân thể co rụt lại, song chưởng vội vàng nghênh tiếp."),t("br"),t("br"),n._v("Tạch tạch! Tạch tạch!"),t("br"),t("br"),n._v("Lực lượng khổng lồ bên dưới, hai tay trực tiếp bị chấn động đến trật khớp, trên không thân ảnh như là bị đánh bay bóng chày, thẳng tắp đánh tới hướng mặt đất, đập ra một cái to lớn cái hố, đầy đất khói bụi."),t("br"),t("br"),n._v("“Cho ngươi mặt mũi, để ngươi giáo huấn ta?”"),t("br"),t("br"),n._v("Quất bay Khúc trưởng lão, Trịnh Dương giống như là làm chuyện vi bất túc đạo, tùy tiện vỗ tay một cái, trường thương thu lại."),t("br"),t("br"),n._v("“Khúc trưởng lão...”"),t("br"),t("br"),n._v("“Ngươi muốn làm gì?”"),t("br"),t("br"),n._v("Không nghĩ tới người này không có chút nào cố kỵ, trực tiếp ra tay đem Khúc trưởng lão đánh bay, bốn phía danh sư tất cả đều xôn xao, từng cái vây quanh, khí sắp bùng nổ."),t("br"),t("br"),n._v("“Muốn động thủ với ta?”"),t("br"),t("br"),n._v("Thấy bọn họ vọt tới, Trịnh Dương cũng không căng thẳng, ngược lại ngẩng đầu: “Bọn họ muốn giết các ngươi chiến tử, không muốn không có đường chủ, liền đưa cho ta ngăn lại!”"),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Thanh âm kết thúc, đám người chỉ cảm thấy thấy hoa mắt, không gian bốn phía một hồi vặn vẹo, tám vị lão giả, bất ngờ xuất hiện tại trước mặt, cả đám đều mang theo mạnh mẽ sát phạt chi khí, vừa nhìn liền biết theo trên chiến trường mới đi ra, mang theo thiết huyết hương vị."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Tám vị lão giả đồng thời đem trong tay trường kiếm rút ra, rất nhiều danh sư trước mặt không gian, nhất thời xuất hiện từng đạo vết rách."),t("br"),t("br"),n._v("“Chư vị còn xin dừng bước, tướng ở bên ngoài, quân lệnh có thể không nhận! Chúng ta Chiến Sư đường thật vất vả xuất hiện một vị chiến tử, một vị đường chủ, không muốn ra đương nhiệm gì vấn đề...”"),t("br"),t("br"),n._v("Một cái lão giả nhìn quanh một vòng, cất cao giọng nói."),t("br"),t("br"),n._v("“Ngược, là thật ngược...”"),t("br"),n._v(" Thấy cảnh này, Nhậm Thanh Viễn chỉ cảm thấy sắp nổ, hừng hực liệt hỏa, bất cứ lúc nào cũng sẽ theo ngực thiêu đốt."),t("br"),t("br"),n._v("Danh Sư đường xây dựng vài vạn năm, từ trước tới nay đều là thống ngự người khác, để cho người ta kính sợ cùng bội phục, còn chưa bao giờ có thế lực nào, dám trắng trợn đối nghịch... Ngày hôm nay không chỉ thấy, còn một chút bốn cái..."),t("br"),t("br"),n._v("“Không có cái gì phản không phản, nước có thể chở thuyền cũng có thể lật thuyền, Danh Sư đường nếu như đều là chính xác, không có người sẽ phản bác, mà nếu như đi lên sai lầm con đường, người người đều là phản!”"),t("br"),t("br"),n._v("Cười nhạt một tiếng, Trịnh Dương không tiếp tục để ý vây chung quanh cửu tinh danh sư, thân thể nhoáng một cái, rơi xuống trên mặt đất, hai đầu gối mềm nhũn, quỳ rạp xuống đất."),t("br"),t("br"),n._v("“Đồ nhi Trịnh Dương, gặp qua lão sư!”"),t("br"),t("br"),n._v("“Đứng lên đi!”"),t("br"),t("br"),n._v("Trương Huyền nhẹ gật đầu."),t("br"),t("br"),n._v("Không nghĩ tới mấy tháng không gặp, Trịnh Dương cũng đã trưởng thành, hơn nữa thực lực, càng là đạt đến liền xem như hắn, đều khó mà nhìn thấu tình trạng."),t("br"),t("br"),n._v("“Lão sư, ta nghe nói, Danh Sư đường sở dĩ bên dưới lệnh truy nã, là bởi vì Trương gia người ác nhân tố cáo, nói lão sư ngươi sử dụng Dị Linh tộc nhân bảo vật... Không biết phải hay không phải?”"),t("br"),t("br"),n._v("Đứng dậy, Trịnh Dương cung kính nhìn qua."),t("br"),t("br"),n._v("“Hẳn là a!” Sửng sốt một chút, Trương Huyền ngay sau đó gật đầu."),t("br"),t("br"),n._v("Cụ thể vì sao Danh Sư đường sau đó lệnh truy nã, hắn không rõ ràng lắm, chẳng qua, nguyên nhân gây ra khẳng định cùng Trương gia đại náo liên quan đến."),t("br"),t("br"),n._v("Lúc ấy vì vội vàng đi cứu Triệu Nhã, từng mượn Ngoan Nhân lực lượng, có lẽ là bởi vì cái này, mới để cho hắn như vậy bẩm báo."),t("br"),t("br"),n._v("Danh Sư đường, từ trước tới nay đối Dị Linh tộc nhân cảnh giác, tin vào đối phương, quá mức xử phạt cũng có khả năng."),t("br"),t("br"),n._v("“Lão sư một mình đối kháng Dị Linh tộc nhân, tiêu diệt một hoàng tộc, bọn họ không khen thưởng công lao, ngược lại bởi vì một chút có cũng được mà không có cũng không sao, không thể nào phán đoán đồ vật, đi định tội... Trương gia, thật là lớn khí thế! Nhìn tới, thật đúng là đệ nhất gia tộc tên tuổi, những năm này đem bọn hắn làm hư!”"),t("br"),t("br"),n._v("Cười lạnh, Trịnh Dương ôm quyền khom người: “Lão sư, còn xin để học sinh cho ngươi trút giận!”"),t("br"),t("br"),n._v("“Trút giận?”"),t("br"),t("br"),n._v("Trương Huyền biết mình người học sinh này tính tình nóng nảy, dám nghĩ dám làm, lúc này nhẹ gật đầu: “Đừng giết người, giáo huấn liền tốt!”"),t("br"),t("br"),n._v("“Là!”"),t("br"),t("br"),n._v("Nhẹ gật đầu, Trịnh Dương trường thương trong tay bỗng nhiên run lên, phát ra kịch liệt nổ vang, ngay sau đó hai mắt nhìn quanh một vòng: “Như Yên, Lộ Xung, Trương gia, Thánh tử điện, Băng Nguyên cung khi dễ chúng ta lão sư, vu cáo hãm hại hắn tội danh, các ngươi có bằng lòng hay không, cùng ta cùng một chỗ, thay lão sư báo thù? Lấy chính lão sư uy nghiêm?”"),t("br"),t("br"),n._v("“Đương nhiên!”"),t("br"),t("br"),n._v("Ngụy Như Yên tiến về phía trước một bước."),t("br"),t("br"),n._v("“Việc nghĩa chẳng từ!”"),t("br"),t("br"),n._v("Lộ Xung cũng tới đến trước mặt."),t("br"),t("br"),n._v("“Vậy là tốt rồi...”"),t("br"),t("br"),n._v("Chúng lãng cười một tiếng, Trịnh Dương mí mắt vừa nhấc, mang theo lạnh lùng: “Trương Vô Ngân, ngươi đối ta sư xuất tay, tổn thương hắn không nói, còn hạ mệnh lệnh, tiến hành đuổi bắt cùng vây giết... Nhục sư mối thù, không đội trời chung, ta Trịnh Dương, ngày hôm nay thế sư xuất chiến, ngươi có dám tiếp?”"),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Nói xong trường thương lần nữa run lên, không gian xuất hiện từng đạo gợn sóng, hình như bốn phía đều muốn bị chấn vỡ."),t("br"),t("br"),n._v("“Ngươi muốn khiêu chiến ta?”"),t("br"),t("br"),n._v("Không nghĩ tới, cái này mười mấy tuổi hài tử, trực tiếp đối với hắn khiêu chiến, Trương Vô Ngân khóe miệng giật một cái, nắm đấm căng thẳng: “Tốt...”"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Lời còn chưa dứt, đột nhiên một đạo dồn dập tiếng gió vang lên, Trương Vô Ngân vội vàng ngẩng đầu, ngay sau đó nhìn thấy một cái mập mạp thân ảnh hạ xuống từ trên trời, thẳng tắp rơi xuống."),t("br"),t("br"),n._v("Tạch tạch!"),t("br"),t("br"),n._v("Xương ngực bị đập gãy tận mấy cái, liền đánh trả lực lượng đều không có, liền bị đặt mông ngồi vào mặt đất, chỉ còn lại có nửa cái đầu, hàm răng mất trên đất, từng ngụm từng ngụm thổ huyết."),t("br"),t("br"),n._v("“Chư vị sư huynh, sư đệ, muốn tỷ thí, thế nào cũng không gọi ta, nhìn ta đều tới chậm...”"),t("br"),t("br"),n._v("Cái mông chủ nhân, nhẹ nhàng cười một tiếng, lộ ra răng trắng như tuyết.."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1549"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1551"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);