(window.webpackJsonp=window.webpackJsonp||[]).push([[64],{419:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1034-bi-ta-đap-chet"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1034-bi-ta-đap-chet"}},[n._v("#")]),n._v(" Chương 1034: Bị ta đập chết")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1034: Bị ta đập chết"),t("br"),t("br"),n._v("Một mực để bọn hắn e ngại, cho rằng hủy diệt phạm vi hơn nghìn dặm phạm vi lực lượng, thế mà đến từ nơi này!"),t("br"),t("br"),n._v("Mấu chốt nhất là, vẫn là Dị Linh tộc nhân muốn loại bỏ phong ấn dẫn phát ra."),t("br"),t("br"),n._v("Khó trách trước đó phía ngoài cấm chế một mực không có lay động, vài ngày trước xuất hiện biến cố... Chỉ sợ sẽ là những người này, theo khi đó bắt đầu."),t("br"),t("br"),n._v("Kết hợp với trước đó Mộc sư, Ngô sư nói tới tin tức... Có Dị Linh tộc nhân lặng lẽ tới, cố ý để Hóa Phàm cảnh đóng giữ người phát hiện..."),t("br"),t("br"),n._v("Trước đó liền tràn đầy kỳ quái, hiện tại xem ra, khẳng định cũng cùng tình huống trước mắt liên quan đến."),t("br"),t("br"),n._v("Đoán chừng bọn họ biết loại bỏ phong ấn, sẽ gây ra cực lớn động tĩnh, mang đến nguy hiểm không biết, cho nên cố ý phái người xuất hiện, để Danh Sư đường người phát giác, sau đó lại ở phía dưới tiến hành công kích..."),t("br"),t("br"),n._v("Cứ như vậy, Danh Sư đường người phát hiện cấm chế sẽ sụp đổ, liền vô cùng có khả năng ra tay giải quyết, mặc dù là vì giải cứu muôn dân, trên thực tế nhưng cho bọn hắn giúp đại ân."),t("br"),t("br"),n._v("Cái này cũng liền có thể giải thích vì cái gì thả ra lực lượng, mặc dù là khoảng cách mười phút đồng hồ, nhưng không có như vậy quy luật."),t("br"),t("br"),n._v("Loại bỏ phong ấn, có sức mạnh liền công kích, cùng vung vẩy đại chùy rèn sắt đồng dạng, có thời gian khoảng cách, nhưng cũng không thể nào làm được, mỗi lần đều hoàn toàn tương đồng."),t("br"),t("br"),n._v("“Nếu thật là như vậy, Dị Linh tộc nhân cùng ngoại giới, tất nhiên có thể câu thông...”"),t("br"),t("br"),n._v("Vẻ mặt nghiêm túc, Trương Huyền trong lòng phỏng đoán."),t("br"),t("br"),n._v("Thật sự là lời như vậy, bọn họ lần này đi vào, thật sẽ rất nguy hiểm, rất rõ ràng, Dị Linh tộc nhân lấy được tin tức so với bọn hắn phải hơn rất nhiều."),t("br"),t("br"),n._v("“Không công kích phong ấn lời nói, cửa toả ra lực lượng khổng lồ, nương theo thời gian trôi qua, hoàn toàn có thể tại dung nham phía trên hình thành cơn bão năng lượng, tích tụ trong cấm chế, tất nhiên sẽ phía trên dung nham đẩy ra, hình thành một cái tự nhiên thông đạo, lão viện trưởng đám người hoàn toàn có thể mượn nhờ cỗ gió lốc này đi vào nơi này...”"),t("br"),t("br"),n._v("Nhìn một hồi, Trương Huyền rốt cuộc minh bạch tới, một mặt cười khổ."),t("br"),t("br"),n._v("Náo loạn hồi lâu, bọn họ hiện tại bất luận thế nào không đến được di tích, cũng không phải là tìm nhầm vị trí, mà là bởi vì hắn tay rảnh rỗi, dùng một cái trận kỳ, đem trong này lực lượng toàn bộ phóng ra ra ngoài... Nói cách khác, nếu như trước mấy ngày không ra tay, phía dưới Dị Linh tộc nhân, sợ hãi mình bị nổ chết, khẳng định không dám tiếp tục công kích..."),t("br"),t("br"),n._v("Đồng dạng, bọn họ đi tới nơi này về sau, vẫn là có thể mượn nhờ gió lốc, thuận lợi tiến vào bên trong, không còn phiền toái như vậy..."),t("br"),t("br"),n._v("Hảo tâm xử lý chuyện xấu..."),t("br"),t("br"),n._v("Cái này kêu chuyện gì!"),t("br"),t("br"),n._v("“Về trước đi bố trí trận pháp, đi vào nơi này lại nói, vừa đến, có thể ngăn cản đám người kia, thứ hai, cũng không cho mọi người lo lắng...”"),t("br"),t("br"),n._v("Nghĩ thông suốt chuyện gì xảy ra, biết tiếp tục đợi ở chỗ này không có cái gì ý nghĩa, Trương Huyền Vu hồn khẽ động, dựa theo trước đó phương hướng quay trở về."),t("br"),t("br"),n._v("Đi vào dung nham tu luyện, không sai biệt lắm đã có hai canh giờ, chắc hẳn phía trên mọi người, từ lâu đẳng cuống cuồng."),t("br"),t("br"),n._v("Trở lại tại chỗ gọi phân thân một tiếng, lần nữa trở lại Kim Nguyên đỉnh."),t("br"),t("br"),n._v("Lần này phân thân vô cùng nghe lời, đồng thời không gây ra cái gì yêu thiêu thân, đem hắn thu vào Thiên Nghĩ Phong sào, này mới khiến lô đỉnh bay ra ngoài."),t("br"),t("br"),n._v("Quả nhiên cùng đoán đồng dạng, phía ngoài mọi người tràn đầy cuống cuồng, thấy hắn đi ra, đồng thời thở phào nhẹ nhõm."),t("br"),t("br"),n._v("“Phía dưới địa hình ta đã dò xét rõ ràng, hiện tại bắt đầu bày trận... Mọi người chuẩn bị kỹ càng, hiện tại tất cả đều đi vào Kim Nguyên đỉnh đợi chờ, một khi thành công, lập tức xuất phát!”"),t("br"),t("br"),n._v("Nhìn quanh một vòng, Trương Huyền nói."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Biết can hệ trọng đại, mọi người đồng thời gật đầu."),t("br"),t("br"),n._v("Vù vù!"),t("br"),t("br"),n._v("Nghe được phân phó, Kim Nguyên đỉnh hét lớn một tiếng, hình thể lập tức tăng lên gấp mười lần, chừng hai, cao ba mươi mét thấp, mọi người nối đuôi nhau mà vào."),t("br"),t("br"),n._v("“Ta liền không tiến vào, cùng ngươi cùng một chỗ đi!”"),t("br"),t("br"),n._v("Lạc Nhược Hi cũng không đi theo mọi người sau lưng đi vào, mà là đi tới trước mặt."),t("br"),t("br"),n._v("“Đi vào lô đỉnh sẽ an toàn một chút, theo ở phía sau, ta sợ nguy hiểm...” Trương Huyền cau mày."),t("br"),t("br"),n._v("Bất kể nói thế nào, lô đỉnh nội tướng đối muốn an toàn một chút, phía ngoài lời nói, một khi tốc độ theo không kịp, dung nham khép lại, vẫn là rất nguy hiểm."),t("br"),t("br"),n._v("“Yên tâm đi, thủ đoạn bảo mệnh ta vẫn là có một ít...” Nhẹ nhàng cười một tiếng, Lạc Nhược Hi không còn quá nhiều giải thích."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Thấy nàng tự tin như vậy, nghĩ đến đối phương có thể là Thánh Nhân quý tộc Lạc gia truyền nhân, Trương Huyền lập tức thở phào nhẹ nhõm, nhẹ gật đầu."),t("br"),t("br"),n._v("“Kim Nguyên đỉnh, nghe ta phân phó, một khi để ngươi xuất phát, lập tức hành động, không được dừng lại!”"),t("br"),t("br"),n._v("Quay đầu phân phó, Trương Huyền lúc này mới thân thể nhoáng một cái, đi tới lực lượng dâng trào bầu trời."),t("br"),t("br"),n._v("Về khoảng cách lần lực lượng dâng trào đã qua bảy, tám phút, chẳng mấy chốc sẽ phun trào."),t("br"),t("br"),n._v("Cổ tay khẽ đảo, lấy ra hơn 100 cây trận kỳ, lơ lửng tại trước mặt, hoa văn con mắt mở ra, gắt gao nhìn chằm chằm phía dưới dung nham."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Một lát sau, lập tức cảm thấy phía dưới dung nham bắt đầu sôi trào, Trương Huyền lông mày giương lên, bàn tay bỗng nhiên một trảo, rất nhiều trận kỳ lập tức theo trước mặt bắn ra, trực tiếp hướng phía dưới bay đi, rơi vào Nghịch Chuyển Hồi Thiên trận chỗ đối ứng mỗi một cái vị trí."),t("br"),t("br"),n._v("“Lên!”"),t("br"),t("br"),n._v("Người trên không trung, bàn chân đạp mạnh, một cỗ sóng khí bốn phía truyền lại, tựa như sóng nước."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Trước đó củng cố trận kỳ, thời gian nháy mắt liền bị kích hoạt, một cái cực lớn trận pháp, vận chuyển lại."),t("br"),t("br"),n._v("Vù vù!"),t("br"),t("br"),n._v("Ngay tại trận pháp khởi động cùng thời khắc đó, phía dưới dung nham rốt cục không chịu nổi, bị lực lượng giải khai, giống như là xé rách gấm lụa, xuất hiện một cái khe nứt to lớn, lực lượng phun trào ra hướng hắn bố trí trận pháp lao đến."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),n._v("Cả hai tiếp xúc, lực lượng lập tức chất đống đứng dậy, bị trận pháp hội tụ."),t("br"),t("br"),n._v("Nghịch Chuyển Hồi Thiên trận, là lúc chiến đấu, dùng để phòng ngự đối thủ tiến công, đồng thời có thể tiến hành phản kích mạnh mẽ trận pháp, vận dụng được rồi, hoàn toàn có thể đem công kích của đối thủ hấp thu, như chiếc gương, hoàn hảo không chút tổn hại phản xạ trở về."),t("br"),t("br"),n._v("Nói cách khác, trận pháp này, không riêng gì phòng ngự, còn có thể đem công kích của đối thủ bắn ngược."),t("br"),t("br"),n._v("Đương nhiên, chỉ cần là sự vật, đều có tính hai mặt, trận pháp này đối bày trận người yêu cầu cực cao, hơn nữa, một khi không chịu nổi, không chỉ trong trận người lại nhận tổn thương, bày trận người càng là đứng mũi chịu sào, làm không cẩn thận sẽ làm tràng tử vong."),t("br"),t("br"),n._v("Nhìn xem lực lượng hội tụ, trận pháp đồng thời không có vỡ vụn dấu hiệu, Trương Huyền thở phào nhẹ nhõm, nhịn không được xoa xoa mồ hôi lạnh trên đầu."),t("br"),t("br"),n._v("Trận pháp có thể ngăn trở hay không phía dưới lực lượng, lúc trước hắn cũng không có tự tin trăm phần trăm, may mắn... Học thất tinh cấp bậc Thiên Đạo trận pháp, nếu không, liền xem như Hàn hội trưởng bố trí, chỉ sợ tại kịch liệt trùng kích vào, đều sẽ một đòn mà bại, chống lại không được."),t("br"),t("br"),n._v("“Trương viện trưởng... Đối với trận pháp lý giải thật lợi hại!”"),t("br"),t("br"),n._v("Kim Nguyên đỉnh bên trong mọi người, từng người đem thần thức thả ra ngoài, đem tất cả những thứ này nhìn ở trong mắt, Hàn hội trưởng con ngươi thu hẹp."),t("br"),t("br"),n._v("Vốn, hắn đối Trương Huyền để hắn giấu đi, bản thân bố trí trận pháp, vẫn là rất có phê bình kín đáo, thấy cảnh này mới hiểu được, may mắn nghe theo phân phó, nếu như cưỡng ép đi làm, làm không cẩn thận di tích vào không được, hắn ngược lại sẽ bị nổ chết tại chỗ."),t("br"),t("br"),n._v("Mặc dù cái này Nghịch Chuyển Hồi Thiên trận là thất tinh đỉnh phong trận pháp, nhưng ở trong tay đối phương, nhưng bố trí ra bát tinh trận pháp hiệu quả, bất kỳ cái gì một chỗ, đều phối hợp hoàn mỹ vô khuyết, không chỉ kết hợp địa hình, lại thêm mượn nhờ phía dưới dung nham nhiệt lượng duy trì vận chuyển, phát huy khó có thể tưởng tượng uy lực cực lớn."),t("br"),t("br"),n._v("Trong lúc khiếp sợ, lực lượng rốt cục dâng trào xong xuôi, trong trận pháp cũng tích góp hoàn thành, Trương Huyền lông mày nâng lên, trong tay xuất hiện lần nữa một cái trận kỳ, trực tiếp một ném."),t("br"),t("br"),n._v("Ầm!"),t("br"),t("br"),n._v("Cùng trước đó xử lý cấm chế lúc đồng dạng, giống như cắm vào trận pháp sơ hở chỗ, một cỗ mênh mông lực lượng, dọc theo trận kỳ vị trí, hung mãnh bắn ra."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Phía dưới dung nham chống đỡ cỗ lực lượng này, lập tức bị cứ thế mà xé ra một đạo vết nứt."),t("br"),t("br"),n._v("“Đi mau!”"),t("br"),t("br"),n._v("Biết thời cơ tới, Trương Huyền hét lớn một tiếng, Kim Nguyên đỉnh không dám do dự, trực tiếp hướng vết nứt vọt tới."),t("br"),t("br"),n._v("Nhìn Lạc Nhược Hi một cái, Trương Huyền cũng theo ở phía sau tiến vào bên trong."),t("br"),t("br"),n._v("Biết thời gian cấp bách, dung nham nứt ra thời gian chỉ có một cái chớp mắt, chân khí trong cơ thể vận chuyển, Thiên Đạo thân pháp thi triển, dùng ra tốc độ nhanh nhất."),t("br"),t("br"),n._v("Trong khoảng thời gian này tu luyện đồng thời, cũng không ngừng thí nghiệm, giờ phút này rốt cục đem Thiên Đạo thân pháp cùng Hồng Trần Đạp Thiên bộ hoàn mỹ dung hợp, cả hai kết hợp, tốc độ nhanh như thiểm điện, so với Thánh vực tứ trọng đỉnh phong Kim Nguyên đỉnh, đều không chậm chút nào."),t("br"),t("br"),n._v("“Lạc sư...”"),t("br"),t("br"),n._v("Sợ cô gái đuổi không kịp, quay đầu nhìn lại, lại thấy nàng theo sau lưng, chỉ kém ba, bốn mét khoảng cách, bất kể hắn bao nhanh, cũng không thể kéo ra chút nào."),t("br"),t("br"),n._v("“Không hổ là người của đại gia tộc... Tốc độ đáng sợ!”"),t("br"),t("br"),n._v("Trương Huyền âm thầm gật đầu."),t("br"),t("br"),n._v("Nói thật, hắn đến bây giờ đều không có nhìn ra cô bé này chuẩn xác thực lực, nhưng mà, từ tốc độ đến xem, so với bản thân toàn lực thi triển Thiên Đạo thân pháp, đều không hề yếu, hơn nữa nhìn đối phương ung dung bộ dáng, chỉ sợ toàn lực thi triển, chỉ biết nhanh hơn."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Hơn trăm mét dung nham, tại Kim Nguyên đỉnh cùng Trương Huyền hai người trước mắt chớp mắt liền qua, một tiếng nổ vang, toàn bộ lạc tại di tích bên trong."),t("br"),t("br"),n._v("“Mọi người sắp đi ra, có Dị Linh tộc nhân, cẩn thận...”"),t("br"),t("br"),n._v("Gào lên một tiếng, Trương Huyền ngăn tại cô gái trước người vội vàng lui về phía sau."),t("br"),t("br"),n._v("Vừa rồi hắn nhìn rõ ràng, phía dưới ít nhất có hơn mười vị Dị Linh tộc nhân, mặc dù đại bộ phận đều chỉ có Thánh vực nhất trọng, nhưng mà nhân số nhiều, cũng mười phần đáng sợ."),t("br"),t("br"),n._v("Đối bọn hắn thừa cơ đánh lén, căn bản là không có cách ngăn cản."),t("br"),t("br"),n._v("“Dị Linh tộc nhân?”"),t("br"),t("br"),n._v("Mới từ lô đỉnh bên trong bay ra tới mọi người, tất cả đều làm ra phòng ngự dáng vẻ, cảnh giác nhìn bốn phía, có điều, chỉ nhìn một cái, tất cả đều nhịn không được ngẩn ngơ."),t("br"),t("br"),n._v("“Trương sư, đây chính là ngươi nói di tích? Không phải nói tất cả đều là lầu các ư? Làm sao... Tất cả đều là phế tích?”"),t("br"),t("br"),n._v("Ngô sư nhìn lại."),t("br"),t("br"),n._v("“Phế tích?”"),t("br"),t("br"),n._v("Trương Huyền sững sờ."),t("br"),t("br"),n._v("Hướng bốn phía nhìn lại, quả nhiên thấy trước đó tinh mỹ lộng lẫy kiến trúc, giờ phút này tất cả đều đổ sụp thành phế tích, từng đống bốc lên khói trắng, hình như vừa mới sụp đổ không lâu."),t("br"),t("br"),n._v("“Chẳng lẽ là... Những cái kia Dị Linh tộc nhân, biết chúng ta tới, cố ý đem di tích tiêu diệt?”"),t("br"),t("br"),n._v("Lông mày giương lên, Trương Huyền nhịn không được nói."),t("br"),t("br"),n._v("“Khụ khụ... Không phải bọn họ phá hủy, mà là... Vừa rồi lực lượng quá lớn, xung kích tới, không chỉ đem dung nham xé rách, còn đem nơi này chấn thành bộ dáng này...”"),t("br"),t("br"),n._v("Thấy hắn một mặt tức giận, Hàn hội trưởng, nói."),t("br"),t("br"),n._v("Vừa rồi Trương Huyền mượn nhờ trận pháp, làm ra lực lượng như thế lớn, không chỉ có đem dung nham xé ra, còn đem nơi này hoàn toàn tiêu diệt."),t("br"),t("br"),n._v("“Ta?” Khóe miệng giật một cái, Trương Huyền tràn đầy xấu hổ, mới vừa rồi còn đang nghĩ, phân thân lần này nghe lời, không làm ra phá hư, không nghĩ tới bản thân liền biến thành cục diện này..."),t("br"),t("br"),n._v("Cái này kêu chuyện gì!"),t("br"),t("br"),n._v("“Đúng rồi, đám kia Dị Linh tộc nhân đâu? Chẳng lẽ chạy?”"),t("br"),t("br"),n._v("Chính nhìn quanh một vòng, tìm kiếm khắp nơi, vừa rồi nhìn thấy những tên kia, liền nghe đến Kim Nguyên đỉnh mang theo ngượng ngùng thanh âm vang lên."),t("br"),t("br"),n._v("“Chủ nhân, bọn họ... Hình như đều bị ta... Đập chết”"),t("br"),t("br"),n._v("(Năm chó tới, mọi người chúc mừng năm mới!)"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1042"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1044"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);