(window.webpackJsonp=window.webpackJsonp||[]).push([[642],{997:function(n,h,i){"use strict";i.r(h);var g=i(44),t=Object(g.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-1554-danh-su-đuong-xin-loi-thuong"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1554-danh-su-đuong-xin-loi-thuong"}},[n._v("#")]),n._v(" Chương 1554: Danh sư đường xin lỗi (thượng)")]),n._v(" "),i("hr"),n._v(" "),i("p",[n._v("Chương 1554: Danh Sư đường xin lỗi (thượng)"),i("br"),i("br"),n._v("“Sư huynh?”"),i("br"),i("br"),n._v("Thân thể nhoáng một cái, Khúc trưởng lão như bị sét đánh."),i("br"),i("br"),n._v("Thế nào cái tình huống?"),i("br"),i("br"),n._v("Dương sư không chỉ có là toàn bộ Danh Sư đường tổng bộ thực lực mạnh nhất, càng là bối phận cao nhất, liền xem như hắn, đều muốn xưng hô một tiếng sư thúc, lại thêm bản thân đối quy củ mười phần vừa ý, hắn thấy, chỉ cần nói ra có người giả mạo qua, nhất định sẽ đột nhiên giận dữ, hung ác rất giáo huấn một lần..."),i("br"),i("br"),n._v("Nằm mơ đều không có nghĩ đến, đi tới về sau, trực tiếp khom người xưng hô “Sư huynh”!"),i("br"),i("br"),n._v("Hắn là sư huynh của ngươi, vậy ta... Chẳng phải cũng muộn một đời?"),i("br"),i("br"),n._v("Không riêng gì hắn, Nhậm Thanh Viễn cũng thiếu chút ngã sấp xuống, sắp khóc."),i("br"),i("br"),n._v("Đồ đệ là Chiến Sư đường đường chủ, Băng Nguyên cung cung chủ, Độc điện điện chủ, khải linh sư công hội hội trưởng... Những này ta đều có thể tiếp nhận, bản thân là Trương gia gia chủ, cũng có thể miễn cưỡng công nhận... Có thể Dương sư xưng hô sư huynh, cái quỷ gì?"),i("br"),i("br"),n._v("Lúc nào, Danh Sư đường bối phận cao nhất người, đều là của ngươi sư đệ?"),i("br"),i("br"),n._v("Cứ như vậy, chẳng phải cho thấy, toàn bộ đại lục tất cả danh sư, đều là của ngươi vãn bối?"),i("br"),i("br"),n._v("Một ngụm máu tươi ngậm trong miệng, bất cứ lúc nào cũng sẽ phun ra, Nhậm Thanh Viễn chỉ cảm thấy cả người buồn bực sắp bùng nổ."),i("br"),i("br"),n._v("Trở thành quyền đường chủ mấy trăm năm, đủ loại thiên tài, cường giả thấy qua vô số, còn là lần đầu tiên nhìn thấy như vậy..."),i("br"),i("br"),n._v("Vốn nghĩ lần này chạy tới, đem hắn bắt lấy, mặc kệ trừng phạt không trừng phạt, cũng có thể trước đem đối phương nhanh chóng tấn cấp bí mật moi ra tới, kết quả... Bị đả kích gần chết không nói, thời gian nháy mắt, còn thành bản thân sư thúc, sư bá bối phận..."),i("br"),i("br"),n._v("Hai mươi tuổi sư thúc... Thế nào cảm giác mộng ảo như vậy đây?"),i("br"),i("br"),n._v("“...”"),i("br"),i("br"),n._v("Hưng kiếm thánh cùng Mộng kiếm thánh cũng đúng nhìn một cái, mỗi người nuốt ngụm nước bọt."),i("br"),i("br"),n._v("Vốn cho rằng cái này mới vừa biết con trai, lớn nhất rung động là thực lực cường đại, hiện tại xem ra, căn bản cũng không gọi sự tình... Thành Dương sư sư huynh, mới để cho người điên cuồng!"),i("br"),i("br"),n._v("Như vậy luận lời nói, bọn họ chẳng phải biến thành Dương sư thúc thúc thế hệ?"),i("br"),i("br"),n._v("Không để ý tới đám người chấn kinh, nhìn về phía Dương sư sau lưng Tôn Cường, Trương Huyền thở phào nhẹ nhõm."),i("br"),i("br"),n._v("Đối phương có thể cố gắng xuất hiện ở đây, xem ra không có xảy ra vấn đề."),i("br"),i("br"),n._v("“Thiếu gia, ta cho rằng sẽ không còn được gặp lại ngươi...”"),i("br"),i("br"),n._v("Nhìn thấy hắn, Tôn Cường vội vàng tiến lên, khóc ròng ròng."),i("br"),i("br"),n._v("Một ngày này nhiều, có thể là hắn kinh hoảng nhất thời kỳ."),i("br"),i("br"),n._v("Nghe được Dương sư tự mình nói, cũng không phải là hắn lão gia, giờ mới hiểu được trước đó một mực sống ở trong khi nói dối, trong đầu tự tin, trong nháy mắt sụp đổ, cả người suýt chút nữa sụp đổ."),i("br"),i("br"),n._v("Ngơ ngơ ngác ngác được đưa tới Danh Sư đường tổng bộ, tại rất nhiều cửu tinh danh sư chỉ điểm, tẩu hỏa nhập ma, vốn cho rằng hẳn phải chết không nghi ngờ, không nghĩ tới Dương sư lại trở về."),i("br"),i("br"),n._v("Lần này Dương sư, thái độ so trước đó hòa ái không biết gấp bao nhiêu lần, lại không còn trước đó uy nghiêm, trực tiếp nói cho hắn biết... Bản thân sai lầm, nguyên lai thiếu gia là hắn sư huynh..."),i("br"),i("br"),n._v("Trong nháy mắt, theo đáy cốc lần nữa trở lại trên trời, nhân sinh thay đổi rất nhanh... Thật sự là quá kích thích."),i("br"),i("br"),n._v("Nếu không phải vị trí là Danh Sư đường, cũng hoài nghi có phải hay không thấy cái giả Dương sư."),i("br"),i("br"),n._v("“Về sau cố gắng tu luyện!”"),i("br"),i("br"),n._v("Thấy cái này hai hàng, dường như lại mập một tia, Trương Huyền không để ý hắn kích động, khoát tay áo."),i("br"),i("br"),n._v("“Là...” Tôn Cường liền vội vàng gật đầu."),i("br"),i("br"),n._v("“Trịnh Dương, Tôn Cường trước giao cho ngươi, nếu như không nghe lời, liền dùng ra các ngươi Chiến Sư đường hình cụ, cố gắng tôi luyện!” Quay đầu khai báo."),i("br"),i("br"),n._v("“Là, lão sư!” Trịnh Dương ôm quyền, cười hắc hắc: “Tôn thúc, xin mời!”"),i("br"),i("br"),n._v("Nghe được tu luyện, Tôn Cường nhất thời lại không còn vừa rồi hưng phấn, trở nên mặt ủ mày chau, một bộ như cha mẹ chết bộ dáng."),i("br"),n._v(" “Làm phiền sư đệ vất vả một chuyến!”"),i("br"),i("br"),n._v("An bài xong Tôn Cường, Trương Huyền khách sáo nói."),i("br"),i("br"),n._v("“Chuyện này, có thể sư phụ huynh cống hiến sức lực, là vinh hạnh của ta...” Dương sư liền vội vàng gật đầu."),i("br"),i("br"),n._v("“...”"),i("br"),i("br"),n._v("Nhìn thấy vị này đương đại đệ nhất nhân khách khí như thế, hưng, Mộng kiếm thánh hai vị Kiếm thánh lần nữa chấn kinh một cái."),i("br"),i("br"),n._v("“Khách khí!”"),i("br"),i("br"),n._v("Biết đối phương biết mình là trời nhận danh sư, lại cao hơn lễ tiết cùng thái độ, cũng không tính là cái gì Trương Huyền cũng không nhiều lời, cho cái ánh mắt, ra hiệu khiêm tốn."),i("br"),i("br"),n._v("Dương sư lúc này mới kịp phản ứng, mặt mo đỏ ửng, con mắt rơi vào Triệu Nhã trên người, tràn đầy khó tin, tấm tắc lấy làm kỳ lạ: “Sư huynh quả nhiên Thiên Nhân thủ đoạn, lúc này mới bao lâu thời gian, Triệu cô nương cũng đã khôi phục như lúc ban đầu, hơn nữa tu vi tiến thêm một bước...”"),i("br"),i("br"),n._v("Triệu Nhã tình huống, hắn tự mình dò xét qua, trong cơ thể kinh mạch toàn bộ vỡ vụn, lấy năng lực của hắn, gần như không có khả năng cứu chữa, bản thân vị sư huynh này, mới dùng ngắn ngủi hơn một canh giờ thời gian, liền để hắn khôi phục như lúc ban đầu... Không hổ là trời nhận danh sư, quả nhiên có làm người ta khó tin thủ đoạn!"),i("br"),i("br"),n._v("Nghe được hắn cảm khái, Trương Huyền cũng không nhiều lời, lúc này mới nhớ tới vừa rồi Nhậm Thanh Viễn tra hỏi, quay đầu nhìn qua: “Nhậm đường chủ, đường chủ lệnh, không phải ta không muốn trả lại ngươi, mà là...”"),i("br"),i("br"),n._v("Vừa định nói ra thứ này đã bị bản thân luyện hóa, liền nghe đến một bên Dương sư mở miệng: “Đường chủ lệnh, là ta để sư huynh cầm trước, Nhậm Thanh Viễn, lần này ngươi không phân tốt xấu, tùy tiện điều động, để Danh Sư đường rơi vào dư luận bên trong, bản thân liền có lỗi lầm lớn. Tiếp tục cầm, sẽ chỉ ảnh hưởng Danh Sư đường uy nghiêm, liền do sư huynh tạm thời đảm bảo. Lấy thực lực cùng địa vị của hắn, sẽ không để cho đường chủ lệnh bị hao tổn!”"),i("br"),i("br"),n._v("“Là...”"),i("br"),i("br"),n._v("Không dám nói nhiều, Nhậm Thanh Viễn đành phải lắc đầu, gọi một đám trưởng lão: “Chúng ta đi!”"),i("br"),i("br"),n._v("Huy động nhân lực mà tới, không nghĩ tới loại kết quả này mà quay về."),i("br"),i("br"),n._v("“Chậm đã!”"),i("br"),i("br"),n._v("Mới đi mấy bước, liền thấy Chiến Sư đường đám người vây quanh ở bốn phía."),i("br"),i("br"),n._v("“Chiến tử có ý tứ gì?”"),i("br"),i("br"),n._v("Nhậm Thanh Viễn quay đầu nhìn lại."),i("br"),i("br"),n._v("“Không có ý tứ gì! Chuyện của các ngươi là giải quyết, lão sư ta sự tình, còn không có giải quyết! Các ngươi bỗng dưng cho lão sư chụp đội chụp mũ, đối với hắn tiến hành bắt cùng truy nã, càng là thống hạ sát thủ, nếu không phải chúng ta tới nhanh, chỉ sợ đã đem người giết! Ảnh hưởng tới lão sư danh dự, thân phận, lại muốn giết người... Như vậy đi, có phần quá tiện nghi đi!”"),i("br"),i("br"),n._v("Mí mắt vừa nhấc, Trịnh Dương trường thương trong tay nghẹn ngào hí dài."),i("br"),i("br"),n._v("“Lão sư nhân nghĩa, thả các ngươi Danh Sư đường người, không có nghĩa là chúng ta cũng dễ nói, ngày hôm nay không cho cái khai báo, liền ở lại chỗ này a!” Ngụy Như Yên cũng tiến về phía trước một bước, trên người khí độc sôi trào."),i("br"),i("br"),n._v("Tam đại thế lực cùng Trương Huyền hoà giải, Danh Sư đường là không truy cứu, nhưng ngươi thân là Danh Sư đường, đại lục làm gương mẫu, bỗng dưng ô người trong sạch, không cho cái khai báo liền muốn đi, nào có dễ dàng như vậy!"),i("br"),i("br"),n._v("Bọn họ cũng không muốn lão sư của mình, một mực tiếp nhận người khác bêu danh."),i("br"),i("br"),n._v("Nếu là thay lão sư đòi lại công đạo, vậy liền đánh bọn họ sợ hãi mới thôi, nếu không không đau không ngứa, về sau tái phạm làm sao bây giờ?"),i("br"),i("br"),n._v("Rầm!"),i("br"),i("br"),n._v("Kèm theo hai người lời nói, Triệu Nhã, Vương Dĩnh, Lộ Xung mấy người cũng lần nữa vây quanh, từng cái khí tức ngút trời, dường như Danh Sư đường không cho bọn họ thoả mãn, ngày hôm nay liền đem nó chém xuống dưới ngựa."),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Da mặt run run một hồi, Nhậm Thanh Viễn thân thể run rẩy, lại nói không ra lời nói tới."),i("br"),i("br"),n._v("Danh Sư đường tổng bộ, quản lý đại lục, lúc nào, bị bức ép thành như vậy!"),i("br"),i("br"),n._v("“Các ngươi muốn thế nào?”"),i("br"),i("br"),n._v("Một vị cửu tinh danh sư cũng nhịn không được nữa, lớn tiếng hỏi."),i("br"),i("br"),n._v("“Chẳng ra sao cả, xin lỗi! Hơn nữa thông báo đại lục tất cả Danh Sư đường! Mặt khác, tuyệt phẩm linh thạch các loại, lại đền bù cái mấy ngàn vạn viên, chuyện này cứ như vậy qua...”"),i("br"),i("br"),n._v("Viên Đào nói.."),i("br"),i("br"),n._v("Người đăng: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-1563"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-1565"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=t.exports}}]);