(window.webpackJsonp=window.webpackJsonp||[]).push([[644],{999:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1556-đai-luc-bi-van"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1556-đai-luc-bi-van"}},[n._v("#")]),n._v(" Chương 1556: Đại lục bí văn")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1556: Đại lục bí văn"),t("br"),t("br"),n._v("“Cổ Thánh?”"),t("br"),t("br"),n._v("Con ngươi co rụt lại, Trương Huyền tràn đầy khó tin: “Không phải nói, Cổ Thánh không cách nào đột phá ư?”"),t("br"),t("br"),n._v("Trước đó, Dương sư từng nói qua, Cổ Thánh cấp bậc khó mà đột phá, liền xem như hắn, cũng vây ở tại chỗ, không cách nào tiến bộ, chỉ có thể chạy thiên hạ, tìm kiếm cơ duyên, đã như vậy, vị này Dị Linh chi Hoàng, làm sao có thể mạnh như vậy?"),t("br"),t("br"),n._v("“Ta nói hắn nắm giữ Cổ Thánh cấp bậc, vẫn là là đánh giá thận trọng nhất, chỉ sợ tu vi sẽ cao hơn!”"),t("br"),t("br"),n._v("Gặp hắn không hiểu, Dương sư cũng vẻ mặt nghiêm túc, dừng lại một chút, giải thích nói: “Danh sư đại lục quả thực Cổ Thánh rất khó đột phá, nhưng Dị Linh tộc nhân cùng nhân loại sinh mệnh cấp độ, thể chất hoàn toàn khác biệt! Bọn chúng đột phá phương thức cũng không giống, còn có thể hay không đột phá, ta không rõ ràng lắm, nhưng... Vị này Dị Linh chi Hoàng, khẳng định không phải gần vạn năm qua đột phá!”"),t("br"),t("br"),n._v("“Có ý tứ gì?” Trương Huyền không hiểu."),t("br"),t("br"),n._v("Không phải gần vạn năm qua đột phá, chẳng lẽ, một vạn năm trước cái tên này liền đạt tới Cổ Thánh cấp bậc?"),t("br"),t("br"),n._v("Không đến mức đi!"),t("br"),t("br"),n._v("Cổ Thánh mặc dù cấp bậc cao thâm, thế nhưng là tuổi thọ cũng không phải vô tận, nếu không, Khổng sư cùng rất nhiều đệ tử, cũng sẽ không biến mất không thấy."),t("br"),t("br"),n._v("“Ta thậm chí nghi ngờ, vị này Dị Linh chi Hoàng, thật ra thì... Chính là lúc trước từng cùng Khổng sư đối chiến vị kia!” Dương sư híp mắt lại."),t("br"),t("br"),n._v("“Cùng Khổng sư đối chiến vị kia? Cái này, cái này, Khổng sư rời đi đại lục đều nắm chắc vạn năm đi...”"),t("br"),t("br"),n._v("Trương Huyền giật nảy mình."),t("br"),t("br"),n._v("Khổng sư rời đi danh sư đại lục, đều nắm chắc vạn năm, tới đối chiến Dị Linh chi Hoàng, chẳng lẽ còn có thể sống được?"),t("br"),t("br"),n._v("“Dị Linh tộc nhân tuổi thọ vốn là lâu đời, lại thêm, cũng có thể là nắm giữ một ít về thời gian bí thuật, ngủ say vạn năm, không khó lắm!” Dương sư giải thích một câu: “Đại lục ở bên trên thật ra thì cũng không ít nhân tộc cự đầu, như vậy sống sót!”"),t("br"),t("br"),n._v("Trương Huyền trong lòng nổi lên thao thiên cự lãng."),t("br"),t("br"),n._v("Vốn cho rằng, bất kể tu luyện giả vẫn là Dị Linh tộc nhân, tuổi thọ đến, chỉ có thể lựa chọn tử vong... Nằm mơ đều không có nghĩ đến, còn có loại bí thuật này!"),t("br"),t("br"),n._v("Cũng đúng, loại người hung ác chính là năm đó cùng Khổng sư đối chiến qua tồn tại, vài vạn năm về sau, đồng dạng sống lại."),t("br"),t("br"),n._v("Dị Linh chi Hoàng, địa vị so với loại người hung ác, chỉ mạnh không yếu, nắm giữ sống sót phương pháp, cũng rất bình thường."),t("br"),t("br"),n._v("“Vị này Dị Linh chi Hoàng, lẻn vào đại lục, có phải là vì Xuân Thu đại điển mà đến, ẩn giấu ở nơi nào, cụ thể tới nơi nào, coi như Danh Sư đường cũng đều không hiểu ra sao! Cho nên, tất cả cẩn thận cho thỏa đáng. Thiên nhận danh sư, vài vạn năm mới xuất hiện một cái, một phần vạn bị hắn biết, chém giết tới, bằng vào chúng ta thực lực bây giờ, căn bản ngăn cản không nổi!”"),t("br"),t("br"),n._v("Dương sư một mặt lo lắng."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Thiên nhận danh sư thân phận, che giấu đều đã lâu như vậy, nhiều che giấu một hồi, cũng không tính là gì."),t("br"),t("br"),n._v("Chỉ là không nghĩ tới, vị này Dị Linh chi Hoàng cường đại như vậy."),t("br"),t("br"),n._v("“Nếu như vị này lợi hại như thế, danh sư đại lục, đã không còn Cổ Thánh, vì sao bọn họ không chém giết tới, lần nữa chiếm lĩnh đại lục?”"),t("br"),t("br"),n._v("Thế giới này đã không còn Khổng sư, cũng mất hắn rất nhiều đệ tử, Dị Linh chi hoàng đô Cổ Thánh cường giả, dẫn đầu Dị Linh tộc nhân lần nữa giết tới, rất bình thường đi!"),t("br"),t("br"),n._v("“Bọn họ xung phong liều chết qua nhiều lần, nhưng mà bị thiệt lớn, nếu không, Danh Sư đường, Chiến Sư đường, cũng không cần thời khắc đều là chuẩn bị chiến đấu trạng thái.” Gặp hắn không hiểu, Dương sư giải thích nói: “Hơn nữa, danh sư đại lục, hiện tại mặc dù không ai có thể đột phá Cổ Thánh, nhưng không có nghĩa là, không có Cổ Thánh...”"),t("br"),t("br"),n._v("“Cái này...” Trương Huyền nháy con mắt, càng ngày càng mê hoặc."),t("br"),t("br"),n._v("Đã không có cách nào đột phá, tại sao lại khả năng có?"),t("br"),n._v("“Ta không phải mới vừa nói sao? Đại lục ở bên trên cũng không ít nhân tộc cự đầu, là dựa vào thời gian bí thuật sống sót... Các ngươi Trương gia, sở dĩ có thể trở thành đại lục đệ nhất gia tộc, ai cũng không dám bắt nạt, Danh Sư đường đều cho ba phần mặt mũi, bởi vì... Cũng có loại này sống sót cự đầu! Chỉ bất quá, một mực nằm ở trạng thái ngủ say, chỉ có chân chính đại lục gặp được nguy hiểm lúc, mới có thể đánh thức thôi!”"),t("br"),t("br"),n._v("Dương sư nói."),t("br"),t("br"),n._v("“Trương gia có... Cổ Thánh cấp bậc cự đầu?”"),t("br"),t("br"),n._v("Trương Huyền líu lưỡi."),t("br"),t("br"),n._v("Còn tưởng rằng, Trương gia người mạnh nhất, cũng liền Hưng Mộng kiếm thánh cùng đại trưởng lão Trương Vô Ngân, không nghĩ tới, nắm giữ che giấu bí mật."),t("br"),t("br"),n._v("“Đúng vậy a, những này liên lụy đến đại lục sâu nhất cơ mật, chỉ có trở thành chân chính cửu tinh danh sư, mới có tư cách biết, mong rằng sư huynh không cần thiết truyền ra ngoài...” Dương sư ôm quyền."),t("br"),t("br"),n._v("“Yên tâm, ta biết được!”"),t("br"),t("br"),n._v("Trương Huyền trả lời một câu."),t("br"),t("br"),n._v("“Đại lục Cổ Thánh, cơ bản đều rơi vào trạng thái ngủ say, chiến đấu một lần, tuổi thọ liền giảm bớt một lần, đến cuối cùng, rất có thể hoàn toàn chết đi. Bởi vậy, không có khả năng đem gọi lên, thời khắc đều bảo vệ sư huynh, cho nên, thiên nhận danh sư sự tình, đã không có tiết lộ, trước hết không vội nói ra, nếu không, ai cũng không biết vị này Dị Linh chi Hoàng, biết biến hóa thành cái gì bộ dáng, lại sẽ xuất bây giờ ở địa phương nào, cuối cùng xuất hiện không thể suy đoán nguy hiểm.”"),t("br"),t("br"),n._v("Nói đến đây Dương sư dừng lại một chút, vẻ mặt thành khẩn: “Chúng ta bất luận người nào cũng có thể vẫn lạc, có thể tử vong, nhưng sư huynh ngươi không thể... Ngươi là thiên nhận danh sư, có thể so với Khổng sư tồn tại, nhân tộc có hay không có thể lần nữa thịnh vượng, lần nữa khai sáng Cổ Thánh khắp nơi trên đất thịnh thế, chỉ có thể dựa vào ngươi!”"),t("br"),t("br"),n._v("Trương Huyền chấn động trong lòng."),t("br"),t("br"),n._v("Không nghĩ tới, trước mắt vị này, đem tự xem đi nặng như vậy."),t("br"),t("br"),n._v("“Ta hiểu!”"),t("br"),t("br"),n._v("Biết đối phương mong đợi, Trương Huyền gật đầu, đồng thời âm thầm vui mừng."),t("br"),t("br"),n._v("May mắn hắn tính cách khiêm tốn, không chút đem thiên nhận danh sư sự tình, gióng trống khua chiêng nói ra, nếu không, chỉ sợ sớm đã bị Dị Linh chi Hoàng ngấp nghé, chết cũng không biết chết như thế nào."),t("br"),t("br"),n._v("“Sư đệ mới vừa nói, vị này Dị Linh chi Hoàng vì Xuân Thu đại điển mà đến, đến cùng chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Trương Huyền lần nữa nhìn qua."),t("br"),t("br"),n._v("Xuân Thu đại điển trong truyền thuyết là Khổng sư lưu lại vô thượng bí tịch, nắm giữ thông thiên triệt địa năng lực, trước đó, Thanh Điền hoàng thất muốn tìm kiếm truyền thế thiên phù, đang cùng hắn liên quan đến."),t("br"),t("br"),n._v("“Đại điển là Khổng sư lưu lại vô thượng bí tịch, có thể thay đổi xuân thu, sửa đổi thời gian, Khổng sư rời đi đại lục về sau, đem đặt ở Khúc Phụ Khổng miếu bên trong, nhưng mà... Khổng miếu cụ thể ở nơi nào, không ai biết, hậu thế vô số danh sư, đều thẩm tra không đến.”"),t("br"),t("br"),n._v("“Truyền thuyết, chỉ có gom góp truyền thế thiên phù, mới có thể mở ra đi vào Khổng miếu con đường, tiến vào bên trong, tìm tới đại điển... Bất quá, một năm trước, không biết nguyên nhân gì, Khúc Phụ không gian xuất hiện rung chuyển, dường như có người loáng thoáng Hải Thị Thận Lâu đồng dạng thấy qua Khổng miếu. Tin tức vừa ra, đại lục oanh động, Danh Sư đường biết, lập tức phong tỏa tin tức nguồn gốc, nhưng... Dị Linh tộc nhân vẫn là biết được!”"),t("br"),t("br"),n._v("“Xuân Thu đại điển, là vô thượng chí bảo, nhân tộc nhận được, tất nhiên sẽ thực lực tăng nhiều, mà Dị Linh tộc nhân nhận được, thì vô cùng có khả năng phá tan ấn ký, trở lại nhân gian, đến lúc đó, thật sự sinh linh đồ thán!”"),t("br"),t("br"),n._v("Dương sư sắc mặt nghiêm túc."),t("br"),t("br"),n._v("“Đã đây là Khổng sư luyện chế bảo vật, vì sao không giao cho Danh Sư đường đảm bảo, mà muốn phong ấn, đặt tại Khổng miếu?”"),t("br"),t("br"),n._v("Trương Huyền không hiểu."),t("br"),t("br"),n._v("Khổng sư luyện chế ra đến, đương nhiên là muốn tạo phúc nhân loại, nhưng đem hắn đem gác xó, đây là vì sao?"),t("br"),t("br"),n._v("“Nguyên nhân cụ thể, ta cũng không biết! Chỉ sợ chỉ có thể tiến vào Khổng miếu, mới có thể biết!”"),t("br"),t("br"),n._v("Dương sư nói."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1565"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1567"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);