(window.webpackJsonp=window.webpackJsonp||[]).push([[653],{1008:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1563-thai-đoc-nguon-goc-【-canh-thu-hai-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1563-thai-đoc-nguon-goc-【-canh-thu-hai-】"}},[n._v("#")]),n._v(" Chương 1563: Thai độc nguồn gốc 【 canh thứ hai 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1563: Thai độc nguồn gốc "),t("br"),t("br"),n._v("“Chỉ cần tỉnh lại, chính là chiến đấu, chính là chém giết, chính là trọng thương... Có thể chỉ cần có thể bảo hộ nhân tộc, để nhân tộc Tân Hỏa không ngừng, bọn họ nguyện ý trả giá!”"),t("br"),t("br"),n._v("Hưng kiếm thánh hốc mắt cũng có chút ửng hồng: “Chính là có những người này thủ hộ, chúng ta nhân tộc, mới có thể một mực ổn định phát triển, các thành phố lớn người người an cư, không đến mức lần nữa biến thành nô lệ, không rõ sống chết!”"),t("br"),t("br"),n._v("“Căn cứ ta biết tư liệu, lúc ấy có một vị Cổ Thánh, chỉ có hơn năm trăm tuổi, còn có hơn một ngàn năm tuổi thọ có thể sống, hơn nữa mấu chốt nhất là, mới vừa cùng một vị Cổ Thánh kết làm đạo lữ, nhưng... Biết đại lục lại không cách nào xuất hiện Cổ Thánh về sau, dứt khoát kiên quyết sử dụng bí pháp, đi vào ngủ say!”"),t("br"),t("br"),n._v("“Sau này trên vạn năm đến, mỗi lần đại chiến, mới có thể cùng vợ gặp lại, thậm chí không thể nói nửa câu... Càng khiến người ta thương tâm là, năm ngàn năm trước một lần đại chiến bên trong, đạo lữ của hắn bị ba vị Dị Linh tộc Hoàng giả vây công, mà chết đi... Vì cứu đối phương, cũng thiêu đốt sinh mệnh, cùng người yêu, cùng đi!”"),t("br"),t("br"),n._v("“Không tuyển chọn như vậy lời nói, hơn một ngàn năm tuổi thọ, có thể tiêu dao thiên địa, trải qua Thần Tiên đồng dạng sinh hoạt, thậm chí cũng có thể bồi dưỡng ra một nhóm lớn đệ tử... Đáng tiếc, vì nhân tộc có thể kiên trì lâu hơn một chút, từ bỏ tất cả!”"),t("br"),t("br"),n._v("“Còn có một vị Cổ Thánh, làm ra quyết định thời điểm, con gái mới vừa vặn ra đời... Nữ nhi của hắn thiên phú không cao, liền Thánh vực đều không có đạt tới, cùng người chiến đấu bị thương, sống không đến một trăm tuổi liền chết, nhưng... Trong khoảng thời gian này không có chiến tranh, vị này Cổ Thánh, một mực ngủ say, thẳng đến nữ nhi của hắn chết, đều không có nhìn thấy một mặt!”"),t("br"),t("br"),n._v("“Tỉnh lại lần nữa, biết con gái đã qua đời mấy thập niên, buồn bực hồi lâu, một câu không nói, lần nữa xông lên chiến trường... Đáng tiếc là, cũng là có đi không về không có...”"),t("br"),t("br"),n._v("Hưng kiếm thánh trên nắm tay gân xanh toác ra, nhớ tới những này từng vị vì nhân tộc trả giá tất cả tiền bối, kìm lòng không được run rẩy."),t("br"),t("br"),n._v("“Cổ Thánh ngủ say bí pháp, có thể làm cho bọn họ một mực còn sống còn là... Đồng dạng sẽ già?” Buồn bực phút chốc, Trương Huyền hỏi."),t("br"),t("br"),n._v("Những này ngủ say bí pháp, cùng kiếp trước đóng băng người rất giống, tuy là có thể sống càng dài, nhưng đối với sinh mạng, cũng có không thể xóa nhòa tổn thương."),t("br"),t("br"),n._v("Coi như về sau lần nữa phục sinh, khẳng định cũng không thể giống như trước đồng dạng."),t("br"),t("br"),n._v("“Cổ Thánh bí pháp, có thể kéo dài sinh mệnh, nhưng... Sinh mệnh vẫn như cũ là tại trôi đi, nói cách khác, đồng dạng sẽ ở trong ngủ mê già đi! Chẳng qua là già chậm một chút mà thôi. Thật ra thì những bí pháp này, nói đến phức tạp, giải thích mười phần đơn giản. Chính là... Bọn họ ngủ say địa phương, tốc độ thời gian trôi qua cùng nơi này không giống, chậm hơn một chút!”"),t("br"),t("br"),n._v("“Bên trong một năm, bên ngoài mười năm!”"),t("br"),t("br"),n._v("“Chỉ là... Cho dù là 1: 10 tỉ lệ, sinh mệnh vẫn như cũ sẽ già yếu, trên vạn năm đi qua, tương đương với sống hơn một ngàn năm... Lúc ấy ngủ say người trẻ tuổi, giờ phút này cũng đến già trên 80 tuổi chi niên, huống chi, thường xuyên đối mặt thảm liệt chiến sự, bị Dị Linh tộc điên cuồng sát lục chi khí xâm nhập thân thể... Già yếu càng nhanh!”"),t("br"),t("br"),n._v("Hưng kiếm thánh tiếp tục nói: “Cho nên... Năm đó sống sót cái đám kia Cổ Thánh, hầu như tất cả đều tuổi thọ đến điểm cuối cùng... Dị Linh tộc nhân, tuy là vẫn như cũ trong thời gian ngắn không cách nào phá tan phong ấn, quy mô xâm phạm, nhưng... Một khi mất đi Cổ Thánh lực lượng, nhân tộc sẽ bất cứ lúc nào cũng sẽ bị diệt!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Cổ Thánh, trên thực tế liền cùng kiếp trước bom nguyên tử có chút tương tự, có, dù là không ra, cũng đại biểu nội tình cùng thực lực, không có, vậy thì chờ lấy tùy thời bị hủy diệt."),t("br"),t("br"),n._v("Nhân tộc đại lục, Dị Linh tộc nhân đã sớm ngấp nghé không biết bao lâu, một mực ẩn núp, chỉ sợ cũng là đang chờ đợi loại này thời cơ."),t("br"),t("br"),n._v("Dù sao, Dị Linh tộc nhân trời sinh liền so với thọ mệnh của nhân loại dài, coi như bọn họ không ngủ say, cũng có thể đem ngủ say nhân tộc Cổ Thánh tươi sống ngao chết."),t("br"),t("br"),n._v("Đây là chủng tộc khác biệt, không có cách nào bù đắp."),t("br"),t("br"),n._v("“Hai mươi năm trước, Khúc Phụ rung chuyển, Khổng miếu lúc ẩn lúc hiện, dẫn tới Dị Linh tộc nhân rối loạn, bất cứ lúc nào cũng sẽ tấn công, đại chiến, tại Địa quật, khắp nơi phát sinh... Mà nhân tộc Cổ Thánh, còn lại số lượng, đã không nhiều lắm!”"),t("br"),t("br"),n._v("Hưng kiếm thánh nói tiếp: “Ta Trương gia vị cuối cùng Cổ Thánh, đi vào chiến đấu!”"),t("br"),t("br"),n._v("“Vị cuối cùng? Trương gia... Có mấy vị như vậy ngủ say Cổ Thánh?”"),t("br"),n._v(" Trương Huyền nhịn không được hỏi."),t("br"),t("br"),n._v("“Hết thảy chín vị, chỉ bất quá... Nhiều năm chiến tranh, một cái tiếp một cái, tất cả đều vẫn lạc hoặc là ngủ say, lại không cách nào tỉnh lại, hiện tại chỉ còn lại có cái này một cái, cũng chính là ta Trương gia, thứ hai mươi bảy đảm nhiệm gia chủ, Trương Hoằng Thiên!”"),t("br"),t("br"),n._v("“Một vị?” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Chín vị Cổ Thánh, chỉ còn lại có một người, Trương gia xác thực vì nhân tộc làm rất lớn cống hiến."),t("br"),t("br"),n._v("“Đúng vậy a, hai mươi năm trước chiến tranh, mười phần thảm liệt, Trương Hoằng Thiên tiên tổ bị trọng thương, bất cứ lúc nào cũng sẽ triệt để tử vong... Muốn cho hắn tiếp tục sống sót, vì nhân tộc tiếp tục chiến đấu, chỉ có một cái phương pháp... Cái kia chính là, truyền vào càng tinh thuần Trương gia huyết mạch!”"),t("br"),t("br"),n._v("Hưng kiếm thánh nói đến đây, bắp thịt trên mặt xiết chặt, hốc mắt bắt đầu ửng hồng: “Lúc ấy... Trong tộc tất cả mọi người huyết mạch, đều không đủ tư cách, không đạt tới tiêu chuẩn... Chỉ có còn tại thai mẹ bên trong ngươi, thông qua được kiểm tra!”"),t("br"),t("br"),n._v("“Ta?”"),t("br"),t("br"),n._v("Thân thể chấn động, Trương Huyền rốt cuộc minh bạch vì sao đối phương muốn dẫn bản thân tới nơi này."),t("br"),t("br"),n._v("“Lúc ấy hai loại tình huống, thứ nhất, không dùng huyết mạch của ngươi, như thế, Trương Hoằng Thiên tiên tổ liền sẽ trực tiếp tử vong, nhân tộc thiếu một cái chống cự Dị Linh tộc nhân hậu thuẫn! Thứ hai, mượn dùng huyết mạch của ngươi cứu người, nhưng... Còn chưa ra đời, sinh mệnh liền gặp được nguy hiểm, bất cứ lúc nào cũng sẽ tử vong...”"),t("br"),t("br"),n._v("Nói đến đây dừng lại lên, qua thật lâu, Hưng kiếm thánh mới than thở lên tiếng: “Ta lựa chọn đầu thứ hai!”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trương Huyền lặng im."),t("br"),t("br"),n._v("Cái này đích xác là cái khó khăn nhất nan đề."),t("br"),t("br"),n._v("Đặc biệt là đối với Hưng kiếm thánh loại này mới vừa có hài tử phụ thân tới nói."),t("br"),t("br"),n._v("Lựa chọn đầu thứ nhất, nhân tộc thiếu đi Cổ Thánh, khả năng bất cứ lúc nào cũng sẽ bị diệt, tổ chim bị phá, trứng có an toàn?"),t("br"),t("br"),n._v("Nhân tộc cũng bị mất, Trương gia đương nhiên cũng liền không còn tồn tại."),t("br"),t("br"),n._v("Có thể lựa chọn đầu thứ hai, tương đương với đem bản thân không có ra đời hài tử, tự tay bóp chết... Trong lòng khó chịu có thể tưởng tượng được."),t("br"),t("br"),n._v("Bất quá, có thể lựa chọn đầu thứ hai, bản thân vị này mới vừa biết cha, mặc dù nói chuyện không đáng tin cậy, đặt tên không quá đáng tin cậy, còn có chút sợ vợ... Nhưng làm việc, thật không thể nói."),t("br"),t("br"),n._v("“Một người bị tước đoạt huyết mạch, chỉ có một con đường có thể đi, cái kia chính là chết... Nhưng ta không cam tâm! Tìm được Dương sư, mượn nhờ một cái trong truyền thuyết bí pháp... Để một vị độc sư, tại mẹ của ngươi trong bụng bên trong hạ một loại đặc thù kịch độc, dùng độc mô phỏng theo huyết mạch, bảo đảm cho dù bị tước đoạt huyết mạch, cũng có thể có một chút sống sót sinh cơ...”"),t("br"),t("br"),n._v("“Ngươi sau khi sinh, Dương sư tự thân ra tay! Cuối cùng thành công, Trương Hoằng Thiên tiên tổ, rốt cục được cứu vớt, lần nữa lâm vào ngủ say, nhưng... Ngươi cũng trọng thương bất cứ lúc nào cũng sẽ tử vong, vì có thể để ngươi sống sót, đành phải lợi dụng truyền tống trận, đưa đến không biết tên cổ địa, hi vọng có một ngày, huyết mạch có thể một lần nữa sinh ra...”"),t("br"),t("br"),n._v("Nói đến đây, Hưng kiếm thánh nhìn qua, khắp khuôn mặt là áy náy: “Hiện tại xem ra... Thành công!”"),t("br"),t("br"),n._v("“Hài tử... Là cha không có năng lực, để ngươi chịu khổ!”"),t("br"),t("br"),n._v("[ truyen cua tui ʘʘ vn]"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1573"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1575"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);