(window.webpackJsonp=window.webpackJsonp||[]).push([[655],{1010:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1565-đai-thanh-canh-gioi-【-canh-4-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1565-đai-thanh-canh-gioi-【-canh-4-】"}},[n._v("#")]),n._v(" Chương 1565: Đại Thánh cảnh giới 【 Canh [4] 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1565: Đại Thánh cảnh giới "),t("br"),t("br"),n._v("Vu hồn rời đi thân thể, đem Hưng kiếm thánh cấp cho tuyệt phẩm linh thạch cùng ẩn chứa linh khí bảo vật, hội tụ vào một chỗ, Trương Huyền tinh thần tập trung, trong cơ thể nhanh chóng vận chuyển pháp quyết."),t("br"),t("br"),n._v("Kẽo kẹt! Kẽo kẹt!"),t("br"),t("br"),n._v("Hồn thể lấy tốc độ mà mắt thường cũng có thể thấy được nhanh chóng tăng thêm."),t("br"),t("br"),n._v("Tâm cảnh khắc độ đạt tới cửu tinh danh sư đỉnh phong, tu luyện, càng thêm chuyên tâm, vu hồn tấn cấp tốc độ càng nhanh, trước đó cần một canh giờ mới có thể tấn thăng một cái tiểu cấp bậc, hiện tại chỉ cần hơn nửa canh giờ liền có thể làm được."),t("br"),t("br"),n._v("Không biết qua bao lâu, hai mắt bỗng nhiên mở ra, trước mắt không gian giống như là bị xé nứt đồng dạng, xuất hiện nổ vang."),t("br"),t("br"),n._v("Vu hồn nhẹ nhàng xé ra, phạm vi trong vòng mười thước, sẽ xuất hiện từng đạo đen kịt vết rách, không gian đều không chịu nổi sức mạnh mạnh mẽ."),t("br"),t("br"),n._v("“Đáng tiếc, Vu Hồn Xuyên Toa không có đạt tới hoàn mỹ cấp bậc, không dễ tu luyện!”"),t("br"),t("br"),n._v("Trong mắt tràn đầy tiếc hận."),t("br"),t("br"),n._v("Lộ Xung đem từ tổ địa lấy được truyền thừa cho mình, Vu Hồn Xuyên Toa loại bí thuật này, tự nhiên cũng ở trong đó, chỉ tiếc, thiếu hụt quá nhiều, hơn nữa thực lực bây giờ hơi có khiếm khuyết, không cách nào tu luyện."),t("br"),t("br"),n._v("Nếu không, cùng đồ nhi đồng dạng, hồn phách có thể qua lại không gian, sức chiến đấu, nhất định tiến thêm một bước."),t("br"),t("br"),n._v("Đương nhiên, vu hồn chỉ là bị bức ép rơi vào đường cùng, mới sẽ sử dụng, danh sư đại lục đối loại nghề nghiệp này, còn là tồn tại cực lớn kính sợ."),t("br"),t("br"),n._v("Nói thật, ban ngày nếu không phải Dương sư cuối cùng xuất hiện, xưng hô “Sư huynh”, đừng nói để Danh Sư đường bồi thường, Độc điện, vu hồn sư hai loại chức nghiệp đều rất khó tốt."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Hồn thể đi vào thân thể."),t("br"),t("br"),n._v("Trương Huyền đứng dậy, nhẹ nhàng khẽ động, liền cảm thấy toàn thân lực lượng, cuồn cuộn không ngừng, so trước đó mạnh mẽ không biết gấp bao nhiêu lần."),t("br"),t("br"),n._v("Ngón tay nhẹ nhàng bắn ra."),t("br"),t("br"),n._v("Tê lạp!"),t("br"),t("br"),n._v("Viện lạc không gian xé rách ra một khối lớn, đem rơi vào trong đó đồ vật, toàn bộ biến mất."),t("br"),t("br"),n._v("“Tuy là cùng dung hợp đường chủ lệnh sau lực lượng so, kém không biết bao nhiêu, nhưng... Bình thường Thánh vực cửu trọng, không đối thủ nữa! Thậm chí Băng Nguyên cung vị đại trưởng lão kia, U Nhược Tâm, cũng có thể bằng vào thực lực đánh một trận!”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Trương Huyền lộ ra vẻ hài lòng."),t("br"),t("br"),n._v("Lần tu luyện này, chân khí, vu hồn, đều có cực lớn tiến bộ, cả hai điệp gia, nửa bước Đại Thánh cũng chưa chắc có thể chống lại, cẩn thận tính toán ra, đã đứng tại thế giới đỉnh phong hàng ngũ."),t("br"),t("br"),n._v("“Cũng nên cho Địa Ngục Thanh Long thú cùng Hỏa Diễm Kỳ Lân thú tăng lên chút lực lượng...”"),t("br"),t("br"),n._v("Cái này hai đầu Thánh thú, vì bảo vệ hắn, bỏ ra rất nhiều, nhất là người sau, lần này bị đại Trương Vô Ngân bắt lại, chịu không nhỏ tổn thương, nếu không phải mình thân phận chuyển đổi, biến thành Trương gia tiểu thiên tài, làm không cẩn thận đều có thể sẽ bị đánh chết."),t("br"),t("br"),n._v("Đại trưởng lão bị giam giữ, Thánh thú tự nhiên cũng lần nữa trở lại hắn nơi này."),t("br"),t("br"),n._v("Đem hắn thả ra, mỗi đầu đều ban cho mấy cái cấp chín đan dược."),t("br"),t("br"),n._v("Ăn vào đan dược về sau, Kỳ Lân thú thương thế rất nhanh triệt để khôi phục, lực lượng mặc dù không tiếp tục làm ra đột phá, nhưng cũng trở nên càng thêm cường đại, xem ra, bất cứ lúc nào cũng sẽ đột phá Thánh vực cửu trọng ràng buộc, xung kích cảnh giới cao hơn."),t("br"),t("br"),n._v("Còn Thanh Long thú, có cấp chín đan dược và Trương Huyền trợ giúp, không đến một đêm công phu, cũng từ Thánh vực cửu trọng sơ kỳ, trực tiếp trùng kích tới được đỉnh phong!"),t("br"),t("br"),n._v("So với Kỳ Lân thú, đều không kém quá nhiều."),t("br"),t("br"),n._v("Làm xong những này, thiên đã sáng choang, mới vừa đẩy cửa ra khỏi phòng, liền thấy cha Hưng kiếm thánh đã đi tới viện lạc."),t("br"),t("br"),n._v("“Bắt đầu tế tổ... Chúng ta đi qua đi!”"),t("br"),t("br"),n._v("Nói một tiếng, Hưng kiếm thánh lúc này mới phát hiện nhi tử thực lực cùng hôm qua dường như lại có biến hóa, con mắt nhịn không được sáng lên: “Tu vi của ngươi...”"),t("br"),t("br"),n._v("Hôm qua thoạt nhìn chỉ là Động Hư cảnh sơ kỳ, một đêm không thấy, đã đạt đến đỉnh phong, trên người lực lượng càng là mạnh mẽ vô song, Thánh vực cửu trọng cường giả cũng chưa chắc có thể thắng được."),t("br"),n._v(" “May mắn có chút đột phá...” Trương Huyền cười cười."),t("br"),t("br"),n._v("“May mắn?” Hưng kiếm thánh khóe miệng giật một cái."),t("br"),t("br"),n._v("Hôm qua cùng đối phương đối chiến, lực lượng so với hắn còn cường đại hơn, vốn cho rằng là chân thật thực lực, nhưng đi qua tiếp xúc, biết mượn bí pháp nào đó, vốn lo lắng, bí pháp sử dụng sau đó, sẽ tu vi tiêu giảm, hiện tại xem ra, suy nghĩ nhiều!"),t("br"),t("br"),n._v("Không chỉ không có tiêu giảm, còn dường như có càng lớn tiến bộ!"),t("br"),t("br"),n._v("Thật không biết đến cùng tu luyện thế nào."),t("br"),t("br"),n._v("“Ngươi đánh ta một quyền, ta nhìn ngươi không mượn bí pháp, rốt cuộc mạnh cỡ nào thực lực!”"),t("br"),t("br"),n._v("Trong lòng cảm xúc, Hưng kiếm thánh lần nữa nhìn lại."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Biết cha thực lực, tại không sử dụng đường chủ lệnh dưới tình huống, vượt xa bản thân, Trương Huyền cũng không có gì có thể xoắn xuýt, hít sâu một hơi, một quyền bỗng nhiên đánh ra."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Trước mắt không khí nổ tung, cuồn cuộn sóng khí, hướng về phía trước bỗng nhiên lan tràn, Hưng kiếm thánh lông mày giương lên, đem lực lượng nắm tại lòng bàn tay."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Khí lực tiêu tán, kìm lòng không được lui về sau một bước, con mắt tràn đầy rung động."),t("br"),t("br"),n._v("“Loại lực lượng này, so với Kiến Thần Bất Hoại cường giả, đều không kém quá nhiều...” Hưng kiếm thánh nhịn không được cảm xúc."),t("br"),t("br"),n._v("“Kiến Thần Bất Hoại?”"),t("br"),t("br"),n._v("Trương Huyền nghi ngờ nhìn qua."),t("br"),t("br"),n._v("“Tu vi đạt tới ngươi cấp bậc này, hẳn là cũng biết, Thánh vực cửu trọng cũng không phải là tu luyện điểm cuối cùng, mặt trên còn có Đại Thánh cấp bậc!” Hưng kiếm thánh giải thích."),t("br"),t("br"),n._v("“Ừm!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Chuyện này, hắn nghe Dương sư nói qua một lần, chỉ bất quá không phải rất kỹ càng."),t("br"),t("br"),n._v("“Võ giả cửu trọng, Hóa Phàm cửu trọng, Thánh vực cũng là cửu trọng, nhưng Đại Thánh cấp bậc, chỉ có tứ trọng!”"),t("br"),t("br"),n._v("Hưng kiếm thánh nói: “Đệ nhất trọng, chính là mới vừa nói Kiến Thần Bất Hoại!”"),t("br"),t("br"),n._v("“Đột phá Thánh vực, tu luyện giả trở nên càng thêm cường đại, nhưng bất kể nguyên thần còn là thân thể, đều đạt đến cực hạn, rất dễ dàng xuất hiện chia lìa, Kiến Thần Bất Hoại, trên thực tế chính là gặp Thần biết chân ngã, muốn chia lìa nguyên thần cùng thân thể, tiến hành sửa chữa, biến vô bệnh vô tai, càng thêm phù hợp tự nhiên, hòa vào tự nhiên! Tu vi đạt tới loại cảnh giới này, tuổi thọ lại tăng thêm 500 tuổi, có thể đạt tới 1500.”"),t("br"),t("br"),n._v("Bình thường cường giả Thánh vực, chỉ có một ngàn tuổi tuổi thọ, coi như một chút lợi hại cường giả có thể may mắn sống lâu chút tuế nguyệt, cũng sẽ không vượt qua quá nhiều, chẳng qua đạt tới Đại Thánh, Kiến Thần Bất Hoại, có thể làm dịu trong cơ thể già yếu, tuổi thọ đã đột phá đến 1500."),t("br"),t("br"),n._v("Chớ xem thường năm trăm năm, biến động bất ngờ, cường giả cũng không biết sinh ra bao nhiêu."),t("br"),t("br"),n._v("“Kiến Thần Bất Hoại cảnh giới, liên lụy đến càng sâu pháp quyết tu luyện, cùng bình thường Thánh vực khác biệt. Bình thường Thánh vực mỗi cái cấp bậc chia làm sơ kỳ, trung kỳ, hậu kỳ cùng đỉnh phong bốn cái tiểu cấp bậc, mà Đại Thánh bốn cái đại cấp bậc, mỗi một cái đều ẩn chứa sáu cái tiểu cấp bậc!”"),t("br"),t("br"),n._v("“Theo thứ tự là, sơ kỳ, trung kỳ, hậu kỳ, đỉnh phong, viên mãn cùng đại viên mãn!”"),t("br"),t("br"),n._v("“Cái này sáu cái, một cái so một cái khó, có ít người tấn cấp một cái, đều phải cần bỏ ra tới trăm năm thời gian... Đối với linh khí cần, cũng nhiều vô cùng, chính vì vậy, đột phá Thánh vực cửu trọng, liền càng ngày càng khó lấy tiến bộ!”"),t("br"),t("br"),n._v("“Ngươi vừa rồi một quyền, không kém đối nắm giữ Kiến Thần Bất Hoại sơ kỳ lực lượng, tuy là không tính là cực cao, nhưng thế hệ trẻ tuổi, đã đủ để vẫy vùng thiên hạ, không người có thể địch!”"),t("br"),t("br"),n._v("Trương Huyền nhẹ gật đầu, trong lòng nhịn không được có chút hiếu kỳ: “Kiến Thần Bất Hoại là Đại Thánh cảnh giới thứ nhất, cái kia cái thứ hai đâu?”"),t("br"),t("br"),n._v("“Cái thứ hai cảnh giới là Kim Thân cảnh!”"),t("br"),t("br"),n._v("Hưng kiếm thánh nói: “Kiến Thần Bất Hoại, Thần Minh mà tự biết. Kim Thân Vô Cấu, thần tính mà trường tồn. Đạt tới loại cảnh giới này, địa hỏa, hàn thủy, đã không cách nào tổn thương, bình thường thương thế, cũng có thể nhẹ nhõm khôi phục, không cần đan dược. Ngươi cái kia học sinh Viên Đào, Long Tê huyết mạch toàn bộ kích hoạt, liền có thể đạt tới loại cảnh giới này!”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1575"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1577"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);