(window.webpackJsonp=window.webpackJsonp||[]).push([[668],{1022:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1577-ai-dam-ton-thuong-han-【-canh-thu-nhat-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1577-ai-dam-ton-thuong-han-【-canh-thu-nhat-】"}},[n._v("#")]),n._v(" Chương 1577: Ai dám tổn thương hắn? 【 canh thứ nhất 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1577: Ai dám tổn thương hắn? "),t("br"),t("br"),n._v("Nữ tử trước mắt này, mặc dù so trước kia thấy qua Lạc Thất Thất, đẹp không chỉ gấp mười lần, nhưng khí chất, ánh mắt, nhất cử nhất động, vẫn như cũ đó có thể thấy được, chính là cái kia đối với hắn hâm mộ có ngợi khen, tràn đầy quyến luyến luyện đan học sinh, Lạc Thất Thất!"),t("br"),t("br"),n._v("Huyễn Vũ đế quốc quen biết, nghiêm túc học tập, cố gắng tu luyện, chưa hề là trái qua mệnh lệnh, cũng không có biểu hiện ra qua thân phận, để cho người ta sinh thương yêu."),t("br"),t("br"),n._v("Thanh Nguyên thành từ biệt, chân tình thổ lộ, một câu “Ta đối với ngươi không chỉ là ưa thích”, làm cho người ta tan nát cõi lòng."),t("br"),t("br"),n._v("Vốn cho rằng, lần trước từ biệt, lại khó gặp lại, nằm mơ cũng không ngờ tới..."),t("br"),t("br"),n._v("Lần nữa gặp mặt, lại là như vậy tình cảnh."),t("br"),t("br"),n._v("“Tiểu công chúa thế nào lại là Thất Thất?”"),t("br"),t("br"),n._v("Trương Huyền chỉ cảm thấy cả người đều nhanh muốn sụp đổ."),t("br"),t("br"),n._v("Một mực đến nay, hắn đều cho rằng, Lạc Nhược Hi chính là Lạc gia tiểu công chúa, vì cùng với nàng, liều mạng cố gắng, thậm chí cố ý đi tìm Trương gia phiền phức, cố ý tiếp cận Lạc Huyền Thanh..."),t("br"),t("br"),n._v("Thậm chí còn chuyên môn dùng thư viện dò xét qua."),t("br"),t("br"),n._v("Hiện tại đột nhiên nói Thất Thất là tiểu công chúa, Lạc Nhược Hi cũng không phải là... Đầu óc sắp nổ tung, khó mà tiếp nhận."),t("br"),t("br"),n._v("“Lạc lão sư...”"),t("br"),t("br"),n._v("Nhìn về phía cách đó không xa Lạc Nhược Hi, Lạc Thất Thất cũng là thân thể mềm mại run lên."),t("br"),t("br"),n._v("Nàng biết Trương sư, đối với mình chỉ là bình thường ưa thích, cũng không phải là cái gọi là yêu thương, nguyên nhân chính là như vậy, nghe được đối phương tràn đầy hưng phấn đến đây đón dâu, có chút hoảng hốt, vẫn muốn để ca ca xác nhận."),t("br"),t("br"),n._v("Hiện tại Lạc Nhược Hi xuất hiện, giờ mới hiểu được tới."),t("br"),t("br"),n._v("Sẽ không phải... Là coi nàng là thành đối phương đi!"),t("br"),t("br"),n._v("Hắn chân chính ưa thích, chẳng lẽ là vị này, Hồng Viễn đế quốc danh sư, Lạc sư?"),t("br"),t("br"),n._v("Nếu không, nàng nói “Hỏa Nguyên thành, mở rộng nội tâm; Khưu Ngô cung, ước định tam sinh” có ý tứ gì?"),t("br"),t("br"),n._v("Trong lòng như là đao quấy, vừa rồi hạnh phúc, trong chớp mắt hóa thành dòng nước, thân thể mềm mại dừng ở tại chỗ, không ngừng run rẩy, cả người đều có chút hoảng hốt, nói không ra lời."),t("br"),t("br"),n._v("Tuy là dùng Tĩnh Không châu phong ấn tu vi, tai hại rất nhiều, tu vi không đạt tới cảnh giới nhất định, một số ký ức, không cách nào khôi phục, nhưng... Trước mắt vị này, quả thực làm cảm động nàng tâm, đi vào nội tâm, dù là lúc trước, còn là hiện tại ký ức hoàn toàn thức tỉnh."),t("br"),t("br"),n._v("Ý nghĩ của nàng rất đơn giản, chỉ hy vọng có một ngày, mình thích nam nhân, có thể tại một cái muôn người chú ý dưới tình huống đem cưới về nhà chồng... Nhiều cao thủ như vậy, tự thân tới chúc mừng, quả thực là muôn người chú ý."),t("br"),t("br"),n._v("Nhưng... Đoán trúng mở đầu, lại không đoán được kết quả."),t("br"),t("br"),n._v("Khó trách lúc trước, Trương sư một mực cự tuyệt, bất kể như thế nào đều không chấp nhận, thậm chí đủ loại nói sang chuyện khác... Nguyên lai, khi đó đã có ưa thích người, chính là trước mắt vị này."),t("br"),t("br"),n._v("Nếu không, đối phương cũng không có khả năng lúc này xông tới, nói ra nói như vậy ngữ..."),t("br"),t("br"),n._v("Thân thể nhịn không được nhẹ nhàng run rẩy, một câu cũng nói không nên lời, Lạc Thất Thất như là ngu."),t("br"),t("br"),n._v("“Ngươi đã nói phải chờ ta, bất kể khó khăn đi nữa, cũng muốn cùng một chỗ...” Không để ý tới bốn phía ánh mắt, Lạc Nhược Hi nhìn qua."),t("br"),t("br"),n._v("Nàng bởi vì thân thế, từ trước tới nay không dám có cảm tình, bất kỳ người nào nhìn thấy, đều là lạnh lùng như băng, nhưng mà, lạnh lùng tâm, bị trước mắt vị này dần dần hòa tan... Khưu Ngô cung thề non hẹn biển, trả bất cứ giá nào đều nguyện ý, lúc này mới ngắn ngủi mấy tháng, liền cùng người khác thành thân, đây là cái gọi là thật tình?"),t("br"),t("br"),n._v("“Ngươi hiểu lầm, ta...”"),t("br"),t("br"),n._v("Gặp nàng bộ dáng này, Trương Huyền tràn đầy lo lắng: “Ta vẫn cho là, Lạc gia tiểu công chúa là ngươi...”"),t("br"),t("br"),n._v("“Ta?”"),t("br"),t("br"),n._v("Lạc Nhược Hi nghi ngờ."),t("br"),t("br"),n._v("“Đúng, ngươi chưa nói qua thân phận của ngươi, thực lực lại mạnh như vậy, lại thêm họ Lạc, ta mới cho rằng... Là luyện hóa Tĩnh Không châu Lạc gia công chúa...”"),t("br"),t("br"),n._v("Chỉ cảm thấy da đầu đều phải nổ tung, Trương Huyền cũng không biết giải thích như thế nào."),t("br"),t("br"),n._v("Người khác nhận sai ngược lại cũng thôi, bạn gái nhận sai, đây là thọ tinh treo cổ, chán sống ah!"),t("br"),t("br"),n._v("Có thể... Nhược Hi đi rồi, một câu đều không có nói, cũng không giải thích, đến cùng là ai, trẻ tuổi như vậy liền có loại thực lực này, đổi lại ai, đều sẽ liên tưởng đến Lạc gia công chúa."),t("br"),t("br"),n._v("Mấu chốt nhất là... Cơ duyên xảo hợp, cho tới hôm nay mới thôi, trước đến giờ đều không có cùng Lạc gia công chúa gặp mặt qua, thậm chí liền hình ảnh cũng không thấy qua."),t("br"),t("br"),n._v("“Thân phận của ta, tạm thời không thể cùng ngươi nói... Như vậy đi, hiện tại theo ta đi, ta dẫn ngươi đi một chỗ!”"),t("br"),t("br"),n._v("Thấy hắn tràn đầy lo lắng, Lạc Nhược Hi dừng lại một chút, cũng không biết là tin tưởng vẫn là không tin, mở miệng nói."),t("br"),t("br"),n._v("“Một chỗ? Nơi nào?”"),t("br"),t("br"),n._v("Nghe nàng không có tiếp tục truy cứu, biết có cơ hội giải thích, Trương Huyền lúc này mới thở phào nhẹ nhõm."),t("br"),t("br"),n._v("“Đi tới ngươi liền sẽ biết!” Lạc Nhược Hi nhẹ gật đầu, không tại nhiều nói, thân thể mềm mại xoay một cái, liền muốn rời khỏi."),t("br"),t("br"),n._v("“Tốt...”"),t("br"),n._v(" Chần chờ một chút, cắn răng một cái, Trương Huyền liền muốn đuổi theo."),t("br"),t("br"),n._v("Làm ra như thế lớn Ô Long, đã rất làm cho đối phương tức giận, nếu như lại mặc cho hắn đi xa, chỉ sợ kiếp này lại không cơ hội gặp lại."),t("br"),t("br"),n._v("“Chậm đã!”"),t("br"),t("br"),n._v("Mới đi hai bước, một bóng người liền ngăn tại trước mặt, chính là Lạc Huyền Thanh, chỉ thấy vị này ngày xưa hảo hữu, lúc này toàn thân run rẩy, tức giận đến bất cứ lúc nào cũng sẽ nổ tung: “Trương Huyền, ngươi muốn làm gì, người kia là ai?”"),t("br"),t("br"),n._v("“Lạc huynh, ta có việc...” Dừng lại một chút, Trương Huyền đạo, lời còn chưa dứt, chỉ thấy một cái nắm đấm, bỗng nhiên giáng xuống."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Không khí nổ tung, một quyền này, tích súc Lạc Huyền Thanh tất cả lực lượng, không khí đều bị bóp méo, còn chưa tới đến trên mặt, liền cảm nhận được đau rát đau, tựa hồ muốn người xé rách."),t("br"),t("br"),n._v("“Lạc huynh...”"),t("br"),t("br"),n._v("Không nghĩ tới hắn lại đột nhiên ra tay, Trương Huyền lui về phía sau một bước."),t("br"),t("br"),n._v("“Đừng gọi ta Lạc huynh, ta không chịu đựng nổi!” Gầm lên giận dữ, Lạc Huyền Thanh nắm đấm lít nha lít nhít rơi xuống, phảng phất muốn đem người trước mắt này, đánh thành bánh thịt, mới có thể cam tâm."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Trương Huyền liên tục trốn tránh, muốn giải thích, lại không biết nói như thế nào lên."),t("br"),t("br"),n._v("Bản thân cho rằng Lạc Nhược Hi, chính là Lạc gia tiểu công chúa, mới đồng ý hôn ước, nhiều người như vậy, đến đây tham gia đón dâu, mắt thấy đại hôn sắp tới, đột nhiên rời đi... Nói nhận lầm người..."),t("br"),t("br"),n._v("Chờ tại xé Lạc gia da mặt, mạnh mẽ trên mặt đất đạp, đổi lại ai, đều sẽ không chịu nổi."),t("br"),t("br"),n._v("“Là nam nhân, cũng đừng có trốn tránh, cùng ta đánh, hoặc là đem ta giết, giẫm lên thi thể của ta rời đi, hoặc là liền giải thích cho ta rõ ràng, người kia là ai, cùng ngươi quan hệ thế nào!”"),t("br"),t("br"),n._v("Một bên gào thét, Lạc Huyền Thanh một bên tấn công, như là mọc ra tám đầu cánh tay, tức giận bên trong, lực lượng cuồng bạo, mặc dù chỉ là Thánh vực bát trọng sơ kỳ, nhưng phát huy ra có thể so với Động Hư cảnh đỉnh phong lực lượng."),t("br"),t("br"),n._v("“Lạc huynh, ta...” Trương Huyền lần nữa lui về phía sau."),t("br"),t("br"),n._v("“Đủ rồi, ta nói không nên gọi ta là Lạc huynh, ta không chịu đựng nổi!”"),t("br"),t("br"),n._v("Liên tục công kích không đến đối phương, Lạc Huyền Thanh cắn răng một cái, lực lượng trong cơ thể nhanh chóng bạo tăng, thời gian nháy mắt, liền đạt đến một loại mức độ kinh người."),t("br"),t("br"),n._v("Huyết mạch lực lượng!"),t("br"),t("br"),n._v("Hắn thế mà không có chút nào cố kỵ, trực tiếp vận dụng huyết mạch chi lực."),t("br"),t("br"),n._v("Tê lạp!"),t("br"),t("br"),n._v("Thi triển huyết mạch chi lực Lạc Huyền Thanh, thực lực nhanh chóng bạo tăng, một cái chớp mắt đã có Thánh vực cửu trọng sơ kỳ thực lực, cổ tay khẽ đảo, một thanh trường kiếm xuất hiện tại lòng bàn tay, bỗng nhiên nhanh đâm mà tới."),t("br"),t("br"),n._v("Một kiếm này so vừa rồi nắm đấm mạnh mẽ không biết gấp bao nhiêu lần, nhắm thẳng vào mi tâm, hô hấp ở giữa, đem không gian xé rách, xuất hiện từng đạo vết rách."),t("br"),t("br"),n._v("Lạc gia bản thân liền am hiểu khống chế không gian, lại thêm vận dụng huyết mạch chi lực, lần này sức mạnh công kích, vậy mà so Vũ Không cảnh cường giả đỉnh phong tấn công đều phải đáng sợ, liền xem như loại này cường giả, đều chống lại không được."),t("br"),t("br"),n._v("Bất quá, Trương Huyền mặc dù là Vũ Không cảnh sơ kỳ, nhưng sức chiến đấu đã có thể so với Đại Thánh, lần nữa nhoáng một cái, tránh khỏi."),t("br"),t("br"),n._v("“Lạc huynh, lần này là ta không đúng, trở lại sẽ hướng ngươi giải thích cặn kẽ...”"),t("br"),t("br"),n._v("“Không cần trở lại, cũng không cần giải thích, ngày hôm nay ngươi không chết, chính là ta vong!”"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Trường kiếm mang theo xé rách không gian uy thế, tiếp tục bổ tới, một chiêu tiếp theo một chiêu, liên tục mấy chiêu chồng chất lên nhau, bốn phía tựa như tạo thành không gian phong bạo."),t("br"),t("br"),n._v("“Nếu như đâm trúng ta, ngươi có thể thoải mái chút, liền động thủ đi!”"),t("br"),t("br"),n._v("Thấy đối phương không cách nào khuyên can, cũng biết sai lầm tại bản thân, Trương Huyền lại không trốn tránh, ngừng lại."),t("br"),t("br"),n._v("Phốc!"),t("br"),t("br"),n._v("Trường kiếm thấu vai mà vào, xuất hiện một cái vết thương thật lớn, máu tươi cuồn cuộn chảy xuôi xuống."),t("br"),t("br"),n._v("“Chết!”"),t("br"),t("br"),n._v("Đâm thủng bờ vai của hắn, Lạc Huyền Thanh cắn răng một cái, trường kiếm bỗng nhiên run lên, ý định tiếp tục đánh xuống."),t("br"),t("br"),n._v("Bất quá, mới nhúc nhích một chút, liền cảm thấy toàn thân cứng đờ, giống như là bị một cỗ lực lượng khổng lồ cầm cố lại, muốn nhúc nhích, đều làm không được."),t("br"),t("br"),n._v("“Ta Lạc Nhược Hi ưa thích người, ta xem các ngươi, ai dám tổn thương hắn!”"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Một cỗ sóng khí kéo tới, Lạc Huyền Thanh “Lộc cộc lộc cộc lộc cộc lộc cộc!” Liên tục lui về phía sau, trường kiếm trong tay cũng không cầm giữ được nữa, thẳng tắp bay ra ngoài, trong chớp mắt biến mất ở phía xa, không thấy tung tích."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1587"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1589"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);