(window.webpackJsonp=window.webpackJsonp||[]).push([[67],{422:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1037-mot-nuoc-tieu-pha-phong-an"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1037-mot-nuoc-tieu-pha-phong-an"}},[n._v("#")]),n._v(" Chương 1037: Một nước tiểu phá phong ấn!")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1037: Một nước tiểu phá phong ấn!"),t("br"),t("br"),n._v("Thấy hắn dừng ở tại chỗ, biểu lộ kỳ lạ, Ngô sư nhịn không được nhìn lại: “Trương sư, chẳng lẽ cái này phong ấn có vấn đề gì?”"),t("br"),t("br"),n._v("Tại trong ấn tượng của hắn, trước mắt vị này Trương viện trưởng, từ trước tới nay bình tĩnh ổn trọng, bất kể sự tình gì đều mặt không đổi sắc, bây giờ lại bộ dáng này, khẳng định là xuất hiện khó mà giải quyết việc lớn."),t("br"),t("br"),n._v("“A, không có gì, chẳng qua là cảm thấy có chút kỳ quái thôi...”"),t("br"),t("br"),n._v("Khóe miệng giật một cái, Trương Huyền không nói thêm lời, đi đến bốn mươi mét có hơn, lúc này mới hướng mọi người vẫy vẫy tay: “Chư vị trước không vội tu luyện, chúng ta thương nghị một chút, như thế nào đem cái này phong ấn giải trừ, đi vào cửa!”"),t("br"),t("br"),n._v("“Trương sư, ngươi đã tìm tới phương pháp giải quyết?”"),t("br"),t("br"),n._v("Mọi người đồng loạt đi tới, tất cả đều con mắt tỏa ánh sáng."),t("br"),t("br"),n._v("“Là nghĩ đến một cái, chỉ là...”"),t("br"),t("br"),n._v("Gãi đầu một cái, Trương Huyền biết nhìn thấy nội dung quá mức không thể tưởng tượng, thực sự không cách nào lối ra, không thể làm gì khác hơn nói: “Các ngươi ai trên thân mang theo Thanh Cam linh dịch?”"),t("br"),t("br"),n._v("“Thanh Cam linh dịch?”"),t("br"),t("br"),n._v("Mọi người tất cả đều ngẩn ngơ, nguyên một đám hai mặt nhìn nhau."),t("br"),t("br"),n._v("Thanh Cam linh dịch, là Thánh thú nước tiểu học gọi, có vẻ chẳng phải thấp kém, cái đồ chơi này, bình thường tu luyện giả, đều sẽ mang theo một chút, ra ngoài thí luyện thời điểm, vẽ ra phạm vi, ban đêm phòng ngừa linh thú quấy rối..."),t("br"),t("br"),n._v("Lúc trước, Trương Huyền cùng Vưu Hư y sư sinh tử đấu, chính là bức đối phương uống cái này, để hắn buồn bực suýt chút nữa tại chỗ hộc máu mà chết."),t("br"),t("br"),n._v("“Cái này di tích, cổ xưa xa xăm, ta nghĩ lấy... Hẳn không có Thánh thú linh thú, cho nên, liền không chuẩn bị thứ này...”"),t("br"),t("br"),n._v("“Ta cũng vậy, ta trước đó chuẩn bị một bình, chẳng qua nương theo thực lực cường đại, cảm thấy không có gì dùng, một mực chứa, có nhục lịch sự, cũng liền ném đi!”"),t("br"),t("br"),n._v("“Chúng ta cái này Chiến sư đường, không bao giờ dùng thứ này, nếu có linh thú vây công, giết chính là, giết nhiều, tự nhiên cũng không đến làm loạn...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Mọi người tất cả đều lắc đầu."),t("br"),t("br"),n._v("Lần này có thể tới chỗ này, hầu như đều là có mặt mũi người có thân phận, Hàn Tự, Thanh Nguyên đế quốc trận pháp sư công hội hội trưởng, Ngô Như Phong, Mộc Viên thất tinh danh sư! Ô Thiên Khung, Thẩm Bình Triều, Ngũ Nhiên, ba đại học viện viện trưởng..."),t("br"),t("br"),n._v("Lại thêm Chiến sư đường, hầu như đều không có tùy thân mang theo Thánh thú nước tiểu thói quen, cho nên, hỏi như thế, tất cả mọi người đều có chút choáng váng."),t("br"),t("br"),n._v("“Không mang?”"),t("br"),t("br"),n._v("Xoa xoa mi tâm, Trương Huyền nhìn một cái cách đó không xa Lạc Nhược Hi."),t("br"),t("br"),n._v("Cô gái một mặt phẫn hận cắn răng: “Ta cũng không mang!”"),t("br"),t("br"),n._v("“Tốt a! Đã mọi người không mang...”"),t("br"),t("br"),n._v("Thấy tất cả mọi người không mang, Trương Huyền lắc đầu, xoa xoa mi tâm, cổ tay khẽ đảo, mặt đất xuất hiện một đống nồi bát hồ lô chậu, một mặt bất đắc dĩ nhìn qua: “Cái kia... Bản thân nước tiểu đi!”"),t("br"),t("br"),n._v("“Phốc!”"),t("br"),t("br"),n._v("Tất cả mọi người đồng thời đánh cái lảo đảo, cảm giác muốn hộc máu."),t("br"),t("br"),n._v("Ngươi muốn tìm Thanh Cam linh dịch, để chúng ta nước tiểu... Chuyển hướng có muốn hay không nhanh như vậy?"),t("br"),t("br"),n._v("Hơn nữa, tất cả mọi người là nhân vật có mặt mũi, trực tiếp nước tiểu... Tất cả mọi người mặt mũi phát điên, muốn làm chuyện điên rồ."),t("br"),t("br"),n._v("“Động tác nhanh lên, hơn nữa, tận lực nhiều tới một chút, có thể hay không phá giải phong ấn, liền nhìn các ngươi cung cấp bao nhiêu...”"),t("br"),t("br"),n._v("Không để ý tới mọi người mặt mũi xoắn xuýt, Trương Huyền khoát tay áo."),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Mọi người chỉ cảm thấy vẻ mặt xanh lét, thật sắp khóc."),t("br"),t("br"),n._v("“Được rồi, mỗi người căn cứ từ mình tình huống, cầm một cái công cụ...” Trương Huyền một ngón tay trên đất nồi bát hồ lô chậu."),t("br"),t("br"),n._v("Những vật này đều là theo những cái kia Dị Linh tộc nhân trữ vật giới chỉ bên trong tìm ra, hẳn là bọn họ nấu cơm công cụ, mười phần sạch sẽ, dùng để chứa đựng, không thể thích hợp hơn."),t("br"),t("br"),n._v("“Tốt a!”"),t("br"),t("br"),n._v("Thấy hắn vẻ mặt thành thật, không giống cố ý làm loạn, mọi người liếc mắt nhìn nhau, đành phải đồng thời nhẹ gật đầu, cắn răng một cái, từng người lấy một cái vật chứa."),t("br"),t("br"),n._v("Hô hô hô!"),t("br"),t("br"),n._v("Thời gian nháy mắt, người liền đi hết."),t("br"),t("br"),n._v("Lấy nước tiểu, bọn họ cũng không muốn trước mặt nhiều người như vậy... Thực sự quá mất mặt."),t("br"),t("br"),n._v("Trên quảng trường chỉ còn lại có Trương Huyền cùng Lạc Nhược Hi hai người."),t("br"),t("br"),n._v("“Khụ khụ, cái này cho ngươi...” Thấy cô gái còn không có cầm, chần chờ một chút, Trương Huyền từ dưới đất lấy một cái ly đưa tới."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Lạc Nhược Hi răng cắn sắp bể nát, lạnh lùng nhìn trước mắt gia hỏa, hận không thể đem hắn một bàn tay chụp chết."),t("br"),t("br"),n._v("“Thế nào?” Thấy nàng biểu lộ không vui, Trương Huyền đỡ cái cằm, mày nhăn lại: “Chẳng lẽ chê bé? Cái kia... Cái này cho ngươi!”"),t("br"),t("br"),n._v("Nói xong, có chút không tình nguyện đem trong tay mình một cái chậu lớn đưa tới."),t("br"),t("br"),n._v("“Cút!”"),t("br"),t("br"),n._v("Suýt chút nữa điên rồi, Lạc Nhược Hi gào lên một tiếng, cảm giác bất cứ lúc nào cũng sẽ nổ tung."),t("br"),t("br"),n._v("Đi đến bất kỳ địa phương nào, nàng đều là nữ thần, người khác kính ngưỡng tồn tại, cái tên này ngược lại tốt, thế mà cầm ly tới để cho mình... Ghê tởm hơn chính là, còn đem chậu đưa qua!"),t("br"),t("br"),n._v("Ngươi là cảm thấy ta có thể làm tràn đầy ư?"),t("br"),t("br"),n._v("Có tin ta hay không hiện tại liền đem ngươi giết chết..."),t("br"),t("br"),n._v("“Không nước tiểu liền không nước tiểu, nổi giận cái gì...”"),t("br"),t("br"),n._v("Không nghĩ tới đối phương không nhận hảo ý, còn để hắn cút, Trương Huyền một mặt oan ức."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Lạc Nhược Hi thật điên rồi."),t("br"),t("br"),n._v("Một mực đến nay, bất kỳ cái gì chuyện đều không có quá lớn biểu lộ, như là một bức bức tranh, lại hình như là người ngoài cuộc, có thể... Từ khi gặp cái tên này, mỗi lần đều sẽ bị khí gần chết, tất cả bình tĩnh bình tĩnh, đều bị phá trừ không còn một mảnh."),t("br"),t("br"),n._v("“Được rồi, ta đi...”"),t("br"),t("br"),n._v("Thấy nói thêm gì đi nữa, cô gái khẳng định sẽ nổ thành hai nửa, Trương Huyền thân thể xoay một cái, xoay người bỏ chạy."),t("br"),t("br"),n._v("Thấy hắn rời khỏi, Lạc Nhược Hi vẫn như cũ cảm thấy lên cơn giận dữ, chính hận không thể đem cái tên này, bắt trở lại đánh một trận, liền nghe đến cách đó không xa một cái tràn đầy thanh âm hốt hoảng vang lên."),t("br"),t("br"),n._v("“Ta không có đồ vật, không tiểu được làm sao bây giờ?”"),t("br"),t("br"),n._v("Quay đầu nhìn lại, chỉ thấy Kim Nguyên đỉnh, gấp gáp loạn chuyển, tựa hồ tại quan sát bản thân, có thể hay không giải quyết vấn đề này, vừa nhìn phía dưới, càng là gấp gáp sắp khóc."),t("br"),t("br"),n._v("“Các ngươi chớ đi, ai có thể mượn ta một cái... Còn có, chủ nhân, ngươi có thể hay không cho ta rèn đúc một cái...”"),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Lạc Nhược Hi tức xạm mặt lại."),t("br"),n._v("Cái này đều gặp một đám người nào..."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Không biết cô gái vì cái gì nổi giận, Trương Huyền tìm một chỗ không người, chuẩn bị ổn thoả, đang định bản thân cũng làm một chút đi ra, đột nhiên vỗ trán một cái."),t("br"),t("br"),n._v("“Ta làm sao ngốc như vậy...”"),t("br"),t("br"),n._v("Sau một khắc, liền biến mất tại nguyên chỗ, đã đi vào Thiên Nghĩ Phong sào."),t("br"),t("br"),n._v("“Tất cả đều tới đây cho ta...”"),t("br"),t("br"),n._v("Lơ lửng giữa không trung, Trương Huyền hét lớn một tiếng."),t("br"),t("br"),n._v("Hô hô hô!"),t("br"),t("br"),n._v("Thời gian nháy mắt, bên trong chứa đựng rất nhiều Thánh thú liền bay tới."),t("br"),t("br"),n._v("Lần này biết di tích nguy hiểm, về danh sư học viện thời điểm, cố ý mang nhiều một chút, Tử Dương thú, Vân Vụ lĩnh chín đại vương giả cùng vượt qua Thánh vực, cộng lại chừng hơn trăm đầu nhiều."),t("br"),t("br"),n._v("“Đều cho ta nước tiểu chút...”"),t("br"),t("br"),n._v("Lần nữa lấy ra một đống nồi bát hồ lô chậu, hướng chúng thú đưa tới."),t("br"),t("br"),n._v("“Rống?”"),t("br"),t("br"),n._v("“Rống!”"),t("br"),t("br"),n._v("Chúng thú mặc dù nghi ngờ, nhưng đối phương là chủ nhân, không dám vi phạm, tất cả đều từng người lấy một cái chậu lớn xoay người sang chỗ khác."),t("br"),t("br"),n._v("Trong lúc nhất thời mùi khai ngút trời."),t("br"),t("br"),n._v("Thời gian không dài, chỉ thấy mỗi đầu thú đều mang một cái bồn lớn đi tới, ít nhất đều có hơn mười cân."),t("br"),t("br"),n._v("Đám thánh thú này, cái đầu đều nắm chắc mét cao thấp, hình thể khổng lồ, thật muốn nước tiểu lời nói, số lượng tự nhiên cũng nhiều làm người ta kinh ngạc."),t("br"),t("br"),n._v("“Không sai!”"),t("br"),t("br"),n._v("Thấy đám gia hoả này, quả nhiên ra sức, Trương Huyền thoả mãn nhẹ gật đầu, bàn tay một trảo, tất cả chậu lớn Thanh Cam linh dịch hội tụ vào một chỗ, đủ để chứa tràn đầy một cái thùng lớn, tiếp cận hơn hai ngàn cân."),t("br"),t("br"),n._v("“Được rồi, đều đi nghỉ ngơi đi!”"),t("br"),t("br"),n._v("Khoát tay áo, Trương Huyền không tiếp tục để ý đám người kia, thân thể nhoáng một cái, theo Thiên Nghĩ Phong sào bay ra ngoài."),t("br"),t("br"),n._v("Lần nữa trở lại quảng trường, chỉ thấy không ít người đã bay trở về, chính là Chiến sư đường mọi người."),t("br"),t("br"),n._v("“Đây là thứ ngươi muốn!”"),t("br"),t("br"),n._v("Phùng Huân hừ một tiếng, đem trong tay chậu đưa tới, mọi người khác cũng đều nhao nhao đưa tới, có điều, số lượng cũng không nhiều, Chiến sư đường hơn mười người, tính gộp lại, cũng chỉ có non nửa chậu mà thôi."),t("br"),t("br"),n._v("“Ít như vậy?”"),t("br"),t("br"),n._v("Trương Huyền nhìn một cái, nguyên một đám hùng dũng oai vệ khí phách hiên ngang Chiến sư đường mọi người, nhịn không được nhướng mày."),t("br"),t("br"),n._v("Nhiều người như vậy, cả đám đều long tinh hổ mãnh, không nghĩ tới chỉ có ngần ấy..."),t("br"),t("br"),n._v("“Ít? Ngươi đâu? Có bản lĩnh lấy ra cho chúng ta nhìn một chút, đoán chừng đều không có chúng ta nhiều...”"),t("br"),t("br"),n._v("Nghĩ tới bị đánh hình, Phùng Huân liền giận không chỗ phát tiết, cắn răng trách mắng."),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Trương Huyền đang muốn nói chuyện, chỉ thấy Ô Thiên Khung mấy vị viện trưởng cùng Mộc sư, Ngô sư đám người bay tới, từng người vẻ mặt đỏ lên, có chút xấu hổ."),t("br"),t("br"),n._v("Đem cái chậu bên trong đông tây hợp thành cùng một chỗ, phát hiện còn không có Chiến sư đường mọi người nhiều."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trương Huyền bất đắc dĩ."),t("br"),t("br"),n._v("May mắn hắn nghĩ ra biện pháp, nếu không, chỉ bằng vào bọn họ những người này, những vật này, đừng nói phá trận, liền cho người ta gãi ngứa ngứa đều không đủ."),t("br"),t("br"),n._v("“Quên đi, các ngươi những này không cần, quá ít, dùng ta đi!” Đem mọi người tụ lại, liền một chậu cũng chưa tới đồ vật, tiện tay ném xuống đất, Trương Huyền lắc đầu."),t("br"),t("br"),n._v("“Dùng ngươi? Một mình ngươi chẳng lẽ có thể so sánh chúng ta nhiều người như vậy còn có nhiều?”"),t("br"),t("br"),n._v("Nghe hắn nói ra lời này, Phùng Huân càng thêm tức giận, nhịn không được nói."),t("br"),t("br"),n._v("“Đây là ta...”"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Một cái cực lớn thùng gỗ rơi trên mặt đất, chừng hơn hai ngàn cân, đập lên một mảnh bụi bặm."),t("br"),t("br"),n._v("“Phốc...”"),t("br"),t("br"),n._v("“Làm sao có thể?”"),t("br"),t("br"),n._v("Tất cả danh sư, Chiến sư nhìn qua, đều giống như muốn điên rồi, ánh mắt quái dị, mặt mũi phát điên."),t("br"),t("br"),n._v("Cho dù ngươi bàng quang lớn, cũng phải có cái hạn độ đi, nhiều như vậy, đều có thể đi vào tắm rửa, nhẹ nhõm chết đuối ngươi đến mấy lần... Từ nơi nào làm ra?"),t("br"),t("br"),n._v("Nhất là Phùng Huân, chỉ cảm thấy máu tươi lần nữa xông lên cổ họng."),t("br"),t("br"),n._v("Đến cùng làm sao làm ra?"),t("br"),t("br"),n._v("Sức chiến đấu mạnh mẽ ngược lại cũng thôi, còn nước tiểu nhiều như vậy... Ngươi bàng quang bên trong, có không gian gấp ư?"),t("br"),t("br"),n._v("Sớm biết, một mình ngươi liền có thể làm ra nhiều như vậy, chúng ta còn chơi đùa cái gì ah!"),t("br"),t("br"),n._v("“Được rồi, ta phá trận...”"),t("br"),t("br"),n._v("Không để ý tới vẻ mặt của mọi người, Trương Huyền bàn tay một trảo, chân khí hội tụ, đem thùng gỗ nhấc lên, bàn chân đạp mạnh, thân thể treo lơ lửng giữa trời, trực tiếp hướng phong ấn bay đi."),t("br"),t("br"),n._v("Thấy hắn muốn phá trận, mọi người không còn xoắn xuýt sự tình vừa rồi, đồng loạt hướng cửa nhìn lại."),t("br"),t("br"),n._v("Cái này phong ấn, bọn họ vừa rồi nghiên cứu đã lâu rồi, không có bất kỳ biện pháp nào, nhiều như vậy Dị Linh tộc nhân cũng chỉ có liều mạng công kích, nghĩ không ra cái khác chiêu số, vị này Trương sư, chỉ mò thoáng cái, tìm đến phương pháp, đồng thời để bọn hắn chuẩn bị nước tiểu..."),t("br"),t("br"),n._v("Tất cả mọi người muốn nhìn một chút, đến cùng dùng phương pháp gì, cùng nước tiểu lại có quan hệ thế nào."),t("br"),t("br"),n._v("Vù vù!"),t("br"),t("br"),n._v("Ánh mắt mọi người bên trong, Trương Huyền đi tới cửa ngay phía trên, ánh mắt ngưng trọng, đột nhiên đối với phía dưới thùng gỗ bỗng nhiên một chút."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Thùng gỗ lập tức xuất hiện một cái lỗ thủng, ẩn chứa trong đó nước tiểu, trực tiếp hướng phong ấn bắn tới."),t("br"),t("br"),n._v("Xì xì xì xì...!"),t("br"),t("br"),n._v("Thứ này cùng phong ấn tiếp xúc, lập tức giống như là lưu toan gặp sắt thép, người sau phát ra bị ăn mòn thanh âm, nguyên bản chắc chắn vô cùng phong ấn, trong nháy mắt tan rã."),t("br"),t("br"),n._v("(Nhìn lão nhai ăn tết đều đổi mới, cả năm không ngừng, có nên hay không ném hai cái nguyệt phiếu, vì chăm chỉ người reo hò? Để chúng ta đem những cái kia xin nghỉ phép các đại thần tiêu diệt đi! Ta đều nhìn không được!)"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1045"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1047"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);