(window.webpackJsonp=window.webpackJsonp||[]).push([[675],{1030:function(n,h,t){"use strict";t.r(h);var c=t(44),g=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1582-bach-khe-son-【-canh-thu-hai-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1582-bach-khe-son-【-canh-thu-hai-】"}},[n._v("#")]),n._v(" Chương 1582: Bạch Khê sơn 【 canh thứ hai 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1582: Bạch Khê sơn "),t("br"),t("br"),n._v("“Lần này muốn đi địa phương, là liên minh đế quốc Bạch Khê sơn!”"),t("br"),t("br"),n._v("Đứng tại một đầu Thánh thú lưng thú bên trên, nhìn về phía trước nhanh chóng bay lượn mây trắng, Lạc Nhược Hi giải thích nói."),t("br"),t("br"),n._v("“Bạch Khê sơn?” Trương Huyền thân thể sững sờ: “Ta biết nơi này...”"),t("br"),t("br"),n._v("Từ Thanh Điền Hoàng trong tay lấy được bộ kia tranh vẽ, từng vẽ ra tòa rặng núi này hình dạng, trong đó có Khổng sư hư ảnh xuất hiện, về sau bị hắn đổi thành mỹ nữ đi tắm... Lúc ấy một mực không biết cụ thể ở đâu, còn là hỏi thăm Lạc Huyền Thanh, mới đến chính xác trả lời chắc chắn."),t("br"),t("br"),n._v("Lại về sau, sự tình lần lượt từng kiện, một mực không có cơ hội đi qua nhìn một chút."),t("br"),t("br"),n._v("Chẳng lẽ, cái kia cái gọi là Khổng sư hư tượng, thật ra thì ẩn giấu đi hắn lưu lại truyền thế thiên phù?"),t("br"),t("br"),n._v("Còn thật sự có khả năng, nếu không, cũng không có khả năng dẫn tới Dị Linh tộc Thanh Điền hoàng thất, dốc toàn bộ lực lượng, thậm chí Ngoan Nhân, đều dính dáng trong đó."),t("br"),t("br"),n._v("“Nếu biết địa phương, không bằng sử dụng truyền tống trận, tốc độ càng nhanh!” Biết muốn đi vị trí, Trương Huyền nói."),t("br"),t("br"),n._v("“Này cũng không cần!”"),t("br"),t("br"),n._v("Lạc Nhược Hi mỉm cười: “Sắp đặt trận pháp quá phiền toái, hơn nữa không quá an toàn... Ngột Thần!”"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Đồng tử nhẹ gật đầu, tiến về phía trước một bước, đi tới phía trước cửa sổ, ngón tay nhẹ nhàng điểm một cái."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Trước mắt không khí điên cuồng bắt đầu vặn vẹo, không gian cũng xuất hiện gấp, thời gian nháy mắt, tạo thành một vòng xoáy khổng lồ."),t("br"),t("br"),n._v("Vòng xoáy càng chuyển càng nhanh, ảnh hưởng phạm vi cũng càng lúc càng lớn, rất nhanh nơi trung tâm nhất, bão mắt đồng dạng, tạo thành một cái ổn định mà trống không khu vực."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Dưới thân Thánh thú dường như sớm đã thành thói quen loại tình cảnh này, cánh vũ động, thẳng tắp hướng trước mắt bão mắt vọt tới."),t("br"),t("br"),n._v("“Đây là không gian áp súc...” Trương Huyền chấn động trong lòng, khắp khuôn mặt là khó tin."),t("br"),t("br"),n._v("Lợi hại cường giả có thể đem không gian áp súc lên, làm được Súc Địa Thành Thốn, một bước ngàn dặm."),t("br"),t("br"),n._v("Vốn cho rằng chỉ là truyền thuyết, không nghĩ tới trước mắt cái này đồng tử, tiện tay liền làm được."),t("br"),t("br"),n._v("“Không tính là không gian áp súc, đây là không gian một loại lợi dụng mà thôi.” Đồng tử cười cười, nhìn về phía trước: “Bình thường không gian, chúng ta thoạt nhìn là mười phần ổn định, không có kẽ hở, liền xem như thần thức, cũng không cách nào phát giác dị thường. Nhưng trên thực tế có một ít đặc thù lỗ sâu tồn tại, chỉ cần tìm được liền có thể nhẹ nhõm nhảy đến chỗ xa hơn, từ đó không nhận khoảng cách ràng buộc.”"),t("br"),t("br"),n._v("“Ah!” Trương Huyền cái hiểu cái không."),t("br"),t("br"),n._v("Tuy là hắn học qua Diễn Không thiên thư, nhưng chỉ đạt đến đệ tam trọng cảnh giới, khoảng cách đỉnh phong nhất còn có khoảng cách rất lớn."),t("br"),t("br"),n._v("“Loại này bước nhảy không gian là Cổ Thánh cường giả, mới có thể sử dụng thủ đoạn, Ngột Thần cũng là mượn một số đặc thù Thánh thú huyết dịch, hơn nữa đối với không gian lý giải, mới lấy thành công.”"),t("br"),t("br"),n._v("Lạc Nhược Hi giải thích."),t("br"),t("br"),n._v("“Ừm!” Trương Huyền gật đầu, cũng nhịn không được nữa trong lòng tò mò: “Ngột Thần bằng chừng ấy tuổi, tại sao có thể có cường đại như vậy thực lực?”"),t("br"),t("br"),n._v("Trước mắt vị này đồng tử, thoạt nhìn liền mười ba mười bốn tuổi bộ dạng, lại nắm giữ bản thân sử dụng Khổng sư chi huyết mới có thể địch nổi thực lực."),t("br"),t("br"),n._v("Cho dù tận mắt nhìn thấy, đều cảm thấy có chút không thể tưởng tượng."),t("br"),t("br"),n._v("“Hắn huyết mạch đặc thù, thoạt nhìn nhỏ tuổi, trên thực tế lại sống không biết bao lâu.” Lạc Nhược Hi lắc đầu, không tại vấn đề này tiếp tục xoắn xuýt, chỉ về phía trước: “Đến!”"),t("br"),t("br"),n._v("Vội vàng nhìn về phía trước, Trương Huyền lúc này mới phát hiện dưới thân phi hành Thánh thú đã từ trong thông đạo xuyên qua, một tòa nguy nga sơn mạch bất ngờ xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Ngọn núi lớn này cắm vào trong mây không biết cao bao nhiêu, chiếm diện tích bao la, cũng không biết rộng bao nhiêu, nhìn một cái, to lớn cảm giác áp bách, đấu đá mà đến, để cho người ta hô hấp đều cảm thấy không quá trôi chảy."),t("br"),t("br"),n._v("“Là Bạch Khê sơn!” Trương Huyền vẻ mặt nghiêm túc."),t("br"),n._v(" Trước mắt ngọn núi lớn này, chính là trước đó tại mặt nạ đồng xanh bức họa kia bên trên nhìn thấy, giống nhau như đúc, không sai chút nào."),t("br"),t("br"),n._v("Chỉ bất quá chân thực, điệu bộ bên trong càng thêm nguy nga, càng thêm làm cho người rung động."),t("br"),t("br"),n._v("“Truyền thế thiên phù ngay tại tòa rặng núi này bên trong, vị trí cụ thể ở đâu? Ta cũng không rõ lắm, có điều, có thể dùng đặc thù đồ vật tiến hành điều tra.”"),t("br"),t("br"),n._v("Hừ một tiếng, Lạc Nhược Hi cổ tay nhẹ nhàng trên không trung vạch một cái, một quyển sách bộ dáng đồ vật xuất hiện tại trước mặt."),t("br"),t("br"),n._v("Quyển sách này mang theo đặc thù khí tức, cho người ta một loại tựa như ảo mộng cảm giác, Trương Huyền nhìn một cái, đang muốn nói chuyện, đột nhiên cảm giác được cái gì, cổ tay khẽ đảo, một thủy tinh cầu xuất hiện tại lòng bàn tay."),t("br"),t("br"),n._v("Khưu Ngô cung!"),t("br"),t("br"),n._v("Chỗ này di tích, là năm đó Khổng sư tôi tớ, Khưu Ngô Cổ Thánh lưu lại, cùng Lạc Nhược Hi cũng là ở đây, xác định tình lữ quan hệ, thời khắc này Khưu Ngô cung, không ngừng lay động, hình như đối cái này thư tịch, có cái gì đặc thù tình cảm."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trương Huyền tràn đầy nghi ngờ."),t("br"),t("br"),n._v("Làm Khưu Ngô cung chưởng khống giả, hắn rõ ràng cảm thụ được, trước mắt thư tịch, cùng lòng bàn tay thủy tinh cầu, cùng ra nhất mạch, có không thể dứt bỏ nguồn gốc."),t("br"),t("br"),n._v("“Quyển sách này, chính là từ Khưu Ngô cung lấy được, có thể nhờ vào đó, tìm kiếm truyền thế thiên phù vị trí cụ thể!” Nhìn ra trong lòng của hắn nghi ngờ, Lạc Nhược Hi nói."),t("br"),t("br"),n._v("“Cái này...” Trương Huyền sững sờ."),t("br"),t("br"),n._v("Lúc đó hắn cùng cô bé trước mắt, một mực tại cùng một chỗ, lúc nào cầm đi cái này, cũng không hiểu rõ tình hình?"),t("br"),t("br"),n._v("Trong truyền thuyết, Khưu Ngô cung tồn tại có thể tìm kiếm truyền thế thiên phù bảo vật, hắn từng chuyên môn đi vào qua mấy lần, đều không có phát giác, không nghĩ tới, đã đến nàng trong tay."),t("br"),t("br"),n._v("“Ta đi vào Khưu Ngô cung chính là vì tìm kiếm thứ này, đi vào cung điện về sau, từng đơn độc đi lại một đoạn thời gian, chính là lúc này, tìm tới!” Thấy hắn nghi ngờ, Lạc Nhược Hi giải thích nói."),t("br"),t("br"),n._v("“Ừm!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Lúc trước nữ hài khăng khăng muốn cùng hắn cùng một chỗ đi vào Khưu Ngô cung, cuối cùng lại bảo vật gì đều không có muốn, công pháp bí tịch, cũng không xem thêm liếc mắt, thậm chí liền cung điện, đều bị bản thân luyện hóa, vẫn cảm thấy rất kỳ quái, nguyên lai mục đích là cái này."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Hai người đang khi nói chuyện, thư tịch phát ra một tiếng kêu khẽ, thẳng tắp hướng về trên núi bay đi."),t("br"),t("br"),n._v("“Đuổi theo!”"),t("br"),t("br"),n._v("Lạc Nhược Hi không nói thêm lời, theo sát trên đó, Trương Huyền cũng theo sau."),t("br"),t("br"),n._v("Thẳng tắp bay lên trên đi, thời gian không dài, đi tới giữa sườn núi vị trí, thư tịch ngừng lại."),t("br"),t("br"),n._v("“Chính là chỗ này...”"),t("br"),t("br"),n._v("Nhìn kỹ liếc mắt, Trương Huyền nhẹ gật đầu."),t("br"),t("br"),n._v("Nơi này chính là lúc trước tranh vẽ bên trong, bị hắn đổi thành mỹ nhân đi tắm địa phương."),t("br"),t("br"),n._v("Nhìn như vậy lên, quyển sách này tìm kiếm không có sai lầm chút nào, nơi đây hẳn là có cái không gian gấp, có thể đi vào nơi nào đó di tích, từ đó tìm kiếm cái gọi là truyền thế thiên phù."),t("br"),t("br"),n._v("“Xác định vị trí, chúng ta ở đây chờ đợi là được, không có gì bất ngờ xảy ra, cái kia bắt Triệu Nhã, Ngụy Như Yên người, nhất định sẽ tới!”"),t("br"),t("br"),n._v("Vẫy tay một cái, đem thư tịch thu về nhẫn, Lạc Nhược Hi cũng không lo lắng tìm kiếm cái gọi là không gian gấp."),t("br"),t("br"),n._v("“Truyền thế thiên phù, Trương gia cùng Lạc gia cùng Danh Sư đường đều có một cái, ngươi xác định, bọn họ sẽ đến?” Trương Huyền nghi ngờ."),t("br"),t("br"),n._v("Đã mục đích của đối phương là mở ra Khổng miếu, hiện tại đã bắt được Triệu Nhã đám người, coi như muốn tìm kiếm truyền thế thiên phù, Danh Sư đường, Trương gia, Lạc gia là mục tiêu lớn nhất, không có khả năng biết nơi này cũng có a?"),t("br"),t("br"),n._v("Dù sao, không có bức kia cầu, cùng Lạc Nhược Hi trong tay thư tịch, liền xem như bọn họ, cũng không có khả năng biết nơi này."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1593"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1595"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);