(window.webpackJsonp=window.webpackJsonp||[]).push([[68],{423:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1038-that-lon-ah"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1038-that-lon-ah"}},[n._v("#")]),n._v(" Chương 1038: Thật lớn ah!")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1038: Thật lớn ah!"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Đem một màn này nhìn ở trong mắt, Hàn hội trưởng giống như là nhớ tới cái gì, con ngươi co rụt lại, âm thanh run rẩy: “Ta hiểu được...”"),t("br"),t("br"),n._v("“Hiểu?”"),t("br"),t("br"),n._v("“Đến cùng chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Hắn hiểu được, mọi người còn tất cả đều tràn đầy mê hoặc, nhịn không được nhìn lại."),t("br"),t("br"),n._v("Liền một Thánh vực tứ trọng Ngô sư, toàn lực công kích, đều không tổn hại chút nào mạnh mẽ phong ấn, làm sao vẩy chút... Thứ này, liền tan thành mây khói, bắt đầu sụp đổ?"),t("br"),t("br"),n._v("“Nếu ta đoán không lầm, cái này phong ấn hẳn là Thuần Dương thuộc tính, không nhìn được nhất vẩn đục tính đồ vật cùng nước... Nếu như chỉ là nước lời nói, muốn phá vỡ cũng là rất khó, có thể dùng để cho người ta khó có thể chịu đựng Thanh Cam linh dịch, liền sẽ phòng ngự không được, tự động sụp đổ...”"),t("br"),t("br"),n._v("Hàn hội trưởng nói."),t("br"),t("br"),n._v("“Thuần Dương thuộc tính phong ấn, cũng không có loại này đặc tính a? Ta trước đó làm sao trước đến giờ chưa nghe nói qua?” Ngô sư cau mày."),t("br"),t("br"),n._v("Thuần Dương phong ấn, gặp qua không ít, nhưng từ chưa nghe nói qua, dùng nước tiểu liền có thể phá giải, nếu như đơn giản như vậy, loại trận pháp này cũng không có tồn tại ý nghĩa."),t("br"),t("br"),n._v("“Đơn thuần Thuần Dương phong ấn, đương nhiên sẽ không có loại này đặc tính, nhưng... Nếu là không tì vết Thuần Dương phong ấn, liền không nói được rồi. Thật ra thì rất nhiều trận pháp, đều sợ hãi dơ bẩn, thật giống như có người bị nhốt trận pháp, làm sao đều phá giải không được, kết quả nữ tử sản xuất, liền sẽ để linh khí tán loạn, trận pháp hoàn toàn sụp đổ!”"),t("br"),t("br"),n._v("Hàn hội trưởng nói."),t("br"),t("br"),n._v("“Cái này... Ta hình như nghe nói qua...”"),t("br"),t("br"),n._v("Một bên Mộc sư gật đầu."),t("br"),t("br"),n._v("Rất nhiều người đều cho rằng nữ tử sản xuất lúc chảy ra máu là dơ bẩn nhất, dùng để phá giải trận pháp ổn định, không thể thích hợp hơn, không ít người liền dùng thứ này, rèn luyện phá trận lợi khí, chuyên môn dùng để phá giải trận pháp."),t("br"),t("br"),n._v("“Liếc mắt nhìn, liền biết rõ ràng phong ấn thiếu hụt, Trương viện trưởng đối với trận pháp lý giải, quả thực nghe rợn cả người...”"),t("br"),t("br"),n._v("Thấy mọi người hiểu được, Hàn hội trưởng lần nữa nhìn về phía cách đó không xa lơ lửng thanh niên, một tiếng cảm khái."),t("br"),t("br"),n._v("Không tì vết Thuần Dương phong ấn, nói đến đơn giản, trên thực tế, căn bản không dễ dàng như vậy, cho dù nắm giữ nước tiểu loại này cực bẩn đồ vật, tìm không thấy phong ấn tiết điểm, một khi giội lên đi, không chỉ không thể phá giải, làm không cẩn thận còn có thể gặp phải phản kích."),t("br"),t("br"),n._v("Hình như dùng sức mạnh tiến hành công kích đồng dạng, thi triển ra càng lớn, chịu đến phản kích, liền sẽ trở nên mạnh hơn, cuối cùng để cho người ta chống lại không được."),t("br"),t("br"),n._v("Cái này phong ấn chính là như vậy."),t("br"),t("br"),n._v("Thoạt nhìn Trương viện trưởng động tác đơn giản, đem thùng gỗ mang theo bầu trời, làm phá liền đem phong ấn giải trừ, trên thực tế, thùng gỗ độ cao, chất lỏng lực trùng kích, rơi vào phong ấn bên trên tiết điểm... Kém một chút đều không được."),t("br"),t("br"),n._v("Đối chưởng khống chế lực yêu cầu cực cao."),t("br"),t("br"),n._v("Đoán chừng cũng chỉ có tên biến thái này, mới có thể nghĩ ra loại này phương pháp phá giải, hơn nữa một lần thành công, không xuất hiện bất kỳ sai lầm."),t("br"),t("br"),n._v("“Quỷ dị như vậy phương pháp phá giải, cái kia... Lúc trước Hồng Viễn học viện Chương Dẫn Khâu đám người, là như thế nào đi vào?” Một cái Chiến sư nhịn không được nhìn qua."),t("br"),t("br"),n._v("Những người khác cũng đều tràn đầy kỳ quái."),t("br"),t("br"),n._v("Loại này phương pháp cổ quái, đoán chừng cũng chỉ có cái này Trương viện trưởng mới có thể nghĩ ra, Chương Dẫn Khâu thái độ làm người ngay thẳng, cứng nhắc, cho dù biết, đoán chừng cũng không muốn đi làm."),t("br"),t("br"),n._v("“Cụ thể như thế nào đi vào, ta không rõ ràng, bất quá... Trước đó nhiều năm như vậy cái này di tích, cũng không có xuất hiện qua, bọn họ sau khi tiến vào, chúng ta mới có thể đi tới nơi này... Chỉ sợ, nắm giữ chúng ta không biết bí mật, hoặc là nhận được đi vào chìa khóa nơi này!”"),t("br"),t("br"),n._v("Suy tư một chút, Hàn hội trưởng nói."),t("br"),t("br"),n._v("Bất luận cái gì di tích, muốn cho hậu nhân đi vào, liền khẳng định sẽ có gọi là chìa khoá, hoặc là đi lại pháp bảo, chỉ cần mang theo vật này, rất nhiều đối bọn hắn tới nói, khó mà giải quyết phong ấn liền sẽ trở nên mười phần đơn giản, khẳng định so với bọn hắn loại này, thấy trận phá trận, xông vào muốn dễ dàng nhiều."),t("br"),t("br"),n._v("“Ngược lại là có khả năng...” Mọi người đồng thời nhẹ gật đầu."),t("br"),t("br"),n._v("Cũng đúng, nếu như không có cái gọi là “Chìa khoá”, muốn đi vào, thật rất khó, không có vị này Trương viện trưởng lời nói, bọn họ chỉ sợ vẫn như cũ vây ở dung nham nhỏ hơn vuông, không có biện pháp nào."),t("br"),t("br"),n._v("Kẽo kẹt! Kẽo kẹt!"),t("br"),t("br"),n._v("Đối thoại quá trình bên trong, trong thùng gỗ nước tiểu không ngừng chảy xuôi, tới tiếp xúc phong ấn đôm đốp nứt ra, chậm rãi tiêu tán, không cần một lát, cánh cửa khổng lồ hoàn toàn xuất hiện ở trước mặt mọi người."),t("br"),t("br"),n._v("“Có thể tiến vào...”"),t("br"),t("br"),n._v("Thân thể nhoáng một cái, Trương Huyền bay xuống, đem thùng gỗ ném ra ngoài, thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Thư viện biểu hiện thiếu hụt, liền biết đây là cái không tì vết Thuần Dương trận pháp, nước tiểu vừa vặn tới tương khắc, lúc này mới có cái này to gan ý nghĩ, không nghĩ tới quả nhiên có thể thực hiện!"),t("br"),t("br"),n._v("“Tốt...”"),t("br"),t("br"),n._v("Mọi người đồng thời nhẹ gật đầu, đồng loạt hướng cửa bay đi."),t("br"),t("br"),n._v("Vẫn như cũ là Ngô sư đi đầu đi vào, thấy không có nguy hiểm về sau, lúc này mới gọi mọi người đi vào."),t("br"),t("br"),n._v("Trước đó tại phong ấn bên ngoài cảm nhận được cực nóng vô cùng, mà tiến vào trong đó, lại phát hiện loại này nhiệt lượng đã biến mất không thấy gì nữa, ngược lại theo cửa bên trong để lộ ra từng tia từng tia mát mẻ, thật giống như trước đó là ảo giác đồng dạng."),t("br"),t("br"),n._v("“Chẳng lẽ trước đó cảm giác sai?” Một vị Chiến sư nhịn không được nói."),t("br"),t("br"),n._v("Vừa rồi tại phong ấn bên ngoài, còn mượn nhờ trong đó toả ra nhiệt lượng tiến hành tu luyện, đối tu vi có chỗ tốt rất lớn... Vốn cho rằng tiến vào bên trong sẽ bị thiêu đốt không thể thừa nhận, làm sao tình huống hoàn toàn trái lại, không những không có cảm giác đến nóng, còn cảm thấy mát mẻ?"),t("br"),t("br"),n._v("Chẳng lẽ trước đó trải qua tất cả, là giả?"),t("br"),t("br"),n._v("Có phần quá mức quỷ dị đi!"),t("br"),t("br"),n._v("“Mọi người không nên hoảng hốt, nếu ta đoán không lầm, trước đó trải qua sự tình thật, hiện tại cũng là thật!”"),t("br"),t("br"),n._v("Hàn hội trưởng vẻ mặt nghiêm túc, nói: “Vô cùng có khả năng, đây là một cái nóng lạnh giao hòa trận pháp, cùng khống chế khí hậu trận pháp đồng dạng, có thể tùy ý thay đổi nhiệt độ, cho người ta cảm thụ bất đồng.”"),t("br"),n._v("Rất nhiều trận pháp sư công hội, đều sẽ bố trí một chút có thể khống chế nhiệt độ không khí trận pháp, có lẽ cánh cửa này chính là loại tình huống này."),t("br"),t("br"),n._v("Trước đó mọi người tại bên ngoài cảm thấy khó có thể chịu đựng, mà tiến vào trong đó, vô cùng có khả năng xúc động một loại nào đó riêng biệt cơ quan, trở nên nhiệt độ không khí thích ứng, mới có loại cảm giác này."),t("br"),t("br"),n._v("“Có khả năng...” Nghe được lời giải thích này, mọi người đồng thời nhẹ gật đầu."),t("br"),t("br"),n._v("Mặc dù cảm thấy chỉ bằng vào trận pháp liền để nhiệt độ không khí chênh lệch lớn như vậy, có chút khó tin, nhưng có thể đem cái này di tích lưu lại người, không biết là cảnh giới bao cao cường giả, làm ra loại trận pháp này cũng không thể quở trách nhiều."),t("br"),t("br"),n._v("Dù sao bị giới hạn tu vi, bọn họ cảm thấy không thể lý giải, cũng không đại biểu không thể hoàn thành, liền cùng Trương viện trưởng dùng nước tiểu phá giải phong ấn đồng dạng."),t("br"),t("br"),n._v("“Đây không phải trận pháp...”"),t("br"),t("br"),n._v("Mọi người gật gật đầu, đang muốn tiếp tục hướng bên trong tiến, liền nghe đến một cái thanh âm nhàn nhạt vang lên, nói chuyện không phải là Ngô sư, cũng không phải Trương Huyền, mà là từ trước tới nay trầm mặc Lạc Nhược Hi."),t("br"),t("br"),n._v("“Không phải trận pháp, đó là cái gì?”"),t("br"),t("br"),n._v("Thấy nàng nói đến chắc chắn như thế, mọi người kỳ quái nhìn qua."),t("br"),t("br"),n._v("“Là không gian vận dụng!” Lạc Nhược Hi nói."),t("br"),t("br"),n._v("“Không gian?” Mọi người nhìn nhau, không rõ ràng cho lắm."),t("br"),t("br"),n._v("“Cùng không gian gấp cùng trữ vật giới chỉ đồng dạng, cánh cửa này thông hướng một cái không gian khác, cùng trước đó phong ấn lại che lấp, không thuộc về cùng một cái phương diện, tự nhiên cho người cảm giác cũng không giống nhau!”"),t("br"),t("br"),n._v("Lạc Nhược Hi nói."),t("br"),t("br"),n._v("“Thông hướng một cái không gian khác?” Tất cả mọi người cảm thấy chấn động toàn thân, tràn đầy kinh hãi."),t("br"),t("br"),n._v("Liên lụy đến trận pháp, tất cả mọi người có thể hiểu, tăng lên không gian cũng có chút khó có thể lý giải được."),t("br"),t("br"),n._v("Lưu lại di tích tiền bối, lại có thể liên lụy đến cấp độ này, nên cường đại cỡ nào cao thủ?"),t("br"),t("br"),n._v("“Nghe nói, Thánh vực cửu trọng cường giả, có thể lĩnh ngộ không gian pháp tắc, bố trí không gian gấp, để sinh mệnh tiến vào bên trong, đều không cảm thấy buồn bực... Chẳng lẽ, di tích chủ nhân, đã đạt tới loại cảnh giới này?”"),t("br"),t("br"),n._v("Ngô sư nắm đấm xiết chặt."),t("br"),t("br"),n._v("Thân là thất tinh thượng phẩm cấp bậc danh sư, biết đến bí mật rất nhiều, Thánh vực cửu trọng cường giả, mặc dù thưa thớt, nhưng cũng xem qua liên quan thư tịch, biết đáng sợ."),t("br"),t("br"),n._v("Cảnh giới như thế cường giả, vật lưu lại đối bọn hắn tới nói, có thể ngộ nhưng không thể cầu, nhưng tùy tiện một cái cơ quan, một cái trận pháp, cũng không phải là bọn họ có thể lý giải."),t("br"),t("br"),n._v("Lạc Nhược Hi lắc đầu, cũng không biết là không biết, vẫn là tại phủ nhận câu trả lời của hắn."),t("br"),t("br"),n._v("“Thánh vực cửu trọng mới có thể lý giải không gian pháp tắc?” Trương Huyền nháy nháy mắt."),t("br"),t("br"),n._v("Nói thật, tin tức này đối với hắn mà nói, là rất rung động."),t("br"),t("br"),n._v("Tại Phong Thánh đài, hắn từng tiến vào một cái không gian gấp, đồng thời để Thiên Nghĩ Phong Mẫu đem hắn thu lấy, nói như vậy... Cái chỗ kia là Thánh vực cửu trọng cường giả lưu lại?"),t("br"),t("br"),n._v("Lúc đó Khổng sư, mới vừa vặn đột phá, tất nhiên không phải tự tay bố trí, vô cùng có khả năng khi đó, liền có loại cường giả cấp bậc này đi theo tại sau lưng, trung thành đi theo."),t("br"),t("br"),n._v("Lấy Khổng sư đặc biệt nhân cách mị lực cùng năng lực, thu phục mấy cái so với hắn thực lực mạnh tùy tùng, không khó lắm."),t("br"),t("br"),n._v("“Lần trước vừa tới danh sư học viện bên trên công khai khóa thời điểm, nhìn thấy Lạc Nhược Hi, nàng đi trong đám người, nhưng không có một lần va chạm, liền cùng nắm trong tay không gian đồng dạng...”"),t("br"),t("br"),n._v("Một cái ý nghĩ xông ra, chẳng qua lập tức cười khổ lắc đầu: “Điều đó không có khả năng! Nàng bất quá là cái lục tinh danh sư, thực lực cho dù so với ta mạnh hơn, cũng tất nhiên chẳng mạnh đến đâu, đoán chừng là một loại bí pháp đặc thù...”"),t("br"),t("br"),n._v("Đem danh sư học viện thư tịch nhìn xong, biết người danh sư này đại lục, không chỉ có thể chất đặc thù, còn có huyết thống, rất nhiều lợi hại gia tộc, dựa dẫm huyết mạch chi lực, thôi thúc bí pháp, uy lực to lớn, vượt xa võ kỹ."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, Thánh vực quý tộc mới như vậy để cho người ta sợ hãi, không dám vi phạm."),t("br"),t("br"),n._v("Trong lòng nghi ngờ, nhịn không được nhìn về phía cô bé trước mắt: “Ngươi sao có thể xác định, đây không phải trận pháp, mà là dính dáng không gian?”"),t("br"),t("br"),n._v("Ô Thiên Khung mấy người cũng nghi ngờ nhìn qua."),t("br"),t("br"),n._v("Quả thực, dính dáng không gian, thực sự quá thâm ảo, thực sự để cho người ta khó có thể lý giải được."),t("br"),t("br"),n._v("“Rất đơn giản, các ngươi hướng về phía trước nhìn một chút liền biết...”"),t("br"),t("br"),n._v("Lạc Nhược Hi nháy nháy mắt, chỉ về phía trước."),t("br"),t("br"),n._v("Mọi người ngẩng đầu, quả nhiên thấy xuyên qua cửa về sau, cảnh tượng trước mắt thay đổi."),t("br"),t("br"),n._v("Cửa đằng sau, là một mảnh liên miên chập trùng núi cao, một cái không nhìn thấy phần cuối, phía trên đều là xanh mơn mởn cây cối, ôn nhuận khí tức truyền tới, cho người ta cảm giác đã cách xa Hỏa Nguyên thành, mà là đến một cái tựa như mùa xuân thành thị."),t("br"),t("br"),n._v("“Đây là... Không gian gấp? Thật lớn ah...”"),t("br"),t("br"),n._v("Trương Huyền ngẩn ngơ."),t("br"),t("br"),n._v("Theo lần thứ nhất thấy Thiên Nghĩ Phong sào bắt đầu, gặp qua mấy chỗ không gian gấp, thậm chí mình còn có một cái... Có thể lớn như vậy, đừng nói chưa thấy qua, nghe đều không có nghe qua."),t("br"),t("br"),n._v("Hắn không gian gấp, đường kính mấy trăm mét là tốt lắm rồi, trong này còn dung nạp núi lớn, sông lớn... Không nhìn thấy phần cuối, vậy thì kinh khủng!"),t("br"),t("br"),n._v("Phải biết không gian gấp càng lớn, ngoại giới áp lực lại càng lớn, hắn tính ổn định cũng liền càng kém..."),t("br"),t("br"),n._v("Như thế lớn, đến cùng làm sao làm được?"),t("br"),t("br"),n._v("“Không đúng, đây không phải không gian gấp, mà là... Không gian trận pháp!”"),t("br"),t("br"),n._v("Đang tại khiếp sợ, liền nghe đến một bên Hàn hội trưởng thanh âm, vang lên, quay đầu nhìn lại, chỉ thấy hắn mày nhăn lại, thân thể cứng ngắc."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1046"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1048"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);