(window.webpackJsonp=window.webpackJsonp||[]).push([[684],{1039:function(n,h,g){"use strict";g.r(h);var i=g(44),t=Object(i.a)({},(function(){var n=this,h=n.$createElement,g=n._self._c||h;return g("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[g("h1",{attrs:{id:"chuong-1589-khong-gian-cat"}},[g("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1589-khong-gian-cat"}},[n._v("#")]),n._v(" Chương 1589: Không gian cát")]),n._v(" "),g("hr"),n._v(" "),g("p",[n._v("Chương 1589: Không gian cát "),g("br"),g("br"),n._v("Cung điện mang theo mênh mông xa xăm khí tức, phảng phất đứng sừng sững ở đó không biết đã bao nhiêu năm, cho người ta một loại kéo dài lịch sử tang thương."),g("br"),g("br"),n._v("“Đây cũng là Nhiễm Cầu Cổ Thánh lưu lại, nếu có truyền thế thiên phù, nhất định ở bên trong!” Lạc Nhược Hi nói."),g("br"),g("br"),n._v("Tòa cung điện này, vừa nhìn liền biết bất phàm, tuyệt không phải người bình thường có thể kiến tạo, ẩn chứa trong đó lực lượng cùng thủ đoạn, ngay cả Đại Thánh, đều phải theo không kịp, chỉ sợ chính là xuất từ Khổng sư vị kia cường đại nhất học sinh tay."),g("br"),g("br"),n._v("Nhẹ gật đầu, Trương Huyền không tại suy nghĩ lôi kiếp sự tình, Minh Lý Chi Nhãn nhúc nhích, hướng bốn phía nhìn lại, một lát sau, vẻ mặt nghiêm túc: “Dấu vết ở đây dừng lại giữa chừng, không có gì bất ngờ xảy ra, bốn người kia, đã tiến vào, chúng ta cũng nhanh lên một chút đi!”"),g("br"),g("br"),n._v("Biết chuyện này càng trọng yếu hơn, ba người cũng không nhiều lời, thẳng tắp hướng trước mắt cung điện bay đi."),g("br"),g("br"),n._v("Cung điện thoạt nhìn khoảng cách không xa, cũng liền hơn mười cây số bộ dáng, có điều, bay một hồi, nhất thời cảm giác được không đúng, bất kể bọn họ làm sao tiến lên, dùng tốc độ bao nhanh, cung điện vẫn tại nơi nào, hình như không có chút nào tới gần."),g("br"),g("br"),n._v("“Chẳng lẽ, chúng ta lại lâm vào huyễn cảnh?” Trương Huyền sắc mặc nhìn không tốt."),g("br"),g("br"),n._v("Lúc trước tại Kiếm lão nhân lưu lại Kiếm trì, liền từng gặp được loại tình huống này, cùng quỷ đả tường tương tự, đi như thế nào, đều tại nguyên chỗ, lần này sẽ không cũng là như vậy đi!"),g("br"),g("br"),n._v("“Không phải!”"),g("br"),g("br"),n._v("Cũng cảm giác được vấn đề, Lạc Nhược Hi đôi mi thanh tú nhăn lại: “Nếu như không nhìn lầm, hẳn là không gian cát!”"),g("br"),g("br"),n._v("“Không gian cát?” Trương Huyền không hiểu ra sao, ngay cả một bên Ngột Thần, dường như cũng không biết rõ."),g("br"),g("br"),n._v("“Ta từng tại một cái trong điển tịch thấy qua tương tự giới thiệu, không gian cát, là một loại vỡ vụn như là đất cát bộ dáng nghiền nát không gian, liên hợp cùng một chỗ, đồng dạng có thể tạo thành chân chính không gian, nhưng muốn ở trong đó ngang qua, liền sẽ cùng đi ngược dòng nước đồng dạng, tiến lên càng nhanh, di động càng nhanh! Dù là thực lực ngươi mạnh hơn, cũng chỉ có thể tại nguyên chỗ dừng lại, không cách nào tiến lên mảy may... Nói cách khác, chúng ta thoạt nhìn là bình thường phi hành, nhưng không gian trong cát không gian, là di động, không chỗ gắng sức, tự nhiên là cùng ốc sên đồng dạng, không nhìn thấy đi tới!”"),g("br"),g("br"),n._v("Lạc Nhược Hi giải thích."),g("br"),g("br"),n._v("“Còn có loại sự tình này?” Trương Huyền ngạc nhiên."),g("br"),g("br"),n._v("Không gian cát, chưa từng nghe qua, nhưng Lạc Nhược Hi một giải thích, lập tức hiểu được, bọn họ hiện tại, liền giống như ô tô lâm vào tổ hợp ròng rọc bên trong, lốp xe chuyển động, ròng rọc cũng tại đánh chuyển, hai hai triệt tiêu, dùng lại lớn lực lượng, cũng chỉ là tại nguyên chỗ, mà không cách nào tiến lên."),g("br"),g("br"),n._v("Lại hình như đi tại không có lực ma sát mặt băng, không chỗ mượn lực, càng cất bước tử, càng đi bất động."),g("br"),g("br"),n._v("“Nếu như là như vậy, quả thực không cách nào tiến lên, cái kia... Bốn người bọn họ là như thế nào thông qua nơi này?”"),g("br"),g("br"),n._v("Ngột Thần tràn đầy nghi ngờ."),g("br"),g("br"),n._v("Liền bọn họ cũng bay không đi qua, phía trước bốn người, cũng không thấy so với bọn hắn càng thêm cường đại, làm sao lại thuận lợi thông qua?"),g("br"),g("br"),n._v("“Bọn họ hẳn là mượn pháp bảo nào đó, định trụ không gian cát di động, liền giống như bờ đê chặn lại hồng lưu đồng dạng, chỉ cần không gian cát không chảy xuôi, muốn xuyên qua, cũng không khó khăn.” Lạc Nhược Hi nói."),g("br"),g("br"),n._v("“Vậy chúng ta mượn nhờ lỗ sâu không gian nhảy đi qua, chẳng phải có thể trực tiếp đi vào cung điện, không cần đang bay qua đi tới?”"),g("br"),g("br"),n._v("Trương Huyền nói."),g("br"),g("br"),n._v("Trước đó, Ngột Thần sử dụng bí pháp, mượn nhờ bước nhảy không gian, hơn trăm vạn cây số khoảng cách, chớp mắt vượt qua, từ nơi này đến cung điện chẳng qua hơn mười cây số, hẳn là càng đơn giản hơn."),g("br"),g("br"),n._v("“Lỗ sâu không gian nhảy, cùng truyền tống trận có chút tương tự, quả thực có thể để cho khoảng cách rút ngắn, xuyên qua không gian, nhưng... Không gian cát, vốn là không ổn định, một khi sử dụng không gian bên trên bí pháp, ta sợ sẽ dẫn tới không gian loạn lưu, nói như vậy, chỉ sợ càng thêm phiền phức!”"),g("br"),g("br"),n._v("Lạc Nhược Hi mày nhíu lại nhanh."),g("br"),g("br"),n._v("Không gian loạn lưu, Kiến Thần Bất Hoại cường giả rơi vào trong đó, đều khó mà bảo toàn, trọng yếu nhất chính là, lại khó trốn đi, thật muốn như vậy, so hiện tại càng thêm phiền phức."),g("br"),g("br"),n._v("“Nói như vậy lên, chẳng phải không có cách nào đi qua?”"),g("br"),g("br"),n._v("Trương Huyền lắc đầu."),g("br"),n._v(" Bay không đi qua, bước nhảy không gian cũng không có cách nào sử dụng, chẳng lẽ cung điện rõ ràng đang ở trước mắt, lại chỉ có thể trơ mắt nhìn?"),g("br"),g("br"),n._v("“Ngược lại cũng không phải, nếu như có thể cùng mấy người bọn họ đồng dạng, định trụ di động không gian cát, đồng dạng có thể nhẹ nhõm đi vào!” Lạc Nhược Hi nói."),g("br"),g("br"),n._v("“Định trụ không gian cát?”"),g("br"),g("br"),n._v("Trương Huyền nhìn về phía một bên Ngột Thần."),g("br"),g("br"),n._v("Vị này đối không gian lĩnh ngộ cực mạnh, so với Lạc gia cường giả, đều không hề yếu, có lẽ sẽ có phương pháp."),g("br"),g("br"),n._v("“Nếu như ta có Lạc gia Tĩnh Không châu, ngược lại là có thể hoàn thành... Hiện tại, khẳng định là làm không được!” Thấy hắn ánh mắt, Ngột Thần lắc đầu."),g("br"),g("br"),n._v("“Tĩnh Không châu?”"),g("br"),g("br"),n._v("“Món bảo vật này, là Lạc gia tiên tổ, dưới cơ duyên xảo hợp, mới lấy được! Có định trụ không gian, ổn định loạn lưu tác dụng, trước mắt không gian cát, mặc dù một mực di động, khó mà gắng sức, nhưng có thứ này tại, khẳng định cũng có thể trực tiếp bao vây!” Ngột Thần gật đầu nói."),g("br"),g("br"),n._v("“Lợi hại như vậy?”"),g("br"),g("br"),n._v("Trương Huyền líu lưỡi."),g("br"),g("br"),n._v("Đã sớm nghe nói, tiểu công chúa luyện hóa Tĩnh Không châu, tu vi đại tăng, thực lực cũng cực kỳ đáng sợ, nhưng chưa hề cùng hắn giao thủ qua, trong lòng của hắn, còn là cái kia đơn thuần, ưa thích luyện đan, xưng hô lão sư hắn, cung kính lễ độ tiểu nha đầu."),g("br"),g("br"),n._v("Hiện tại nghe tới, mới biết được thứ này lợi hại như thế."),g("br"),g("br"),n._v("“Tĩnh Không châu là rất lợi hại, hơn nữa, cho ta cảm giác, vị kia Lạc Thất Thất, khoảng cách có thể phát huy sức mạnh lớn nhất, còn kém rất nhiều!” Lạc Nhược Hi cũng nhẹ gật đầu: “Bất quá, bây giờ nói những thứ vô dụng này, Tĩnh Không châu bây giờ không có ở đây nơi này, coi như đi tìm, cũng không kịp!”"),g("br"),g("br"),n._v("“Này ngược lại là...”"),g("br"),g("br"),n._v("Trương Huyền bất đắc dĩ."),g("br"),g("br"),n._v("Tĩnh Không châu mạnh hơn, không tại trước mặt cũng là vô dụng."),g("br"),g("br"),n._v("Lại nói, tổn thương Lạc Thất Thất sâu như vậy, cho dù hắn da mặt dù dày, cũng không tiện đi mượn người ta Trấn tộc chi bảo!"),g("br"),g("br"),n._v("“Nói đến Tĩnh Không châu, ta ngược lại thật ra nghĩ tới, nếu như... Thôi được rồi!”"),g("br"),g("br"),n._v("Đột nhiên chen vào nói, nói đến một nửa, Ngột Thần mặt mũi cười khổ ngừng lại."),g("br"),g("br"),n._v("Trương Huyền nhìn qua: “Có ý nghĩ gì, cứ nói đừng ngại!”"),g("br"),g("br"),n._v("“Thật ra thì, bất luận Lạc gia còn là Khưu Ngô Cổ Thánh, đối không gian lý giải, đều nguồn gốc từ Tĩnh Không châu, bọn họ lĩnh ngộ, là bao vây cùng ổn định không gian phương diện chân giải... Nếu như có thể lĩnh ngộ loại này chân giải, có lẽ liền có thể trong thời gian ngắn, ổn định trước mắt không gian cát, nhẹ nhõm bay qua!”"),g("br"),g("br"),n._v("Ngột Thần nói: “Chỉ bất quá, chân giải khó mà luyện thành, huống chi không gian, so kiếm đạo các loại càng khó... Ta có thể có hiện tại đối không gian lĩnh ngộ, cũng là hao tốn không biết bao nhiêu năm, nghiên cứu không biết bao nhiêu cổ tịch, mới thành công...”"),g("br"),g("br"),n._v("“Cái này... Nếu như đem không gian chia làm súc địa, xuyên qua, nắm giữ, sáng tạo cùng hủy diệt mấy cảnh giới lời nói, muốn đạt tới loại cảnh giới nào, mới có thể lĩnh ngộ chân giải?”"),g("br"),g("br"),n._v("Dừng lại một chút, nhớ tới một việc, Trương Huyền nhịn không được nhìn lại."),g("br"),g("br"),n._v("“Chia mấy cái này cảnh giới, đạt tới cái thứ tư, cũng chính là sáng tạo cảnh giới, chẳng khác nào lĩnh ngộ loại này chân giải!” Suy tư một chút, Ngột Thần nói."),g("br"),g("br"),n._v("“Cái thứ tư?”"),g("br"),g("br"),n._v("Ánh mắt sáng lên, Trương Huyền khóe miệng không khỏi nâng lên, lần nữa nhìn lại, trong ánh mắt mang theo hừng hực: “Ngươi mới vừa nói... Nghiên cứu qua không ít liên quan tới không gian cổ tịch... Hiện tại có hay không mang theo? Toàn bộ lấy ra cho ta xem một chút!”"),g("br"),g("br"),n._v("Người đăng: ThấtDạ"),g("br"),g("br"),g("br"),g("br")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("a",{attrs:{href:"./chap-1600"}},[n._v("Chương trước")]),n._v("  |  "),g("a",{attrs:{href:"./chap-1602"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=t.exports}}]);