(window.webpackJsonp=window.webpackJsonp||[]).push([[69],{425:function(n,h,g){"use strict";g.r(h);var t=g(44),i=Object(t.a)({},(function(){var n=this,h=n.$createElement,g=n._self._c||h;return g("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[g("h1",{attrs:{id:"chuong-1039-hanh-gia-vo-cuong-thuong"}},[g("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1039-hanh-gia-vo-cuong-thuong"}},[n._v("#")]),n._v(" Chương 1039: Hành giả Vô Cương (thượng)")]),n._v(" "),g("hr"),n._v(" "),g("p",[n._v("Chương 1039: Hành giả Vô Cương (thượng)"),g("br"),g("br"),n._v("“Không gian trận pháp?”"),g("br"),g("br"),n._v("Trương Huyền không hiểu."),g("br"),g("br"),n._v("Hắn nghe nói qua mê trận, huyễn trận, sát trận, không gian trận pháp là cái gì, cùng không gian gấp khác nhau ở chỗ nào?"),g("br"),g("br"),n._v("“Không gian trận pháp chính là trong trận pháp, tự thành không gian, tính ổn định không bằng không gian gấp, nhưng lại càng dễ dung nạp sát trận, huyễn trận, mê trận loại hình đại trận... Để cho người ta rơi vào trong đó, đều không thể tự biết!”"),g("br"),g("br"),n._v("Hàn hội trưởng thanh âm phát run."),g("br"),g("br"),n._v("Thân là thất tinh đỉnh phong trận pháp sư, biết loại trận pháp này đáng sợ, tự thành không gian, chỉ cần rơi vào trong đó, ý thức liền sẽ chịu đến pháp tắc ảnh hưởng, căn bản đi không ra, trọng yếu nhất chính là, bên trong vô cùng có khả năng che giấu một chút lợi hại sát trận, cho dù thực lực ngươi cao cường, chỉ cần không đạt tới Thánh vực cửu trọng liền không khả năng phá giải."),g("br"),g("br"),n._v("Nghe được hắn, liếc mắt nhìn nhau, mọi người tất cả đều nhíu mày."),g("br"),g("br"),n._v("Mặc dù bọn họ vẫn như cũ đối không gian trận pháp không biết rõ, nhưng Hàn hội trưởng từ trước tới nay ổn trọng, có thể để cho như vậy biến sắc, nói nơi này tất nhiên đáng sợ đến cực điểm."),g("br"),g("br"),n._v("“Nếu như trực tiếp đem trận pháp này phá giải đâu?” Trương Huyền nhìn lại."),g("br"),g("br"),n._v("Quản hắn trận pháp có lợi hại hay không, cùng lắm thì lãng phí một chân công phu, không đến mức khẩn trương như vậy đi!"),g("br"),g("br"),n._v("“Cái khác trận pháp đều có thể phá giải, không gian trận pháp gần như không có khả năng, trừ phi... Ngươi nắm giữ cho dù không gian vỡ vụn, cũng sẽ không làm bị thương thực lực bản thân!”"),g("br"),g("br"),n._v("Hàn hội trưởng nói."),g("br"),g("br"),n._v("Trương Huyền khóe miệng giật một cái."),g("br"),g("br"),n._v("Cái này có thể so sánh nổ tung uy lực còn lớn hơn nhiều lắm, thật nếu gặp phải, đừng nói hắn chỉ có Tòng Thánh thực lực, cho dù lại đề cao sáu, bảy cái đại cấp bậc đồng dạng gánh không được, thậm chí nguyên thần tại không gian vỡ vụn bên trong, đều sẽ bị dễ dàng giảo sát!"),g("br"),g("br"),n._v("Sinh hoạt tại không gian bên trong, không gian bể nát, còn thế nào sống sót?"),g("br"),g("br"),n._v("Da chi không còn, lông đem chỗ này phụ?"),g("br"),g("br"),n._v("“Vâng! Nguyên nhân chính là như vậy, không gian trận pháp chỉ có thể dựa theo đối phương thiết kế con đường, từng chút một tiến lên, không thể có mảy may vượt qua!”"),g("br"),g("br"),n._v("Nói đến đây Hàn hội trưởng thanh âm trầm thấp: “Nói cách khác, chúng ta bây giờ xem như đi vào di tích, đã từng bước đều là nguy hiểm...”"),g("br"),g("br"),n._v("“Từng bước nguy hiểm?”"),g("br"),g("br"),n._v("Mọi người cũng nguyên một đám vẻ mặt nghiêm túc đứng dậy."),g("br"),g("br"),n._v("Trước đó hai đóng, mặc dù rất khó khăn, nhưng mà nếu như không cách nào thông qua, tính nguy hiểm cũng không phải rất lớn."),g("br"),g("br"),n._v("Thật giống như tại dung nham phía trên, không cách nào thông qua cực nóng dung nham đi vào phía dưới cung điện, có thể bay ở phía trên, trong thời gian ngắn là không có nguy hiểm tính mạng."),g("br"),g("br"),n._v("Còn có cửa thứ hai, không công kích phong ấn, cho dù ở bên trong ở lại mấy năm, cũng sẽ không bị thương tổn."),g("br"),g("br"),n._v("Mà bây giờ, nghe Hàn hội trưởng khẩu khí, đi vào trận pháp này không gian liền không đồng dạng, bất kể đi cùng không đi, tiến lên không tiến tiến, tùy thời đều có thể xuất hiện nguy hiểm, nói cách khác, cái này di tích trực tiếp khảo nghiệm vừa mới bắt đầu."),g("br"),g("br"),n._v("“Đã đến nơi này, vậy thì yên ổn mà ở thôi, đã nơi này là chân chính di tích, vậy đã nói rõ, Chương Dẫn Khâu đám người khẳng định tới qua, chúng ta có thể tìm tìm bọn họ đi qua con đường, chỉ cần có thể tìm tới, tính an toàn tất nhiên sẽ tăng lớn không ít!”"),g("br"),g("br"),n._v("Ô Thiên Khung nói."),g("br"),g("br"),n._v("“Không sai!” Những người khác cũng đồng thời nhẹ gật đầu."),g("br"),g("br"),n._v("Lần này tới, đã sớm đem Sinh Tử không để ý, như là đã đi vào di tích, nói rõ mục đích đã đạt tới, không có đi sai chỗ đưa, hẳn là cao hứng mới là."),g("br"),g("br"),n._v("“Chương Dẫn Khâu bọn họ là hai năm trước tới, cho dù có dấu vết gì, cũng gần như tiêu tán, có điều, đã có thể đem tin tức truyền ra ngoài, khẳng định là bị vây ở một nơi, chỉ cần tiến lên, nên có thể phát hiện cái gì...”"),g("br"),g("br"),n._v("Ngô sư nhẹ gật đầu, đi đầu đi thẳng về phía trước."),g("br"),g("br"),n._v("Lão viện trưởng chết cùng không chết, ai cũng không cách nào xác nhận, nhưng đã có thể đem tin tức truyền ra ngoài, lúc ấy khẳng định còn sống, đã sống, liền nhất định sẽ lưu lại dấu vết, để kẻ đến sau tìm, nếu không, đưa tin cũng liền không có cái gì ý nghĩa."),g("br"),g("br"),n._v("“Ừm!”"),g("br"),g("br"),n._v("Trương Huyền cũng không nhiều lời, Minh Lý Chi Nhãn lặng lẽ vận chuyển, hướng bốn phía liếc nhìn, nhìn một lát, nhịn không được lắc đầu."),g("br"),g("br"),n._v("Mặc kệ là trận pháp không gian, vẫn là không gian gấp, lưu lại nơi này người, thực lực hơn xa với hắn, cấp bậc chênh lệch quá lớn, liền xem như Minh Lý Chi Nhãn loại này vạn năm không gặp năng lực đặc thù, đồng dạng không cách nào xem thấu."),g("br"),g("br"),n._v("“Thiếu hụt...” Ngay sau đó trong lòng hơi động."),g("br"),g("br"),n._v("Thời gian không dài, đồng dạng lắc đầu."),g("br"),g("br"),n._v("Cái này mặc dù là cái trận pháp, nhưng mà thân ở trong đó, thật giống như vây ở giữa thiên địa, Thiên Đạo thư viện chỉ có thể điều tra, chạm đến hoặc là đang tại vận hành đồ vật, đối hắn không thể làm gì."),g("br"),g("br"),n._v("Thật giống như tại ngoại giới không có khả năng lợi dụng mặt trời lên mặt trăng lặn, mưa hàng mây thư thả đến tìm kiếm thiên địa thiếu hụt đồng dạng."),g("br"),g("br"),n._v("Phạm vi quá lớn, cho dù nghịch thiên vô cùng thư viện, cũng không có biện pháp gì."),g("br"),g("br"),n._v("“Xem ra chỉ có thể từng bước một tiến về phía trước...”"),g("br"),g("br"),n._v("Biết không có cách nào làm phá hư, Trương Huyền tràn đầy buồn bực, theo sát ở sau lưng mọi người, đi thẳng về phía trước."),g("br"),g("br"),n._v("Không thể không nói trận pháp này không gian, thực sự quá mức rất thật, đi ở trong đó, nếu như không phải trước thời hạn biết thân ở dung nham phía dưới không biết bao sâu vị trí, có thể sẽ thật sự cho rằng là cái ánh nắng tươi sáng, ôn nhuận như xuân sơn mạch."),g("br"),g("br"),n._v("Dọc theo con đường tiến lên, nhìn xem bốn phía xanh mơn mởn cây cối, không những chưa đi đến vào di tích hoảng sợ, ngược lại cho người ta một loại nhàn nhã hài lòng."),g("br"),g("br"),n._v("Nhất là nghe được gió núi thổi lên cây cối phát ra nghẹn ngào thanh âm, lại thêm cho người ta một loại tâm tình thả lỏng, không tranh quyền thế cảm giác."),g("br"),g("br"),n._v("“Đây không phải không có gì nguy hiểm không?”"),g("br"),g("br"),n._v("“Đúng vậy a! Cảm giác nơi này linh khí đầy đủ, nếu như về sau không muốn tranh đấu, có thể ở tại nơi này, khẳng định sẽ phi thường không sai!”"),g("br"),g("br"),n._v("“Cái này môi trường quả thực rất tốt, thích hợp tu tâm dưỡng tính...”"),g("br"),g("br"),n._v("..."),g("br"),g("br"),n._v("Đi một hồi, mọi người tất cả đều con mắt tỏa ánh sáng."),g("br"),g("br"),n._v("Trước đó Hàn hội trưởng nói khủng bố như vậy, bọn họ đều khẩn trương muốn mạng, chân chính đi, mới phát hiện hoàn toàn không phải nói như vậy, ngược lại cho người ta một loại yên tĩnh an lành cảm giác."),g("br"),n._v("Thong thả trong đó, để cho người ta sinh ra một loại muốn cả một đời đều đợi ở bên trong cảm giác."),g("br"),g("br"),n._v("“Không đúng, mọi người nâng lên tinh thần, chúng ta chỉ sợ đã gặp phải công kích...”"),g("br"),g("br"),n._v("Mọi người một đường tiến lên, không cảm thấy có vấn đề gì thời điểm, Hàn hội trưởng nhịn không được nói."),g("br"),g("br"),n._v("“Gặp phải công kích, Hàn hội trưởng nói đùa a? Ta làm sao không thấy được?” Một vị Chiến sư cười nói."),g("br"),g("br"),n._v("“Đúng vậy a, ta cảm thấy Hàn hội trưởng là có chút thần kinh quá độ, buồn lo vô cớ, chúng ta nhiều người như vậy, nếu quả thật gặp phải công kích, khẳng định sẽ phát hiện.” Lại một vị Chiến sư gật đầu."),g("br"),g("br"),n._v("Bọn họ mỗi một cái đều là kinh nghiệm sa trường nhân vật, nếu quả như thật bị công kích, khẳng định sẽ tiến hành phản kích, không có khả năng giống bây giờ đồng dạng, cảm giác gì đều không có."),g("br"),g("br"),n._v("“Không phát hiện, mới là kinh khủng nhất!”"),g("br"),g("br"),n._v("Hàn hội trưởng vẻ mặt khó coi."),g("br"),g("br"),n._v("Nếu như là rõ ràng đao ăn cướp trắng trợn tiến công, đánh không lại cũng có thể liều mạng, sợ nhất chính là nước ấm nấu ếch xanh, để cho người ta lâm vào công kích, vẫn còn không tự biết."),g("br"),g("br"),n._v("“Hàn hội trưởng có phải hay không phát hiện cái gì?” Ngô sư hỏi."),g("br"),g("br"),n._v("“Các ngươi có phát hiện hay không, chúng ta từ khi tiến vào nơi này, nguyên bản thần kinh căng thẳng tất cả đều trầm tĩnh lại, loại tình huống này, một khi xuất hiện nguy hiểm, chỉ sợ liền ứng đối biện pháp đều không có...”"),g("br"),g("br"),n._v("Hàn hội trưởng nói: “Hơn nữa... Mấu chốt nhất là, chúng ta đi thời gian dài như vậy, nhưng thủy chung không nhìn thấy bất cứ dấu vết gì cùng phần cuối... Thật giống như có đồ vật gì dẫn dắt chúng ta đi thẳng đi xuống đồng dạng! Thực sự quá quỷ dị...”"),g("br"),g("br"),n._v("“Cái này...”"),g("br"),g("br"),n._v("Ngô sư sững sờ."),g("br"),g("br"),n._v("Đối phương không nói, còn không cảm thấy được, vừa nói như vậy, cũng lập tức cảm thấy không đúng."),g("br"),g("br"),n._v("Đi ở đây, quá buông lỏng, liền cơ bản nhất phòng ngự đều ném sau ót, làm cho lòng người cam tình nguyện lưu lại, không muốn rời đi, không nói cái khác, chỉ điểm này, liền mười phần kỳ lạ, không giống bình thường."),g("br"),g("br"),n._v("“Phốc!”"),g("br"),g("br"),n._v("Hai người chính đang thương nghị, liền nghe đến cách đó không xa một cái hộc máu thanh âm vang lên, vội vàng quay đầu nhìn lại, chỉ thấy mới vừa nói Hàn hội trưởng thần kinh quá độ Chiến sư, khuôn mặt trắng, máu tươi không ngừng theo khóe miệng tràn ra, trên mặt nhưng như cũ mang theo mỉm cười, đi theo mọi người sau lưng, tiếp tục đi tới."),g("br"),g("br"),n._v("“Cái này...”"),g("br"),g("br"),n._v("Thấy cái tên này hình như không biết thống khổ, không ngừng hộc máu, khóe miệng còn nhẹ giương nhẹ lên, tựa như mỉm cười..."),g("br"),g("br"),n._v("Thấy cảnh này, hai người tất cả đều rùng mình."),g("br"),g("br"),n._v("Cảnh tượng trước mắt thực sự quá mức quỷ dị!"),g("br"),g("br"),n._v("Cho dù kiến thức rộng rãi, cũng chưa từng có gặp được chuyện kỳ quái như thế."),g("br"),g("br"),n._v("“Hắn đây là chân khí hao hết, thể lực chống đỡ hết nổi...”"),g("br"),g("br"),n._v("Nhìn một hồi, Ngô sư sầm mặt lại."),g("br"),g("br"),n._v("Vị này Chiến sư tình huống, lấy hắn thất tinh danh sư nhãn lực, có thể nhẹ nhõm nhìn ra, là chân khí tiêu hao sạch sẽ, có chút thoát lực đưa đến."),g("br"),g("br"),n._v("Lúc này mới đi được bao lâu?"),g("br"),g("br"),n._v("Làm sao lấy lực lượng dài lấy gọi Chiến sư, liền không chịu nổi?"),g("br"),g("br"),n._v("Hàn hội trưởng đang tại kỳ quái, liền nghe đến Ngô sư căng lên thanh âm tiếp tục vang lên: “Ngươi xem một chút đan điền của mình...”"),g("br"),g("br"),n._v("“Đan điền?”"),g("br"),g("br"),n._v("Sửng sốt một chút, Hàn hội trưởng cúi đầu nhìn sang, vừa nhìn phía dưới, con ngươi kìm lòng không được thu hẹp."),g("br"),g("br"),n._v("Chỉ thấy hắn trong cơ thể hùng hậu chân khí, lúc này cũng tiêu hao xấp xỉ, nếu như đem đan điền so sánh hồ nước, hiện tại cũng đã sắp thấy đáy, bất cứ lúc nào cũng sẽ khô quắt xuống."),g("br"),g("br"),n._v("“Chân khí của ta... Làm sao tiêu hao nhanh như vậy?”"),g("br"),g("br"),n._v("Thân thể phát run."),g("br"),g("br"),n._v("Hàn hội trưởng là thất tinh đỉnh phong trận pháp sư, Thánh vực tứ trọng cường giả đỉnh phong, trong mọi người thực lực mạnh nhất, chân khí cũng nhất hùng hậu, đừng nói đi đường, cho dù chiến đấu, đều có thể duy trì một hai canh giờ, sẽ không suy kiệt..."),g("br"),g("br"),n._v("Nhưng bây giờ, mới đi một hồi đường, thậm chí không có phi hành, liền tiêu hao xấp xỉ, đến cùng xảy ra chuyện gì?"),g("br"),g("br"),n._v("Có chút thật là đáng sợ đi!"),g("br"),g("br"),n._v("Phốc! Phốc! Phốc!"),g("br"),g("br"),n._v("Đang tại khiếp sợ, lần nữa nghe được hộc máu thanh âm, lại có mấy vị tu vi thấp Chiến sư, phun ra máu đến, ngay cả Ô Thiên Khung, Thẩm Bình Triều các loại ba vị danh sư học viện viện trưởng, cũng khuôn mặt trắng bệch, trên đầu gân xanh toác ra, hình như lực lượng sắp tiêu hao hầu như không còn, bất cứ lúc nào cũng sẽ không chịu nổi."),g("br"),g("br"),n._v("Nhất là Ô Thiên Khung, không chỉ có vẻ mặt khó coi, tóc dường như cũng biến thành trắng hơn, như là trong thời gian ngắn già yếu mười tuổi."),g("br"),g("br"),n._v("“Cái này...”"),g("br"),g("br"),n._v("Hàn hội trưởng thân thể run lên, nhịn không được nhìn về phía Ngô sư."),g("br"),g("br"),n._v("Hắn mặc dù xác nhận nơi này là trận phát không gian, biết nguy hiểm, nhưng đối trước mắt tình huống, hoàn toàn không hiểu rõ, căn bản không biết xảy ra chuyện gì."),g("br"),g("br"),n._v("“Ta cũng không biết...” Ngô sư lắc đầu."),g("br"),g("br"),n._v("Bọn họ hiện tại thật giống như lọt vào giếng sâu bên trong, trong nhà chưa tỏ, ngoài ngõ đã tường, cho dù xem qua vô số thư tịch, cũng y nguyên không rõ ràng cho lắm."),g("br"),g("br"),n._v("“Chúng ta hẳn là bị trong núi những này tiếng gió thu hút, lâm vào một loại đặc thù ý cảnh, không chỉ chân khí tiêu hao quá nhanh, tuổi thọ cũng sẽ hao tổn, nếu như không đi đi ra, liền sẽ giống như bọn họ, một mực đi về phía trước, không biết mệt mỏi, thẳng đến chân khí, tinh huyết hao hết mà chết!”"),g("br"),g("br"),n._v("Hai người đang tràn đầy không hiểu, Trương Huyền thanh âm vang lên."),g("br"),g("br"),n._v("Quay đầu nhìn lại, chỉ thấy người thanh niên này, mày nhăn lại, không biết nghĩ cái gì."),g("br"),g("br"),n._v("(Bên trên chương vấn đề đã sửa chữa, thật ra thì cổ đại trọng nam khinh nữ, đây là chuyện không có cách nào khác, khụ khụ. Còn hiện tại nha, không nói, ta đi làm cơm, rửa chén, ô ô.)"),g("br"),g("br"),g("br"),g("br")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("a",{attrs:{href:"./chap-1047"}},[n._v("Chương trước")]),n._v("  |  "),g("a",{attrs:{href:"./chap-1049"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);