(window.webpackJsonp=window.webpackJsonp||[]).push([[690],{1047:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1595-cua-đa"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1595-cua-đa"}},[n._v("#")]),n._v(" Chương 1595: Cửa đá")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1595: Cửa đá"),t("br"),t("br"),n._v("“Đi thôi!”"),t("br"),t("br"),n._v("Thấy tượng đá đi vào, Trương Huyền nói một tiếng, ba người lập tức theo sát ở phía sau, đi thẳng về phía trước."),t("br"),t("br"),n._v("Đại điện phía trong đèn đuốc sáng trưng, vô số mặt trời đồng dạng dạ quang thạch treo ở bầu trời, chiếu sáng bốn phía, để giam cầm gian phòng, biến giống như ban ngày."),t("br"),t("br"),n._v("Gian phòng trong thông đạo, điêu khắc cổ xưa chữ viết, viết là một số quy phạm đạo đức, cùng lễ nghi tiêu chuẩn."),t("br"),t("br"),n._v("“Ngươi không phải nói, Nhiễm Cầu Cổ Thánh, đối lễ nghi những này không phải mười phần để ý, làm sao...” Đi tới tượng đá đồng tử trước mặt, Trương Huyền tràn đầy nghi ngờ."),t("br"),t("br"),n._v("Mới vừa nhìn thấy cái này đồng tử thời điểm, hắn nói Nhiễm Cầu Cổ Thánh, đối đủ loại lễ tiết quy củ, cũng không thèm để ý, làm sao trong điện đường, viết đầy những thứ này."),t("br"),t("br"),n._v("“Bởi vì không chú trọng, nhận lấy Khổng sư chỉ trích, mới ở đây điêu khắc xuống, thời khắc ghi nhớ!” Tượng đá đồng tử nhẹ gật đầu."),t("br"),t("br"),n._v("Trương Huyền giật mình."),t("br"),t("br"),n._v("Khổng sư từng nói, ba tỉnh thân ta, Nhiễm Cầu Cổ Thánh đem những này điêu khắc ở đây, mỗi ngày đều có thể quan sát, xem như ngày ngày tự suy ngẫm."),t("br"),t("br"),n._v("“Chúng ta trước đó, có phải hay không có bốn người, đã thông qua được khảo hạch?”"),t("br"),t("br"),n._v("Chần chờ một chút, Trương Huyền hạ giọng."),t("br"),t("br"),n._v("Phía trước tới bốn cái Dị Linh tộc nhân, hiện tại cũng không thấy được tung tích, chẳng lẽ cũng thông qua được khảo hạch?"),t("br"),t("br"),n._v("Thực lực mạnh yếu là chuyện nhỏ, sẽ không phải vị này Khổng sư đệ nhất chiến lực học sinh, lưu lại di tích, liền Dị Linh tộc nhân đều phân biệt không ra?"),t("br"),t("br"),n._v("Thật muốn như vậy, cũng quá mất thân phận!"),t("br"),t("br"),n._v("“Bọn họ tiến vào!” Cũng không cự tuyệt hắn tra hỏi, tượng đá đồng tử lên tiếng."),t("br"),t("br"),n._v("Biết hắn nhận được Khưu Ngô Cổ Thánh truyền thừa về sau, thái độ rõ ràng tốt hơn nhiều."),t("br"),t("br"),n._v("“Thật thông qua được... Vậy bọn hắn... Nhưng có cái gì dị thường?” Trương Huyền cau mày."),t("br"),t("br"),n._v("Vốn định trực tiếp hỏi, đối phương trong cơ thể có hay không sát lục chi khí, cuối cùng vẫn là nhịn xuống."),t("br"),t("br"),n._v("“Dị thường?” Tượng đá đồng tử lắc đầu: “Không có, bọn họ đều là đúng quy đúng củ đánh bại sư chữ kim binh, hơn nữa, một người trong đó còn lấy ra Ngôn Yển Cổ Thánh bảo vật!”"),t("br"),t("br"),n._v("“Ngôn Yển Cổ Thánh?” Trương Huyền chấn động."),t("br"),t("br"),n._v("Ngôn Yển Cổ Thánh là Khổng sư môn hạ, mười đại hiền giả bên trong, am hiểu nhất lễ nhạc Cổ Thánh, cùng Bốc Thương Cổ Thánh, Nhiễm Cầu Cổ Thánh đám người nổi danh, năm đó ma âm sư tổ sẽ có thể truyền thừa xuống, có hắn công lao thật lớn, nguyên nhân chính là như vậy, người sau một mực phụng hắn là tổ sư."),t("br"),t("br"),n._v("Cái này bốn cái Dị Linh tộc nhân, lại có bảo vật của hắn..."),t("br"),t("br"),n._v("“Có thể đi tới danh sư đại lục không bị phát hiện, tìm tới Danh Sư đường đều không thể biết được di tích, càng là lấy ra Ngôn Yển Cổ Thánh bảo vật... Nhìn tới đám này Dị Linh tộc nhân, công tác chuẩn bị làm mười phần cẩn thận!”"),t("br"),t("br"),n._v("Vẻ mặt nghiêm túc."),t("br"),t("br"),n._v("Dị Linh tộc nhân năm đó cùng nhân tộc đại chiến, hai bên đều có tổn thương, nhân tộc không ít bảo vật rơi mất, bị hắn bọn họ chiếm làm của riêng, cũng là rất bình thường."),t("br"),t("br"),n._v("Chỉ là không nghĩ tới, những người này lần này tới, có thể chuẩn bị như thế đầy đủ, ngay cả cái này đồng tử, đều không có phát giác dị thường."),t("br"),t("br"),n._v("Vừa rồi cố ý hỏi thăm, nếu như đối phương lộ ra chân tướng, có thể nhân cơ hội để tượng đá làm ra đề phòng, nhưng bây giờ cái dạng này, chỉ sợ nói nhiều rồi ngược lại sẽ để hắn chán ghét, một khi bị trả đũa, đem được không bù mất."),t("br"),t("br"),n._v("Khó trách có thể đem Triệu Nhã đám người bắt đi, một chút tin tức đều không có tiết lộ, mấy tên này, quả nhiên không tưởng tượng đơn giản như vậy."),t("br"),t("br"),n._v("“Đến!”"),t("br"),t("br"),n._v("Đang tràn đầy khiếp sợ, liền nghe đến một cái thanh âm nhàn nhạt vang lên, Trương Huyền vội vàng ngẩng đầu, ngay sau đó nhìn thấy đã đi tới một cái rộng lớn đại sảnh."),t("br"),t("br"),n._v("Gian phòng trình viên hình, bốn phía tất cả đều là cửa, lít nha lít nhít, chừng trên trăm cái nhiều."),t("br"),t("br"),n._v("“Nơi này có chín mươi chín cánh cửa, đều có khác biệt khảo nghiệm, mỗi người các ngươi chỉ có một lần lựa chọn cơ hội, hơn nữa chỉ có thể tiến vào bên trong một cái, nếu như có thể thuận lợi thông qua, liền có thể đến phần cuối, tìm tới các ngươi muốn đồ vật! Không cách nào thông qua, thật không tiện, chỉ có ở lại chỗ này, theo ta cái này tượng đá, mãi cho đến tuổi thọ hao hết!”"),t("br"),t("br"),n._v("Nhìn chung quanh một tuần, tượng đá đồng tử nhìn lại: “Nói cách khác, chín mươi chín cánh cửa, chọn đúng, thông qua khảo hạch, sai, tự gánh lấy hậu quả!”"),t("br"),n._v(" “Cái này...” Trương Huyền nhìn qua: “Những này cửa, có thể tùy ý lựa chọn?”"),t("br"),t("br"),n._v("“Không sai, muốn vào cái nào cũng được, hiện tại các ngươi có một nén hương thời gian, mau chóng làm ra quyết định đi!” Tượng đá đồng tử nói xong, lui về sau hai bước, mặt không hề cảm xúc."),t("br"),t("br"),n._v("Biết cái tên này sẽ không cho cùng nhắc nhở, Trương Huyền cùng Lạc Nhược Hi, Ngột Thần liếc mắt nhìn nhau, riêng phần mình gật đầu, đồng thời nhìn về phía trước."),t("br"),t("br"),n._v("Những này môn, tất cả đều tùy đá điêu khắc mà thành, giống nhau như đúc, nhìn không ra bất kỳ phân biệt, liên tục chuyển lên hai vòng lời nói, chỉ sợ liền vừa rồi từ nơi nào đi vào, đều lại tìm tìm không được."),t("br"),t("br"),n._v("“Quả thực cùng mê cung đồng dạng...”"),t("br"),t("br"),n._v("Nhìn một vòng, cảm thấy có chút quáng mắt, cái gì đều tìm không ra đến, Trương Huyền hừ nhẹ một tiếng, trong lòng hô khẽ: “Minh Lý Chi Nhãn!”"),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Từng đạo hoa văn từ trong mắt hiện lên, Trương Huyền vội vàng hướng chung quanh nhìn lại."),t("br"),t("br"),n._v("Đã bốn cái Dị Linh tộc nhân đã đi vào cung điện, vậy đã nói rõ, bọn họ vô cùng có khả năng cùng mình đám người đồng dạng, đã tới qua nơi này, hơn nữa làm ra lựa chọn."),t("br"),t("br"),n._v("Những người này, có thể tìm tới có thể tìm tới nơi này, hơn nữa đem Ngôn Yển Cổ Thánh bảo vật đều tùy thân mang theo, có lẽ cũng có thể trước thời hạn biết nơi này, tìm tới an toàn nhất, đáng tin cậy cửa."),t("br"),t("br"),n._v("“Ừm? Tại sao không có dấu vết?”"),t("br"),t("br"),n._v("Rất nhanh, nhìn chung quanh một tuần, Trương Huyền mày nhíu lại thành cục."),t("br"),t("br"),n._v("Vốn cho rằng sẽ cùng bên ngoài đồng dạng, tìm tới chút dấu vết để lại, không nghĩ tới bốn người này, cùng lúc trước bắt đi Triệu Nhã lúc đồng dạng, không có lưu lại bất cứ dấu vết gì, cái gì đều thẩm tra không đến."),t("br"),t("br"),n._v("“Không cần tìm, nếu là khảo hạch, là không thể nào cho ngươi nhìn ra gì gì đó, hơn nữa cái này tượng đá cũng nói, một người chỉ có thể tiến vào bên trong một cái, nói cách khác, coi như chọn được bọn họ tiến vào cửa đá, cũng không cách nào đi vào!”"),t("br"),t("br"),n._v("Dường như nhìn ra ý nghĩ của hắn, Lạc Nhược Hi truyền âm tới."),t("br"),t("br"),n._v("“Cái này...” Trương Huyền câm ở."),t("br"),t("br"),n._v("Quả thực."),t("br"),t("br"),n._v("Nếu là khảo hạch, như thế nào lại lưu lại rõ ràng như vậy sơ hở? Dù sao người khác tuyển qua, chỉ cần đi vào, cơ quan trận pháp các loại đồ vật, khẳng định đã vận chuyển, lại không nguy hiểm có thể nói."),t("br"),t("br"),n._v("“Vậy làm sao bây giờ?”"),t("br"),t("br"),n._v("Quay đầu nhìn lại."),t("br"),t("br"),n._v("“Tùy tiện tuyển đi!” Tiến về phía trước một bước, Lạc Nhược Hi tùy tiện tuyển cửa hộ, tay ngọc kéo ra muốn đi đi vào."),t("br"),t("br"),n._v("“Ngươi... Cẩn thận!” Gặp nàng trực tiếp tiến, Trương Huyền có chút nóng nảy, vội vàng khai báo."),t("br"),t("br"),n._v("“Yên tâm đi!” Nữ hài cười nhạt một tiếng, ánh mắt lộ ra nồng đậm tự tin: “Nhiễm Cầu Cổ Thánh lưu lại khảo hạch tuy là sẽ không đơn giản, nhưng mà muốn lưu lại ta, còn không có dễ dàng như vậy!”"),t("br"),t("br"),n._v("Nói xong, nhấc chân đi vào."),t("br"),t("br"),n._v("“Cũng phải...”"),t("br"),t("br"),n._v("Mặc dù không chút thấy Lạc Nhược Hi từng ra tay, nhưng đồng tử Ngột Thần đều lợi hại như vậy, làm tiểu thư, khẳng định mạnh hơn."),t("br"),t("br"),n._v("Cổ Thánh lưu lại khảo hạch, đối phó một số bình thường thiên tài còn có thể, đối phó nàng, sẽ không có quá lớn nguy hiểm."),t("br"),t("br"),n._v("“Ta cũng đi qua!”"),t("br"),t("br"),n._v("Nàng đi vào cửa, Ngột Thần cũng tùy tiện tuyển một gian, sải bước đi đi vào."),t("br"),t("br"),n._v("“Tới phiên ngươi...”"),t("br"),t("br"),n._v("Tượng đá đồng tử vẻ mặt lạnh nhạt nhìn qua."),t("br"),t("br"),n._v("“Ừm!” Trương Huyền nhẹ gật đầu, đang muốn cũng tùy tiện vào một cái phòng, đột nhiên trong lòng hơi động: “Minh Lý Chi Nhãn nhìn không ra những này phía sau cửa tốt xấu... Thư viện đâu?”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1606"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1608"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);