(window.webpackJsonp=window.webpackJsonp||[]).push([[694],{1049:function(n,h,t){"use strict";t.r(h);var g=t(44),c=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1598-long-cot-than-thuong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1598-long-cot-than-thuong"}},[n._v("#")]),n._v(" Chương 1598: Long cốt thần thương")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1598: Long cốt thần thương"),t("br"),t("br"),n._v("Ô ô ô!"),t("br"),t("br"),n._v("Giọt nước bí mật mang theo đâm rách không khí lực lượng, thời gian nháy mắt, liền xuất hiện ở trước mắt, muốn đem hắn xuyên thủng."),t("br"),t("br"),n._v("“Tùy tiện vung ra giọt nước, cứ như vậy lớn uy lực?”"),t("br"),t("br"),n._v("Đồng tử co rụt lại, Trương Huyền trong lòng kịch liệt rung động."),t("br"),t("br"),n._v("Còn tưởng rằng giọt nước này là thế nào tới, tận mắt nhìn đến, mới biết được kinh khủng."),t("br"),t("br"),n._v("Trong ao gia hoả kia, tùy tiện run lên, phổ thông giọt nước liền để bản thân ngăn cản không nổi, nên mạnh bao nhiêu? Dương sư có thể thắng hay không qua, cũng chưa biết chừng!"),t("br"),t("br"),n._v("“Huyết mạch bùng cháy...”"),t("br"),t("br"),n._v("Không để ý tới cảm xúc, tinh thần khẽ động, trước đó còn thừa lại nửa giọt huyết dịch, lần nữa thiêu đốt lên, trước mắt thời gian lần nữa trở nên chậm đồng dạng, xông tới giọt nước, chậm chạp xuất hiện tại trước mặt."),t("br"),t("br"),n._v("“Nhất định phải giải quyết căn nguyên, nếu không, không có một chút tác dụng nào!”"),t("br"),t("br"),n._v("Thân thể nhoáng một cái, từ rất nhiều giọt nước bên trong xuyên qua mà qua, mấy bước đi tới ao nước trước mặt."),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Mới vừa làm xong những này, nửa giọt huyết dịch bùng cháy hầu như không còn, sau lưng giọt nước tiếp tục bắn ra, đánh vào trên vách đá, phát ra “Phốc phốc” thanh âm."),t("br"),t("br"),n._v("Trương Huyền lúc này mới hướng trước mắt nhìn lại."),t("br"),t("br"),n._v("Từ trong ao xuất hiện, là một bộ to lớn khung xương, trước sau liên kết, chừng cao mấy chục mét thấp, mỗi một cây đều óng ánh long lanh, tựa như ngọc thạch, tuy là nhìn không ra đến cùng có bao nhiêu cứng rắn, nhưng có thể nhẹ nhàng hất lên, liền để giọt nước nắm giữ đánh giết Thánh vực cửu trọng đỉnh phong lực lượng, thực lực mạnh mẽ, có thể xưng kinh khủng."),t("br"),t("br"),n._v("“Đây là... Rồng khung xương?”"),t("br"),t("br"),n._v("Nhìn rõ ràng đối phương chân chính dung mạo, Trương Huyền chấn động."),t("br"),t("br"),n._v("Bởi vì thuần phục Địa Ngục Thanh Long thú, đối loại sinh mạng này xương cốt cấu tạo, biết mười phần kỹ càng, trước mắt này tấm, so Thanh Long xương thú bố cục càng thêm hợp lý, hoàn mỹ, không có đoán sai, tuyệt đối là một con rồng xương cốt."),t("br"),t("br"),n._v("Coi như không phải thuần chủng huyết mạch Long tộc, huyết mạch hùng hậu mức độ, cũng nhất định muốn so Thanh Long thú cao cấp không biết gấp bao nhiêu lần."),t("br"),t("br"),n._v("“Ngươi thế mà có thời gian khống chế năng lực? Không đúng, là ỷ lại một loại huyết mạch... Ngươi là ai? Vị nào Thánh Nhân đời sau?”"),t("br"),t("br"),n._v("Khung xương thấy hắn tránh thoát giọt nước, lộ ra một tia kinh ngạc, nổ vang thanh âm vang lên, đinh tai nhức óc."),t("br"),t("br"),n._v("“Tại hạ Trương Huyền!”"),t("br"),t("br"),n._v("Thấy đối phương không công kích, Trương Huyền đưa khẩu khí, ôm quyền khom người, đồng thời cẩn thận hướng trước mắt tất cả mọi người nhìn sang."),t("br"),t("br"),n._v("Trước mắt khung xương, toàn thân đen kịt, nói chuyện đầu rồng chỗ, con mắt là hai cái lỗ thủng, phát ra lục quang nhàn nhạt, nếu không phải phát biểu, đều tưởng tượng không ra, đối phương là sống, nắm giữ độc lập ý thức."),t("br"),t("br"),n._v("“Trương? Không phải Khổng sư con cháu?”"),t("br"),t("br"),n._v("Cốt long nghi ngờ."),t("br"),t("br"),n._v("“Không phải!” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Chư Tử bách gia rời đi về sau, Trương gia mới bắt đầu quật khởi, Trương gia tiên tổ, quả thực không phải tiếp nhận Khổng sư truyền thừa."),t("br"),t("br"),n._v("“Không phải Khổng sư con cháu, lại lĩnh ngộ Khổng sư mới có thể sử dụng thời gian pháp tắc, nhìn tới hậu nhân, cũng có không kém thiên tài!”"),t("br"),t("br"),n._v("Nhẹ gật đầu, cốt long lần nữa nhìn qua: “Có thể chống cự áp lực của ta, hơn nữa tránh né giọt nước, xem như có nhất định thực lực, có điều, chỉ bằng vào điểm ấy, vô dụng! Muốn thu hoạch được ta thừa nhận, nhất định phải đem ta đánh bại!”"),t("br"),t("br"),n._v("“Đạt được ngươi thừa nhận?”"),t("br"),t("br"),n._v("Trương Huyền sững sờ."),t("br"),t("br"),n._v("“Không sai, cái lối đi này, là chín mươi chín cái lối đi bên trong kỳ ngộ lớn nhất, cũng đồng dạng là nguy hiểm nhất, nhận được ta thừa nhận, có thể đem ta mang đi, không chiếm được, sẽ bị ta giết chết!” Cốt long nhàn nhạt nhìn qua."),t("br"),t("br"),n._v("“Đưa ngươi mang đi?” Trương Huyền ánh mắt sáng lên."),t("br"),n._v("Xương đầu này rồng, tùy tiện làm chút nước giọt, liền có thể đem Thánh vực cửu trọng cường giả đỉnh phong đánh giết, thực lực bản thân, chỉ sợ so với Dương sư đều chỉ mạnh không yếu, nếu có thể đem hắn mang đi, thực lực tổng hợp đem lần nữa tăng thêm!"),t("br"),t("br"),n._v("“Không sai, có điều, muốn cho ta đồng ý, nhất định phải nắm giữ vượt qua ta thực lực, thủ đoạn khác đều vô dụng, năm đó Nhiễm Thánh cũng là dùng sức mạnh, đem ta thuyết phục, mới thề sống chết cống hiến, ở đây ngủ say vài vạn năm, chờ đợi hậu nhân!”"),t("br"),t("br"),n._v("Cốt long nhẹ gật đầu."),t("br"),t("br"),n._v("“Nhiễm Thánh... Ngươi là Nhiễm Cầu Cổ Thánh vũ khí? Chẳng lẽ... Là cái kia chuôi long cốt thần thương?”"),t("br"),t("br"),n._v("Nhớ tới cái gì, Trương Huyền chấn động toàn thân."),t("br"),t("br"),n._v("Hắn nhìn qua liên quan tới Nhiễm Cầu Cổ Thánh thư tịch, biết vị này Cổ Thánh một số cuộc đời, một chuôi long cốt thần thương, tại Dị Linh tộc giết bảy vào bảy ra, uy phong hiển hách, ngay cả Khổng sư đều khen không dứt miệng..."),t("br"),t("br"),n._v("Chẳng lẽ, trước mắt xương đầu này rồng, chính là chuôi này?"),t("br"),t("br"),n._v("Nếu thực như thế, cái lối đi này, quả thực quá trọng yếu."),t("br"),t("br"),n._v("“Không sai!” Cốt long nhẹ gật đầu: “Bất quá, ta bị Nhiễm Thánh phong ấn lực lượng, chỉ có thể thi triển ra Đại Thánh cấp bậc sức chiến đấu, ngươi có thể thắng được, liền có thể đem ta mang đi, thắng không nổi... Thật không tiện, bất kể là ai, lai lịch ra sao, đều sẽ bị ta đâm thủng! Biến thành thi thể.”"),t("br"),t("br"),n._v("“Nói có đạo lý, bất quá...” Trương Huyền ngẩng đầu, mỉm cười, khắp khuôn mặt là chân thành: “Ngươi xem, ta hiện tại chỉ có Thánh vực cửu trọng đỉnh phong thực lực, coi như ngươi giờ phút này chỉ có thể phát huy Đại Thánh sức chiến đấu, cũng nhất định không cách nào thắng được... Ngươi có thể hay không đem tu vi, áp chế cùng ta tương đồng, lại tiến hành tỷ thí?”"),t("br"),t("br"),n._v("“Đè thấp tu vi?” Cốt long thanh âm bên trong mang theo không vui."),t("br"),t("br"),n._v("“Không sai, ngươi ở đây hẳn là ở lại thời gian cũng lâu, muốn đi ra ngoài đại triển hùng phong đi! Hiện tại chỉ có ta một người lựa chọn cái thông đạo này, về sau lại không ai tới lời nói, chẳng phải chứng tỏ, mãi mãi cũng không cách nào rời đi? Đây đối với một cái nắm giữ đỉnh phong sức chiến đấu thần binh tới nói, có phần quá mức bi ai đi!”"),t("br"),t("br"),n._v("Trương Huyền giải thích nói: “Dù sao đều là tỷ thí, chỉ cần đem lực lượng áp chế cùng ta tương đồng, cũng chờ tại chiến đấu, mọi người đẳng cấp tương đương, không tính lấy lớn hiếp nhỏ...”"),t("br"),t("br"),n._v("Cốt long chần chừ."),t("br"),t("br"),n._v("“Liền như thế quyết định, ngươi là Nhiễm Thánh thần binh lợi khí, coi như giảm thấp xuống tu vi, sức chiến đấu khẳng định cũng là cùng cấp bậc đỉnh phong nhất, sẽ không phải... Cảm thấy đè thấp đến Thánh vực cửu trọng đỉnh phong, không phải ta đối thủ a?”"),t("br"),t("br"),n._v("Trương Huyền nghi ngờ nhìn qua: “Chẳng lẽ... Cổ Thánh thần binh, cũng sợ thua?”"),t("br"),t("br"),n._v("“Hừ, đè thấp tu vi, ta cũng sẽ không thua!”"),t("br"),t("br"),n._v("Bị hắn phép khích tướng kích thích, cốt long hừ một tiếng: “Tốt, hiện tại ta đem tu vi áp chế đến Thánh vực cửu trọng đỉnh phong!”"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Tiếng nói kết thúc, cốt long trên người toả ra khí tức cường đại, liên tục lùi về phía sau, thời gian nháy mắt, liền biến thành cùng hắn tương đồng cấp bậc, Thánh vực cửu trọng đỉnh phong."),t("br"),t("br"),n._v("“Bắt đầu đi...”"),t("br"),t("br"),n._v("Đè thấp thật tu vi, cốt long một mặt lạnh nhạt, đang muốn chuẩn bị một chút, cùng kẻ trước mắt này giao thủ, đột nhiên cảm thấy thấy hoa mắt, đứng tại phía trước cách đó không xa thanh niên, chẳng biết lúc nào đã đi tới trước mặt, một quyền liền hướng về phía hắn đầu rồng đập xuống."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Không nghĩ tới đối phương tốc độ nhanh như vậy, hơn nữa một chút quy tắc đều không nói, trực tiếp tấn công, cốt long có chút chán nản, vẫy đuôi một cái, vừa định đem hắn quất bay, liền cảm thấy không gian bốn phía bị trong nháy mắt phong cấm, toàn thân giống như là đóng băng tại dòng sông bên trong, không cách nào động đậy mảy may."),t("br"),t("br"),n._v("“Khưu Ngô Cổ Thánh không gian phong cấm?”"),t("br"),t("br"),n._v("Thân thể cứng đờ, cốt long giật nảy mình, vừa nghĩ tới nên làm thế nào cho phải, liền cảm thấy đầu đau đớn, đối phương nắm đấm đã rơi xuống."),t("br"),t("br"),n._v("Phanh phanh phanh phanh!"),t("br"),t("br"),n._v("Nắm đấm, bàn chân, thời gian nháy mắt, toàn thân trên dưới từng cơn kịch liệt đau đớn, lực lượng của đối phương giống như nước sông cuồn cuộn, liên miên không ngớt, giống như Hoàng Hà ngập lụt, đã xảy ra là không thể ngăn cản."),t("br"),t("br"),n._v("Bị liên tục công kích, không chỉ xương cốt bị tổn thương, linh hồn đều nhận xung kích, tựa như lúc nào cũng sẽ tán loạn."),t("br"),t("br"),n._v("“Làm sao lại mạnh như vậy...”"),t("br"),t("br"),n._v("Cốt long tràn đầy phát điên."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1609"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1611"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);