(window.webpackJsonp=window.webpackJsonp||[]).push([[70],{424:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1040-hanh-gia-vo-cuong-ha"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1040-hanh-gia-vo-cuong-ha"}},[n._v("#")]),n._v(" Chương 1040: Hành giả Vô Cương (hạ)")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1040: Hành giả Vô Cương (hạ)"),t("br"),t("br"),n._v("“Tiếng gió?”"),t("br"),t("br"),n._v("Ngô sư, Hàn hội trưởng sững sờ, vội vàng hướng chung quanh nhìn lại."),t("br"),t("br"),n._v("Tiếng gió rất nhẹ, vẫn như cũ thổi lá cây phát ra “Ào ào!” Thanh âm, trước đó bọn họ cảm thấy, nghe được cái này yên tĩnh thản nhiên, cho người ta một loại tâm cảnh thả lỏng cảm giác, mà bây giờ nhưng rùng mình, không tự chủ được run rẩy."),t("br"),t("br"),n._v("“Đóng kín thính giác...”"),t("br"),t("br"),n._v("Hô nhỏ một tiếng, hai người đồng thời đem thính giác đóng kín, không nghe thấy không vang."),t("br"),t("br"),n._v("Bất quá, chẳng biết tại sao, cái kia đạo vắng lặng gió minh, vẫn như cũ có thể truyền vào trong óc, để bọn hắn bất tri bất giác liền chịu đến khống chế."),t("br"),t("br"),n._v("“Vô dụng, đây là một loại Ma âm, coi như đóng kín lục thức, đồng dạng có thể đi vào nội tâm, khó lòng phòng bị!”"),t("br"),t("br"),n._v("Lắc đầu, Trương Huyền truyền âm qua."),t("br"),t("br"),n._v("Ma âm nếu như dễ dàng như vậy liền có thể cự tuyệt, cũng không xứng xưng là “Ma”, đồng thời độc tự phân liệt thành một cái đơn độc chức nghiệp, truyền thừa vạn năm bất hủ."),t("br"),t("br"),n._v("“Vậy làm sao bây giờ?”"),t("br"),t("br"),n._v("Hai người lần nữa đem che đậy thính giác mở ra, tràn đầy lo lắng."),t("br"),t("br"),n._v("Không thể cự tuyệt, lại không thể trốn tránh, chẳng lẽ bọn họ đám người này, mới đi vào di tích, liền muốn hao tổn ở đây?"),t("br"),t("br"),n._v("“Trương sư... Có thể phát giác Ma âm, có thể hay không đã nghĩ ra biện pháp?”"),t("br"),t("br"),n._v("Ngô sư vội vàng nhìn tới."),t("br"),t("br"),n._v("“Đây là bát tinh cấp bậc Ma âm sư phát ra Ma âm, ta cũng chỉ có thể miễn cưỡng chống cự, muốn đối kháng, để mọi người theo đặc thù ý cảnh bên trong đi ra, chỉ có thể lấy âm phá âm!”"),t("br"),t("br"),n._v("Suy tư một chút, Trương Huyền nói."),t("br"),t("br"),n._v("Hắn vừa tiến đến liền biết đây là Ma âm, nhờ vào Thiên Đạo chân khí cùng Ma âm sư thân phận, bản thân có thể chống lại, nhưng là muốn phá giải, nhưng thủy chung không nghĩ tới... Trừ phi, bản thân cũng đạt tới bát tinh Ma âm sư thực lực, lấy âm phá âm!"),t("br"),t("br"),n._v("“Lấy âm phá âm?”"),t("br"),t("br"),n._v("Hai người sắc mặt trắng nhợt."),t("br"),t("br"),n._v("Lúc này đi nơi nào tìm một cái bát tinh Ma âm sư?"),t("br"),t("br"),n._v("Thật muốn có loại thực lực này, khẳng định cũng sẽ không để mọi người rơi vào loại này không cách nào tự kềm chế đặc thù ý cảnh."),t("br"),t("br"),n._v("“Cái này bát tinh cấp bậc, nhưng mà sơ kỳ, nếu không, các ngươi nguyên thần cũng sẽ nhận xao động, giống như những người khác, liền bị thương cũng không biết. Bất quá, ta hiện tại nhưng mà lục tinh đỉnh phong cấp bậc Ma âm sư, coi như dùng hết toàn lực, cũng rất khó giải quyết...”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Cái này tiếng gió, chỉ tương đương với bát tinh sơ kỳ Ma âm sư tấu vang lên âm nhạc, nếu không, Hàn hội trưởng cùng Ngô sư, khẳng định cũng không chịu nổi, không có khả năng giống bây giờ đồng dạng, duy trì thanh tỉnh."),t("br"),t("br"),n._v("Dù vậy, cũng không phải hắn có thể chống lại."),t("br"),t("br"),n._v("Thực lực bây giờ, vẫn là quá yếu."),t("br"),t("br"),n._v("“Vậy làm sao bây giờ?”"),t("br"),t("br"),n._v("“Các ngươi ai có thất tinh cấp bậc liên quan tới Ma âm thư tịch?”"),t("br"),t("br"),n._v("Trương Huyền hỏi."),t("br"),t("br"),n._v("Nếu có thất tinh cấp sách khác tịch, ngưng tụ thành công pháp, bắt đầu tu luyện, coi như không đạt tới bát tinh, cũng chí ít có thể đi đến thất tinh đỉnh phong, loại thực lực này, lại phối hợp Thiên Đạo công pháp tự nhiên vượt cấp tính, đối kháng cái này Ma âm, hẳn là sẽ không quá khó."),t("br"),t("br"),n._v("“Ma âm?”"),t("br"),t("br"),n._v("Liếc mắt nhìn nhau, Hàn hội trưởng cùng Ngô sư đồng thời lắc đầu."),t("br"),t("br"),n._v("Một cái là trận pháp sư, một cái khác danh sư, mặc dù phụ tu bảy loại chức nghiệp, nhưng không có Ma âm cái này một hạng, do đó, thư tịch cũng không có tùy thân mang theo."),t("br"),t("br"),n._v("“Cái kia thì khó rồi...”"),t("br"),t("br"),n._v("Thấy bọn họ lắc đầu, Trương Huyền cau mày."),t("br"),t("br"),n._v("Không có, không học được cấp bậc cao hơn Ma âm, muốn loại bỏ Ma âm, độ khó liền lớn không biết gấp bao nhiêu lần."),t("br"),t("br"),n._v("“Như vậy đi, các ngươi hai cái chăm sóc mọi người, tận lực để bọn hắn không cần thoát lực mà chết, thực sự không được, nhiều bổ sung chút đan dược... Ta đi tìm cái này tiếng gió nguồn gốc, nếu như có thể tìm tới, liền có khả năng giải quyết...”"),t("br"),t("br"),n._v("Chần chờ một chút, Trương Huyền nói."),t("br"),t("br"),n._v("Không thể lấy lực loại bỏ, vậy cũng chỉ có thể tìm kiếm căn nguyên, nghĩ biện pháp đem căn nguyên tiêu diệt."),t("br"),t("br"),n._v("“Ừm, làm phiền Trương viện trưởng...”"),t("br"),t("br"),n._v("Hai người đồng thời cúi đầu."),t("br"),t("br"),n._v("Hiện tại cũng không có những biện pháp khác, chỉ có thể như thế."),t("br"),t("br"),n._v("“Khách khí!”"),t("br"),t("br"),n._v("Thấy bọn họ thái độ như thế, Trương Huyền lắc đầu, không nói thêm lời, thân thể nhoáng một cái, dọc theo thanh âm vang vọng phương hướng thẳng tắp bay đi."),t("br"),t("br"),n._v("Thanh âm thanh đạm, vang ở trong tai, liền xem như hắn, đều cảm thấy có chút mê muội, nếu không phải linh hồn đi qua dung nham rèn luyện tiến thêm một bước, chỉ sợ cũng có thể không chịu nổi."),t("br"),t("br"),n._v("“Cái này Ma âm, mặc dù có thể khiến người ta thoát lực, nhưng không đến mức chí tử... Chỉ cần bổ sung đan dược kịp thời, vấn đề không lớn...”"),t("br"),t("br"),n._v("Hướng (về) sau nhìn thoáng qua, phát hiện vừa rồi hộc máu mấy vị Chiến sư, bị Ngô sư hai người bổ sung đan dược về sau, vẻ mặt khôi phục không ít."),t("br"),t("br"),n._v("Bất quá, thân thể vẫn như cũ không bị khống chế, tiếp tục hướng phía trước."),t("br"),t("br"),n._v("Bọn hắn giờ phút này, giống như bị người giật dây con rối đồng dạng, biết rõ lâm vào cục diện bị động, muốn tránh thoát, nhưng không có bất kỳ biện pháp nào."),t("br"),t("br"),n._v("Ma âm sư sở dĩ có thể khiến người ta hoảng sợ, danh chấn đại lục, thậm chí Hồng Viễn học viện, đều phải đặc biệt mở một cái viện hệ... Nguyên nhân chính là để cho người ta biết rõ nguy hiểm, nhưng không thể trốn đi đâu được, tránh cũng không thể tránh."),t("br"),t("br"),n._v("Phi hành một hồi, phát hiện thanh âm không có bất kỳ biến hóa nào, Trương Huyền nhíu nhíu mày, dựa theo tốc độ của hắn, cái này nửa ngày, ít nhất phi hành mấy trăm cây số, loại tình huống này, đều không có thoát ly Ma âm phạm vi, thậm chí liền ngọn nguồn đều không tìm được..."),t("br"),n._v(" Thanh âm bao phủ khó tránh khỏi có chút quá lớn đi!"),t("br"),t("br"),n._v("Lấy hắn làm thí dụ, đàn tấu đàn ngọc, có thể để cho phương viên vài trăm mét người điên cuồng, có thể nghĩ lại muốn phạm vi lớn, liền lực có không bằng... Coi như bát tinh Ma âm sư, cũng không có khả năng tấu âm vang vui, bao phủ mấy trăm cây số đi!"),t("br"),t("br"),n._v("Thật muốn như vậy, một vị mạnh như thế người, chỉ cần tại hoàng thành đánh đàn, mấy trăm triệu sinh linh đều lại nhận mê hoặc, há không chiến đấu lực so danh sư đều còn đáng sợ hơn?"),t("br"),t("br"),n._v("“Chẳng lẽ đây chính là trận pháp không gian quỷ dị? Rõ ràng ta đi về phía trước, nhưng như cùng ở tại xoay quanh, tại cùng một cái phạm vi chạy?”"),t("br"),t("br"),n._v("Đột nhiên trong lòng hơi động, nghĩ tới kiếp trước trong truyền thuyết quỷ đả tường."),t("br"),t("br"),n._v("Có thể hay không rõ ràng cảm giác quyết định phương hướng, mà trên thực tế cũng không ngừng tại nguyên chỗ bọc vòng?"),t("br"),t("br"),n._v("Loại tình huống này, tại trong trận pháp cũng là rất thường gặp."),t("br"),t("br"),n._v("Rất nhiều mê trận, tự mang để cho người ta đánh mất phương hướng đặc thù lực lượng, chỉ cần đi vào trong đó, liền sẽ mất đi phương hướng cảm giác, thậm chí la bàn đều mất đi hiệu quả, để ngươi rõ ràng cảm thấy đi hướng đông, trên thực tế lại là hoàn toàn trái lại, hoặc là lệch rất nhiều."),t("br"),t("br"),n._v("“Đã nơi này sẽ cho người mất đi phương hướng, ta liền bố trí cái trận pháp, để phương hướng trở nên rõ ràng...”"),t("br"),t("br"),n._v("Ngừng trên không trung không tiếp tục hướng phía trước, Trương Huyền lông mày vung lên, cổ tay khẽ đảo, lòng bàn tay lần nữa thêm ra một đống trận kỳ."),t("br"),t("br"),n._v("Ma âm thực lực, hắn hiện tại vẫn như cũ là lục tinh đỉnh phong, có thể so với thất tinh sơ kỳ, không cách nào đối kháng cái này tiếng gió, nhưng... Trên trận pháp nhưng đã đạt đến thất tinh đỉnh phong, hoàn toàn có thể bố trí ra một cái, rõ ràng phân biệt phương hướng đại trận, chống cự công kích của đối phương."),t("br"),t("br"),n._v("Rất nhiều trận pháp tại trong óc chảy xuôi, rất nhanh một cái trận đồ hiện lên ở trong óc, lông mày vung lên, nhẹ nhàng cười một tiếng."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Trước mắt lơ lửng trận kỳ, lập tức kích bắn đi ra, dựa theo đặc thù phương vị rơi trên mặt đất."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Một tiếng oanh minh, trận pháp vận chuyển lại, Trương Huyền đứng ở trong đó, ngừng nghỉ một hồi, mở mắt lần nữa."),t("br"),t("br"),n._v("“Phương hướng quả nhiên là sai...”"),t("br"),t("br"),n._v("Khóe miệng vung lên."),t("br"),t("br"),n._v("Thông qua trận pháp này, hắn lập tức phân biệt ra được chân thực phương hướng, trước đó cảm giác quả nhiên là sai."),t("br"),t("br"),n._v("Xem ra trận pháp này trong không gian, không chỉ có Ma âm đánh lén, còn có nhiễu loạn phương hướng, để cho người ta phân biệt không ra phương hướng đồ vật tồn tại."),t("br"),t("br"),n._v("Không phân rõ được phương hướng, lại thêm Ma âm, tiến vào bên trong người tu luyện, liền sẽ không ngừng đi lại, thậm chí đem lực lượng trong cơ thể, tất cả đều hao hết, đều không tự biết."),t("br"),t("br"),n._v("“Lão viện trưởng bọn họ, có lẽ mang năm đó Ngô Dương Tử tiền bối lưu lại , thứ này mặc dù không phải Thánh khí, nhưng hơn hẳn Thánh khí, tìm kiếm trên phương hướng, khó đưa ra phải, lúc này mới thuận lợi đi ra ngoài...”"),t("br"),t("br"),n._v("Rõ ràng những này, Trương Huyền nhẫn gật đầu không ngừng."),t("br"),t("br"),n._v("Lão viện trưởng rõ ràng đi ra ngoài, nếu quả thật chết ở chỗ này, khẳng định sớm liền phát hiện hài cốt."),t("br"),t("br"),n._v("Bọn họ tu vi so Ngô sư đám người muốn thấp nhiều lắm, nhưng có thể đi ra, đoán chừng là dùng một loại nào đó bảo vật."),t("br"),t("br"),n._v("Thông qua luyện khí sư học viện rất nhiều thư tịch, biết học viện từng tích trữ Ngô Dương Tử một cái đắc ý tác phẩm, tìm hướng châm!"),t("br"),t("br"),n._v("Đây là một loại dùng đặc thù tìm hướng mộc, rèn luyện mà thành đặc thù vật phẩm, mặc dù không phải Thánh khí, nhưng đang tìm kiếm trên phương hướng, so Thánh khí đều cường đại hơn không biết gấp bao nhiêu lần."),t("br"),t("br"),n._v("Chỉ bất quá, không gặp được loại này xóc cũng Âm Dương Ngũ Hành vị trí, căn bản không cần đến, luyện chế thành công cũng là không coi ra gì, đưa cho danh sư học viện."),t("br"),t("br"),n._v("Bản thân đến thời điểm, từng chuyên môn đi tàng bảo khố nhìn, cũng chưa phát hiện, đoán chừng lần trước lão viện trưởng đi ra thời điểm, đã lấy đi."),t("br"),t("br"),n._v("Cũng chính là nhờ vào đó, mới đi ra khỏi địa phương này."),t("br"),t("br"),n._v("Đương nhiên, cụ thể dùng bao lâu, cũng không biết, cũng có thể là một canh giờ, càng có thể có thể ba ngày, năm ngày, mới phát giác vấn đề trong đó, dù sao khẳng định đối với bọn họ phát giác nhanh như vậy."),t("br"),t("br"),n._v("Dù sao, liền Ngô sư đều chẳng qua là cảm thấy kỳ quái, cũng không tìm được nguyên nhân, hay là hắn, không sợ Ma âm, mới ngoài cuộc tỉnh táo trong cuộc u mê."),t("br"),t("br"),n._v("Xác nhận phương hướng, Trương Huyền theo trong trận pháp nhảy ra, dựa theo mới vừa mới tìm được vị trí, thẳng tắp bay tới đằng trước, bay hơn mười dặm, biết chắc lại sai lầm, lần nữa lấy ra chân khí, lại bố trí một tương đồng trận pháp."),t("br"),t("br"),n._v("Tìm đúng phương hướng, tiếp tục tiến lên."),t("br"),t("br"),n._v("Như thế liên tục, rốt cục nhảy ra một mực bọc vòng tình huống, một cái cự đại bia đá xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Không biết dùng cái gì điêu khắc, cao hơn ba mét, rộng hơn hai mét, đứng sừng sững ở tại chỗ không biết bao lâu, chịu đựng mưa gió ăn mòn, đã có chút trắng bệch."),t("br"),t("br"),n._v("Nhẹ nhàng run rẩy, phát ra êm tai để mọi người mê say vang lên."),t("br"),t("br"),n._v("Mấy bước đi tới gần, ngẩng đầu nhìn lại, chỉ thấy phía trên ba chữ to, rồng bay phượng múa, không biết ai lưu lại, tựa như lúc nào cũng sẽ bay ra ngoài."),t("br"),t("br"),n._v("“Vô Cương giới?”"),t("br"),t("br"),n._v("Trương Huyền lông mày nhíu một cái."),t("br"),t("br"),n._v("Tiếp tục nhìn xuống, chỉ thấy chữ lớn đằng sau còn viết một hàng chữ nhỏ."),t("br"),t("br"),n._v("“Vô Cương giới, hành giả Vô Cương. Phàm là tiến vào bên trong, tất lại nhận Ma âm ảnh hưởng, không phân biệt đông tây nam bắc, một mực tại vòng tròn bên trong loạn chuyển, khó mà rời đi. Chỉ có tâm chí kiên định hạng người, mới có thể không bị mê hoặc.”"),t("br"),t("br"),n._v("“Như có thể trong vòng ba ngày phân rõ phương hướng, phân biệt Ma âm, tìm ở đây, đem được ta một môn tuyệt học; Trong vòng mười ngày hoàn thành, được ta lưu lại một trận Tạo Hóa; Mười ngày về sau, nói rõ tư chất bình thường, ngươi ta một trận duyên phận, cho khuyến cáo, nhanh chóng trở về đi!”"),t("br"),t("br"),n._v("Đem nội dung phía trên nhìn xong, Trương Huyền nháy nháy mắt."),t("br"),t("br"),n._v("“Ta từ khi đi vào cửa đến nơi đây, có lẽ... Còn chưa đủ nửa canh giờ đi! Trong ba ngày phân rõ phương hướng, tới chỗ này đều có ban thưởng, cái khảo hạch này có phần quá đơn giản chút, thật chẳng lẽ có đần như vậy, ba ngày đều không ý thức được vấn đề?”"),t("br"),t("br"),n._v("Trong lòng thầm nhủ, xoay người hướng bia đá đằng sau đi đến tiếp tục hướng xuống nhìn lại, chỉ thấy cùng lúc trước khác biệt bộ dáng kiểu chữ xuất hiện ở trước mắt."),t("br"),t("br"),n._v("“Ta Chương Dẫn Khâu, đem người đến đây, mất phương hướng vào trong, tròn nửa tháng có thừa, vô duyên với ban thưởng, chỉ cầu có thể ngăn cản Dị Linh tộc nhân...”"),t("br"),t("br"),n._v("“Hơn nửa tháng mới phát hiện?”"),t("br"),t("br"),n._v("Khóe miệng giật một cái, Trương Huyền mặt mũi xấu hổ."),t("br"),t("br"),n._v("“Không nghĩ tới, thật là có đần như vậy...”"),t("br"),t("br"),n._v("Thứ hai, cầu hai tấm phiếu đề cử!"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1048"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1050"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);