(window.webpackJsonp=window.webpackJsonp||[]).push([[700],{1055:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1604-mau-phu-nhan-chu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1604-mau-phu-nhan-chu"}},[n._v("#")]),n._v(" Chương 1604: Mẫu phù nhận chủ")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1604: Mẫu phù nhận chủ"),t("br"),t("br"),n._v("“Ngươi nói cái gì, ta không rõ ràng, ta chỉ là Cổ Thánh ở lại chỗ này một cái khí linh, chịu trách nhiệm tiếp đón các ngươi mà thôi...”"),t("br"),t("br"),n._v("Tượng đá đồng tử sững sờ, xấu hổ cười một tiếng."),t("br"),t("br"),n._v("Lạc Nhược Hi cùng Ngột Thần cũng đầy là kỳ lạ."),t("br"),t("br"),n._v("Cái này đồng tử, bọn họ vừa vào cửa liền gặp được, chỉ là tượng đá, có tối đa nhất linh tính, thế nào lại là truyền thế thiên phù?"),t("br"),t("br"),n._v("Nếu thật là, vì sao trước mặt bốn người, nắm giữ nhiều như vậy Cổ Thánh bảo vật, cũng đều không có phát hiện?"),t("br"),t("br"),n._v("“Ồ? Khí linh, không biết ra sao khí linh? Có thể hay không cùng ta nói tỉ mỉ thoáng cái?” Không để ý tới nó giải thích, Trương Huyền mỉm cười."),t("br"),t("br"),n._v("“Tại hạ là trong tượng đá đản sinh khí linh...” Tượng đá đồng tử giải thích."),t("br"),t("br"),n._v("“Tượng đá đản sinh khí linh?” Trương Huyền khóe miệng nâng lên, hai tay chắp sau lưng, tại nó bên cạnh quay một vòng, vừa đi vừa nói: “Điêu khắc ngươi cái này đá, gọi Vu linh thạch, sinh ra từ đại lục phía đông nam nhất Lô Húc sơn, trải qua đại lục biến thiên, nước, lục va chạm, thậm chí dung nham thiêu đốt, mới có thể hình thành, toàn bộ đại lục, đều không có bao nhiêu, tuyệt không vượt qua mười khối.”"),t("br"),t("br"),n._v("“Thứ này chỗ tốt lớn nhất, là tẩm bổ hồn phách, để tiến vào bên trong linh hồn, càng thêm cường đại, nhưng... Thiếu hụt cũng rất lớn, cái kia chính là, linh tính quá đủ, du tẩu quá mức kịch liệt, mà không cách nào hội tụ! Nói cách khác... Cho dù khải linh sư thủ đoạn cao minh đến đâu, cũng không thể ở phía trên khải linh thành công! Ngươi nói ngươi là tượng đá linh tính, vậy ta hỏi ngươi, là ai cho ngươi sinh mệnh?”"),t("br"),t("br"),n._v("“Ta...” Tượng đá đồng tử sững sờ, không nghĩ tới người trước mắt này, liền loại này thưa thớt đá, đều có thể nhận ra, không tự chủ được lui về sau một bước, nói: “Là Nhiễm Cầu Cổ Thánh... Cổ Thánh thủ đoạn thông thiên, ngươi không cách nào làm được, không có nghĩa là người khác làm không được!”"),t("br"),t("br"),n._v("“Ha ha!”"),t("br"),t("br"),n._v("Trương Huyền khóe miệng nâng lên: “Nhiễm Cầu Cổ Thánh làm thập hiền một trong, đủ loại thủ đoạn, liên tiếp xuất hiện, hậu nhân không dám phỏng đoán, nhưng... Chỉ cần bị khải linh đồ vật, trời sinh đối khải linh người có đặc thù tình cảm, có thể rất dễ dàng nhận chủ... Cổ Thánh đã đem nơi này giao cho ngươi, cho ngươi dẫn đầu hậu nhân, nhất định muốn thu phục mới có thể yên tâm... Nhưng vì sao, ngươi vẫn luôn là xưng hô ‘Nhiễm Cầu Cổ Thánh’, mà không phải... Chủ nhân?”"),t("br"),t("br"),n._v("[ ngantruyen.com"),t("br"),n._v("]  Chỉ cần là khải linh qua đồ vật, đối khải linh người, đều có dị dạng cảm xúc, rất dễ dàng thuần phục, liền giống như lúc trước hắn khải linh tượng đá, người khác, đều không nghe, chỉ nghe hắn."),t("br"),t("br"),n._v("Gà ra vỏ trứng, nhìn thấy cái thứ nhất sinh mệnh, sẽ tưởng rằng mẹ nó mẹ."),t("br"),t("br"),n._v("Linh tính cũng là như vậy, rất khó sửa đổi."),t("br"),t("br"),n._v("Nhiễm Cầu Cổ Thánh đã muốn cái tên này quản lý nơi này, lại là hắn khải linh thành công, tất nhiên sẽ thu phục, nhưng đối phương từ đầu đến cuối, đều không có xưng hô một tiếng “Chủ nhân”."),t("br"),t("br"),n._v("Càng không cho thấy cỡ nào tôn trọng."),t("br"),t("br"),n._v("“Ta...” Tượng đá đồng tử câm ở, một lát sau: “Ta coi như không có nhận chủ, cũng không thể nào là truyền thế thiên phù đi! Ngươi cái này suy luận, không có bất kỳ cái gì lý do...”"),t("br"),t("br"),n._v("“Nhìn tới không nói rõ ràng, ngươi là sẽ không thừa nhận!”"),t("br"),t("br"),n._v("Mắt sáng lên, Trương Huyền lần nữa nhìn tới: “Vừa mới tiến tới thời điểm, một mình ta đối chiến hai cái kim binh, thi triển ra phong cấm không gian, ngươi nói một câu ‘Nguyên lai là Khâu Ngô Tử truyền nhân, còn xin hạ thủ lưu tình!’ ”"),t("br"),t("br"),n._v("“Không sai, cái này lại làm sao?” Tượng đá đồng tử nghi ngờ."),t("br"),t("br"),n._v("Lời này quả thực nói qua, không tính là gì."),t("br"),t("br"),n._v("“Khưu Ngô Cổ Thánh, tuy là Khổng sư hạ nhân, lại so với hắn lớn tuổi, hơn nữa lấy ngang hàng mà nói, Nhiễm Cầu Cổ Thánh là Khổng sư đệ tử, dựa theo vai vế, coi như nhìn thấy, cũng muốn cung kính hành lễ, không dám láo xược!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Liền giống như Tôn Cường tuy là hắn hạ nhân, nhưng Trịnh Dương đám người nhìn thấy, vẫn như cũ muốn lấy “Tôn thúc” xưng hô, không dám vượt qua."),t("br"),t("br"),n._v("Khưu Ngô Cổ Thánh, mặc dù là Khổng sư hạ nhân, tuổi tác lại so với hắn muốn dài, Khổng sư đều tôn kính mấy phần, làm học sinh, lại không dám không giữ lễ pháp."),t("br"),n._v(" “Có thể ngươi... Trực tiếp xưng hô đối phương ‘Khâu Ngô Tử’, đây là ngang hàng, mới có thể có xưng hô!” Trương Huyền nói tiếp: “Nhiễm Cầu Cổ Thánh khải linh bảo vật, như vậy mở miệng... Ngươi không cảm thấy không đúng ư?”"),t("br"),t("br"),n._v("“Ngươi, ngươi...” Sửng sốt một cái, tượng đá đồng tử lần nữa lui về phía sau mấy bước, giống như là quái lạ đồng dạng biểu lộ."),t("br"),t("br"),n._v("Đối phương chỉ bằng một cái xưng hô, liền suy đoán ra nó là truyền thế thiên phù... Có phần thật là đáng sợ đi!"),t("br"),t("br"),n._v("“Ngươi lúc nào thì nhìn ra được?”"),t("br"),t("br"),n._v("Kinh ngạc sau đó, biết tiếp tục giấu diếm cũng không có ý nghĩa gì, ngẩng đầu nhìn tới, tượng đá đồng tử tràn đầy không hiểu."),t("br"),t("br"),n._v("Hắn tự tin ngụy trang thiên y vô phùng, coi như Cổ Thánh đích thân tới, cũng chưa chắc có thể nhìn thấu, đối phương đến cùng lúc nào phát hiện?"),t("br"),t("br"),n._v("“Tiến thông đạo trước đó, ta vỗ một cái bờ vai của ngươi, trên thực tế là muốn xác định ngươi chất liệu, lúc này mới lên ngờ vực!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Đi vào trước thông đạo, hắn đi tới đối phương trước mặt, vỗ một cái, dùng thư viện dò xét đá chất liệu, biết thứ này khó mà tụ linh, có điều, thông đạo nguy hiểm, không có thời gian suy nghĩ nhiều, giờ phút này những cái kia Dị Linh tộc nhân chạy trốn, kết hợp trước đó xưng hô, lại không rõ ràng, liền thật ngốc."),t("br"),t("br"),n._v("Truyền thế thiên phù, là Khổng sư luyện chế ra tới bảo vật, xưng hô Khâu Ngô Tử, không có bất cứ vấn đề gì, coi như đối Nhiễm Cầu Cổ Thánh không khách khí, người sau cũng sẽ không nhiều nói."),t("br"),t("br"),n._v("Chủ yếu nhất là... Cái này viên thiên phù phía trên, hắn cảm ứng được, cùng hắn có chút tương tự khí tức."),t("br"),t("br"),n._v("Cái khác sáu cái thiên phù, là Khổng sư sưu tập thể chất đặc thù huyết dịch, cô đọng mà thành, mà cái này cái gọi là mẫu phù... Đoán không sai, hẳn là lấy Khổng sư huyết dịch ngưng tụ mà thành, để hắn cảm ứng tương tự, chính là... Trời nhận danh sư!"),t("br"),t("br"),n._v("Có loại cảm giác này, lại xác định ra đối phương là ai, cũng liền dễ như trở bàn tay."),t("br"),t("br"),n._v("“Thì ra là thế... Không hổ là Khâu Ngô Tử hậu nhân, nắm trong tay phong cấm chân giải, càng là thuần phục long cốt thần thương... Phần này nhãn lực, quả thực không phải người thường có thể bằng...” Nghe hắn giải thích xong, tượng đá đồng tử nhẹ gật đầu, lại không phủ nhận: “Không sai, ta chính là Khổng sư luyện chế ra tới truyền thế mẫu phù, bọn họ lấy đi cái kia, bất quá là một cái hàng giả thôi!”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, tượng đá đứng tại chỗ bất động, một đạo quang mang từ đỉnh đầu bay ra, trên không trung chậm chạp hội tụ, thời gian nháy mắt, tạo thành một tấm bùa chú."),t("br"),t("br"),n._v("Cùng vừa rồi trắng nõn thanh niên cướp đi tấm kia, giống nhau như đúc."),t("br"),t("br"),n._v("“Ta là Khổng sư luyện chế ra tới mẫu phù, có ta mới có thể đi vào Khổng miếu chủ điện... Bất quá, ta được luyện chế đi ra, chỉ nghe Khổng sư dặn dò, cũng không phải cái gì mọi người có thể thần phục, đừng nói ngươi một cái Khâu Ngô Tử truyền nhân, coi như Khâu Ngô Tử đích thân tới, để cho ta thần phục, cũng làm không được!”"),t("br"),t("br"),n._v("Nhẹ nhàng vụt qua, bùa chú thanh âm tiếp tục vang lên."),t("br"),t("br"),n._v("Trương Huyền cười khanh khách nhìn qua: “Không thể thần phục với ta?”"),t("br"),t("br"),n._v("“Đương nhiên không thể!”"),t("br"),t("br"),n._v("Bùa chú thanh âm bên trong mang theo khinh thường cùng tự ngạo."),t("br"),t("br"),n._v("“Vậy thì tốt, ta cho ngươi xem một vật, sau khi xem, lựa chọn ra sao, tùy ngươi!”"),t("br"),t("br"),n._v("Ngón tay duỗi ra, Trương Huyền nhẹ nhàng điểm tại bùa chú phía trên."),t("br"),t("br"),n._v("“Nhìn cái gì cũng vô dụng, ta là Khổng sư luyện chế mà ra, há có thể thần phục hậu nhân, người, người...”"),t("br"),t("br"),n._v("Đang tràn đầy kiêu ngạo nói chuyện, bùa chú đột nhiên run rụt lại, giống như là quái lạ đồng dạng trên không trung cuộn mình, ngay sau đó bỗng nhiên biến thẳng đứng, như là một trương lơ lửng bài poker, trong giọng nói mang theo cung kính cùng lấy lòng: “Tiểu Phù Phù, gặp qua chủ nhân!”"),t("br"),t("br"),n._v("P/S: Chết cười Tiểu Phù Phù =) ) ) ) ) ) ) )"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1615"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1617"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);