(window.webpackJsonp=window.webpackJsonp||[]).push([[703],{1058:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1607-chu-tu-bach-gia-muon-ty-thi-【-canh-3-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1607-chu-tu-bach-gia-muon-ty-thi-【-canh-3-】"}},[n._v("#")]),n._v(" Chương 1607: Chư Tử bách gia muốn tỷ thí 【 Canh [3] 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1607: Chư Tử bách gia muốn tỷ thí "),t("br"),t("br"),n._v("“Chư Tử bách gia người?”"),t("br"),t("br"),n._v("Sửng sốt một chút, Trương Huyền vội vàng ngẩng đầu, ngay sau đó nhìn thấy năm người, lơ lửng ở phía trên."),t("br"),t("br"),n._v("Nói chuyện chính là cái khoảng bốn mươi tuổi người trung niên, màu nâu xanh râu, mang theo thật dài mũ, một bộ cổ nho hình ảnh, trên người trường bào, cùng danh sư bào có chút tương tự, chỉ bất quá, dùng màu đen vải vóc cắt quần áo mà thành, từ xa nhìn lại, có chút âm trầm."),t("br"),t("br"),n._v("“Cùng bắt đi Triệu Nhã đám người Dị Linh tộc nhân trang phục đồng dạng!” Trương Huyền lông mày giương lên."),t("br"),t("br"),n._v("Người trung niên này quần áo, cùng trước đó Bạch Khê sơn nhìn thấy bốn cái Dị Linh tộc nhân, hoàn toàn tương đồng, nhìn tới người sau, là muốn ngụy trang thành Chư Tử bách gia cường giả, lừa dối qua ải, hơn nữa cũng quả thực lừa qua Tiểu Phù Phù... Chỉ bất quá, tại long cốt thần thương tấn công bên dưới, hiển lộ nguyên hình."),t("br"),t("br"),n._v("Nam Cung Nguyên Phong bên cạnh bốn cái, đều là hai mươi tuổi thanh niên, từng cái khí tức như rồng, mang theo tiêu sát."),t("br"),t("br"),n._v("“Bọn họ đều là Đại Thánh nhất trọng, Kiến Thần Bất Hoại cảnh giới cao thủ!”"),t("br"),t("br"),n._v("Lạc Nhược Hi nhẹ gật đầu: “Vị này Nam Cung Nguyên Phong thì là  hậu bối con cháu, Đại Nho đường nho học đại sư!”"),t("br"),t("br"),n._v("“Tử Dung Cổ Thánh?” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Vị này Cổ Thánh, là Khổng sư bảy mươi hai vị thân truyền một trong, trứ danh hiền giả, tuy là không kịp Nhiễm Cầu Cổ Thánh, Bốc Thương Cổ Thánh, Ngôn Yển Cổ Thánh như vậy có danh, nhưng cũng vang danh thiên hạ, hậu thế vô số người tế bái."),t("br"),t("br"),n._v("Không nghĩ tới vị này, lại là hắn hậu nhân!"),t("br"),t("br"),n._v("“Đại Nho đường là cái gì?”"),t("br"),t("br"),n._v("Sau khi hết khiếp sợ, Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Đại Nho đường cùng loại với đại lục Danh Sư đường, Danh Sư đường, giáo hóa vạn vật, người bình thường, tu luyện giả, đều tại giáo hóa phạm vi bên trong, mà Đại Nho đường khác biệt, chỉ truyền dạy tinh nghiên nho học, hạng người tu vi cao thâm... Bình thường đều phải đạt tới Đại Thánh cấp bậc, mới có tư cách tiến vào trong đó!”"),t("br"),t("br"),n._v("Lạc Nhược Hi giải thích."),t("br"),t("br"),n._v("“Đại Thánh mới có tư cách?” Trương Huyền tặc lưỡi."),t("br"),t("br"),n._v("Vốn cho rằng Thánh tử điện, là học phủ cao nhất, không nghĩ tới, Chư Tử bách gia còn có cái Đại Nho đường."),t("br"),t("br"),n._v("“Lạc gia quyền gia chủ Lạc Càn Trinh, gặp qua Nam Cung tiền bối, mời vào bên trong!”"),t("br"),t("br"),n._v("Đang muốn tiếp tục hỏi thăm, liền nghe đến Lạc gia một thanh âm vang lên, ngay sau đó Lạc Huyền Thanh cha, mang theo Lạc Khinh Trần rất nhiều trưởng lão, bay lên, lơ lửng giữa không trung, từng cái thái độ cung kính."),t("br"),t("br"),n._v("“Đi xuống đi!”"),t("br"),t("br"),n._v("Thấy hắn ra nghênh tiếp, trả cái lễ tiết, Nam Cung Nguyên Phong mỉm cười, theo sát tại Lạc gia mọi người bay xuống."),t("br"),t("br"),n._v("“Chư Tử bách gia người, lúc này tới, nhất định là có chuyện, chúng ta cũng đi qua!”"),t("br"),t("br"),n._v("Trương Huyền truyền âm."),t("br"),t("br"),n._v("Sớm biết Chư Tử bách gia người tới danh sư đại lục, không nghĩ tới vừa đưa ra năm vị, cái này mấu chốt, chạy tới, chỉ sợ cùng Khổng miếu mở ra, có quan hệ lớn lao."),t("br"),t("br"),n._v("“Ừm!” Lạc Nhược Hi gật đầu."),t("br"),t("br"),n._v("Hai người đều thực lực cực mạnh, giấu diếm được Lạc gia người bình thường tai mắt mười phần bình thường, tại thêm vào Trương Huyền đối với trận pháp tinh thông, Lạc gia giờ phút này lại có chút hỗn loạn, một đường đi vào, không có bị bất luận người nào phát hiện."),t("br"),t("br"),n._v("Đông đông đông đông!"),t("br"),t("br"),n._v("Đi tới một hồi, đang muốn tìm người hỏi thăm Lạc Càn Trinh đám người đi nơi nào, liền nghe đến một cái dồn dập tiếng chuông vang lên."),t("br"),t("br"),n._v("“Là gia tộc khẩn cấp tập hợp chuông vang, nhất định là chuyện gì xảy ra!”"),t("br"),t("br"),n._v("Lông mày giương lên, Trương Huyền theo sát mà Lạc gia mọi người, thẳng tắp hướng tiếng chuông vang lên phương hướng bay lượn mà đi."),t("br"),t("br"),n._v("Thời gian không dài, nhìn thấy một cái rộng lớn quảng trường, giờ phút này đã người đông nghìn nghịt, Lạc gia tất cả mọi người, nghe được tiếng chuông, tất cả đều dùng tốc độ nhanh nhất chạy tới."),t("br"),t("br"),n._v("Xuyên thấu qua đám người, Trương Huyền hướng về phía trước nhìn lại, ngay sau đó nhìn thấy vừa rồi Chư Tử bách gia Nam Cung Nguyên Phong đám người, đang ngồi ở ở giữa trên đài cao, Lạc Càn Trinh, Lạc Khinh Trần đám người thình lình xuất hiện."),t("br"),t("br"),n._v("“Vị bằng hữu này, chuyện gì xảy ra? Gấp gáp như vậy gõ vang tập hợp chuông?”"),t("br"),t("br"),n._v("Nhìn thấy phía trước mấy cái người mặc hạch tâm quần áo Lạc gia con cháu, đang thấp giọng trò chuyện, Trương Huyền đi tới trước mặt."),t("br"),n._v("Dù sao hiện tại, hạch tâm, bàng chi tới một đống lớn, ở giữa không có khả năng toàn bộ quen biết."),t("br"),t("br"),n._v("“Ngươi là...” Vị này hạch tâm con cháu thấy hắn lạ mặt, sửng sốt một chút."),t("br"),t("br"),n._v("“Tại hạ Lạc Thiên Nhai, là bàng chi con cháu!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Ah!” Vị này hạch tâm con cháu nhẹ gật đầu: “Là Chư Tử bách gia cường giả, muốn Lạc gia một cái bảo vật, gia chủ không đồng ý, sau đó hắn yêu cầu, bản thân mang tới người trẻ tuổi, cùng Lạc gia tuổi tác không cao hơn một trăm thế hệ trẻ tuổi, tỷ thí đối không gian lĩnh ngộ... Nếu như bọn họ chiến thắng, Lạc gia liền không có tư cách khống chế món bảo vật này, nhất định phải giao cho bọn hắn Đại Nho đường đảm bảo! Trái lại, bọn họ thua, cũng sẽ đưa tặng một cái bảo vật cho Lạc gia!”"),t("br"),t("br"),n._v("“Bảo vật gì?”"),t("br"),t("br"),n._v("Trương Huyền nhìn qua."),t("br"),t("br"),n._v("Chư Tử bách gia muốn bảo vật, đoán chừng tám chín phần mười là truyền thế thiên phù, đã lấy bảo vật làm tiền đặt cược, đối phương lấy ra, nhất định cũng không đơn giản."),t("br"),t("br"),n._v("“Là Tử Dung Cổ Thánh từng dùng qua một thanh trường kiếm!”"),t("br"),t("br"),n._v("Vị này hạch tâm con cháu chỉ về phía trước: “Chính là cái kia...”"),t("br"),t("br"),n._v("Theo ngón tay hắn, Trương Huyền nhìn lại, quả nhiên thấy một thanh trường kiếm, đang yên tĩnh lơ lửng tại trên đài cao, cho người ta một loại lạnh lùng cảm giác, tuy là không có ra khỏi vỏ, nhưng như cũ có thể cảm nhận được trong đó toả ra ý lạnh âm u."),t("br"),t("br"),n._v("“Mặc dù là Đại Thánh đỉnh phong cấp bậc bảo vật, nhưng Tử Dung Cổ Thánh sử dụng qua, so với bình thường nửa bước Cổ Thánh thần binh, cũng không yếu quá nhiều... Bất quá, cùng ngươi long cốt thần thương so, kém rất lớn một đoạn!”"),t("br"),t("br"),n._v("Lạc Nhược Hi nhìn một cái, truyền âm tới."),t("br"),t("br"),n._v("“Ừm!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Tuy là khoảng cách rất xa, không cách nào chạm đến, không biết chuôi kiếm này chân chính cấp bậc, nhưng bằng cho hắn mượn nhãn lực, cũng có thể nhìn ra, cùng long cốt thần thương kém không ít."),t("br"),t("br"),n._v("Chuôi này thần thương là Nhiễm Cầu Cổ Thánh đỉnh phong nhất lúc đều là dùng tuyệt thế vũ khí, Thượng Cổ thời đại đều tên tuổi lẫy lừng, Tử Dung Cổ Thánh từng dùng qua kiếm thôi, tự nhiên kém xa tít tắp."),t("br"),t("br"),n._v("“Chư vị, Chư Tử bách gia đến đây cường giả, Nam Cung Nguyên Phong tiền bối, dẫn đầu hậu bối, muốn cùng ta Lạc gia con cháu, tỷ thí đối không gian lĩnh ngộ, chỉ cần tuổi tác không cao hơn một trăm, đều có thể tham dự, không biết ai dám lên tới nghênh chiến?”"),t("br"),t("br"),n._v("Nhìn quanh một tuần, Lạc Càn Trinh thanh âm vang lên."),t("br"),t("br"),n._v("Nghe hắn thanh âm, tuy là ổn định, lại mang theo một cỗ tức giận."),t("br"),t("br"),n._v("Nhìn tới, cái gọi là tỷ thí, cũng không phải là hắn vốn ý nguyện, chỉ sợ trước đó cùng Chư Tử bách gia người, cũng náo động lên chút không vui cùng mâu thuẫn."),t("br"),t("br"),n._v("Chỉ bất quá, đối phương quá mức mạnh mẽ, bọn họ lại vừa mới thụ trọng thương, không dám phản bác thôi."),t("br"),t("br"),n._v("“Ta!”"),t("br"),t("br"),n._v("Hét lớn một tiếng, một bóng người thẳng tắp bay đi lên."),t("br"),t("br"),n._v("Trương Huyền nhìn lại, không phải người khác, chính là vị bằng hữu kia, Lạc Huyền Thanh."),t("br"),t("br"),n._v("Chỉ bất quá, bởi vì Lạc Thất Thất sự tình, giờ phút này coi như muốn nhận đối phương làm bằng hữu, cũng làm không được."),t("br"),t("br"),n._v("“Còn có ta!”"),t("br"),t("br"),n._v("“Ta cũng nguyện ý thử một lần!”"),t("br"),t("br"),n._v("“Cùng ta Lạc gia nam nhi tỷ thí đối không gian lĩnh ngộ! Hừ, ta nhìn các ngươi tự tìm cái chết...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Lại có ba vị thanh niên bay lên đài cao."),t("br"),t("br"),n._v("Tất cả đều là Lạc gia không cao hơn trăm tuổi cường giả, từng cái khí tức mạnh mẽ, có hai vị đã trở thành trưởng lão, cùng Trương Huyền đồng dạng, đạt đến Thánh vực cửu trọng đỉnh phong."),t("br"),t("br"),n._v("“Rất tốt!”"),t("br"),t("br"),n._v("Vừa ý tới mấy người, Lạc Càn Trinh thoả mãn nhẹ gật đầu, quay đầu nhìn về phía cách đó không xa Nam Cung Nguyên Phong: “Nam Cung tiền bối, làm sao tỷ thí, còn xin cho cái điều lệ đi!”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1618"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1620"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);