(window.webpackJsonp=window.webpackJsonp||[]).push([[716],{1070:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1618-lac-gia-tang-bao-kho-【-canh-thu-hai-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1618-lac-gia-tang-bao-kho-【-canh-thu-hai-】"}},[n._v("#")]),n._v(" Chương 1618: Lạc gia tàng bảo khố 【 canh thứ hai 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1618: Lạc gia tàng bảo khố "),t("br"),t("br"),n._v("Thánh vực cửu trọng về sau, cần cá nhân lý giải, mỗi người đi con đường không thông, phương pháp tu luyện cũng không giống, bởi vậy, không có riêng biệt đột phá phương pháp, cũng không có riêng biệt công pháp, hắn hiện tại, chỉ có thể đi tìm càng rất mạnh hơn người lưu lại tâm đắc, sau đó lại tìm ra một đầu, thích hợp bản thân phương pháp."),t("br"),t("br"),n._v("“Đột phá Đại Thánh tâm đắc? Các đời cường giả vô số, loại này thư tịch, nắm giữ không ít, bất quá... Chỉ có thể tham khảo, không thể dựa theo học tập...”"),t("br"),t("br"),n._v("Lạc Càn Trinh nói."),t("br"),t("br"),n._v("Tiền bối tâm đắc, có thể tham khảo, nhưng... Mỗi người thể chất khác biệt, kỳ ngộ khác biệt, không có khả năng hoàn toàn rập khuôn, nếu không, không những không đột phá nổi, còn vô cùng có khả năng tẩu hỏa nhập ma."),t("br"),t("br"),n._v("“Đa tạ nhắc nhở, ta hiểu!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Vậy là tốt rồi, tộc trưởng là gia tộc vài vạn năm đến, đỉnh phong nhất thiên tài một trong, tự nhiên biết mức độ nặng nhẹ, mời đi theo ta!”"),t("br"),t("br"),n._v("Nghe hắn nói như vậy, Lạc Càn Trinh cười cười."),t("br"),t("br"),n._v("Đối phương lĩnh ngộ phong cấm chân giải, so với hắn đối không gian lý giải đều sâu, thiên phú mạnh mẽ, không thể nghi ngờ, nói nhiều, ngược lại có mấy lời lao."),t("br"),t("br"),n._v("“Đi đi, ta ở chỗ này chờ ngươi là được!”"),t("br"),t("br"),n._v("Biết hắn muốn nhìn sách, Lạc Nhược Hi cũng không nhiều lời, nhẹ gật đầu."),t("br"),t("br"),n._v("“Ừm!” Trương Huyền theo sát tại Lạc Càn Trinh sau lưng, đi ra ngoài, thời gian không dài đi tới một cái Tàng Thư khố."),t("br"),t("br"),n._v("“Phía dưới ba tầng, là gia tộc hạch tâm con cháu có thể học tập, phía trên mới là trưởng lão có thể nhìn... Ghi chép đột phá Đại Thánh tâm đắc, đều ở nơi này!”"),t("br"),t("br"),n._v("Dọc theo cầu thang đi đến tầng thứ tư, Lạc Càn Trinh chỉ về phía trước."),t("br"),t("br"),n._v("Trương Huyền nhìn lại, trước mắt là khắp phòng thư tịch, cơ bản cũng là đủ loại bút ký, không ít lộn xộn kinh khủng, cũng có chút cất giữ thời gian lâu dài, đã xuất hiện thiếu chữ cùng ít trang."),t("br"),t("br"),n._v("“Những sách này, chỉ là tham khảo, mọi người tùy tiện lật xem thoáng cái, không có hình thành hệ thống, tộc trưởng có thể tùy ý quan sát, có gì cần lời nói, cùng ta nói là được!”"),t("br"),t("br"),n._v("Lạc Càn Trinh nói."),t("br"),t("br"),n._v("“Ừm, đa tạ, ta ở đây tùy tiện nhìn một chút, có chuyện gì, khẳng định sẽ làm phiền ngươi!”"),t("br"),t("br"),n._v("Nói xong, không cần phải nhiều lời nữa, hướng trước mắt giá sách nhìn lại."),t("br"),t("br"),n._v("Lạc Càn Trinh không nói thêm lời, đi xuống."),t("br"),t("br"),n._v("Đối phương rời đi, Trương Huyền trong đầu thở nhẹ “Thiếu hụt”, đem trên giá sách thư tịch, từng tầng từng tầng thu vào thư viện, ước chừng bận rộn hơn mười phút, thu sạch lấy xong xuôi."),t("br"),t("br"),n._v("“Đúng đắn!”"),t("br"),t("br"),n._v("Tinh thần khẽ động, một quyển sách xuất hiện tại trong óc."),t("br"),t("br"),n._v("Vội vàng mở ra, rất mau nhìn xong."),t("br"),t("br"),n._v("“Đem những này tâm đắc sưu tập lên, tuy là không có hình thành Thiên đạo công pháp, lại làm cho ta đối Đại Thánh có càng sâu hiểu rõ!”"),t("br"),t("br"),n._v("“Kiến Thần Bất Hoại, là thần thức đắm chìm trong mỗi một cái tế bào, mỗi một đầu kinh mạch, lợi dụng thay đổi một cách vô tri vô giác lực lượng, cải tạo nhân thể, trở nên vượt qua Thánh vực, chỉ có như vậy, tuổi thọ, mới có thể có chỗ kéo dài...”"),t("br"),t("br"),n._v("Trước đó thần thức liếc nhìn, có thể thẩm thấu cơ bắp, đi vào kinh mạch, mà đạt tới Kiến Thần Bất Hoại cảnh giới, đã có thể đi vào tế bào."),t("br"),t("br"),n._v("Như vậy, có hoại tử, hoặc là xuất hiện ổ bệnh, cũng có thể nghĩ biện pháp thay thế, từ đó để thân thể một mực nằm ở trạng thái đỉnh phong, cùng người chiến đấu, cũng thực lực mạnh hơn."),t("br"),t("br"),n._v("Bình thường Thánh vực, tuổi thọ tại một ngàn tuổi khoảng chừng, tám trăm tuổi về sau, thể lực liền bắt đầu dần dần suy giảm, sức chiến đấu đã cùng không thể so với trước kia, không chỉ có như vậy, còn muốn làm ra đột phá, cũng càng khó."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, đột phá thừa dịp trẻ tuổi, lời này một mực tại danh sư bên trong lưu truyền."),t("br"),t("br"),n._v("Lúc tuổi còn trẻ không đột phá, đến thể lực suy giảm, cho dù lĩnh ngộ cao thâm hơn cảnh giới, cũng không cách nào tiến thêm một bước."),t("br"),t("br"),n._v("Bất quá... Đạt đến Kiến Thần Bất Hoại cảnh giới về sau, lại khác biệt."),t("br"),t("br"),n._v("Thần thức đi vào tế bào, có thể tùy thời phát hiện lão tổn hại, cũng tăng thêm thay thế, để hắn duy trì đỉnh phong... Cho dù đại nạn sắp tới, có chỗ lĩnh ngộ lời nói, cũng có thể tốt hơn đột phá, thậm chí nắm giữ lúc tuổi còn trẻ sức chiến đấu."),t("br"),n._v("Nguyên nhân chính là như vậy, Đại Thánh cấp bậc cường giả, cũng không phải là càng trẻ sức chiến đấu càng mạnh, có chút lớn tuổi, ngược lại càng thêm lợi hại."),t("br"),t("br"),n._v("“Ta tu luyện Thiên đạo công pháp, vừa bắt đầu liền không có thiếu hụt, Thiên đạo chân khí tinh thuần vô cùng, đột phá Đại Thánh độ khó, rõ ràng so người khác lớn...”"),t("br"),t("br"),n._v("Đem sưu tập chỉ là tại trong óc qua một lần, Trương Huyền cân nhắc nửa ngày, bất đắc dĩ lắc đầu."),t("br"),t("br"),n._v("Cấp bậc này quả nhiên không phải dễ dàng như vậy, cho dù nhìn nhiều như vậy sách, vẫn không có thích hợp nhất phương pháp."),t("br"),t("br"),n._v("“Trước đem nơi này tất cả thư tịch toàn bộ sưu tập, không được về Trương gia tại từ từ nghiên cứu, Trương gia hẳn là cũng có tương tự tâm đắc...”"),t("br"),t("br"),n._v("Biết chuyện này gấp không được, đành phải bỏ xuống trong lòng phân loạn suy nghĩ, Trương Huyền tiếp tục hướng đi lên lầu."),t("br"),t("br"),n._v("Tầng này là liên quan tới đột phá Đại Thánh tâm đắc, phía trên hẳn là còn có cấp bậc cao hơn sách."),t("br"),t("br"),n._v("Thời gian không dài, đi đến mái nhà, mỗi tầng thư tịch, cũng tất cả đều thu vào thư viện."),t("br"),t("br"),n._v("Đi ra tàng thư các, phát hiện Lạc Càn Trinh cũng không rời đi, vẫn tại cách đó không xa đợi chờ, Trương Huyền cười cười, tiếp tục hỏi: “Ta đột phá, cần một chút tương tự tuyệt phẩm linh thạch như vậy ẩn chứa tinh thuần linh khí bảo vật, không biết chúng ta trong tộc ở nơi đó nhận lấy? Ta làm tộc trưởng, có thể lấy bao nhiêu? Có hay không định lượng?”"),t("br"),t("br"),n._v("Nhìn xong thư tịch, muốn đột phá, còn cần linh thạch các loại bảo vật, đã làm tới cái này Lạc gia gia chủ, tự nhiên muốn thật tốt lợi dụng tài nguyên."),t("br"),t("br"),n._v("Dù sao đối với loại này truyền thừa vài vạn năm đại gia tộc tới nói, một ít linh thạch, bảo vật mà thôi, không đáng kể chút nào."),t("br"),t("br"),n._v("“Tộc trưởng là có định lượng, có điều, vừa mới vì gia tộc dựng lên như thế lớn công lao, tài nguyên tùy tiện sử dụng!”"),t("br"),t("br"),n._v("Còn tưởng rằng cái đại sự gì, Lạc Càn Trinh cười cười, tiếp tục dẫn đường, chỉ chốc lát đi tới một cái Tàng Bảo các: “Đây là gia tộc bọn ta chứa đựng bảo vật địa phương, trong tộc đệ tử ban thưởng, cũng đều là từ nơi này phát! Tộc trưởng có thể tùy ý quan sát, chỉ cần vừa ý, lấy đi là được, không cần đăng ký...”"),t("br"),t("br"),n._v("“Đa tạ!”"),t("br"),t("br"),n._v("Không nghĩ tới tộc trưởng còn có loại này đặc quyền, Trương Huyền hai mắt sáng lên."),t("br"),t("br"),n._v("Có thể tùy ý lấy đi, cái kia... Có thể hay không đem những bảo vật này tất cả đều chuyển tới bản thân trong giới chỉ?"),t("br"),t("br"),n._v("Thật muốn như vậy, chẳng phải thoáng cái liền phát tài?"),t("br"),t("br"),n._v("Trong lòng tuy là nghĩ như vậy, nhưng Trương Huyền cũng biết, khẳng định không thể làm như vậy."),t("br"),t("br"),n._v("Bất cứ chuyện gì, đều hăng quá hoá dở, hắn là danh sư, cũng không phải ăn trộm, trực tiếp đem người ta nội tình chuyển không, khẳng định không thích hợp."),t("br"),t("br"),n._v("Tàng bảo khố trong phòng, đủ loại đặc thù ra vẻ san sát, phía trên bày đầy đủ loại vũ khí, bảo thạch, cùng linh thạch."),t("br"),t("br"),n._v("Có long cốt thần thương, những này tuyệt phẩm vũ khí các loại, cũng chướng mắt, tu vi đạt tới loại cảnh giới này, tuyệt phẩm linh thạch, hiệu quả cũng không lớn, cho nên, không có quá nhiều dừng lại."),t("br"),t("br"),n._v("“Ừm, đây là cái gì?”"),t("br"),t("br"),n._v("Đi tới đi tới, đột nhiên dừng bước, lông mày giương lên."),t("br"),t("br"),n._v("Cách đó không xa trên giá, một cái đóng chặt hộp ngọc, tản mát ra để hắn đều cảm thấy động tâm linh lực."),t("br"),t("br"),n._v("“Chỉ là thiên ngoại phi thạch, là tổ tiên trong lúc vô tình lấy được, cụ thể là cái gì cũng không rõ ràng, chỉ biết là không cách nào luyện hóa, cũng không có cách nào lấy ra linh khí trong đó, liền đặt ở nơi này...”"),t("br"),t("br"),n._v("Thấy hắn nhìn về phía cái này hộp ngọc, Lạc Càn Trinh cười giải thích."),t("br"),t("br"),n._v("“Thiên ngoại phi thạch?” Trương Huyền sửng sốt một chút, hai bước đi tới trước mặt, bàn tay duỗi ra, đem hộp ngọc cầm tới, từ từ mở ra."),t("br"),t("br"),n._v("“Cho ta, cho ta...”"),t("br"),t("br"),n._v("Còn chưa kịp quan sát đến cùng là cái gì, đột nhiên trong cơ thể một hồi kịch liệt lay động, một thanh âm gấp rút truyền tới."),t("br"),t("br"),n._v("“Ai?” Trương Huyền không khỏi kinh hãi."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1629"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1631"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);