(window.webpackJsonp=window.webpackJsonp||[]).push([[721],{1076:function(n,h,t){"use strict";t.r(h);var g=t(44),c=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1623-truy-lung-【-canh-thu-nhat-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1623-truy-lung-【-canh-thu-nhat-】"}},[n._v("#")]),n._v(" Chương 1623: Truy lùng 【 canh thứ nhất 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1623: Truy lùng "),t("br"),t("br"),n._v("Khôi Thánh tượng, Chiêm sư đám người khả năng cũng nhìn không ra có vấn đề gì, nhưng mà Trương Huyền biết rất rõ ràng."),t("br"),t("br"),n._v("Trong đó, lặng lẽ chứa đựng Ngoan Nhân xương ngực!"),t("br"),t("br"),n._v("Lúc trước, dùng thư viện, dò xét đến về sau, còn muốn lấy đem pho tượng này đạp nát, đem khung xương lấy ra, cuối cùng vẫn ngừng lại."),t("br"),t("br"),n._v("Dù sao hắn là danh sư, còn là điện chủ, có nguyên tắc của mình, không thể làm ra như vậy ly kinh phản đạo sự tình."),t("br"),t("br"),n._v("Giờ phút này, Dị Linh tộc nhân xông tới, cái gì đều không cướp, chỉ cướp thứ này, nhất định vì thế!"),t("br"),t("br"),n._v("Dù sao, Ngoan Nhân đã từng là Dị Linh tộc đỉnh phong nhất Hoàng giả một trong, cướp đoạt bộ xương này, có lẽ có thể có rất lớn tác dụng, thậm chí... Cũng giống như mình, để hắn phục sinh!"),t("br"),t("br"),n._v("“Ngoan Nhân?”"),t("br"),t("br"),n._v("Nghe được hắn tự lẩm bẩm, Chiêm sư sững sờ."),t("br"),t("br"),n._v("“A, không có gì!” Trương Huyền lông mày giương lên: “Hết thảy mấy người, đều thực lực gì?”"),t("br"),t("br"),n._v("“Có bốn người, thực lực cụ thể, ta cũng không nhìn ra, chỉ là... Thánh tử điện đại trận, đều không có ngăn lại, bị tuỳ tiện phá hư...” Chiêm sư mặt mũi xấu hổ."),t("br"),t("br"),n._v("Đường đường Thánh tử điện, đại lục đệ nhất học phủ, nửa cái tháng sau công phu, liên tục bị hai người phá tan chạy trốn, cái thứ nhất là bọn họ điện chủ, nói ra không mất mặt, nhưng bây giờ bị Dị Linh tộc nhân đoạt đồ vật, còn chạy trốn..."),t("br"),t("br"),n._v("Nói thật, đều không có dám lên báo danh sư đường tổng bộ!"),t("br"),t("br"),n._v("Gánh không nổi người này!"),t("br"),t("br"),n._v("“Bốn người?” Trương Huyền hít sâu một hơi, con mắt mê lên: “Hướng phương hướng nào chạy trốn?”"),t("br"),t("br"),n._v("“Hướng bên kia... Chúng ta tu vi quá thấp, đuổi một hồi không đuổi kịp, đành phải cho điện chủ đưa tin...” Tiện tay một chỉ, Chiêm sư nói."),t("br"),t("br"),n._v("“Ta đã biết, các ngươi lần nữa sửa chữa những này sụp đổ kiến trúc, khôi phục trận pháp!” Híp mắt lại, Trương Huyền nhìn về phía Chiêm sư mới vừa nói phương hướng, thân thể khẽ động, một bước hướng về phía trước bước ra ngoài: “Ta đi xem một chút, nhất định đem Khôi Thánh tượng đoạt về!”"),t("br"),t("br"),n._v("Khôi Thánh tượng không chỉ liên lụy đến Thánh tử điện tôn nghiêm, càng cùng Ngoan Nhân khung xương liên quan đến, cướp không trở lại, hắn người điện chủ này, làm cũng trên mặt tối tăm... Mặc dù còn không có tiến hành kế nhiệm đại điển."),t("br"),t("br"),n._v("Thi triển ra đối không gian lĩnh ngộ, tốc độ bay làm được cực nhanh, hơn mười hô hấp sau đó, cũng đã bay ra hơn vạn cây số."),t("br"),t("br"),n._v("Quay đầu nhìn lại, thấy Lạc Nhược Hi vẫn như cũ theo ở phía sau, thở phào nhẹ nhõm, Minh Lý Chi Nhãn bốn phía chiếu rọi, muốn tìm kiếm có hay không dấu vết."),t("br"),t("br"),n._v("“Như vậy tìm không thấy!” Thấy hắn động tác, Lạc Nhược Hi lắc đầu: “Bốn người này, hẳn là chúng ta tại Bạch Khê sơn gặp phải mấy cái, đều có Đại Thánh đỉnh phong tu vi, hơn nữa chuyên môn dùng phương pháp đặc thù xóa đi dấu vết, rất khó thẩm tra, trừ phi... Minh Lý Chi Nhãn đạt tới cảnh giới thứ tư!”"),t("br"),t("br"),n._v("“Quả thực điều tra không được...”"),t("br"),t("br"),n._v("Thật ra thì không cần đối phương nói, hắn cũng phát giác."),t("br"),t("br"),n._v("Lần này, cùng lần trước bắt Triệu Nhã đám người đồng dạng, không có bất kỳ cái gì dấu vết để lại lưu lại, cho dù là hắn, cũng dò xét không được."),t("br"),t("br"),n._v("“Bất quá, muốn chạy trốn, cũng không dễ dàng như vậy...”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, cổ tay khẽ đảo, một quyển sách xuất hiện ở trước mắt, tản mát ra Thiên đạo mùi vị."),t("br"),t("br"),n._v("Thiên đạo chi sách."),t("br"),t("br"),n._v("Triệu Nhã đám người bị bắt, tìm không thấy dấu vết, liền không cách nào thẩm tra, nhưng... Khôi Thánh tượng khác biệt!"),t("br"),t("br"),n._v("Cất giấu trong đó Ngoan Nhân khung xương, hắn thẩm tra không được, chỉ cần Ngoan Nhân có thể cảm ứng được phương hướng, đến nơi nhất định phạm vi, liền nhất định có thể tìm!"),t("br"),t("br"),n._v("Trừ phi... Đối phương đem khung xương dùng đặc thù không gian bí pháp ẩn giấu đi!"),t("br"),t("br"),n._v("Bất quá, mới vừa cướp đi, thời gian không dài, tình huống bình thường, hẳn là còn làm không được điểm ấy."),t("br"),t("br"),n._v("Thấy hắn lấy ra sách, Lạc Nhược Hi nhìn một cái, cũng không nói nhiều."),t("br"),t("br"),n._v("“Tiểu Ngoan Ngoan, chớ ngủ, cảm ứng thoáng cái, ngươi xương ngực ở đâu!”"),t("br"),t("br"),n._v("Vỗ một cái thư tịch, Trương Huyền nói."),t("br"),t("br"),n._v("“Ừm...”"),t("br"),t("br"),n._v("Nghe được xưng hô như vậy, Ngoan Nhân co quắp thoáng cái, cuối cùng vẫn là nhẹ gật đầu, cảm ứng phút chốc, thư tịch lập tức chỉ hướng một cái phương hướng: “Ở bên kia!”"),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Thấy nó quả nhiên có thể cảm ứng được, Trương Huyền thở phào nhẹ nhõm, dựa theo đối phương chỉ thị phương hướng, nhanh chóng tiến lên."),t("br"),t("br"),n._v("Lạc Nhược Hi cũng không nói chuyện, theo sát ở phía sau."),t("br"),t("br"),n._v("Phi hành đại khái hơn mười vạn cây số, đi hơn một canh giờ, Ngoan Nhân mở miệng: “Ngay ở phía trước cái thành phố kia bên trong... Ừm, nguy rồi, cảm ứng biến mất...”"),t("br"),n._v("Trương Huyền vội vàng nhìn về phía trước, quả nhiên thấy nơi xa một tòa thành trì đứng sừng sững."),t("br"),t("br"),n._v("Thành thị cực kỳ bao la, so với lúc trước liên minh đế quốc, đều không kém chút nào, thậm chí còn hơn."),t("br"),t("br"),n._v("“Cảm ứng biến mất? Có ý tứ gì?”"),t("br"),t("br"),n._v("Trương Huyền cau mày."),t("br"),t("br"),n._v("Coi như đi vào thành trì, lấy ngươi Cổ Thánh cảnh giới năng lực, cũng không có khả năng không cảm ứng được đi! Làm sao lại hư không tiêu thất?"),t("br"),t("br"),n._v("“Hẳn là bị một loại nào đó trận pháp che đậy, hoặc là giấu rồi, mới vừa rồi còn có thể cảm ứng được, đột nhiên liền biến mất...”"),t("br"),t("br"),n._v("Ngoan Nhân nói."),t("br"),t("br"),n._v("“Trận pháp che đậy?” Trương Huyền vẻ mặt khó coi."),t("br"),t("br"),n._v("Trước đó còn muốn lấy, có thể thừa dịp không có giấu đi phía trước, đem hắn đuổi theo, làm sao cũng không ngờ tới, đối phương nhanh như vậy."),t("br"),t("br"),n._v("Đã đuổi rất nhanh, còn là chậm một bước."),t("br"),t("br"),n._v("Thật muốn bị che đậy, như thế lớn thành thị, thật đúng là khó tìm."),t("br"),t("br"),n._v("Liền giống như lúc trước đặt ở Thánh tử trong điện viện, không thể nhận ra cảm giác đồng dạng."),t("br"),t("br"),n._v("“Ngươi còn có thể nhớ, vừa rồi biến mất vị trí cụ thể ư?”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("“Cũng chỉ có thể phỏng đoán cái đại khái phạm vi...” Ngoan Nhân chần chờ một chút."),t("br"),t("br"),n._v("“Dẫn đường!”"),t("br"),t("br"),n._v("Lười nói nhảm, Trương Huyền nói."),t("br"),t("br"),n._v("Khung xương lần này thật muốn bị mang đi, Thánh tử điện rất mất thể diện không có gì, hắn bên này Ngoan Nhân, còn muốn tăng tiến, liền khó khăn."),t("br"),t("br"),n._v("Tuy là gần nhất Ngoan Nhân không góp sức, nhưng cũng là hắn một chỗ dựa lớn."),t("br"),t("br"),n._v("Dung hợp trái tim, xương ngón tay cùng đầu, liền nắm giữ Thánh vực bát, cửu trọng thực lực, một khi dung hợp cả phó hoàn chỉnh xương ngực, còn không trực tiếp tấn cấp Đại Thánh?"),t("br"),t("br"),n._v("Làm xong, Cổ Thánh cũng có thể!"),t("br"),t("br"),n._v("Bên cạnh nắm giữ một cái Cổ Thánh cấp bậc tay chân, là ai, cũng sẽ không từ bỏ."),t("br"),t("br"),n._v("Ngoan Nhân phía trước dẫn đường, phi hành thời gian không dài, đi tới trong thành thị ở giữa vị trí."),t("br"),t("br"),n._v("“Đại khái ngay tại cái phạm vi này biến mất, có điều, ta cũng không cách nào xác định...” Ngoan Nhân có chút hậm hực."),t("br"),t("br"),n._v("Năm đó tung hoành chư thiên siêu cấp cường giả, hiện tại phân tán ngay cả mình xương cốt cũng không tìm tới, nói ra cũng đủ mất mặt."),t("br"),t("br"),n._v("“Ngươi cái này đại khái phạm vi, có thể chính xác đến bao nhiêu?” Trương Huyền nhíu nhíu mày."),t("br"),t("br"),n._v("Như thế đại nhất tòa thành thị, phủ đệ đếm mãi không hết, từng cái dò xét, mệt chết tại đây cũng vô dụng, nếu như có thể chính xác một điểm, có lẽ có thể dễ dàng không ít."),t("br"),t("br"),n._v("“Cái này... Đại khái phạm vi một trăm cây số...”"),t("br"),t("br"),n._v("Ngoan Nhân xấu hổ."),t("br"),t("br"),n._v("“Một trăm cây số?” Trương Huyền vụt qua, thiếu chút nữa ngất đi."),t("br"),t("br"),n._v("Phạm vi một trăm cây số, nhân khẩu tối thiểu hơn ngàn vạn, tìm một cái tượng, hơn nữa bị ẩn núp, thần thức dò xét không được... Có thể tìm được, mới kêu gặp quỷ!"),t("br"),t("br"),n._v("“Nó là đột nhiên biến mất, nếu là chúng ta lại nhanh chút, có lẽ có thể cảm ứng ra cái gì...”"),t("br"),t("br"),n._v("Biết độ khó, Ngoan Nhân nhỏ giọng giải thích."),t("br"),t("br"),n._v("“Ta đã biết...”"),t("br"),t("br"),n._v("Biết đối phương cũng muốn để cho mình dung hợp, trở nên càng thêm cường đại, huống chi, cái này xương ngực, bị Cổ Thánh tượng mượn nhờ Thánh tử điện lực lượng áp chế, sẽ không có sinh ra ý niệm, rất dễ dàng luyện hóa, hiện tại biến mất không thấy gì nữa, hẳn là so với chính mình càng lo lắng, cũng liền không có quá nhiều nói, mà là ngẩng đầu hướng bốn phía nhìn sang."),t("br"),t("br"),n._v("Dòng người như thoi đưa, bởi vì mỗi ngày đều có không ít cường giả bay tới bay lui, Trương Huyền cùng Lạc Nhược Hi, không có phóng thích lực lượng, người khác nhìn không ra tu vi, cũng không có gây nên quá nhiều kinh ngạc cùng chú ý."),t("br"),t("br"),n._v("Nếu không, lấy thực lực của bọn hắn khẳng định toàn bộ thành thị đều sôi trào."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1634"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1636"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);