(window.webpackJsonp=window.webpackJsonp||[]).push([[727],{1083:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1628-ton-cuong-banh-truong-【-canh-thu-nhat-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1628-ton-cuong-banh-truong-【-canh-thu-nhat-】"}},[n._v("#")]),n._v(" Chương 1628: Tôn Cường bành trướng 【 canh thứ nhất 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1628: Tôn Cường bành trướng "),t("br"),t("br"),n._v("“Bảo tàng?”"),t("br"),t("br"),n._v("Trương Huyền lúc này mới kịp phản ứng."),t("br"),t("br"),n._v("Lần này không chỉ đem Ngoan Nhân xương ngực, trực tiếp dung hợp, còn đánh chết một đầu Cổ Thánh."),t("br"),t("br"),n._v("Loại cường giả cấp bậc này, cho dù chết, cũng là bảo tàng khổng lồ, vô số người thèm nhỏ dãi không dứt."),t("br"),t("br"),n._v("“Nếu như có thể luyện chế thành Vô Hồn Kim Nhân, liền phát đạt...”"),t("br"),t("br"),n._v("Hai mắt sáng lên."),t("br"),t("br"),n._v("Hắn hoàn chỉnh tiếp nhận vu hồn sư truyền thừa, biết Vô Hồn Kim Nhân phương pháp luyện chế, nếu là lấy đầu này thi thể, vì nguyên vật liệu, chẳng phải chứng tỏ, chỉ cần vu hồn ly thể tiến vào bên trong, liền nắm giữ Cổ Thánh cấp bậc sức chiến đấu?"),t("br"),t("br"),n._v("Nếu thực như thế, tung hoành thiên địa, lại có gì sợ hãi?"),t("br"),t("br"),n._v("Vừa đem Khổng sư chi huyết cùng Thiên đạo chi sách tiêu hao sạch sẽ, đang cảm thấy nhức nhối không dứt, nhưng nếu có thể đem thứ này luyện chế thành Kim Thân, tất cả liền đều đáng giá."),t("br"),t("br"),n._v("“Cái này đích xác là một đại bảo tàng, ta lấy đi!”"),t("br"),t("br"),n._v("Thấy Lạc Nhược Hi cùng Ngột Thần cũng không có ý xuất thủ, Trương Huyền nhẹ gật đầu, bàn tay lăng không một trảo, tinh thần khẽ động, trước đem hắn thu vào trữ vật giới chỉ, lại phát hiện, giống như là đang di động một tòa núi lớn, tinh thần gần như hao hết, thi thể lại đều không nhúc nhích."),t("br"),t("br"),n._v("“Chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Khắp khuôn mặt là khó tin."),t("br"),t("br"),n._v("Tinh thần lực của hắn, tuy nói không mạnh, nhưng cũng đạt đến Thánh vực cửu trọng đỉnh phong, hơn nữa đối với không gian hiểu... Thậm chí ngay cả một đầu thi thể, không thu vào nhẫn, khó tránh khỏi có chút quá khó mà tin nổi đi!"),t("br"),t("br"),n._v("“Thử một chút lực lượng!”"),t("br"),t("br"),n._v("Chân khí tuôn ra, lập tức đem trước mắt thi thể bọc, bỗng nhiên nhắc tới, nhất thời cảm thấy trên đầu mồ hôi phả ra."),t("br"),t("br"),n._v("“Cái này, cái này...” Trương Huyền con mắt trợn tròn."),t("br"),t("br"),n._v("Hắn gần như hao hết toàn thân chân khí, vậy mà không có đem thi thể cầm lên!"),t("br"),t("br"),n._v("Phải biết, hắn hiện tại Khổng sư chi huyết lực lượng còn không có hao hết, nói cách khác... Còn nắm giữ Cổ Thánh phía dưới lực lượng vô địch!"),t("br"),t("br"),n._v("“Đại Thánh nhị trọng Kim Thân cảnh, thân thể đi qua rèn luyện, liền sẽ thay đổi nặng nề vô cùng, trở thành Cổ Thánh, càng thêm cường đại, đừng nói chém giết, không đạt tới cùng cấp bậc, liền thi thể, đều phá hư không đi!”"),t("br"),t("br"),n._v("Thấy hắn cái bộ dáng này, Lạc Nhược Hi cười cười."),t("br"),t("br"),n._v("“Cái kia... Chẳng phải chứng tỏ, muốn nhận đến không gian trữ vật đều làm không được?”"),t("br"),t("br"),n._v("Trương Huyền mặt mũi buồn bực."),t("br"),t("br"),n._v("Mới vừa còn muốn lấy, đem cái này thi thể luyện chế thành Vô Hồn Kim Nhân, để hắn nắm giữ Cổ Thánh sức chiến đấu, nhưng bây giờ, liền thi thể đều không cầm lên được, đừng nói khống chế, động cũng không có cách nào động ah!"),t("br"),t("br"),n._v("“Dưới tình huống bình thường, là hết cách rồi, nhưng ngươi long cốt thần thương, từng là Cổ Thánh thần binh, mặc dù bây giờ bị phong ấn lực lượng, có thể... Để hắn mang vào trữ vật giới chỉ, vẫn là rất đơn giản!”"),t("br"),t("br"),n._v("Lạc Nhược Hi nói."),t("br"),t("br"),n._v("“Đúng a!” Trương Huyền vỗ trán một cái, lúc này mới kịp phản ứng."),t("br"),t("br"),n._v("Làm sao quên chuyện này."),t("br"),t("br"),n._v("Lực lượng của hắn là không đủ, nhưng long cốt thần thương mạnh mẽ ah!"),t("br"),t("br"),n._v("Tùy tiện vung ra đáy nước, đều có thể đánh giết Thánh vực cửu trọng đỉnh phong, lực lượng mạnh mẽ, có thể xưng kinh khủng."),t("br"),t("br"),n._v("Bàn tay hướng về phía trước, vỗ một cái trường thương, đen kịt thân thương, lập tức hóa làm một hàng dài, đem trên mặt đất Cổ Thánh thi thể cuốn lại."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Sau một khắc, đã xuất hiện tại trữ vật giới chỉ."),t("br"),t("br"),n._v("“Quả nhiên có thể!”"),t("br"),t("br"),n._v("Thấy long cốt thần thương quả nhiên làm được, Trương Huyền thở phào nhẹ nhõm, lần nữa nhìn về phía Tề Chân đám người, vẻ mặt âm trầm."),t("br"),t("br"),n._v("Đường đường thiên cơ sư, vậy mà đầu nhập vào Dị Linh tộc nhân, quả thực tội không thể tha."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Tinh thần khẽ động, mấy đạo lôi điện, đồng thời bổ xuống, Tề Chân đám người lập tức bị nhốt trong đó, thống khổ không ngừng kêu thảm."),t("br"),t("br"),n._v("Bàn tay nhẹ nhàng vạch một cái, đại điện mai rùa, bị lôi điện đánh cho vỡ vụn, toàn bộ thiên cơ sư công hội nhất thời lâm vào thiên kiếp trừng phạt bên trong."),t("br"),n._v("Thiên cơ sư, vốn là chiếm lấy thiên cơ, bị Thiên đạo kiêng kị, lúc này lại còn dám đầu nhập vào Dị Linh tộc nhân, có thể nói, quả thực tự tìm cái chết."),t("br"),t("br"),n._v("Cảm giác dẫn tới lôi điện, hủy diệt công hội, tư duy lần nữa rõ ràng không ít, thư viện hình như có chỗ tiến bộ, Trương Huyền lúc này mới thoả mãn nhẹ gật đầu, lấy ra đưa tin ngọc bài, báo tin Chiêm sư đám người tới."),t("br"),t("br"),n._v("Thời gian không dài, Chiêm sư, Cát sư đám người liền bay tới."),t("br"),t("br"),n._v("“Mấy cái này thiên cơ sư liền giao cho các ngươi, thông đồng Dị Linh tộc nhân, trộm lấy Khôi Thánh tượng, đúng là bọn họ làm, nghĩ biện pháp thẩm vấn ra mục đích...”"),t("br"),t("br"),n._v("Nói xong khoát tay áo."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Tràn đầy nghi ngờ, Chiêm sư đám người cuối cùng vẫn nhẹ gật đầu."),t("br"),t("br"),n._v("“Chúng ta đi thôi!”"),t("br"),t("br"),n._v("Trộm lấy tượng thủ phạm tuy là không có bắt được, nhưng làm rõ những ngày này phi công mục đích, nhất định có thể tìm hiểu nguồn gốc tra ra Dị Linh tộc nhân nội tình cùng mục đích, loại sự tình này, giao cho Thánh tử điện xử lý, so với hắn xử lý càng tốt hơn, cũng sẽ không cần quan tâm."),t("br"),t("br"),n._v("Hiện tại việc cấp bách, là mau chóng trở lại Trương gia, nghĩ biện pháp hóa giải Trương, Lạc hai nhà mâu thuẫn, ứng đối lại sắp tới nguy cơ."),t("br"),t("br"),n._v("Dị Linh tộc nhân liền Cổ Thánh đều xuất hiện, hắn cũng không cho rằng, Trương gia loại này chỉ còn lại có một vị Cổ Thánh gia tộc, đại loạn bên trong có thể may mắn thoát khỏi."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Rời đi thiên cơ sư công hội, mượn nhờ Ngột Thần mở ra thông đạo, thời gian không dài trở lại Trương gia."),t("br"),t("br"),n._v("“Huyền nhi, ngươi trở về...”"),t("br"),t("br"),n._v("Thấy hắn xuất hiện, Hưng kiếm thánh đám người tới đón."),t("br"),t("br"),n._v("“Vị này là Nhược Hi cô nương a, lớn lên thật là xinh đẹp, đến ta bên này...”"),t("br"),t("br"),n._v("Nhìn thấy nhi tử mang theo cái nữ hài tới, Mộng kiếm thánh hưng phấn mà con mắt đều sắp lóe ra hoa tới."),t("br"),t("br"),n._v("Nhìn thấy đối phương nhiệt tình như vậy, Lạc Nhược Hi hơi đỏ mặt, muốn nói chuyện lại không biết nên nói cái gì, đành phải nhìn về phía Trương Huyền, hướng hắn cầu cứu."),t("br"),t("br"),n._v("Trương Huyền làm bộ không nhìn thấy."),t("br"),t("br"),n._v("“Vị này là...” Hưng kiếm thánh một chỉ Ngột Thần."),t("br"),t("br"),n._v("Tại Lạc gia, bọn họ thế nhưng là tận mắt chứng kiến qua vị này đồng tử mạnh mẽ, loại này cường giả, cũng muốn nhận thức một chút."),t("br"),t("br"),n._v("“Hắn kêu Ngột Thần, là Nhược Hi đồng tử...”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Vừa mới giới thiệu xong, Hưng kiếm thánh cũng không kịp trả lời, liền nghe đến một bên một cái tiếng hừ vang lên: “Đồng tử, vậy cũng là người làm? Ừm, về sau các ngươi tiểu thư cùng nhà ta thiếu gia thành thân, nghe ta điều khiển là được!”"),t("br"),t("br"),n._v("Quay đầu nhìn lại, chỉ thấy Tôn Cường chẳng biết lúc nào đi tới, vỗ đồng tử bả vai, một mặt thoả mãn."),t("br"),t("br"),n._v("Liên minh đế quốc, Tôn Cường bị nhận về tới về sau, để hắn theo Trịnh Dương tu luyện, Triệu Nhã đám người xảy ra chuyện về sau, lý do an toàn, Trịnh Dương, Vương Dĩnh mấy người cũng đi tới Trương gia, Tôn Cường tự nhiên cũng đi theo tới."),t("br"),t("br"),n._v("Người quản gia này, biết nhà mình thiếu gia, hiện tại là Trương gia tộc trưởng, càng là Thánh tử điện điện chủ về sau, trước kia hùng tâm lần nữa khôi phục, nhất cử nhất động, đều tràn đầy nồng đậm tự tin."),t("br"),t("br"),n._v("Cho người ta một loại đại gia tộc quản gia cái kia có phong phạm."),t("br"),t("br"),n._v("“Nghe ngươi điều khiển?” Ngột Thần một đầu sao."),t("br"),t("br"),n._v("“Không sai, làm sao, còn không nguyện ý? Nói cho ngươi, ta là thiếu gia duy nhất quản gia, dám không nghe lời nói, giáo huấn ngươi một trận, chắc hẳn, cũng không ai dám nói thêm cái gì...”"),t("br"),t("br"),n._v("Thấy hắn mất hứng, Tôn Cường nhướng mày, hướng về phía đầu của đối phương vỗ một cái, khẽ nói."),t("br"),t("br"),n._v("Một cái đồng tử mà thôi, gặp phải ta quản gia, còn không ngoan ngoãn nghe lời, dám cau mày, tin hay không, mạnh mẽ đánh bên trên ngừng lại, lập thoáng cái quy củ?"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Không nghĩ tới nhi tử cái này quản gia, như vậy không đáng tin cậy, Hưng kiếm thánh, Mộng kiếm thánh đám người, tất cả đều khóe miệng giật một cái, thiếu chút nữa ngất đi."),t("br"),t("br"),n._v("Cái này đồng tử, xông vào Lạc gia, vô số trưởng lão cũng không là đối thủ, ngươi một cái vừa mới đến Thánh vực gia hỏa... Dám xuống tay đánh người... Là ngại chết chậm ư?"),t("br"),t("br"),n._v("“...” Trương Huyền cũng che cái trán, hận không thể có một cái lỗ chui vào."),t("br"),t("br"),n._v("Cái này hai hàng... Trước mấy ngày còn bị đả kích dục tiên dục tử, làm sao mấy ngày nay lại bắt đầu bành trướng?"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1639"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1641"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);