(window.webpackJsonp=window.webpackJsonp||[]).push([[729],{1084:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1630-đam-đai-chan-thanh"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1630-đam-đai-chan-thanh"}},[n._v("#")]),n._v(" Chương 1630: Đạm Đài Chấn Thanh")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1630: Đạm Đài Chấn Thanh "),t("br"),t("br"),n._v("“Tại hạ Đạm Đài Chấn Thanh, là Tử Vũ Cổ Thánh hậu nhân, gặp qua Trương gia gia chủ, chư vị!”"),t("br"),t("br"),n._v("Khuôn mặt xấu xí trung niên nhân đứng dậy, khom người đến cùng, lễ tiết mười phần, để cho người ta tìm không ra một chút tật xấu, mấu chốt nhất là, rõ ràng tới khiêu chiến, bất kể phong thái vẫn là cử chỉ, đều cho người ta một loại không cách nào tức giận cảm giác."),t("br"),t("br"),n._v("“Đây mới thật sự là Đại Nho...”"),t("br"),t("br"),n._v("Trương Huyền thầm than."),t("br"),t("br"),n._v("Trước đó, gặp qua không ít danh sư, cường giả, có khí độ cũng không ít, nhưng cùng trước mắt vị này so, rõ ràng đều kém một đoạn dài."),t("br"),t("br"),n._v("Vừa nhìn liền biết là cái chân chính nho học chi sĩ."),t("br"),t("br"),n._v("“Đạm Đài tiền bối khách khí...” Đứng dậy đáp lễ."),t("br"),t("br"),n._v("“Chúng ta mục đích đến, Nguyên Phong huynh, chắc hẳn đã nói rõ, Khổng sư lưu lại, chúng ta danh sư, khi tranh, không cho, cho nên, ta cũng sẽ không lùi bước! Đằng sau ta mấy vị, là ta thân truyền, theo sau lưng, học tập không ít thời gian, khiêu chiến quý gia tộc đối thời gian lĩnh ngộ... Hôm nay tỷ thí, bất kể thắng thua, cái này viên tiên tổ từng đã dùng qua nghiên mực, đều đưa tặng cho Trương gia, lấy đó áy náy.”"),t("br"),t("br"),n._v("Đạm Đài Chấn Thanh cổ tay khẽ đảo, một cái đen kịt nghiên mực lơ lửng giữa không trung."),t("br"),t("br"),n._v("“Tử Vũ Cổ Thánh đồ vật?”"),t("br"),t("br"),n._v("Hưng kiếm thánh cùng Trương gia tộc người, tất cả đều chấn động."),t("br"),t("br"),n._v("Nghiên mực không lớn, lại cho người ta một loại nghiền ép chư thiên cảm giác, hình như do hắn làm kinh sợ, hỗn loạn thời không có thể có thể ổn định."),t("br"),t("br"),n._v("“Đây là... Chân chính Cổ Thánh chí bảo!”"),t("br"),t("br"),n._v("Trương Huyền lông mày giương lên."),t("br"),t("br"),n._v("Lạc gia, Nam Cung Nguyên Phong lấy ra trường kiếm, chỉ là Tử Dung Cổ Thánh đã dùng qua vũ khí, cũng không phải là đỉnh phong tất cả, tuy có giá trị, nhưng cũng không tính quá lớn, nhưng trước mắt cái này nghiên mực, nhưng là Tử Vũ Cổ Thánh, từ đầu đến cuối đều dùng dùng."),t("br"),t("br"),n._v("“Bất quá, cùng long cốt thần thương so, vẫn là kém một đoạn dài...”"),t("br"),t("br"),n._v("Không nói trước, Nhiễm Cầu Cổ Thánh bản thân thực lực liền thắng được Tử Vũ Cổ Thánh, chỉ nói long cốt thần thương là chiến đấu vũ khí, nghiên mực là văn phòng tứ bảo, cả hai liền không tại cùng một cái khái niệm bên trên."),t("br"),t("br"),n._v("Nhưng dù cho như thế, nếu như có thể nhận được, lưu tại gia tộc, cũng tuyệt đối là trấn áp số mệnh chí bảo."),t("br"),t("br"),n._v("“Xem tỷ thí lần này, không thể tránh được!”"),t("br"),t("br"),n._v("Đem ánh mắt từ trên nghiên mực thu hồi lại, Trương Huyền ôm quyền."),t("br"),t("br"),n._v("“Đúng!” Đạm Đài Chấn Thanh gật đầu: “Mong được tha thứ!”"),t("br"),t("br"),n._v("“Khổng miếu, là chúng ta danh sư, cả đời muốn đi chỗ, khách khí nhiều hơn nữa, cũng tránh không được tranh đấu sự thật, đã như vậy, so liền so đi!” Nhẹ nhàng cười một tiếng, Trương Huyền khoát tay chặn lại, đứng dậy: “Không biết làm sao làm sao tỷ thí? Còn xin nói quy tắc đi!”"),t("br"),t("br"),n._v("Đối phương tới này mục đích đúng là truyền thế thiên phù, đã như vậy, nói nhiều hơn nữa, đều không có bất cứ chỗ ích lợi nào, còn không bằng trực tiếp bắt đầu được rồi."),t("br"),t("br"),n._v("“Trương tộc trưởng nếu như tin tưởng tại hạ, ta liền nói một chút quy tắc của ta!” Đạm Đài Chấn Thanh gật đầu: “Chúng ta Chư Tử bách gia, tiến hành loại tỷ đấu này, chia làm đấu văn cùng đấu võ, đấu văn không thương tổn hòa khí, đấu võ mỗi người dựa vào bản lĩnh.”"),t("br"),t("br"),n._v("“Đấu văn là sao? Đấu võ là sao?”"),t("br"),t("br"),n._v("“Rất đơn giản, ta chỗ này có một mặt thời không kính, chiếu xạ nguyên thần, có thể để người ta ý niệm tiến vào bên trong, trong đó có chuyên môn bố trí thời gian trận pháp cùng cạm bẫy... Đây là đấu văn.”"),t("br"),t("br"),n._v("Một cái kim sắc gương xuất hiện ở trước mặt mọi người."),t("br"),t("br"),n._v("Cái này gương, không phải một cái mặt phẳng, mà là rất nhiều góc cạnh, mỗi cái mặt kính, lẫn nhau phản xạ, để cho người ta nhìn một cái lít nha lít nhít, có chút chóng mặt."),t("br"),t("br"),n._v("“Đấu võ rất đơn giản, mọi người áp chế đến tương đồng thực lực, mỗi người dựa vào thủ đoạn, thực lực mạnh, thủ đoạn nhiều người, chiến thắng!”"),t("br"),t("br"),n._v("Giải thích xong, Đạm Đài Chấn Thanh không nói thêm gì nữa, mà là mặt mũi mỉm cười nhìn qua, chờ đợi Trương gia mọi người làm ra quyết định."),t("br"),t("br"),n._v("“Thời không cảnh, chúng ta Trương gia con cháu, chưa hề tiếp xúc qua, cũng không biết trong đó cơ quan cùng bí ẩn, tiến hành tỷ thí, nhất định là phải ăn thiệt thòi! Còn đấu võ, Chư Tử bách gia, đủ loại võ kỹ, liên tiếp xuất hiện, cùng cấp bậc muốn chiến thắng, cũng không dễ dàng như vậy!”"),t("br"),t("br"),n._v("Một vị trưởng lão nhịn không được nói."),t("br"),t("br"),n._v("“Đúng vậy a, bọn họ đây là lấy thế lẫn nhau áp!”"),t("br"),t("br"),n._v("“Không có cách nào sự tình, Trương gia tuy là không yếu, nhưng cùng Chư Tử bách gia so, vẫn là kém quá xa, không thể so sánh nổi! Ngày hôm nay bọn họ tới đây, thái độ rất rõ ràng, so cũng muốn so, không thể so với cũng muốn so!”"),t("br"),t("br"),n._v("“Vậy chúng ta làm sao bây giờ?”"),t("br"),n._v(" Bên trong căn phòng tất cả trưởng lão từng cái vẻ mặt, cũng không quá đẹp mắt."),t("br"),t("br"),n._v("Đấu văn, nghe không thương tổn hòa khí, nhưng thời không cảnh, cụ thể là cái gì, không ai biết, đối phương đệ tử một khi quen thuộc, coi như đối thời gian lĩnh ngộ tương đồng, cũng không có khả năng chiến thắng."),t("br"),t("br"),n._v("Liền giống như, tỷ thí thi chạy, đối đường đua chưa quen thuộc, tương đồng thực lực, cũng khó có thể thắng được đồng dạng."),t("br"),t("br"),n._v("Còn đấu võ, thoạt nhìn công bằng, có thể Chư Tử bách gia, truyền thừa tại Khổng sư, đủ loại kỹ xảo, bí pháp, nhiều vô số kể, Trương gia cho dù tiền bối thiên phú tuyệt cao, lại có ai có thể so sánh qua được Khổng sư, hơn được Chư Tử?"),t("br"),t("br"),n._v("Có thể nói, bất kể là loại nào giao đấu, đều không chiếm ưu thế."),t("br"),t("br"),n._v("“Đấu văn liền thôi, trực tiếp đấu võ đi! Tốc độ nhanh một chút, miễn cho tổn thương hòa khí!” Không để ý tới mọi người lo lắng, Trương Huyền thản nhiên nói."),t("br"),t("br"),n._v("“Đấu võ? Cái kia tốt!”"),t("br"),t("br"),n._v("Gật đầu cười, Đạm Đài Chấn Thanh vẫy tay một cái, sau lưng đi ra một thanh niên: “Vị này Đạm Thai Kiếm Khuê, là ta học sinh thân truyền, năm nay ba mươi, đối thời gian pháp tắc, lĩnh ngộ một chút da lông, có thể muốn bêu xấu!”"),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Tiếng nói kết thúc, thanh niên đi đến trước mặt mọi người, người không động, khí tức trong người, bay thẳng mà lên, cho người ta một loại kịch liệt áp bức."),t("br"),t("br"),n._v("“Đại Thánh nhất trọng, Kiến Thần Bất Hoại cảnh!”"),t("br"),t("br"),n._v("Hưng kiếm thánh con mắt lập tức nheo lại."),t("br"),t("br"),n._v("Mới ba mươi tuổi liền đạt tới Đại Thánh cấp bậc, vị này Đạm Thai Kiếm Khuê, thiên phú mạnh mẽ, Trương gia loại trừ hắn vị này mới vừa tìm trở về nhi tử, không người có thể so sánh!"),t("br"),t("br"),n._v("Thậm chí liền xem như chính hắn, đạt tới Đại Thánh cấp bậc, cũng vượt xa ở độ tuổi này."),t("br"),t("br"),n._v("“Đạm Thai Kiếm Khuê, còn xin Trương gia cao thủ chỉ giáo!”"),t("br"),t("br"),n._v("Hai tay ôm quyền, thanh niên hành lễ."),t("br"),t("br"),n._v("“Huyền nhi, thực lực của ngươi tuy là đã đạt tới Thánh vực cửu trọng đỉnh phong, nhưng chưa từng tới Trương gia, không có hệ thống học qua Trương gia đối thời gian pháp tắc lĩnh ngộ... Thực sự không cách nào thắng được, có thể sử dụng đặc thù bí pháp, chính là lần trước cùng ta chiến đấu, thực lực tăng lên tới Bất Hủ cảnh cái kia...”"),t("br"),t("br"),n._v("Thấy nhi tử nhúc nhích một chút, Hưng kiếm thánh vội vàng truyền âm."),t("br"),t("br"),n._v("Trong tộc tuổi tác thấp hơn trăm tuổi, hơn nữa thiên phú cao nhất, chỉ có Trương Huyền."),t("br"),t("br"),n._v("Nếu như hắn sử dụng lần trước tới giao đấu lúc dùng ra bí pháp, trong thời gian ngắn chiến lực vô địch, thắng được đối phương, dễ như trở bàn tay."),t("br"),t("br"),n._v("“Cái loại này bí pháp... Lại không cách nào sử dụng...” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Đó là mượn nhờ Khổng sư chi huyết."),t("br"),t("br"),n._v("Mà Khổng sư chi huyết chỉ có ba giọt, hiện tại đã toàn bộ tiêu hao sạch sẽ, coi như muốn dùng, cũng dùng không được."),t("br"),t("br"),n._v("“Không cách nào sử dụng?”"),t("br"),t("br"),n._v("Hưng kiếm thánh lông mày nhảy một cái: “Cái kia...”"),t("br"),t("br"),n._v("Không còn loại bí pháp này, lấy Thánh vực cửu trọng đỉnh phong, muốn thắng được Kiến Thần Bất Hoại cường giả, dù là đối phương áp chế tu vi... Cũng không dễ dàng như vậy."),t("br"),t("br"),n._v("“Không cần lo lắng, ta lại không nói ta muốn tỷ thí...”"),t("br"),t("br"),n._v("Trương Huyền mỉm cười."),t("br"),t("br"),n._v("“Không phải ngươi tỷ thí?”"),t("br"),t("br"),n._v("Hưng kiếm thánh sững sờ, mọi người khác cũng đồng loạt nhìn lại, tràn đầy nghi ngờ."),t("br"),t("br"),n._v("Ngươi không thể so với, ai so?"),t("br"),t("br"),n._v("“Dĩ nhiên không phải ta, loại này tỷ thí, ta thân là tộc trưởng, ra tay khó tránh khỏi lấy lớn hiếp nhỏ...”"),t("br"),t("br"),n._v("Trương Huyền khóe miệng nâng lên: “Vẫn là đem cơ hội nhường cho người trẻ tuổi... Để cho ta đồ nhi, Trương Cửu Tiêu tới đi!”"),t("br"),t("br"),n._v("(Ngày hôm nay Canh [3], 500 tấm nguyệt phiếu tăng thêm một chương, mọi người có phiếu, mặc dù đưa tới, thổ hào không nên đánh thưởng, mời phát nguyệt phiếu hồng bao. Lão nhai thật rất muốn thứ nhất, cám ơn! Còn tháng này minh chủ, lão nhai tháng sau tăng thêm. Tháng này bạo phát, chỉ vì nguyệt phiếu!)"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1641"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1643"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);