(window.webpackJsonp=window.webpackJsonp||[]).push([[747],{1102:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1647-vu-hon-bi-mat-ha"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1647-vu-hon-bi-mat-ha"}},[n._v("#")]),n._v(" Chương 1647: Vu hồn bí mật (hạ)")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1647: Vu hồn bí mật (hạ)"),t("br"),t("br"),n._v("“Vu hồn sư công hội hội trưởng hồn ấn?” Trương Huyền đồng tử co rụt lại."),t("br"),t("br"),n._v("Khó trách thứ này, nặng nề như vậy, không lĩnh ngộ linh hồn chân giải, liền vu hồn đều cầm không đi, không nghĩ tới có này lai lịch."),t("br"),t("br"),n._v("“Rốt cuộc chuyện gì xảy ra? Tưởng gia, làm sao nhận được vu hồn sư truyền thừa, như thế nào lại phụng khống chế hồn ấn người làm chủ? Vu hồn sư không phải phản bội nhân tộc, đã sớm bị Danh Sư đường diệt sát ư?”"),t("br"),t("br"),n._v("Trương Huyền tràn đầy hồ đồ."),t("br"),t("br"),n._v("Là thật không hiểu."),t("br"),t("br"),n._v("Tưởng gia, làm một trong tam đại gia tộc, Danh Sư đường nghiêm khắc giám sát xuống, có phải hay không vu hồn sư, nhất định có thể tuỳ tiện nhìn ra, chỉ là am hiểu linh hồn bí pháp, cùng vu hồn hoàn toàn là hai loại khái niệm, sao có thể là người sau truyền nhân?"),t("br"),t("br"),n._v("“Chuyện này, thuộc về Tưởng gia cơ mật cốt lõi nhất, nơi này không tiện đàm phán, không biết Trương sư, có thể hay không cùng chúng ta về đại điện?”"),t("br"),t("br"),n._v("Chần chờ một chút, Tưỏng Phương Du nói."),t("br"),t("br"),n._v("Tuy là có hộ tộc đại trận cách xa nhau, ngoại nhân không cách nào dò xét, nhưng muốn nói sự tình, thực sự quá mức cơ mật, tất cả vẫn là phải cẩn thận."),t("br"),t("br"),n._v("“Ừm!” Trương Huyền gật gật đầu."),t("br"),t("br"),n._v("“Mời tới bên này!” Tưỏng Phương Du thở phào nhẹ nhõm, đi đầu bay tới đằng trước, các trưởng lão khác theo sát phía sau."),t("br"),t("br"),n._v("Trương Huyền cũng không chậm trễ, thời gian không dài, đi tới một cái rộng lớn đại điện."),t("br"),t("br"),n._v("Cầm trong tay đối phương gia chủ lệnh, khống chế hộ tông đại trận, cũng không lo lắng xuất hiện chuyện ngoài ý muốn."),t("br"),t("br"),n._v("Đi vào đại điện, Trương Huyền ngồi tại chủ vị, những người khác lúc này mới thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Vận chuyển trận pháp, đem bốn phía toàn bộ phong ấn, Tưỏng Phương Du lúc này mới nhìn lại: “Nghe nói, Trương sư từng truyền thụ một vị gọi Lộ Xung học sinh, vu hồn tinh diệu, nắm giữ Đại Thánh cấp bậc thực lực... Không biết nhưng có việc này?”"),t("br"),t("br"),n._v("“Thật có việc này!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Lộ Xung lúc trước vì cứu hắn, cùng Danh Sư đường mọi người đại chiến, sớm đã huyên náo xôn xao, đối phương biết, không tính là gì."),t("br"),t("br"),n._v("“Nói như vậy lên... Trương sư cũng là một vị vu hồn sư?”"),t("br"),t("br"),n._v("“Ừm!” Trương Huyền lên tiếng."),t("br"),t("br"),n._v("“Vậy thì không sai...”"),t("br"),t("br"),n._v("Thấy hắn thừa nhận, mọi người tất cả đều thở phào nhẹ nhõm, Tưỏng Phương Du cũng nhìn qua, trong mắt mang theo kích động: “Không biết... Trương sư vu hồn truyền thừa đến từ nơi nào? Có thể thấy được qua cái khác vu hồn sư?”"),t("br"),t("br"),n._v("“Truyền thừa của ta... Đến từ một cái Thượng Cổ sống sót hồn phách, cũng chưa gặp qua, cái khác vu hồn sư!” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Mặc Hồn Sinh chỉ là sống sót một cái tàn hồn, chuẩn xác tới nói, đã không tính là chân chính vu hồn sư."),t("br"),t("br"),n._v("Còn cái khác, thật đúng là chưa thấy qua, cũng không tính là nói dối."),t("br"),t("br"),n._v("“Chưa thấy qua?” Tưỏng Phương Du sững sờ."),t("br"),t("br"),n._v("“Đúng vậy a!” Trương Huyền nghi ngờ nhìn qua: “Vu hồn sư, không phải người nào cấm kỵ tồn tại ư? Danh sư đại lục, truyền thừa sớm đã cắt đứt đi!”"),t("br"),t("br"),n._v("“Cấm kỵ?” Nghe nói như thế, Tưỏng Phương Du trầm mặc phút chốc, lần nữa nhìn qua: “Nhìn tới... Trương sư, cũng không biết những sự tình kia!”"),t("br"),t("br"),n._v("“Chuyện gì?”"),t("br"),t("br"),n._v("“Thật ra thì... Vu hồn sư, năm đó cũng không phản bội nhân tộc!” Tưỏng Phương Du nói."),t("br"),t("br"),n._v("“Chưa phản bội?” Trương Huyền tràn đầy nghi ngờ."),t("br"),n._v(" Chuyện này, Danh Sư đường tất cả tư liệu, đều có ghi chép, là danh sư đại lục, người người đều biết sự tình... Chẳng lẽ là giả?"),t("br"),t("br"),n._v("“Dị Linh tộc nhân quá mức mạnh mẽ, năm đó, liền xem như Khổng sư dẫn dắt bảy mươi hai vị thân truyền, đồng dạng chống lại không được... Thậm chí, Khổng sư đều bị trong đó người mạnh nhất, vây ở Trần Thái chỗ, không cách nào chạy ra...”"),t("br"),t("br"),n._v("Than thở một tiếng, Tưỏng Phương Du chậm rãi nói."),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Chuyện này, hắn nghe Ngoan Nhân đề cập qua một câu."),t("br"),t("br"),n._v("Ngay cả Khổng sư, cũng từng ở hắn trên tay, bị nhiều thua thiệt."),t("br"),t("br"),n._v("“Tình huống lúc đó là, Dị Linh tộc nhân, đối với chúng ta rõ như lòng bàn tay, mà chúng ta đối với đối phương, biết rất ít, đặc biệt là sát lục chi khí, hầu như không người có thể chống lại... Vô số cường giả, thực lực cùng đối phương tương đương, chỉ khi nào gặp được loại khí tức này, liền sẽ trở nên ý thức mơ hồ, bó tay chịu trói, bị tại chỗ đánh chết!”"),t("br"),t("br"),n._v("Tưỏng Phương Du tiếp tục nói."),t("br"),t("br"),n._v("Trương Huyền lặng im."),t("br"),t("br"),n._v("Lần đầu tiên nhìn thấy Dị Linh tộc nhân thời điểm, chính là loại tình huống này."),t("br"),t("br"),n._v("Sát lục chi khí đâm thẳng nội tâm, ảnh hưởng linh hồn, rất nhiều người, đứng cũng không vững, thậm chí danh sư, đều làm không được, huống chi người bình thường."),t("br"),t("br"),n._v("“Vì để cho nhân loại chiến thắng, Khổng sư quyết định, để am hiểu linh hồn tu luyện giả, chui vào Dị Linh tộc, dò xét bọn họ linh hồn bí ẩn...”"),t("br"),t("br"),n._v("Tưỏng Phương Du thần sắc ảm đạm xuống: “Vu hồn sư, nhận nhiệm vụ lúc lâm nguy, đồng ý! Bất quá... Vì để cho đối phương tin tưởng, nhất định phải, cùng nhân loại, cùng Danh Sư đường, triệt để đoạn tuyệt! Ngay sau đó... Liền có hậu thế ghi lại những cái kia, vu hồn sư bởi vì tu luyện tà dị, đầu nhập vào Dị Linh tộc, bị Danh Sư đường đồ sát, truyền thừa hủy diệt...”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trương Huyền thân thể chấn động."),t("br"),t("br"),n._v("Đối phương nếu như nói chính là thật, cái kia... Vu hồn sư bỏ ra cũng quá lớn đi!"),t("br"),t("br"),n._v("Rõ ràng là nhân tộc anh hùng, lại bị nhục mạ vài vạn năm..."),t("br"),t("br"),n._v("“Khi đó nhân tộc, tại Dị Linh tộc nhân chèn ép xuống, trải qua trâu ngựa không bằng cuộc sống, sinh tử cũng không khỏi bản thân khống chế, có thể đem hắn đuổi ra ngoài, là tất cả Nhân tộc hi vọng, bị chút tủi thân, đáng là gì!”"),t("br"),t("br"),n._v("Biết hắn nghĩ cái gì, Tưỏng Phương Du nói tiếp: “Vì để cho Dị Linh tộc tin tưởng, Danh Sư đường vận dụng đại lượng danh sư, vây quét vu hồn sư, cái nghề nghiệp này, hầu như một đêm diệt tuyệt. Lúc đó công hội hội trưởng, cũng đang bị bắt về sau, đoạn tuyệt tại Danh Sư đường, tại chỗ bỏ mình.”"),t("br"),t("br"),n._v("“Tự sát? Cái kia...” Trương Huyền tràn đầy khó tin."),t("br"),t("br"),n._v("Người đã chết, làm sao chui vào Dị Linh tộc?"),t("br"),t("br"),n._v("“Lúc đó hội trưởng, thoạt nhìn là tự sát, trên thực tế, vu hồn cũng không chết, mà là lưu lại nhất mạch truyền thừa, cũng chính là chúng ta bây giờ Tưởng gia, bản thân thì đi Dị Linh tộc!”"),t("br"),t("br"),n._v("Than thở một tiếng, Tưỏng Phương Du tiếp tục nói: “Vu hồn thảm trạng, Dị Linh tộc nhân vị cường giả kia, tận mắt nhìn thấy, cho nên đối vị này chỉ còn lại có hồn thể hội trưởng, tin tưởng không nghi ngờ. Ở chung lâu, hội trưởng dần dần biết rõ sát lục chi khí bí ẩn... Ngay sau đó liền nghĩ biện pháp đem tin tức truyền lại bỏ ra đến, còn thiết kế một bộ, phục sát Dị Linh tộc vị kia cường giả tuyệt thế phương pháp!”"),t("br"),t("br"),n._v("“Không chỉ có như vậy, càng còn nhằm vào đối phương đặc điểm, làm ra không ít tu luyện hồn phách pháp quyết! Chỉ bất quá, là sai lầm, bình thường tu luyện không có vấn đề, cũng sẽ để hồn lực tăng cường, nhưng thời gian lâu dài, sát lục chi khí sẽ càng ngày càng yếu, dẫn đến xuất hiện đủ loại vấn đề, cuối cùng để hắn sức chiến đấu trở nên càng ngày càng yếu nhỏ!”"),t("br"),t("br"),n._v("“Sai lầm pháp quyết?” Chấn động toàn thân, Trương Huyền trong đầu nổ vang."),t("br"),t("br"),n._v("Lúc trước, lần đầu tiên đi vào Địa quật, tính giết Thanh Diệp Vương đám người, cướp được không ít tu luyện linh hồn pháp quyết, kết quả lại phát hiện, sai lầm rất nhiều... Khi đó liền cảm thấy kỳ lạ, vì sao nắm giữ vu hồn sư, tương trợ Dị Linh tộc nhân tu luyện pháp quyết, có nhiều như vậy sai lầm, hiện tại mới hiểu được... Là vị hội trưởng kia cố ý lưu lại!"),t("br"),t("br"),n._v("Mục đích đúng là vì suy yếu Dị Linh tộc nhân am hiểu nhất đặc tính!"),t("br"),t("br"),n._v("Chịu nhục, lẻn vào ngoại tộc, cố ý truyền thừa sai lầm công pháp, phần này công đức..."),t("br"),t("br"),n._v("Trương Huyền nắm đấm xiết chặt, chính khiếp sợ tột đỉnh, đột nhiên nhớ tới một việc, đồng tử co rụt lại: “Lúc ấy bọn họ muốn phục kích Dị Linh tộc nhân cường giả tuyệt thế, ngươi cũng đã biết... Là ai?”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1658"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1660"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);