(window.webpackJsonp=window.webpackJsonp||[]).push([[750],{1105:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1649-thu-tuong-phi-phi-lam-đe-tu-【-canh-thu-nhat-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1649-thu-tuong-phi-phi-lam-đe-tu-【-canh-thu-nhat-】"}},[n._v("#")]),n._v(" Chương 1649: Thu Tưỏng Phỉ Phỉ làm đệ tử 【 canh thứ nhất 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1649: Thu Tưỏng Phỉ Phỉ làm đệ tử "),t("br"),t("br"),n._v("“Liên hệ vu hồn tiền bối?”"),t("br"),t("br"),n._v("Trương Huyền tràn đầy hồ đồ."),t("br"),t("br"),n._v("Cái tên này thật sự là Tử Uyên Cổ Thánh hậu nhân, như vậy... Thiên cơ sư công hội, khả năng cũng không phải phản bội, Danh Sư đường cùng lý, nhưng... Đường đường Chư Tử bách gia cường giả, tại sao lại cùng Dị Linh tộc nhân cùng một chỗ?"),t("br"),t("br"),n._v("Mấu chốt còn có Dị Linh tộc Cổ Thánh?"),t("br"),t("br"),n._v("Nghĩ như thế nào, đều không nghĩ ra, lại hỏi thăm vài câu, phát hiện vị này Tưỏng Phương Du, cũng không biết đối phương đang làm gì, chỉ là dựa theo đối phương yêu cầu truyền lại tin tức, còn tin tức nội dung, không có tư cách quan sát, tự nhiên cũng không biết."),t("br"),t("br"),n._v('"Tiên tổ vì nhân tộc, bỏ ra như thế lớn, làm hậu nhân, làm sao có thể bôi nhọ anh linh của bọn họ, huống chi, ta Tưởng gia xây dựng vài vạn năm đến, chết ở trong hang cường giả, nhiều vô kể, con cháu coi như bất tài, cũng không có khả năng làm ra quên nguồn quên gốc sự tình!'),t("br"),t("br"),n._v("Thấy Trương Huyền nghi ngờ bọn họ phản bội nhân tộc, Tưỏng Phương Du ôm quyền, một mặt nghiêm mặt."),t("br"),t("br"),n._v("“Thật xin lỗi, là ta lỗ mãng!” Trương Huyền vội vàng đứng dậy."),t("br"),t("br"),n._v("Đối phương tiên tổ vì nhân tộc bỏ ra nhiều như vậy, hắn lại nghi ngờ con cháu đời sau, đã đại bất kính."),t("br"),t("br"),n._v("“Vu hồn sư, mang tiếng xấu, loại suy nghĩ này, cũng rất bình thường...”"),t("br"),t("br"),n._v("Lắc đầu, Tưỏng Phương Du lần nữa nhìn qua, nhớ tới cái gì, bừng tỉnh hiểu ra: “Trương sư chẳng lẽ là cảm thấy chúng ta khả năng đầu nhập vào Dị Linh tộc nhân, mới đưa tàng bảo khố, toàn bộ chuyển không?”"),t("br"),t("br"),n._v("“Đúng...” Trương Huyền hơi đỏ mặt."),t("br"),t("br"),n._v("“Đối phó Dị Linh tộc nhân, quả thực không cần tuân thủ danh sư lễ nghi cùng quy tắc...” Tưỏng Phương Du giờ mới hiểu được tới."),t("br"),t("br"),n._v("Tổ tiên bọn họ, có thể lẻn vào Dị Linh tộc phía trong, chịu nhục, chỉ bằng vào điểm ấy, liền biết đối quy tắc, cũng không coi trọng."),t("br"),t("br"),n._v("Nếu là thật gặp được, phản bội nhân tộc thế lực lớn, trộm đi bọn họ tất cả bảo vật, để hắn không còn cung cấp, quả thực là lựa chọn tốt nhất."),t("br"),t("br"),n._v("“Ta một lát nữa, liền đem các ngươi bảo vật, cho trả lại...” Trương Huyền vội nói."),t("br"),t("br"),n._v("Biết đối phương không những không có phản bội nhân tộc, vẫn là nhân tộc đại công thần, Trương Huyền coi như da mặt dù dày, cũng không tiện đem bảo vật chiếm làm của riêng."),t("br"),t("br"),n._v("Nhìn thấy hắn bộ dáng này, Tưỏng Phương Du nở nụ cười."),t("br"),t("br"),n._v("Tuy là vị này Trương Huyền, làm việc không coi trọng quy tắc, nhưng thường thường có thể tạo được kỳ hiệu, ngày hôm nay gia tộc bọn họ nếu quả như thật phản bội nhân tộc, liền cái này một cái Thánh vực cửu trọng, đoán chừng liền có thể để cho bọn họ nhức đầu không thôi, triệt để loạn trận tuyến."),t("br"),t("br"),n._v("Đem nói chuyện rõ ràng, còn lại cũng liền đơn giản, Trương Huyền đồng ý tạm thay Tưởng gia tộc trưởng."),t("br"),t("br"),n._v("Vu hồn sư không có lật lại bản án trước đó, tạm thời không thể cùng Tưởng gia móc nối, nếu không, mới không làm cái này tộc trưởng, khẳng định nhường cho Lộ Xung."),t("br"),t("br"),n._v("Bản thân người học sinh này, tìm được vu hồn tổ địa, cũng nhận được truyền thừa, về sau nhất định là vu hồn sư nhất mạch, cao nhất nhân vật thủ lĩnh."),t("br"),t("br"),n._v("Nói đến đây cái vu hồn tổ địa, hắn cũng rốt cuộc minh bạch tới."),t("br"),t("br"),n._v("Trước đó, liền suy nghĩ, vu hồn sư bị nhổ tận gốc, tổ địa làm sao có thể may mắn thoát khỏi? Hiện tại xem ra, chỉ sợ cũng là Khổng sư vì lưu hương lửa, cố ý che giấu, hoặc là hạ thủ lưu tình."),t("br"),t("br"),n._v("Nếu không, lấy Danh Sư đường năng lực, thật muốn tiêu diệt, cái gì đều rất khó còn lại."),t("br"),t("br"),n._v("Kế nhiệm đại điển kết thúc, đã đến đêm khuya, đem Tưỏng Phỉ Phỉ gọi tới trước mặt, Trương Huyền đưa tới một quyển sách."),t("br"),t("br"),n._v("“Trong này có ta đối linh hồn chân giải lý giải, dựa theo cái này học tập, hẳn là có thể rất nhanh lĩnh ngộ, không cần lại đi quan sát Tưởng gia ngọc bích.”"),t("br"),t("br"),n._v("Đem Tưởng gia bảo tàng trả lại, chỉ lưu lại một chút có thể cung cấp linh khí bảo vật, Trương Huyền đem vừa mới viết một bản bí tịch đưa tới."),t("br"),t("br"),n._v("“Ngươi lý giải?”"),t("br"),t("br"),n._v("Mặt mũi nghi ngờ đem thư tịch mở ra, rất mau nhìn một lần, Tưỏng Phỉ Phỉ ngay sau đó cả người đôi mắt thanh tú trợn tròn, khó tin."),t("br"),t("br"),n._v("Linh hồn hư vô mờ mịt, là nhất khó tu, nàng cứ việc thiên phú tuyệt cao, có thể nghĩ muốn chân chính lĩnh ngộ chân giải, cũng không có bất kỳ tự tin, thậm chí cảm thấy đến cố gắng cả đời, đều làm không được."),t("br"),t("br"),n._v("Mà bây giờ, vị này tộc trưởng mới nhận chức cho bí tịch, đem linh hồn cụ thể hoá, tu luyện đơn giản hóa, từ nông tới sâu, nhắm thẳng vào áo nghĩa... Căn cứ lý giải, chỉ cần chăm chú học tập, trong một tháng, liền có khả năng lĩnh ngộ chân giải."),t("br"),t("br"),n._v("Một tháng lĩnh ngộ, vô số tiên tổ đều làm không được..."),t("br"),n._v("“Cái này, cái này... Quá quý giá!”"),t("br"),t("br"),n._v("Thân thể mềm mại run rẩy, Tưỏng Phỉ Phỉ cắn môi một cái, sau một lúc lâu, giống như là làm ra quyết định gì đó, nắm đấm xiết chặt: “Ngươi ban tặng ta quý giá như vậy bí tịch, ta không có gì có thể báo đáp... Thực sự không được, liền đáp ứng thông gia...”"),t("br"),t("br"),n._v("Tuy là, cũng không thích vị này, nhưng cũng biết, hắn đối Tưởng gia quá là quan trọng, có thể thông gia lôi kéo lời nói, tất nhiên là tốt nhất."),t("br"),t("br"),n._v("Cùng lắm thì, hi sinh chút."),t("br"),t("br"),n._v("Đối phương nói có người thích, thực sự không được liền làm thiếp."),t("br"),t("br"),n._v("Tin tưởng lấy nàng dung mạo cùng địa vị, hẳn là sẽ không cự tuyệt."),t("br"),t("br"),n._v("“Thông gia? Ngươi cả nghĩ quá rồi, ta bộ công pháp này, tuy là tu luyện đơn giản, nhưng nếu như không có ta chỉ điểm lời nói, đồng dạng có thể tẩu hỏa nhập ma, đi nhầm vào lạc lối...”"),t("br"),t("br"),n._v("Gặp nàng tự cho là đúng, Trương Huyền mỉm cười lắc đầu: “Như vậy đi, ta thu ngươi làm ký danh học sinh, chỉ điểm ngươi pháp quyết tu luyện.”"),t("br"),t("br"),n._v("“Học sinh?”"),t("br"),t("br"),n._v("Tưỏng Phỉ Phỉ ngẩn ngơ."),t("br"),t("br"),n._v("Còn tưởng rằng, cho tốt đẹp như vậy chỗ, là muốn cho nàng cảm động, lấy thân báo đáp, nằm mơ cũng không ngờ tới, là muốn nhận học sinh..."),t("br"),t("br"),n._v("Làm thiếp thật tốt, còn có thể làm ấm giường..."),t("br"),t("br"),n._v("“Còn không mau một chút dập đầu? Tộc trưởng tự thân truyền thụ, cho dù là giảng bài học sinh, cũng đủ để dương danh thiên hạ!”"),t("br"),t("br"),n._v("Gặp nàng ngây người, Tưỏng Phương Du nhịn không được quát."),t("br"),t("br"),n._v("Bọn họ vị tộc trưởng này, không riêng gì Trương gia gia chủ, Lạc gia gia chủ, Thánh tử điện điện chủ, mấy cái học sinh, càng là Chiến Sư đường chiến tử, Băng Nguyên cung công chúa, khải linh sư công hội hội trưởng, Viên gia gia chủ... Mỗi một cái, đều không đơn giản."),t("br"),t("br"),n._v("Nữ nhi có thể trở thành đệ tử, dù chỉ là ký danh, nhất định cũng được ích lợi vô cùng, vượt qua hắn, ở trong tầm tay."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Nghe được cha lời này, Tưỏng Phỉ Phỉ sửng sốt một chút, lúc này mới phản ứng lại."),t("br"),t("br"),n._v("Một mực đem đối phương trở thành Thánh tử điện cái kia khiêu chiến tuổi của nàng nhẹ thiên tài, lại không ý thức được, không đến thời gian một tháng, đối phương y nguyên đứng tại đại lục chi đỉnh, địa vị cùng nàng có cách nhau một trời một vực."),t("br"),t("br"),n._v("Không nói cái khác thân phận, chỉ nói Dương sư sư huynh, thật muốn cẩn thận hàng lời nói, hô một tiếng tổ gia gia, đều không quá đáng."),t("br"),t("br"),n._v("“Đồ nhi, Tưỏng Phỉ Phỉ gặp qua lão sư!”"),t("br"),t("br"),n._v("Đầu gối mềm nhũn, lại không chần chừ, Tưỏng Phỉ Phỉ quỳ rạp xuống đất."),t("br"),t("br"),n._v("“Đứng lên đi!”"),t("br"),t("br"),n._v("Trương Huyền mỉm cười."),t("br"),t("br"),n._v("Vừa rồi nữ hài quỳ rạp xuống đất, trong đầu lần nữa nhiều ra một bản Thiên đạo chi sách, nhịn không được thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Trước đó liền biết thứ này uy lực vô tận, vốn cho rằng, có thể đập chết cái Thánh vực nhất, lưỡng trọng, tam, tứ trọng là tốt lắm rồi, không nghĩ tới Cổ Thánh đều không chịu nổi."),t("br"),t("br"),n._v("Có vật này, thủ đoạn bảo mệnh tuyệt đối gia tăng thật lớn, coi như Cổ Thánh tới, cũng không tính là gì."),t("br"),t("br"),n._v("“Thực sự không được, nhiều hơn nữa thu mấy cái học sinh... Ừm, Dương sư cái kia học sinh, Phùng Tử Dật cũng không tệ, không được, cùng lão Dương thương nghị một chút, chuyển nhượng cho ta coi như?”"),t("br"),t("br"),n._v("Vịn cái cằm, Trương Huyền trong lòng suy tính."),t("br"),t("br"),n._v("Lấy thân phận của hắn bây giờ, muốn thu không sai học sinh, mới có thể phù hợp thân phận, nhưng mà học sinh tốt, đi nơi nào tìm?"),t("br"),t("br"),n._v("Cũng không phải là tất cả mọi người, đều giống như hắn, ưu tú như vậy, hiền lành, ngay thẳng, vĩ đại..."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1660"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1662"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);