(window.webpackJsonp=window.webpackJsonp||[]).push([[752],{1108:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1651-chi-ton-thien-hoa"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1651-chi-ton-thien-hoa"}},[n._v("#")]),n._v(" Chương 1651: Chí tôn thiên hỏa")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1651: Chí tôn thiên hỏa"),t("br"),t("br"),n._v("Khổng sư từng nói qua, Tiên Thiên thai độc, cần thiên hỏa thiêu đốt, mới có thể giải quyết triệt để, vốn cho rằng, chỉ có thực sự trở thành Đại Thánh mới có thể làm đến, không nghĩ tới, vu hồn đi đầu một bước, đem thứ này dẫn tới."),t("br"),t("br"),n._v("Nếu có thể mượn cơ hội rèn luyện thân thể, có thể hay không trực tiếp đem quấy nhiễu hắn một năm Tiên Thiên thai độc, giải quyết triệt để, lại không phiền não?"),t("br"),t("br"),n._v("“Thiên hỏa kiếp thế nhưng là thiên hỏa trực tiếp thiêu đốt vu hồn?”"),t("br"),t("br"),n._v("Nghĩ đến điểm này, Trương Huyền vội vàng hỏi."),t("br"),t("br"),n._v("Phong thánh kiếp chia làm hồn kiếp, thể kiếp cùng tâm kiếp, cái này cái gọi là thiên hỏa kiếp, đã đồng dạng là Thiên đạo đối tu luyện giả trừng phạt, có thể hay không cũng có tương tự bước đi?"),t("br"),t("br"),n._v("“Thiên hỏa kiếp, chia làm ba bộ phận, theo thứ tự là, thiêu hồn, thiêu thần, thiêu tâm! Thiêu hồn lấy thực lực của ngươi, hẳn là có thể kiên trì, nhưng mà thiêu thần cùng thiêu tâm, sẽ rất khó, hơi không cẩn thận, liền sẽ hình thần câu diệt.”"),t("br"),t("br"),n._v("Nghe hắn hỏi thăm, cốt long chần chờ một chút, nói: “Đặc biệt là sau cùng thiêu tâm, dính dáng tâm ma, ảnh hưởng là đạo tâm, đạo tâm vừa diệt, vạn sự đều yên. Nếu như thân thể ngươi, cũng đạt tới Đại Thánh nhị trọng Kim Thân cảnh, hồn phách tới kết hợp, có khí huyết tẩm bổ hồn thể, có lẽ còn có thể chống lại chủ, đơn thuần hồn phách... Khó! Thời kỳ Thượng Cổ, không biết bao nhiêu vu hồn sư, đều chết tại đây cái cảnh giới, hầu như không có nhiều người có thể may mắn thoát khỏi...”"),t("br"),t("br"),n._v("Long cốt thần thương, trước đó tại rồng suối núi thời điểm, chỉ thấy hắn sử dụng qua vu hồn, giờ phút này thấy hắn tu luyện hồn phách, cũng không thèm để ý."),t("br"),t("br"),n._v("“Ngươi ý tứ, thân thể cũng đạt tới Kim Thân cảnh, độ thiên hỏa kiếp, sẽ an toàn hơn?” Nghe nó nói ngưng trọng, Trương Huyền nhịn không được hỏi."),t("br"),t("br"),n._v("“Không sai! Thân thể lực phòng ngự muốn so đơn thuần hồn phách cường đại hơn rất nhiều, do thân thể ngăn cản, hồn phách phụ trợ, tỷ lệ thành công khá lớn, đơn thuần vu hồn... Ta còn chưa từng nghe nói qua, có ai có thể thành công!” Cốt long từ thân thể bên hông, bay ra, lóe lên trong lỗ đen, lộ ra lo lắng."),t("br"),t("br"),n._v("Thật vất vả nhận chủ, ngộ nhỡ cái tên này lại bởi vì tuỳ tiện tu luyện, đem bản thân luyện chết, tìm ai nói rõ lí lẽ đi?"),t("br"),t("br"),n._v("Truyền đi đều không đủ mất rồng..."),t("br"),t("br"),n._v("Cứ việc nó chỉ còn lại có một đống xương đầu..."),t("br"),t("br"),n._v("“Thân thể... Hiện tại còn kém rất lớn một đoạn!”"),t("br"),t("br"),n._v("Nghe được đối phương, Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Hắn thân thể bên trên tu vi, còn không có học tập Khổng sư thượng phẩm phá cảnh pháp, lại thêm không có pháp quyết thích hợp, coi như muốn đột phá, cũng không đột phá nổi."),t("br"),t("br"),n._v("Thậm chí... Coi như đột phá, khoảng cách Kim Thân cảnh cũng có rất lớn khoảng cách, khẳng định không đuổi kịp tình huống trước mắt."),t("br"),t("br"),n._v("“Tới thì tới a, ta ngược lại muốn xem xem, cái này thiên hỏa kiếp, có bao nhiêu lợi hại!”"),t("br"),t("br"),n._v("Cái này long cốt thần thương, từng đi theo Nhiễm Cầu Cổ Thánh chinh chiến thiên hạ, cứ việc bị phong ấn xuống, ký ức mất đi không ít, nhưng ánh mắt cùng kiến thức, nhưng cũng không phải người bình thường có thể so sánh được."),t("br"),t("br"),n._v("Đã nó nói khó khăn, cái này thiên hỏa, chỉ sợ thật không có dễ dàng như vậy."),t("br"),t("br"),n._v("Bất quá, Trương Huyền tu luyện Thiên đạo công pháp, đồng cấp vô địch, mạnh mẽ lại như thế nào?"),t("br"),t("br"),n._v("Người khác có thể vượt qua, ta thì sợ gì chi!"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Trong lòng cảm xúc, bầu trời mây đen, đã tích góp đến cực điểm."),t("br"),t("br"),n._v("Cùng lôi đình mây đen tương tự, toàn thân đen nhánh, tựa như trời đầy mây, nhìn kỹ lại, vô số đạo hỏa diễm ở bên trong cuồn cuộn, thiêu đốt, còn chưa tới đến trước mặt, liền cho người ta một loại, thiêu đốt thiên địa, hủy diệt vạn vật cảm giác."),t("br"),t("br"),n._v("Ầm!"),t("br"),t("br"),n._v("Một đám lửa rơi xuống, thẳng tắp xông về Trương Huyền hồn thể."),t("br"),t("br"),n._v("Cùng thường xuyên nhìn thấy hỏa hồng sắc khác biệt, ngược lại mực nước đồng dạng, lộ ra màu đen kịt."),t("br"),t("br"),n._v("Nếu không phải từ không trung rơi xuống, cũng hoài nghi có phải hay không ai dùng mực in, vẽ ra một bộ đan thanh, cũng không phải là chân thực tồn tại."),t("br"),t("br"),n._v("“Đây là thiên hỏa?”"),t("br"),t("br"),n._v("Gặp hỏa diễm còn chưa tới từng tới trước mặt, phía trên không gian liền xuất hiện từng đạo vết rách, tựa như lưu tinh trụy lạc, tản mát ra mạnh mẽ uy áp cùng khí thế, Trương Huyền da đầu lập tức nổ tung."),t("br"),t("br"),n._v("Hỏa diễm gặp qua không ít, cực nóng, mạnh mẽ, luyện khí trong lò, lợi hại nhất địa hỏa, cũng cùng chi tiếp xúc qua... Nhưng từ chưa thấy qua, quỷ dị như vậy, cường đại như vậy!"),t("br"),t("br"),n._v("Đen như mực, không có chút nào ánh sáng, liền không gian đều không chịu nổi, đây là thiên hỏa?"),t("br"),n._v(" Vu hồn thật sự có thể chống lại?"),t("br"),t("br"),n._v("Đừng nói vu hồn, coi như thân thể của hắn, vượt xa tuyệt phẩm Thánh khí, tới đụng một cái, cũng sợ rằng sẽ trong nháy mắt hòa tan!"),t("br"),t("br"),n._v("“Đây, đây là không phải bình thường thiên hỏa, mà là, mà là... Chí tôn thiên hỏa!”"),t("br"),t("br"),n._v("Hắn bên này khiếp sợ suýt làm chuyện điên rồ, long cốt thần thương dường như cũng không dám tin tưởng, không ngừng run rẩy."),t("br"),t("br"),n._v("“Chí tôn thiên hỏa, đó là cái gì?”"),t("br"),t("br"),n._v("Trương Huyền vội vàng nhìn tới."),t("br"),t("br"),n._v("“Thiên hỏa cũng chia đẳng cấp, bình thường tu luyện giả, bình thường đều là tam phẩm, cũng chính là cấp thấp nhất, hiện hỏa hồng sắc. Lợi hại danh sư, chiến sư, thì là nhị phẩm, hiện sâm bạch chi sắc. Nhiễm Cầu Cổ Thánh bọn họ, chịu đựng Khổng sư tự thân chỉ điểm, tu luyện vô thượng nhất pháp quyết, chân khí tinh thuần, thì là nhất phẩm, hiện màu xanh da trời. Đẳng cấp càng cao, rèn luyện sau đó Kim Thân cũng liền càng cường đại... Trong truyền thuyết, có loại chí tôn thiên hỏa, vượt xa nhất phẩm, mệnh danh Thiên đạo chi hỏa, hiện màu đen... Chỉ có Khổng sư một người trải qua, hơn nữa còn là thân thể đạt tới Kim Thân cảnh lúc mới xuất hiện... Ngươi, ngươi sao có thể dẫn tới?”"),t("br"),t("br"),n._v("Lơ lửng, long cốt thần thương đầu rồng chỗ con mắt, không ngừng lóe lên, một bộ vẻ mặt như gặp phải quỷ."),t("br"),t("br"),n._v("Chí tôn thiên hỏa, thiêu đốt vạn vật, không gì không thiêu cháy, không có gì bất diệt, đừng nói hồn phách, liền xem như Kim Thân cảnh, thậm chí Bất Hủ cảnh cường giả tới, cũng chỉ có bị đốt thành tro bụi một con đường có thể đi..."),t("br"),t("br"),n._v("Kẻ trước mắt này, chỉ là vu hồn đột phá, làm sao lại đưa tới?"),t("br"),t("br"),n._v("Bình thường thiên hỏa, muốn vượt qua, liền không biết có bao nhiêu khó, loại này... Gần như không có khả năng!"),t("br"),t("br"),n._v("Vận may cũng quá củ chuối đi đi!"),t("br"),t("br"),n._v("“Ta làm sao biết...”"),t("br"),t("br"),n._v("Không nghĩ tới cái này hỏa diễm, như vậy có lai lịch, Trương Huyền khóc không ra nước mắt."),t("br"),t("br"),n._v("Chỉ là độ cái thiên hỏa kiếp mà thôi, còn hưng sư động chúng như vậy ư?"),t("br"),t("br"),n._v("Lòng tràn đầy buồn bực, biết lúc này cũng không phải xoắn xuýt thời điểm, mới vừa vận chuyển lực lượng, chỉ thấy hỏa diễm đã đi tới vu hồn trước mặt, đem hắn bao phủ."),t("br"),t("br"),n._v("Hừng hực!"),t("br"),t("br"),n._v("Cùng ngọn lửa màu đen tiếp xúc, hồn phách lập tức thiêu đốt lên, đau đớn kịch liệt, để Trương Huyền biến sắc, không tự chủ được run rẩy lên."),t("br"),t("br"),n._v("Còn tưởng rằng cùng độ lôi kiếp đồng dạng, chỉ cần kiên trì, liền có thể thừa nhận, làm sao cũng không nghĩ đến, những ngọn lửa này, uy lực cường đại như vậy, hơi không cẩn thận, hồn phách đều sẽ bị đốt thành tro bụi, chẳng còn sót lại gì."),t("br"),t("br"),n._v("“Thiên đạo công pháp, vận chuyển!”"),t("br"),t("br"),n._v("Biết tình huống nguy cấp, long cốt thần thương cũng không đáng tin cậy, không dám chần chừ, Trương Huyền cắn răng một cái, lực lượng toàn thân lập tức vận chuyển lại, nhanh chóng cùng hỏa diễm chống lại."),t("br"),t("br"),n._v("Ong ong!"),t("br"),t("br"),n._v("Kim quang đại thịnh."),t("br"),t("br"),n._v("Nguyên bản trong suốt hồn phách, giờ phút này hiển lộ ra kim sắc hình dáng, như là một tòa cự đại tượng phật."),t("br"),t("br"),n._v("Xì xì xì!"),t("br"),t("br"),n._v("Kim quang lực lượng rất mạnh, nhưng thiên hỏa uy lực càng hơn, còn không có cùng ánh sáng tiếp xúc, liền đem không gian chung quanh xé nát, giống như là muốn đem hắn cuốn vào không gian loạn lưu, triệt để xé thành bột phấn, lại không cách nào khôi phục."),t("br"),t("br"),n._v("“Định!”"),t("br"),t("br"),n._v("Không dám chần chừ, Trương Huyền hướng về phía trước một điểm, phong cấm không gian chân giải thi triển đi ra."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Xé rách không gian lần nữa vững chắc xuống."),t("br"),t("br"),n._v("Bất quá, thiên hỏa vẫn như cũ đáng sợ, Kim Thân giống như là bị đồng hóa, cũng dần dần biến thành màu đen."),t("br"),t("br"),n._v("(Tiến cử bạn tốt quyển sách 《 thủ nghệ của ta tác phẩm có thuộc tính 》, đây chính là cùng của ta cơ hữu tốt, cùng một chỗ lẫn nhau cổ vũ, lẫn nhau kiên trì, trọn mười năm. Hôm qua vào VIP, mọi người nhất định phải ủng hộ ah!)"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1662"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1664"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);