(window.webpackJsonp=window.webpackJsonp||[]).push([[754],{1107:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1653-tien-thien-thai-đoc-chay-tron-【-canh-thu-hai-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1653-tien-thien-thai-đoc-chay-tron-【-canh-thu-hai-】"}},[n._v("#")]),n._v(" Chương 1653: Tiên Thiên thai độc chạy trốn 【 canh thứ hai 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1653: Tiên Thiên thai độc chạy trốn "),t("br"),t("br"),n._v("Thời khắc này Trương Huyền, muốn khóc tâm đều có, hận không thể có một cái lỗ chui vào."),t("br"),t("br"),n._v("Lằng nhà lằng nhằng, thời gian, không gian, linh hồn ba loại chân giải toàn bộ thi triển đi ra, đến bây giờ cũng chỉ hấp thu hai hạt to bằng mũi kim ngọn lửa, còn bản thân cảm giác rất có thành tựu..."),t("br"),t("br"),n._v("Kết quả, người ta phân thân xông lên đi lên, liền nuốt trọn một đoàn..."),t("br"),t("br"),n._v("Mấu chốt nhất là... Cái này hai hàng ánh mắt gì? Thái độ gì? Giọng điệu gì?"),t("br"),t("br"),n._v("Mẹ nó, ngươi chỉ là cái phân thân! Phân thân! Phân thân!"),t("br"),t("br"),n._v("Phân thân hiểu có ý tứ gì ư?"),t("br"),t("br"),n._v("Ta mẹ nó mới là bản tôn, có thể phân rõ chủ thứ, biết mình địa vị không?"),t("br"),t("br"),n._v("Vừa nghĩ tới địa vị... Trương Huyền vẻ mặt càng thêm khó coi..."),t("br"),t("br"),n._v("Dường như, từ khi cái này hai hàng xuất hiện về sau, tới đối chiến qua nhiều lần, mỗi lần đều lấy bản thân thất bại mà kết thúc... Thật muốn nói địa vị, cũng là hắn không có..."),t("br"),t("br"),n._v("Tràn đầy đau buồn, ngẩng đầu hướng trên không phân thân nhìn lại, nói thật, còn là lần đầu tiên, hi vọng cái này hai hàng bị ngọn lửa thiêu chết."),t("br"),t("br"),n._v("Kết quả... Đối phương ợ một cái, một mặt hưng phấn: “Ăn ngon!”"),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Một đầu đâm vào mây đen, biến mất không thấy gì nữa."),t("br"),t("br"),n._v("“Rống dát?”"),t("br"),t("br"),n._v("Một bên cốt long không ngừng run rẩy, cả người xương cốt đều sắp tan ra thành từng mảnh, càng là một bộ vẻ mặt như gặp phải quỷ."),t("br"),t("br"),n._v("Phân thân không tính là gì, không ít cường giả đều rèn luyện qua, Khổng sư cũng có, nhưng... Còn lần đầu tiên thấy, so bản tôn còn chảnh chứ!"),t("br"),t("br"),n._v("Mấu chốt nhất là... Đây chính là nó đều kính sợ cực kỳ chí tôn thiên hỏa, cái tên này, không những không sợ chút nào, còn xông tới đi vào... Chẳng lẽ liền không sợ bị đốt thành tro bụi?"),t("br"),t("br"),n._v("Đang tại khiếp sợ, ngay sau đó nhìn thấy một đầu bắp đùi từ mây đen bên trong lộ ra, một bàn tay qua lại loạn chà xát..."),t("br"),t("br"),n._v("Đùng đùng!"),t("br"),t("br"),n._v("Một đoàn lớn màu đen ngọn lửa bị chà xát đều đặn, ở trên người phát ra tiếng nổ, chân chủ nhân không những không có sợ hãi, còn tràn đầy hưởng thụ, phát ra thoải mái tiếng rên rỉ..."),t("br"),t("br"),n._v("“Cái này, cái này... Còn là người sao?”"),t("br"),t("br"),n._v("Cái cổ rụt lại, cốt long run lẩy bẩy."),t("br"),t("br"),n._v("Trước đó, còn cảm thấy thần phục kẻ trước mắt này, thiệt thòi, nhìn thấy đối phương cái này phân thân mới hiểu được, tới tỷ thí, kém quá nhiều."),t("br"),t("br"),n._v("Quay đầu nhìn về phía đứng tại chỗ bản tôn, chỉ thấy hắn tức giận sắc mặt phát xanh, không ngừng run rẩy."),t("br"),t("br"),n._v("“Ai sợ ai, liều mạng!”"),t("br"),t("br"),n._v("Vù!"),t("br"),t("br"),n._v("Cốt long còn không có phản ứng lại, liền thấy cái chủ nhân này vu hồn, cũng thẳng tắp hướng mây đen vọt tới, trong chớp mắt biến mất ở trước mắt."),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Cốt long triệt để điên rồi."),t("br"),t("br"),n._v("Phân thân dám hướng, khả năng bởi vì rèn luyện chất liệu mạnh, mượn thân thể, chống lại hỏa diễm, ngươi... Một đầu vu hồn xông đi vào làm gì?"),t("br"),t("br"),n._v("Ngại chết không nhanh ư?"),t("br"),t("br"),n._v("“Thực sự không được... Hắn chết, ta nhận phân thân làm chủ?”"),t("br"),t("br"),n._v("Một đầu móng vuốt đỡ lấy cái cằm, cốt long vì chính mình chủ mưu đường lui."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Bị phân thân xem thường, Trương Huyền nơi nào chịu được, vọt thẳng vào mây đen."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Thời gian nháy mắt, liền thấy vô số hắc hỏa hướng bên này lao qua."),t("br"),t("br"),n._v("“Phân thân chất liệu là Cửu Thiên Liên Thai, so ta thân thể cứng rắn, nhưng hồn thể, giống như ta, hắn có thể thừa nhận, ta khẳng định cũng có thể...”"),t("br"),n._v(" Tuy là nguy hiểm, nhưng trong lòng cũng không lo lắng."),t("br"),t("br"),n._v("Xông tới, không phải là bởi vì lỗ mãng, mà là trải qua suy tính."),t("br"),t("br"),n._v("Hắn nhận được tu luyện công pháp, đi vào không gian gấp về sau, liền tự động cùng phân thân cộng hưởng, tự mình tu luyện, đối phương cũng tại tu luyện, nói cách khác... Hắn vu hồn đột phá Kim Thân cảnh, đối phương cũng giống vậy, đều là Kim Thân."),t("br"),t("br"),n._v("Đã như vậy, hắn có thể chịu được, vì sao bản thân chịu không được?"),t("br"),t("br"),n._v("Khẳng định có cái gì không có chú ý tới!"),t("br"),t("br"),n._v("Hướng cách đó không xa phân thân nhìn lại, chỉ thấy phân thân tại ngọn lửa màu đen bên trong, khi thì bơi ngửa, khi thì bơi ếch, khi thì bơi chó, khi thì biến thành chữ đại, khi thì biến thành “Thái” chữ... Không nói ra được thoải mái."),t("br"),t("br"),n._v("Ngọn lửa màu đen không ngừng thiêu đốt, để thân thể bắt đầu thả ra kim sắc ánh sáng, linh hồn cũng so trước kia cứng cáp hơn."),t("br"),t("br"),n._v("“Ta hiểu...”"),t("br"),t("br"),n._v("Trong lòng hơi động, Trương Huyền bừng tỉnh hiểu ra: “Long cốt thần thương nói, thiên hỏa kiếp phân vì thiêu hồn, thiêu thần, thiêu tâm... Cho nên, ta vẫn cho rằng, thiêu hồn vì giai đoạn thứ nhất, còn lại từng cái xuất hiện. Hiện tại xem ra, bình thường thiên hỏa kiếp khả năng như vậy, chí tôn thiên hỏa kiếp, cũng không phải là như vậy... Mà là, ba loại đồng thời xuất hiện!”"),t("br"),t("br"),n._v("“Cái này hỏa diễm, cực nóng để cho người ta khó mà chống cự không giả, nhưng cũng không có đạt tới, triệt để không chịu nổi tình trạng, dù sao, ta tu luyện chính là Thiên đạo vu hồn, lại trải qua vô số lần lôi kiếp rèn luyện, hồn thể sớm đã chắc chắn có thể so với Đại Thánh thần binh...”"),t("br"),t("br"),n._v("“Nhưng... Đối mặt một đám lửa, lại sợ hãi dùng hết đủ loại thủ đoạn, tiến hành lẩn tránh, không dám hấp thu... Đây chính là đối đạo tâm khảo nghiệm!”"),t("br"),t("br"),n._v("“Phân thân, dựa dẫm Cửu Thiên Liên Thai đặc thù, không sợ hãi chút nào, vọt thẳng tiến vào, ngược lại thiên hỏa rèn luyện đạo tâm hiệu quả biến mất, cũng liền không tưởng tượng bên trong đáng sợ như vậy!”"),t("br"),t("br"),n._v("“Liền giống như cô độc đợi tại đêm tối, trong lòng càng sợ hãi, càng cảm thấy có người giấu ở trái phải, bất cứ lúc nào cũng sẽ gây bất lợi cho ngươi, mà tâm tình thẳng thắn vô tư, không sợ hãi, ngược lại cảm giác không thấy cái gì...”"),t("br"),t("br"),n._v("Những ý nghĩ này tại trong đầu chợt lóe lên, Trương Huyền lại không quản vọt tới hắc hỏa, vu hồn bỗng nhiên thư giãn mở ra, trong cơ thể kim quang, không tại phóng thích, hình như trong chốc lát, triệt để chạy không bản thân, không đi nữa quản, có thể hay không bị thiêu chết."),t("br"),t("br"),n._v("Xì xì xì!"),t("br"),t("br"),n._v("Hỏa diễm lập tức ở chung quanh thân thể hắn nung khô mở ra, tâm cảnh thả lỏng phía dưới, quả nhiên cảm giác lại không giống trước đó như thế đau đớn, ngược lại tại hỏa diễm nung khô xuống, linh hồn trở nên cứng cáp hơn."),t("br"),t("br"),n._v("“Quả là thế...”"),t("br"),t("br"),n._v("Gặp thật sự hữu hiệu, thở phào nhẹ nhõm, Trương Huyền đồng thời hiểu được."),t("br"),t("br"),n._v("Thiên hỏa kiếp, không chỉ thiêu đốt hồn thể, đối tinh thần, tâm cảnh cũng có rèn luyện tác dụng."),t("br"),t("br"),n._v("Càng sợ hãi, hỏa diễm uy lực lại càng lớn, bởi vì nó thiêu đốt không phải ngươi hồn thể, mà là nội tâm của ngươi."),t("br"),t("br"),n._v("Không sợ hãi lúc, uy lực ngược lại nhỏ đi."),t("br"),t("br"),n._v("Bất quá, cho dù nhỏ, hồn thể tại thiêu đốt bên dưới không ngừng run rẩy, đau đớn từng cơn kéo tới, cảm giác bất cứ lúc nào cũng sẽ bị xé nứt."),t("br"),t("br"),n._v("“Trước mặc kệ, rèn luyện hồn thể...”"),t("br"),t("br"),n._v("Biết rầu rĩ những thứ vô dụng này, tinh thần khẽ động, Trương Huyền hồn thể huyệt đạo lần nữa mở ra, điên cuồng đối bốn phía hỏa diễm thôn phệ mà đi."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Ngọn lửa đi vào trong cơ thể, lập tức không ngừng trườn, vốn là đã biến thành kim sắc hồn thể, càng thêm chói mắt, hào quang rực rỡ, tựa như một tôn kim sắc phật đà."),t("br"),t("br"),n._v("“Nhìn một chút có thể hay không đem thai độc đốt thiêu hủy!”"),t("br"),t("br"),n._v("Cảm thụ hồn thể dần dần thích ứng thiên hỏa nhiệt độ, Trương Huyền tại không chần chờ, tinh thần khẽ động, từng đoàn từng đoàn cực nóng hỏa diễm, phun trào ra hướng thai độc phương hướng vọt tới."),t("br"),t("br"),n._v("Cô cô cô!"),t("br"),t("br"),n._v("Thai độc giống như là thấy được vật gì đáng sợ, không ngừng lui về phía sau."),t("br"),t("br"),n._v("Trương Huyền con mắt sáng lên, tăng tốc khống chế ngọn lửa tràn vào."),t("br"),t("br"),n._v("Chi chi chít chít!"),t("br"),t("br"),n._v("Giống như là chấn kinh đồng dạng thét lên, thai độc không thể kiên trì được nữa, gào thét lên từ hồn thể vọt ra, thò đầu ra mây đen, biến mất không thấy gì nữa."),t("br"),t("br"),n._v("Tiên Thiên thai độc, đã có ý thức của mình, bằng không thì cũng không có khả năng đối mặt Thiên đạo chân khí, chủ động trốn tránh."),t("br"),t("br"),n._v("“Rốt cục thoát khỏi...”"),t("br"),t("br"),n._v("Thấy nó chạy trốn, Trương Huyền thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Thứ này, từ khi phát hiện, liền cảm thấy là quả bom hẹn giờ, tràn đầy sợ hãi, cũng là hắn đoạn đường này đến, cố gắng tu luyện lớn nhất động lực, ngày hôm nay xem như giải quyết triệt để!"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1664"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1666"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);