(window.webpackJsonp=window.webpackJsonp||[]).push([[758],{1111:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1657-thien-nhai-nhuong-hien"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1657-thien-nhai-nhuong-hien"}},[n._v("#")]),n._v(" Chương 1657: Thiên Nhai nhường hiền")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1657: Thiên Nhai nhường hiền"),t("br"),t("br"),n._v("“Tộc trưởng... Không phải là đối thủ!”"),t("br"),t("br"),n._v("Không biết qua bao lâu, Lạc Càn Trinh mặt mũi đắng chát."),t("br"),t("br"),n._v("“Đúng vậy a, vị này Trương Huyền rất rõ ràng tại cấp gia chủ lưu mặt mũi, nếu không, hai mươi chiêu phía trong cũng đã thua!”"),t("br"),t("br"),n._v("Lạc Khinh Trần gật đầu."),t("br"),t("br"),n._v("“Ta nhận thua...”"),t("br"),t("br"),n._v("Bên này vừa dứt lời, trên đài Lạc Thiên Nhai thở hồng hộc, khắp khuôn mặt là chán nản."),t("br"),t("br"),n._v("Dường như kết quả này, hắn cũng không tin."),t("br"),t("br"),n._v("“Lạc huynh không cần để ý, hai ta thực lực, thật ra thì không kém nhiều...” Đi tới trước mặt, Trương Huyền cũng không có thắng được vui sướng, ngược lại một mặt chân thành: “Ngày hôm nay xem như ngang tay như thế nào?”"),t("br"),t("br"),n._v("“Thua thì thua, ta Lạc Thiên Nhai nếu như ngay cả cái này đều không nhận, không bằng sửa họ Trương được rồi...”"),t("br"),t("br"),n._v("Lạc Thiên Nhai lắc đầu: “Bất quá, ta thua, cũng không phải là ta Lạc gia không gian phong cấm chân giải, không bằng các ngươi Trương gia, mà là cá nhân ta năng lực vấn đề!”"),t("br"),t("br"),n._v("“Lạc huynh, khách khí!”"),t("br"),t("br"),n._v("Trương Huyền một mặt nghiêm mặt: “Thời gian, không gian chân giải cùng xưng, không có phân chia mạnh yếu, ta cũng không có chút nào coi thường Lạc gia chi ý, trái lại còn hết sức kính trọng, sở dĩ có thể may mắn giành được một chiêu nửa thức, chủ yếu nhất là... Dùng Lạc gia phong cấm chân giải, hơn nữa đối với cái này lý giải, vượt qua Lạc huynh!”"),t("br"),t("br"),n._v("“Có ý tứ gì?”"),t("br"),t("br"),n._v("Lạc Thiên Nhai nghi ngờ ngẩng đầu."),t("br"),t("br"),n._v("“Chắc hẳn vừa rồi Lạc huynh cũng nhìn ra rồi, ta thi triển cùng ngươi thi triển đồng dạng, đều là không gian phong cấm chân giải!” Trương Huyền giải thích."),t("br"),t("br"),n._v("“Cái này...” Lạc Thiên Nhai sửng sốt, dường như lúc này mới phản ứng lại, nhíu nhíu mày: “Đúng a, ngươi làm sao biết Lạc gia phong cấm chân giải?”"),t("br"),t("br"),n._v("“Phong cấm chân giải, mặc dù là Lạc gia tiên tổ lĩnh ngộ, nhưng... Thật ra thì sớm nhất truyền thừa tại Khưu Ngô Cổ Thánh! Ta được đến qua Cổ Thánh truyền thừa, có thể thi triển ra phong cấm chân giải cũng không có gì!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("“Khưu Ngô Cổ Thánh?” Lạc Thiên Nhai khó tin."),t("br"),t("br"),n._v("“Đây là Cổ Thánh lưu lại di tích!”"),t("br"),t("br"),n._v("Trương Huyền cổ tay khẽ đảo, Lạc Thiên Nhai phía trước, xuất hiện một cái không lớn thủy tinh cầu, trong đó khí tức mờ ảo, cho người ta một loại không gian gấp cảm giác."),t("br"),t("br"),n._v("Lạc Thiên Nhai bàn tay tới vuốt ve, một lát sau, chấn động toàn thân, ánh mắt lộ ra vẻ hâm mộ: “Thật sự là Khưu Ngô Cổ Thánh lưu lại... Trương huynh số mệnh thật sự là nghịch thiên!”"),t("br"),t("br"),n._v("“Ta cũng là cơ duyên xảo hợp mới đến, các ngươi Lạc gia, sở dĩ có rất ít người lý giải không gian chân giải, cũng không phải là tộc nhân thiên tư không được, mà là lấy được truyền thừa, cũng không hoàn chỉnh.”"),t("br"),t("br"),n._v("Trương Huyền nhẹ nhàng cười một tiếng: “Toà này Khưu Ngô cung, ẩn chứa Cổ Thánh lưu lại bản đầy đủ Diễn Không thiên thư, ngày hôm nay liền đưa cho Lạc huynh, tin tưởng đặt ở trong tay ngươi, đặt ở Lạc gia, so trong tay của ta, phát huy ra càng lớn tác dụng!”"),t("br"),t("br"),n._v("Nhìn lấy lòng bàn tay xuất hiện thủy tinh cầu, Lạc Thiên Nhai tràn đầy khiếp sợ, vội vàng lắc đầu: “Cái này như thế nào làm...”"),t("br"),t("br"),n._v("Cổ Thánh truyền thừa, đại lục ai không muốn muốn?"),t("br"),t("br"),n._v("Tiện tay liền cho hắn, ân tình lớn, khó mà đánh giá."),t("br"),t("br"),n._v("Giấu ở một bên Lạc Càn Trinh cùng tất cả trưởng lão, cũng từng cái hai mặt nhìn nhau."),t("br"),t("br"),n._v("Lạc gia truyền thừa xác thực đến từ Khưu Ngô Cổ Thánh, nếu quả thật có thể được đến bản đầy đủ Diễn Không thiên thư, tộc nhân quả thực sẽ có nhanh chóng tiến bộ, coi như lĩnh ngộ không được phong cấm chân giải, cũng nhất định từng cái thực lực tăng nhiều."),t("br"),t("br"),n._v("Chỉ là... Hai nhà có thù, bọn họ lần này tới đánh mặt, đối phương không so đo những này, ngược lại cho lễ vật quý giá như vậy..."),t("br"),t("br"),n._v("Trong nháy mắt, mọi người có loại xấu hổ vô cùng cảm giác."),t("br"),t("br"),n._v("“Trước không vội chối từ, Lạc huynh nghe ta nói hết lời!”"),t("br"),t("br"),n._v("Hai tay chắp sau lưng, Trương Huyền vẻ mặt nghiêm túc: “Khổng miếu không gian chung quanh rung chuyển, bất cứ lúc nào cũng sẽ xuất thế, chắc hẳn Lạc huynh, đã biết!”"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Lạc Thiên Nhai gật đầu."),t("br"),t("br"),n._v("“Chư Tử bách gia xuất hiện khiêu chiến chúng ta hai nhà, mục đích rất rõ ràng, là muốn đoạt đi truyền thế thiên phù, lần này không thành công, nhưng dính dáng Khổng sư bảo tàng, chắc chắn sẽ không tuỳ tiện đến đây dừng tay, khẳng định sẽ còn lần nữa trở về. Hơn nữa, căn cứ mấy ngày nay quan sát, Dị Linh tộc nhân đã có cường giả đi tới đại lục, thậm chí vị kia Dị Linh chi Hoàng, cũng đều lặng lẽ lẻn vào!”"),t("br"),t("br"),n._v("Lông mày nâng lên, Trương Huyền nói: “Đại lục đã đến nguy cấp tồn vong thời khắc, một khi chiến tranh toàn diện bạo phát, đừng nói hai chúng ta đại gia tộc, liền xem như người của cả đại lục loại, đều chỉ sợ phải đối mặt to lớn kiếp nạn... Lúc này, chúng ta không nên đối chiến, mà hẳn là cùng nhau trông coi, cùng độ cửa ải khó! Nếu không, tùy ý người ngoài xâm nhập, sẽ chỉ làm người thân đau đớn, kẻ thù sung sướng!”"),t("br"),t("br"),n._v("Lạc Thiên Nhai lặng im."),t("br"),t("br"),n._v("“Tam đại gia tộc, có thể đứng sừng sững đại lục vài vạn năm không ngã, bằng chính là trợ giúp lẫn nhau, chung đối địch! Lần trước từ hôn sự tình, là ta Trương Huyền làm không đúng, nhưng ta cũng không phải cố ý gây ra, tình cảm việc này, không phải cập mạnh lấy, liền có thể hạnh phúc.”"),t("br"),t("br"),n._v("“Ta cùng Nhược Hi, sớm có biển thề phía trước, tùy tiện đồng ý tiểu công chúa, chẳng phải bội ước, tại thành tín không để ý? Ta thật sự là như vậy người, coi như cưới Thất Thất, cũng sẽ để nàng đau lòng, ngược lại càng có lỗi với Lạc gia. Những ngày này vừa nghĩ đến đây sự tình, ta liền ngủ tịch bất an, lo lắng... Cái này Khưu Ngô cung, cùng truyền thừa Diễn Không thiên thư, liền xem như xin lỗi, mong rằng Lạc huynh chớ nên chối từ, nếu không, ta thật sự không đất dung thân...”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("“Trương huynh đại nghĩa, đã như vậy, ta cũng liền không làm bộ làm tịch!”"),t("br"),t("br"),n._v("Dừng lại một chút, Lạc Thiên Nhai nhận lấy thủy tinh cầu, thu vào nhẫn."),t("br"),t("br"),n._v("“Hắn cũng không phải là cố ý...”"),t("br"),t("br"),n._v("“Cũng đúng, nếu như lúc ấy thật mạnh cưới tiểu công chúa, người này ngược lại bạc tình bạc nghĩa!”"),t("br"),t("br"),n._v("“Nhìn tới hắn không phải có ý định tổn hại ta Lạc gia mặt mũi!”"),t("br"),t("br"),n._v("“Chúng ta chỉ nghĩ mặt mũi, mà hắn nghĩ tới chính là nhân tộc, lòng dạ tuyệt không phải chúng ta có thể so sánh!”"),t("br"),t("br"),n._v("Ẩn thân Lạc gia mọi người, lần nữa lặng im, từng cái vẻ mặt thanh tú hồng."),t("br"),t("br"),n._v("Bọn họ một mực cân nhắc Lạc gia, trên thực tế cũng không đặt mình vào hoàn cảnh người khác vì đối phương nghĩ một hồi, nếu là thật bội ước, coi như cưới tiểu công chúa, lại có thể cho nàng hạnh phúc ư?"),t("br"),t("br"),n._v("Chưa hẳn đi!"),t("br"),t("br"),n._v("“Đa tạ Lạc huynh!”"),t("br"),t("br"),n._v("Trương Huyền khom người đến cùng."),t("br"),t("br"),n._v("“Trương huynh một phen tâm ý, ta trở về nhất định hỗ trợ giải thích!” Vẻ mặt nghiêm túc, Lạc Thiên Nhai trầm ngâm phút chốc, giống như là làm ra quyết định gì đó, cắn răng nói: “Trương huynh, ta thật ra thì còn sự tình muốn nhờ!”"),t("br"),t("br"),n._v("“Lạc huynh mời nói!”"),t("br"),t("br"),n._v("Sửng sốt một chút, Trương Huyền nhìn qua: “Phàm là có thể làm được, tất không chối từ!”"),t("br"),t("br"),n._v("“Ta có một kiện đại sự chưa hết, lần này rời khỏi, vô cùng có khả năng lại khó trở về, tuy là ta trở thành Lạc gia tộc trưởng thời gian không dài, nhưng cũng không muốn gia tộc này, bởi vậy chìm...”"),t("br"),t("br"),n._v("Lạc Thiên Nhai thần sắc ảm đạm, nói."),t("br"),t("br"),n._v("“Lại khó trở về? Lạc huynh là muốn đi nơi nào đó di tích, vẫn là... Muốn đi báo thù? Như cần Trương mỗ hỗ trợ, nói một tiếng...” Trương Huyền sửng sốt một chút, vội vàng ôm quyền."),t("br"),t("br"),n._v("“Trương huynh ý tốt, tại hạ tâm lĩnh, đây là chuyện riêng của ta, không tiện người ngoài nhúng tay, hơn nữa sớm đã báo hẳn phải chết ý chí, dù là bỏ mình, cũng cam tâm tình nguyện!”"),t("br"),t("br"),n._v("Lắc đầu, Lạc Thiên Nhai vẻ mặt nghiêm túc, lần nữa nhìn tới: “Ta yêu cầu Trương huynh cũng không phải là việc này!”"),t("br"),t("br"),n._v("Trương Huyền nghi ngờ."),t("br"),t("br"),n._v("Hít sâu một hơi, cổ tay khẽ đảo, Lạc Thiên Nhai lòng bàn tay một cái lệnh bài xuất hiện tại phía trước, yên tĩnh lơ lửng giữa không trung, chính là Lạc gia gia chủ lệnh."),t("br"),t("br"),n._v("“Ta hi vọng... Trương huynh, có thể tiếp nhận ta làm Lạc gia gia chủ, như vậy, cho dù chết, cũng an tâm!”"),t("br"),t("br"),n._v("“Làm gia chủ?” Trương Huyền thân thể chấn động, liên tục xua tay: “Cái này... Như thế nào có thể!”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1668"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1670"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);