(window.webpackJsonp=window.webpackJsonp||[]).push([[759],{1114:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-158-đen-luot-nguoi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-158-đen-luot-nguoi"}},[n._v("#")]),n._v(" Chương 158: Đến lượt ngươi")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v(" “Bất quá, các ngươi tiếp xúc thư họa thời gian rất ngắn, để cho các ngươi hiện tại đã bắt bút vẽ tranh xem như sát hạch, không những không hiệu quả gì, còn giống như sỉ nhục thư họa nghề nghiệp này, vì lẽ đó, ta cùng Nguyên Ngữ thương nghị một lần, nghĩ ra một cái sát hạch phương pháp.” "),t("br"),t("br"),n._v(" Hoàng Ngữ, Bạch Tốn tính toán đâu ra đấy tiếp xúc thư họa, cũng không tới thời gian một tháng, vậy thì để bọn họ chấp bút, có thể vẽ ra cái gì? Thật muốn làm như vậy, nhất định sẽ mất mặt, vì lẽ đó, Lục Trầm đại sư sát hạch, cũng không phải là để bọn họ vẽ tranh."),t("br"),t("br"),n._v(" Nói đến đây, hắn cười cợt, nói tiếp: “Vậy thì là... Do ta, Nguyên Ngữ, Trương Huyền tiểu huynh đệ vẽ tranh, các ngươi tới giám thưởng, bình luận, ai bình luận đúng trọng tâm nhất, ai liền lấy đi Mặc Hiên đồ!” "),t("br"),t("br"),n._v(" “Vẽ tranh? Hả?” "),t("br"),t("br"),n._v(" Đang ngồi uống trà, dự định lại đây góp đủ số Trương Huyền, nghe nói như thế, đánh cái lảo đảo, suýt chút nữa đem miệng đầy nước trà phun ông lão này một mặt."),t("br"),t("br"),n._v(" Ngươi đùa giỡn đi, ta chính là cái tới xem náo nhiệt, bảo ta vẽ tranh... Ngươi đây là thi bọn họ vẫn là thi ta?"),t("br"),t("br"),n._v(" Ta liền họa bút làm sao nắm cũng không biết, làm len sợi vẽ?"),t("br"),t("br"),n._v(" “Ta xem, ta liền không cần vẽ tranh đi...” Một mặt phiền muộn, Trương Huyền nói."),t("br"),t("br"),n._v(" “Không muốn cự tuyệt, lấy văn kết bạn, rất nhiều Văn hội, đều muốn viết thơ làm phú, hiếm thấy ngươi cùng Nguyên huynh đều tại, chúng ta cũng tới cái lấy vẽ giao lưu, cũng làm cho bọn tiểu bối này mở mang thư họa chân chính mị lực, làm không cẩn thận, bọn họ sẽ chân chính yêu thích, sau đó cũng trở thành một đời đại sư.” "),t("br"),t("br"),n._v(" Lục Trầm đại sư vuốt râu cười nói."),t("br"),t("br"),n._v(" Trương Huyền sắp khóc."),t("br"),t("br"),n._v(" Ngươi có hay không nghiêm túc hãy nghe ta nói?"),t("br"),t("br"),n._v(" Ý của ta, ta không muốn tham gia cái gì vẽ tranh, cũng không muốn vẽ tranh, ngươi ngược lại tốt, lấy vẽ giao lưu..."),t("br"),t("br"),n._v(" Giao lưu cái cây ớt lông a!"),t("br"),t("br"),n._v(" “Khặc khặc, thật không cần, hai vị vẽ tranh là được, ngươi xem ta ngày hôm nay cái gì đều không chuẩn bị...” "),t("br"),t("br"),n._v(" Trương Huyền không thể làm gì khác hơn là đem lại nói càng rõ ràng một ít."),t("br"),t("br"),n._v(" “Giấy và bút mực ta đều chuẩn bị kỹ càng, tiểu huynh đệ, ngươi cũng không cần câu nệ, liền coi nơi này là nhà của chính mình, tùy tiện vẽ tranh, cũng làm cho bọn họ mở mang kiến thức một chút!” Lục Trầm đại sư nói tiếp."),t("br"),t("br"),n._v(" đọc truyệｎ với http://truｙenc"),t("br"),n._v("uatui.net/ “Câu nệ? Kiến thức?” "),t("br"),t("br"),n._v(" Của ta trọng điểm căn bản không phải cái này có được hay không?"),t("br"),t("br"),n._v(" “Được rồi, a thành, đi chuẩn bị giấy và bút mực, Tiểu Ngữ, Bạch Tốn, chúng ta vẽ tranh thời điểm, các ngươi cố gắng nhìn, coi như học tập!” "),t("br"),t("br"),n._v(" Lục Trầm còn tưởng rằng hắn thật không tiện, cười xua tay."),t("br"),t("br"),n._v(" “Vâng!” Quản gia Thành Bá khoát tay chặn lại, mấy cái hầu gái liền đem ra ba bộ giấy và bút mực, đặt ở trước mặt."),t("br"),t("br"),n._v(" Trương Huyền lông mày nhảy nhảy."),t("br"),t("br"),n._v(" Bút chì vẽ tranh hắn đều không biết, còn bút lông..."),t("br"),t("br"),n._v(" Sớm biết ngày hôm nay sát hạch là như vậy, đánh chết cũng không lại đây."),t("br"),t("br"),n._v(" “Chúng ta lấy vẽ kết bạn, liền không quy định đề mục!” Chuẩn bị thỏa đáng, Lục Trầm đại sư cười khanh khách nhìn về phía Nguyên Ngữ đại sư: “Nguyên huynh, ngươi là khách mời, liền tới trước đi!” "),t("br"),t("br"),n._v(" “Tốt, ta liền bêu xấu!” "),t("br"),t("br"),n._v(" Nguyên Ngữ đại sư cười vuốt vuốt chòm râu, đứng ở cái bàn trước mặt, cầm lấy bút lông, hơi hơi trầm tư, liền bắt đầu vẽ tranh."),t("br"),t("br"),n._v(" Viết chữ như rồng bay phượng múa, vốn là hắn đầy mặt cùng tường, vừa bắt đầu vẽ tranh, khí chất nhất thời thay đổi, giống như một cái Cao Sơn Lưu Thủy khó tìm kiếm tri âm đại sư, để người kính nể."),t("br"),t("br"),n._v(" “Vẽ tranh chia làm bồn tầng thứ, phân biệt là lục thực, linh động, ý tồn, kinh hồng; Lục thực là chỉ ghi chép sự thực, vẽ tranh cùng hiện thực nhìn thấy cảnh vật, giống như đúc, tuy rằng vẽ giống y như thật, nhưng là tầng thấp nhất.” "),t("br"),t("br"),n._v(" Nguyên Ngữ đại sư bắt đầu vẽ tranh, Lục Trầm cười cho mọi người giải thích."),t("br"),t("br"),n._v(" “Linh động, là chỉ vẽ ra cảnh vật, không tiếp tục cứng nhắc, mà là ẩn chứa linh khí, để người nhìn chút tựu giống như sống giống như vậy, làm một ví dụ, vẽ một con cá, ngươi biết cảm thấy con cá này cùng sống, bất cứ lúc nào cũng sẽ theo vẽ trên giấy đi lại đi ra.” "),t("br"),t("br"),n._v(" “Cảnh giới thứ ba ý tồn, ý tồn bút trước, vẽ cố gắng ý tại! Loại cảnh giới này, còn chưa vẽ tranh, đầu tiên muốn có ý cảnh trong lòng, vẽ ra tranh vẽ mới có thể làm cho người cảm động, khiến cho người mê muội. Của ta  liền may mắn tiến vào loại cảnh giới này, vì lẽ đó một chút nhìn lại, như cùng ở tại trước mặt đem bức tranh mở ra, có loại thân ở trong đó cảm giác.” "),t("br"),t("br"),n._v(" “Ta cùng Nguyên huynh đều đạt đến loại cảnh giới này, số may, trùng hợp bên dưới, có lẽ liền có thể làm ra cảnh giới thứ tư tác phẩm hội họa. Cảnh giới thứ tư, kinh hồng, chỉ chính là kinh hồng tác phẩm hội họa, vẽ bên trong người, đồ vật, tự mang khí chất, đạt đến liền động vật, man thú đều không thể nhận biết đất bước, Tiểu Ngữ muốn Mặc Hiên đồ như vậy, Nguyên huynh Giang Điểu đồ cũng là như vậy.” "),t("br"),t("br"),n._v(" “Ồ!” "),t("br"),t("br"),n._v(" Trương Huyền gật đầu."),t("br"),t("br"),n._v(" Hắn còn tưởng rằng vẽ tranh, ai vẽ được giống, ai liền lợi hại, nghe được giải thích mới biết, vẽ được giống... Chỉ là cấp thấp nhất cảnh giới."),t("br"),t("br"),n._v(" Một bộ tác phẩm bên trong chỉ có nắm giữ bản thân tinh thần, ẩn chứa đặc biệt ý cảnh, mới tính được là chân chính danh họa."),t("br"),t("br"),n._v(" Trò chuyện bên trong, Nguyên Ngữ đại sư dưới ngòi bút hình ảnh từ từ mở ra."),t("br"),t("br"),n._v(" Là một bộ núi rừng chim tước đồ, yên tĩnh trong rừng cây, hai con chim nhỏ bay lượn, tuy rằng trên không trung không nhúc nhích, lại làm cho người ta một loại tiếng chim to rõ cảm giác, để người phảng phất trong nháy mắt đặt mình vào núi rừng, hoa thơm chim hót."),t("br"),t("br"),n._v(" “Lục đại sư, lẽ nào đây chính là ý tồn cảnh giới tranh vẽ?” "),t("br"),t("br"),n._v(" Hoàng Ngữ không nhịn được nói."),t("br"),t("br"),n._v(" Có thể làm cho người ta cái cảm giác này, nói rõ bức họa này đã ẩn chứa cực sâu ý cảnh, để người nhìn chút bên dưới liền khó có thể dứt bỏ."),t("br"),t("br"),n._v(" “Ừm!” Lục Trầm đại sư gật gật đầu: “Bức họa này xác thực đạt đến ý tồn cảnh giới, bất quá, cũng chỉ là vừa mới đạt đến, về phần tại sao nói như vậy, còn cần các ngươi đi giám thưởng, ai có thể chính xác trả lời đi ra, ai liền thắng lợi.” "),t("br"),t("br"),n._v(" “Vâng!” Hoàng Ngữ, Bạch Tốn đồng loạt đưa mắt tập trung đi qua."),t("br"),t("br"),n._v(" Thời gian không lâu, Nguyên Ngữ đại sư dừng lại bút."),t("br"),n._v(" Bộ này núi rừng chim tước đồ triệt để vẽ xong."),t("br"),t("br"),n._v(" “Ha ha, nhiều ngày không vẽ, bút pháp không thạo không ít, bêu xấu!” Đem bút lông thả xuống, Nguyên Ngữ đại sư cười cợt: “Lục Trầm, đến lượt ngươi!” "),t("br"),t("br"),n._v(" “Tốt!” Lục Trầm đại sư cũng không chối từ, vài bước đi tới trước bàn, bút lông di động, mặc thấu giấy lưng."),t("br"),t("br"),n._v(" Tuy rằng đều là cảnh giới thứ ba thư họa đại sư, rõ ràng Lục Trầm vẽ tranh càng thêm thuận buồm xuôi gió, cũng càng thêm mở ra."),t("br"),t("br"),n._v(" Của hắn vẽ là một mảnh nước sông, một chiếc thuyền con nổi vào trong đó, không có sóng lớn sóng biển, không có mưa to gió lớn, lại làm cho người ta một loại ngược gió tiến lên, cùng sóng lớn vật lộn cảm giác, người trên thuyền, tuy chỉ là lác đác hai bút, lại mang theo không sợ gian hiểm, dũng cảm tiến tới cương nghị, để người nhìn chút bên dưới nhiệt huyết sôi trào."),t("br"),t("br"),n._v(" “Vẫn là Lục lão đệ kỹ cao một bậc!” "),t("br"),t("br"),n._v(" Còn không vẽ xong, Nguyên Ngữ đại sư liền không nhịn được cảm khái."),t("br"),t("br"),n._v(" Hắn tuy rằng cũng am hiểu vẽ tranh, tiếng tăm cũng rất vang dội, nhưng cùng Lục Trầm so, vẫn là chênh lệch một đoạn dài."),t("br"),t("br"),n._v(" Núi rừng chim tước đồ không kém, ý cảnh cũng không sai, nhưng thật phải cẩn thận cân nhắc, cùng Lục Trầm bức họa này ý cảnh, còn kém không ít."),t("br"),t("br"),n._v(" Hắn chỉ là đem núi rừng yên tĩnh cùng huyên náo miêu tả đi ra, mà đối phương lại đem nội tâm tranh đấu đều triển lộ trong đó, trình độ cao không chỉ một bậc."),t("br"),t("br"),n._v(" “Thế nào? Có thể nhìn ra cái gì không?” "),t("br"),t("br"),n._v(" Cảm khái xong, Nguyên Ngữ đại sư cười khanh khách nhìn về phía Bạch Tốn, Hoàng Ngữ hai người."),t("br"),t("br"),n._v(" “Lục đại sư bộ này Giang Lưu đồ, ta có thể nhìn ra dùng ba loại vẽ tranh phương pháp, mười hai loại bút pháp, còn giống như có tám mươi năm trước nổi danh thư họa đại sư Trần Kiều cái bóng.” Hoàng Ngữ suy nghĩ một chút nói."),t("br"),t("br"),n._v(" Trần Kiều, Thiên Huyền Vương quốc nổi tiếng nữ thư họa đại sư, tên nổi như cồn, đầu bút lông nhẵn nhụi, am hiểu sơn thủy, đặc biệt là vẽ nước, có thể nói nhất tuyệt, được khen là Thiên Huyền Vương quốc ba trăm năm qua đệ nhất nhân."),t("br"),t("br"),n._v(" “Ta cũng nhìn ra rồi...” Bạch Tốn vội vã nói tiếp."),t("br"),t("br"),n._v(" “Ân, xem như là có chút nhãn lực!” "),t("br"),t("br"),n._v(" Nguyên Ngữ đại sư cười gật đầu."),t("br"),t("br"),n._v(" “Ba loại vẽ tranh phương pháp? Mười hai loại bút pháp?” "),t("br"),t("br"),n._v(" Một bên Trương Huyền tràn đầy không nói gì."),t("br"),t("br"),n._v(" Ngược lại dưới cái nhìn của hắn, vị này Lục Trầm đại sư, chính là nắm cái bút lông vẽ linh tinh, căn bản không nhìn ra cái gì đặc thù."),t("br"),t("br"),n._v(" Tuy rằng thông qua kim sắc thư tịch, đem sống lại tới nay xem qua thư, tất cả chuyển hóa thành bản thân tri thức, nhưng hầu như đều là liên quan với công pháp, võ kỹ cùng luyện đan, thư họa giống như thư tịch, cơ bản chưa từng xem, gần như một chữ cũng không biết."),t("br"),t("br"),n._v(" “Ha ha, Nguyên huynh khiêm tốn!” "),t("br"),t("br"),n._v(" Nói chuyện công phu, Lục Trầm đại sư cũng đem bộ này Giang Lưu tranh vẽ xong, nở nụ cười một tiếng, đi tới: “Ta là mấy ngày trước đây tại Bôn Mã giang bên trên du lịch, tích trữ thật nhiều ngày tâm tình, biểu lộ cảm xúc, mới có thể kỹ cao một bậc, Nguyên huynh cả ngày trị bệnh cứu người, không ta như thế thanh nhàn, nếu như giống như ta, thoải mái lòng dạ, say mê thư họa, chỉ sợ ta thật liền theo không kịp!” "),t("br"),t("br"),n._v(" “Vẽ tranh coi trọng thiên phú, ta là cảm thấy thiên phú không bằng ngươi, lúc này mới hướng đi y đường!” "),t("br"),t("br"),n._v(" Nguyên Ngữ đại sư lắc lắc đầu."),t("br"),t("br"),n._v(" “Được rồi, chúng ta hai lão liền không nên ở chỗ này lẫn nhau thổi phồng, muốn nói chân chính có thiên phú, còn muốn nói Trương Huyền tiểu huynh đệ, không đủ hai mươi, liền nắm giữ như vậy cao thâm họa đạo kiến giải, chắc hẳn tại vẽ tranh bên trên cũng khẳng định không so hai chúng ta lão già yếu.” "),t("br"),t("br"),n._v(" Lục Trầm đại sư cười lắc đầu, nhìn lại, làm cái “Mời” thủ thế: “Hiện tại hai chúng ta đều vẽ xong, Trương Huyền tiểu huynh đệ, ngươi tới đi!” "),t("br"),t("br"),n._v(" Rào rào."),t("br"),t("br"),n._v(" Tiếng nói của hắn vừa rơi xuống, ánh mắt của mọi người đồng loạt nhìn lại."),t("br"),t("br"),n._v(" Nguyên Ngữ đại sư cũng gật gù, trong mắt mang theo hiếu kỳ."),t("br"),t("br"),n._v(" Trương Huyền sự tình, hắn là nghe Lục Trầm nói, mặc dù đối với người sau mà nói... Không chút nào nghi vấn, nhưng khi thấy hắn còn trẻ như vậy, vẫn còn có chút không thể tin được."),t("br"),t("br"),n._v(" Thư họa một đạo, cùng võ công không giống nhau, không phải thiên phú cao là được, còn cần đối với nhân sinh, đối với sinh mạng nắm giữ rất nhiều cảm ngộ, mới có thể vượt lĩnh ngộ càng nhiều, coi như là hắn, cũng là trị liệu vô số bệnh nhân, có cảm ngộ mới, lúc này mới để thư họa tăng nhanh như gió, đạt đến hiện tại trình độ."),t("br"),t("br"),n._v(" Cái này Trương Huyền không đủ hai mươi, coi như trời sinh thích hợp thư họa, cũng nhiều nhất cơ sở vững chắc, đạt đến lục cảnh thật giới, muốn đạt đến linh động, ý tồn... E sợ còn cần rất nhiều mài giũa mới được."),t("br"),t("br"),n._v(" Đương nhiên, lời này hắn vẫn chưa nói ra, chung quy không quá lễ phép."),t("br"),t("br"),n._v(" Giờ khắc này, nghe được Lục Trầm đại sư để hắn vẽ tranh, không nhịn được muốn nhìn một chút, cái tên này đến cùng có thể đạt đến cái gì trình độ."),t("br"),t("br"),n._v(" Liền ngay cả Bạch Tốn, Hoàng Ngữ cũng trong mắt mang theo hưng phấn."),t("br"),t("br"),n._v(" Trương Huyền lần trước đại triển thần uy, nhìn ra trong bức tranh sai lầm, thiếu sót, chỉ điểm giang sơn, phóng khoáng tha hồ, để người kính phục không ngớt, bọn họ cũng rất muốn biết, vị này không so với bọn họ phần lớn ít Trương lão sư, đến cùng đạt đến cảnh giới cỡ nào, mới có thể làm cho Lục đại sư đều tâm phục khẩu phục."),t("br"),t("br"),n._v(" “Ta?” "),t("br"),t("br"),n._v(" Nhìn thấy mọi người tha thiết ánh mắt, Trương Huyền đầu lần thứ hai lớn lên."),t("br"),t("br"),n._v(" Vốn tưởng rằng đối phương chỉ là tùy tiện nói một chút, vẫn đúng là muốn bản thân vẽ vời a."),t("br"),t("br"),n._v(" Cái gì đều không biết làm sao vẽ?"),t("br"),t("br"),n._v(" Cũng không thể vẽ cái con gà con, lại thêm cái vòng sáng, liền xưng hô chim thần Phượng Hoàng đi."),t("br"),t("br"),n._v(" Thật muốn như vậy, hôm nay sợ rằng cũng không cần đi rồi, nhất định sẽ bị đánh chết tươi."),t("br"),t("br"),n._v(" “Cái này... Cái này... Ta có thể trước xem một hồi thư lại vẽ sao?” "),t("br"),t("br"),n._v(" Gãi đầu một cái, cố nén lúng túng, Trương Huyền nói."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-166"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-168"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);