(window.webpackJsonp=window.webpackJsonp||[]).push([[760],{1117:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1658-tam-toc-so-truong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1658-tam-toc-so-truong"}},[n._v("#")]),n._v(" Chương 1658: Tam tộc sở trường")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1658: Tam tộc sở trường"),t("br"),t("br"),n._v("“Để hắn làm gia chủ của chúng ta? Tộc trưởng điên rồi sao?”"),t("br"),t("br"),n._v("“Tuyệt đối không được...”"),t("br"),t("br"),n._v("Lạc Càn Trinh mấy người cũng tất cả đều giật nảy mình."),t("br"),t("br"),n._v("Bọn họ mặc dù là Trương Huyền lòng dạ cảm thấy bội phục, tha thứ trước đó từ hôn, nhưng cũng không có nghĩa là, có thể tiếp nhận hắn làm gia chủ."),t("br"),t("br"),n._v("“Trương huynh lĩnh ngộ không gian phong cấm chân giải, hơn nữa so ta đều muốn thuần thục, mấu chốt nhất là nhận được Khưu Ngô Cổ Thánh truyền thừa, Cổ Thánh đều công nhận ngươi, chúng ta Lạc gia cũng phải dứt bỏ môn hộ khác biệt!”"),t("br"),t("br"),n._v("Lạc Thiên Nhai nói."),t("br"),t("br"),n._v("“Nhưng...” Trương Huyền liên tục xua tay."),t("br"),t("br"),n._v("“Trương huynh, nếu như ta biết tin tức không giả, ngươi hẳn là vừa mới kế nhiệm Tưởng gia gia chủ a?” Lạc Thiên Nhai nhìn qua."),t("br"),t("br"),n._v("“Cái này... Đúng!” Trương Huyền gật đầu, cổ tay khẽ đảo, một cái lệnh bài xuất hiện trên không trung, chính là Tưởng gia gia chủ lệnh."),t("br"),t("br"),n._v("“Tưởng gia gia chủ?”"),t("br"),t("br"),n._v("“Tưởng gia làm sao sẽ lập hắn một ngoại nhân, làm gia chủ?”"),t("br"),t("br"),n._v("Lạc Càn Trinh đám người tất cả đều mở to hai mắt nhìn, khó có thể tin."),t("br"),t("br"),n._v("Trước mắt chuyện này, quá mức huyền ảo, đã vượt ra khỏi tưởng tượng."),t("br"),t("br"),t("br"),n._v("“Ngươi lĩnh ngộ linh hồn chân giải, Tưởng gia có thể lập ngươi làm gia chủ, ta Lạc gia đương nhiên cũng có loại này lòng dạ! Huống hồ, hiện tại tai nạn sắp tới, muốn để tam đại gia tộc, tốt hơn liên hợp thân mật hợp tác, mà không khúc mắc, đây tuyệt đối là biện pháp tốt nhất!”"),t("br"),t("br"),n._v("Lạc Thiên Nhai ôm quyền: “Mong rằng Trương huynh không muốn chối từ! Huống chi, ta nghe nói, ngươi tại Tưởng gia giảng bài, để vô số người đột phá, tại Trương gia giảng bài, cũng để cho vô số người tấn thăng, nếu như có thể trở thành Lạc gia gia chủ, hy vọng có thể không khác nhau đối đãi, để cho ta Lạc gia càng ngày càng mạnh!”"),t("br"),t("br"),n._v("Trương Huyền nói không ra lời."),t("br"),t("br"),n._v("Không chỉ hắn như vậy, dị thường Lạc gia mọi người cũng từng cái lần nữa lặng im."),t("br"),t("br"),n._v("Vị này Trương Huyền, bọn họ tuy là lòng có khúc mắc, nhưng... Người này giảng bài năng lực, quả thực khoáng tuyệt cổ kim, chỉ sợ cũng chỉ có năm đó Khổng sư mới có thể cùng so đấu nghĩ."),t("br"),t("br"),n._v("Không nói cái khác, hắn mấy cái học sinh, từng cái địa vị cùng thực lực, cũng có thể thấy được."),t("br"),t("br"),n._v("Như vậy danh sư, làm gia chủ, tộc nhân nhất định có thể đột nhiên tăng mạnh, tiến bộ rất nhanh."),t("br"),t("br"),n._v("Nếu là thật như vậy cự tuyệt, chỉ sợ bị Trương, tưởng hai nhà triệt để bỏ xa, còn muốn đuổi theo liền khó khăn."),t("br"),t("br"),n._v("“Trương huynh đồng ý, ta cũng tốt việc nghĩa chẳng từ nan rời khỏi, lại không nỗi lo về sau!” Cổ tay rung lên, Lạc gia gia chủ lệnh bay ra, Lạc Thiên Nhai thái độ chân thành."),t("br"),t("br"),n._v("“Cái này... Cũng được! Ta trước tiên có thể mang theo, đợi... Lạc gia có người có thể kế thừa, tự sẽ đem gia chủ truyền cho!”"),t("br"),t("br"),n._v("Dừng lại một chút, Trương Huyền lại không chối từ, nhận lấy lệnh bài."),t("br"),t("br"),n._v("“Đa tạ Trương huynh... Đã như vậy, cáo từ!”"),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Làm xong những này, Lạc Thiên Nhai lại không chần chừ, thân thể nhảy lên, thẳng tắp hướng lên bầu trời bay đi, thời gian nháy mắt biến mất ở trước mặt mọi người."),t("br"),t("br"),n._v("“Ai! Ta cũng không phải là ham mê quyền thế, nhưng... Tam đại gia tộc chỉ có ngưng tụ cùng một chỗ, mới có thể chống lại Chư Tử bách gia, chống lại Dị Linh tộc... Nếu không, cuối cùng sẽ bị diệt tộc, biến mất tại dòng sông lịch sử!”"),t("br"),t("br"),n._v("Thấy hắn rời khỏi, Trương Huyền than thở một tiếng, lắc đầu, một mặt phức tạp thân thể xoay một cái, cũng biến mất tại nguyên chỗ, hình như chưa hề xuất hiện qua đồng dạng."),t("br"),t("br"),n._v("Hai người toàn bộ rời khỏi, Lạc Càn Trinh đám người từ chỗ tiềm ẩn đi ra."),t("br"),t("br"),n._v("“Đại tộc trưởng...”"),t("br"),t("br"),n._v("Mọi người đồng loạt nhìn qua."),t("br"),t("br"),n._v("“Thiên Nhai nói rất đúng, muốn cho chúng ta Lạc gia, lần này đại loạn bên trong sừng sững không ngã, chỉ có thể dựa vào vị này Trương Huyền...” Lạc Càn Trinh ánh mắt phức tạp."),t("br"),t("br"),n._v("“Hắn chỉ là cái Thánh vực cửu trọng mà thôi...”"),t("br"),t("br"),n._v("Một vị trưởng lão không thuận theo."),t("br"),t("br"),n._v("“Thật sự là hắn chỉ là Thánh vực cửu trọng, nhưng... Hắn là Danh Sư đường Dương sư sư huynh! Là Thánh tử điện điện chủ, Chiến Sư đường chiến tử lão sư, Độc điện, Băng Nguyên cung, Viên gia, khải linh sư công hội... Đều lấy mệnh lệnh của hắn như Thiên Lôi sai đâu đánh đó, lại thêm Trương gia cùng Tưởng gia lời nói, đại lục cao nhất một nhóm thế lực, đều nắm ở trong tay... Nếu như Lạc gia bỏ lỡ cơ hội lần này, sợ thật muốn triệt để suy yếu, lại không trở về khả năng!”"),t("br"),t("br"),n._v("Lạc Càn Trinh nói."),t("br"),n._v("Đơn độc một cái Trương Huyền, quả thực không có nghĩa là cái gì, nhưng hắn thân phận thực sự quá nhiều, dạy dỗ học sinh, cũng từng cái khống chế một phương thế lực."),t("br"),t("br"),n._v("Danh Sư đường cũng chưa chắc có hắn lực hiệu triệu lớn."),t("br"),t("br"),n._v("Để loại này làm tộc trưởng, quả thực là biện pháp tốt nhất."),t("br"),t("br"),n._v("“Ngươi ý tứ, là... Thừa nhận hắn làm tộc trưởng?”"),t("br"),t("br"),n._v("Vị trưởng lão này mặt mũi đắng chát."),t("br"),t("br"),n._v("Chuyện này là sao..."),t("br"),t("br"),n._v("Chạy tới đánh mặt, kết quả, mặt không có đánh thành, nhận đối phương làm gia chủ..."),t("br"),t("br"),n._v("Càng nghĩ càng thấy đến tâm tắc."),t("br"),t("br"),n._v("“Tất cả vì gia tộc, vì tộc nhân!” Biết ý nghĩ của hắn, Lạc Càn Trinh an ủi một câu, nói tiếp: “Nhanh chút trở về đi, Thiên Nhai cũng đã đang chờ chúng ta...”"),t("br"),t("br"),n._v("Mọi người không chần chờ nữa, vội vã trở lại chỗ ở, quả nhiên thấy Lạc Thiên Nhai tọa trong phòng, vẻ mặt chán nản."),t("br"),t("br"),n._v("“Chư vị...”"),t("br"),t("br"),n._v("Thấy mọi người đi tới, Lạc Thiên Nhai chần chờ một chút, mở miệng."),t("br"),t("br"),n._v("“Tộc trưởng không cần nói, để Trương Huyền làm tộc trưởng, thống lĩnh tam đại gia tộc, quả thực là lựa chọn tốt nhất, chúng ta sẽ không cự tuyệt...” Lạc Càn Trinh ôm quyền."),t("br"),t("br"),n._v("“Đã các ngươi đã biết, ta liền không lại nói... Đây là Trương Huyền cho Khưu Ngô di tích, ở lại trong tộc, hảo hảo truyền thừa đi!”"),t("br"),t("br"),n._v("Thấy bọn họ đã biết những việc này, Lạc Thiên Nhai không nói thêm lời, đứng dậy: “Ta lần này rời khỏi, khả năng mãi mãi cũng sẽ không trở về, về sau muốn nghe theo Trương sư mệnh lệnh, để Lạc gia trở nên càng thêm cường đại!”"),t("br"),t("br"),n._v("Nói xong cũng không cần mọi người trả lời, thân thể nhảy lên, biến mất tại tất cả mọi người phía trước, không thấy hình bóng."),t("br"),t("br"),n._v("Thấy hắn rời khỏi, Lạc gia mọi người thất vọng mất mát, không biết nên nói cái gì."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Nhi tử trở về rồi sao?”"),t("br"),t("br"),n._v("Trương gia một ngôi đại điện, Mộng kiếm thánh trong phòng loạn chuyển, đôi mi thanh tú nhíu lên."),t("br"),t("br"),n._v("“Bẩm báo chủ mẫu, tộc trưởng vừa rồi đã trở về, có điều, lại đi nơi nào, cũng không rõ ràng!” Một vị trưởng lão nói."),t("br"),t("br"),n._v("“Tiểu tử này, Lạc gia người lần này tới, rõ ràng lấy muốn đánh mặt, hắn cũng bảo trì bình thản, chạy loạn khắp nơi...”"),t("br"),t("br"),n._v("Mộng kiếm thánh hừ một tiếng."),t("br"),t("br"),n._v("Lạc gia người, còn chưa tới đến trước đó liền huyên náo thanh thế lớn như vậy, rõ ràng tới làm loạn, làm người trong cuộc, dĩ nhiên thẳng đến không ở nhà... Cũng là say."),t("br"),t("br"),n._v("“Huyền nhi từ trước tới nay cơ trí, làm như vậy, khẳng định cũng có mục đích của hắn, dù sao, đối đãi Lạc gia bên trên, chúng ta có thiệt thòi trước...”"),t("br"),t("br"),n._v("Hưng kiếm thánh lắc đầu, nói: “Tránh né một chút cũng là nên, nếu không, hai bên gặp mặt xấu hổ!”"),t("br"),t("br"),n._v("“Tránh né không có gì, nhưng cũng không thể một mực không xử lý đi! Nếu ta nói, cái gì Lạc Nhược Hi, Lạc Thất Thất, một khối cưới thật tốt, cũng liền không có phiền toái nhiều như vậy...”"),t("br"),t("br"),n._v("Hừ một tiếng, Mộng kiếm thánh đang muốn tiếp tục nói chuyện, chỉ thấy một vị trưởng lão vội vã đi tới."),t("br"),t("br"),n._v("“Lão gia chủ, chủ mẫu...”"),t("br"),t("br"),n._v("Vừa vào cửa, trưởng lão là được thi lễ: “Lạc gia sự tình giải quyết...”"),t("br"),t("br"),n._v("Hai người đồng thời sững sờ, Hưng kiếm thánh vội vàng nhìn tới: “Giải quyết như thế nào?”"),t("br"),t("br"),n._v("Theo bọn hắn nghĩ, cái này căn bản là khó mà xử lý cục diện, trừ phi cùng vị kia Lạc Thiên Nhai giao đấu, làm sao còn không có nghe nói muốn so, cũng đã giải quyết xong?"),t("br"),t("br"),n._v("“Cái này... Lạc gia người...”"),t("br"),t("br"),n._v("Đi vào vị trưởng lão này một mặt kỳ lạ: “Nhận Trương Huyền tộc trưởng, làm gia chủ!”"),t("br"),t("br"),n._v("“Nhận Huyền nhi làm gia chủ?”"),t("br"),t("br"),n._v("Nhìn nhau, Hưng, Mộng kiếm thánh đồng thời bối rối."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1669"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1671"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);