(window.webpackJsonp=window.webpackJsonp||[]).push([[764],{1120:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1662-khong-su-tu-đuong-【-canh-thu-hai-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1662-khong-su-tu-đuong-【-canh-thu-hai-】"}},[n._v("#")]),n._v(" Chương 1662: Khổng sư từ đường 【 canh thứ hai 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1662: Khổng sư từ đường "),t("br"),t("br"),n._v("Trương Huyền không vĩ đại, có đôi khi cũng có chút ích kỷ, vừa mới bắt đầu khảo hạch danh sư mục đích, cũng không phải vì cứu nhân loại, ngược lại là vì thân phận, có thể nhìn càng nhiều thư tịch... Nhưng cũng không ảnh hưởng hắn làm ra loại này quyết định."),t("br"),t("br"),n._v("Nhân loại thật muốn bị diệt, mạnh hơn thì có ích lợi gì?"),t("br"),t("br"),n._v("Hắn đã không còn là trước đó cô độc, mà là có người nhà, có học sinh, có một sóng lớn, muốn thủ hộ người."),t("br"),t("br"),n._v("Trước đó, gắng sức để Lạc gia thừa nhận thân phận của hắn, cũng là vì đây."),t("br"),t("br"),n._v("Đã Dị Linh tộc nhân bất cứ lúc nào cũng sẽ xuất hiện, đương nhiên sẽ không chối từ."),t("br"),t("br"),n._v("“Ngươi có thể đồng ý quả thực quá tốt rồi...”"),t("br"),t("br"),n._v("Nghe hắn đồng ý, Nhậm Thanh Viễn đám người tất cả đều sững sờ, ngay sau đó mừng rỡ."),t("br"),t("br"),n._v("Vốn còn nghĩ, nên như thế nào khuyên can, không nghĩ tới, nhẹ nhàng như vậy đáp ứng."),t("br"),t("br"),n._v("“Ta hiện tại liền hạ mệnh lệnh, tam đại gia tộc, nghe theo Danh Sư đường điều khiển!”"),t("br"),t("br"),n._v("Trương Huyền mỉm cười."),t("br"),t("br"),n._v("Thấy hắn đồng ý, không có chút nào hư dữ ủy xà, Nhậm Thanh Viễn cũng không nói thêm lời, dặn dò mọi người một tiếng, rất nhiều cửu tinh danh sư, toàn bộ vội vã rời khỏi."),t("br"),t("br"),n._v("“Trương tộc trưởng tới Danh Sư đường tổng bộ là...”"),t("br"),t("br"),n._v("Có tam đại gia tộc trợ giúp, Dị Linh tộc nhân hẳn tạm thời có thể áp chế lại, Nhậm Thanh Viễn thở phào nhẹ nhõm, tràn đầy nghi ngờ nhìn qua."),t("br"),t("br"),n._v("Trước mắt vị thanh niên này, hắn vừa mới bắt đầu nghe được tên thời điểm, bất quá là giả mạo Dương sư học sinh tiểu nhân vật, hiện tại, đã trở thành có thể cùng hắn sánh vai, thậm chí vượt xa quá hắn đại nhân vật."),t("br"),t("br"),n._v("Ngắn ngủi một tháng không đến thời gian, đại lục biến động bất ngờ, hầu như hạch tâm đều ở trên người hắn, chỉ cần không vẫn lạc, vượt qua Dương sư, cũng chưa biết chừng."),t("br"),t("br"),n._v("“Lần này tới, là có chuyện muốn nhờ...” Trương Huyền ôm quyền: “Ta muốn đi một chuyến... Khổng sư từ đường!”"),t("br"),t("br"),n._v("Tới thời điểm, Lạc Nhược Hi cùng hắn nói, nếu như đột phá Đại Thánh thượng phẩm phương pháp, tại Danh Sư đường chuyển biến xấu, nhất định ở đây."),t("br"),t("br"),n._v("Khổng sư từ đường, là hậu thế vì tưởng niệm Khổng sư công lao, mà lưu lại hội trường tưởng niệm, trong đó có hắn lưu lại thân bút chữ viết, quần áo các nhiều loại trân quý đồ vật."),t("br"),t("br"),n._v("“Còn tưởng rằng là chuyện gì, đạt tới cửu tinh danh sư, đều muốn tiến vào bên trong tham quan... Trương tộc trưởng đương nhiên cũng có tư cách, ta vậy thì mang ngươi tới!” Còn tưởng rằng chuyện gì, nghe được là cái này, Nhậm Thanh Viễn cười cười, nói."),t("br"),t("br"),n._v("“Không nhọc Nhậm sư dẫn đường, ta muốn mang Lạc Nhược Hi đi vào chung, tự mình tham quan, không biết có thể?” Trương Huyền nói."),t("br"),t("br"),n._v("Đột phá Đại Thánh công pháp, nhất định mười phần bí ẩn, đơn thuần tham quan, lại thêm có người đi cùng, muốn tìm được rất khó, chỉ có thể đơn độc hành động."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Nhíu nhíu mày, Nhậm Thanh Viễn sau một lúc lâu, nói: “Dựa theo tình huống bình thường, đi vào Khổng sư từ đường, nhất định phải do người đi cùng... Bất quá, Trương tộc trưởng thân là cửu tinh danh sư, càng làm cho tộc nhân trấn thủ Địa quật, công lao lớn, ta cũng khó có thể bằng được... Như vậy đi, cho ngươi một ngày thời gian, tận lực không muốn đợi đến quá lâu!”"),t("br"),t("br"),n._v("“Đa tạ!”"),t("br"),t("br"),n._v("Ánh mắt sáng lên, Trương Huyền vội vàng ôm quyền."),t("br"),t("br"),n._v("Khổng sư từ đường, nắm giữ hắn lưu lại thân bút cùng đồ vật, đối tu luyện có cực lớn trợ giúp, đi tham quan người, có thể có cảm giác ngộ, tu vi tiến bộ, bởi vậy, đợi một canh giờ, có khối người... Nhưng, đơn độc ở trong đó đợi một ngày, chưa từng nghe nói qua."),t("br"),t("br"),n._v("Nhậm Thanh Viễn có thể đưa ra như vậy ước định, quả thực xem như tận lực."),t("br"),t("br"),n._v("“Ta hiện tại liền đi qua!”"),t("br"),t("br"),n._v("Nhận được đối phương đồng ý, hỏi rõ ràng Khổng sư từ đường vị trí cụ thể, Trương Huyền đi ra đại điện, tìm tới Lạc Nhược Hi, hai người cùng nhau đi tới."),t("br"),t("br"),n._v("Khổng sư từ đường, là Khổng sư sau khi mất tích, thân truyền đệ tử Tử Uyên Cổ Thánh tạo dựng, lưu lại không ít bản thảo, quần áo, cùng đã dùng qua đồ vật, vũ khí các loại, chủ yếu là vì để cho hậu nhân cảm nhận được, vị này vạn thế chi sư tinh thần, trải nghiệm hắn vĩ đại."),t("br"),t("br"),n._v("đọc ngantruyen.com"),t("br"),n._v("Bảo vật trong đó, cũng không phải là tấn công hình, cũng không có quá lớn uy lực, lại có thể cho người một loại gợi ý, cảm xúc rất nhiều."),t("br"),t("br"),n._v("Đi tại trong đường, nhìn thấy Khổng sư cùng Dị Linh tộc nhân đối chiến, lưu lại huyết y, cùng bị nhốt Trần Thái, lưu lại tuyệt bút thư, Trương Huyền thật lâu không nói nên lời."),t("br"),t("br"),n._v("Cường đại như cùng Khổng sư, năm đó đối chiến Dị Linh tộc nhân lúc, đã từng cửu tử nhất sinh, bỏ ra vô số gian khổ."),t("br"),n._v(" “Đây là Khổng sư năm đó đột phá Kiến Thần Bất Hoại lúc, lưu lại Phong Thánh đài, nếu có công pháp, khẳng định chính là ở đây!”"),t("br"),t("br"),n._v("Dọc theo gian phòng đi một hồi, Lạc Nhược Hi chỉ về phía trước."),t("br"),t("br"),n._v("Trương Huyền vội vàng nhìn lại, quả nhiên thấy cách đó không xa trưng bày lấy một cái hình tròn bệ đá, phía trên tỏa ra ánh sáng lung linh, tựa như ngọc thạch."),t("br"),t("br"),n._v("“Đây là... Bị thiên hỏa đốt đi ra?”"),t("br"),t("br"),n._v("Nhận ra được, lông mày nhịn không được giương lên."),t("br"),t("br"),n._v("Cái này bệ đá chất liệu, thoạt nhìn cũng chỉ là bình thường Thanh Ngọc thạch, nhưng mà chẳng biết tại sao, liền thành một khối, cho người ta một loại óng ánh long lanh cảm giác, hình như một khối không tì vết mỹ ngọc."),t("br"),t("br"),n._v("Có thể để cho một khối đá bình thường, xuất hiện loại tình huống này, chỉ sợ chỉ có thiên hỏa thiêu đốt, mới có thể làm đến."),t("br"),t("br"),n._v("Mấu chốt nhất là, hắn vu hồn đi qua thiên hỏa rèn luyện, biết cái thiên kiếp này đáng sợ, có thể từ nơi này trên tảng đá, tìm tới cộng minh."),t("br"),t("br"),n._v("“Là thiên hỏa uy lực!”"),t("br"),t("br"),n._v("Lạc Nhược Hi gật gật đầu."),t("br"),t("br"),n._v("Xác nhận xuống, Trương Huyền cũng không nhiều hỏi, Minh Lý Chi Nhãn chiếu rọi, dọc theo đá quay một vòng, ngay sau đó mày nhăn lại."),t("br"),t("br"),n._v("Thiên hỏa như thế nào thiêu đốt, để Thanh Ngọc thạch biến thành như vậy, hắn đã hiểu rõ, nhưng... Khổng sư như thế nào đột phá đến Thánh vực cửu trọng, hơn nữa đưa tới thiên hỏa kiếp, nhưng là không hiểu ra sao, không có phát hiện bất kỳ dấu vết để lại."),t("br"),t("br"),n._v("“Nếu như... Nơi này thật có công pháp, Danh Sư đường nhiều như vậy lợi hại danh sư đều đến xem qua tảng đá kia, có bí mật cũng bị người khác phát hiện đi...”"),t("br"),t("br"),n._v("Nghi ngờ nhìn về phía nữ hài."),t("br"),t("br"),n._v("Danh sư, từ trước tới nay lấy nhãn lực lấy gọi, hắn Minh Lý Chi Nhãn, cứ việc không sai, có thể... Thánh tử điện, cũng vẫn là có tiếp cận mười vị cường giả nắm giữ."),t("br"),t("br"),n._v("Đã nhiều cường giả như vậy đều sang đây xem qua, không phát hiện được, hắn tới lại có thể có gì phát hiện?"),t("br"),t("br"),n._v("“Đột phá Thánh vực lúc, Khổng sư lưu lại Phong Thánh đài, ngươi mang theo sao?” Lạc Nhược Hi cũng không giải thích, mà là quay đầu hỏi."),t("br"),t("br"),n._v("“Mang theo!”"),t("br"),t("br"),n._v("Sửng sốt một chút, Trương Huyền cổ tay khẽ đảo, một cái bệ đá xuất hiện tại phía trước."),t("br"),t("br"),n._v("Đem Khưu Ngô cung thủy tinh cầu cho Lạc Càn Trinh trước đó, hắn liền đem thứ này, thu vào trữ vật giới chỉ, dính dáng Khổng sư, không nguyện ý bị người Lạc gia biết."),t("br"),t("br"),n._v("“Ngươi thử một chút dùng Phong Thánh giải bên trong chân khí, thôi thúc cái này bệ đá!”"),t("br"),t("br"),n._v("Lạc Nhược Hi gật đầu."),t("br"),t("br"),n._v("Tuy là không biết đối phương ý tứ, Trương Huyền vẫn gật đầu, hít sâu một hơi, chân khí trong cơ thể điên cuồng tuôn ra."),t("br"),t("br"),n._v("Xì xì xì!"),t("br"),t("br"),n._v("Chân khí rơi vào Phong Thánh đài bên trên, người sau lập tức thả ra hào quang bảy màu, chậm rãi bay lên, trong chốc lát, cùng cách đó không xa thiên hỏa thiêu đốt qua bệ đá, dung hợp lẫn nhau."),t("br"),t("br"),n._v("Thời gian qua một lát, không phân khác biệt."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trương Huyền ngây người."),t("br"),t("br"),n._v("Hai cái bệ đá có thể dung hợp, đây là trước đó, không dám tưởng tượng."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Đang muốn hỏi thăm, đến cùng chuyện gì xảy ra, đột nhiên, hào quang lóe lên, trên bệ đá ánh sáng chiếu rọi, một cái hư ảo bóng đen, xuất hiện ở phía trên, khoanh chân lơ lửng, hai mắt nhắm nghiền."),t("br"),t("br"),n._v("“Đây là Khổng sư đột phá Đại Thánh lúc lưu lại ảnh tướng, phương pháp đột phá, ngay tại trong đó!”"),t("br"),t("br"),n._v("Lạc Nhược Hi thanh âm vội vã vang lên."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1673"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1675"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);