(window.webpackJsonp=window.webpackJsonp||[]).push([[77],{431:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1046-loi-nguyen-chau"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1046-loi-nguyen-chau"}},[n._v("#")]),n._v(" Chương 1046: Lôi nguyên châu")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1046: Lôi nguyên châu"),t("br"),t("br"),n._v("Dựa theo bình thường đạo lý, lôi điện trở nên như vậy dày đặc, Ngô sư đám người cho dù toàn thịnh kỳ, đều không thể rời khỏi, lúc này lại hư không tiêu thất, chỉ có thể nói rõ một vấn đề, vị kia Thạch Diệp Vương lại trở về, hơn nữa đối bọn hắn tiến hành công kích!"),t("br"),t("br"),n._v("“Không đúng... Kim Nguyên đỉnh mặc dù thoạt nhìn không đáng tin cậy, trên thực tế sức chiến đấu nhưng không để coi nhẹ, chỉ cần không đạt tới Thánh vực ngũ trọng Xuất Khiếu cảnh, muốn vượt qua... Gần như không có khả năng, từ nó bảo hộ, sẽ không có vấn đề quá lớn!”"),t("br"),t("br"),n._v("Ý nghĩ này tại trong óc xoay một cái, liền bị hắn bác bỏ: “Hơn nữa, ta đi thời gian cũng không dài, cho dù đánh không lại, kiên trì một đoạn thời gian còn có thể làm được, lại nói... Như vậy cuồng bạo lôi đình, cho dù vị kia Thạch Diệp Vương lợi hại hơn nữa, khẳng định cũng phòng ngự không được. Bản thân bảo vệ tính mạng cũng thành vấn đề... Lại thế nào có nhàn hạ, tìm Ngô sư đám người phiền phức!”"),t("br"),t("br"),n._v("Kim Nguyên đỉnh thân là trung phẩm Thánh khí, mặc dù tại không có người khởi động dưới tình huống, sẽ chỉ mạnh mẽ đâm tới, có thể thi triển ra sức chiến đấu có hạn, nhưng từ nó bảo hộ, cho dù cái kia Thạch Diệp Vương muốn động thủ, cũng chưa chắc có thể thành công!"),t("br"),t("br"),n._v("Huống chi lôi đình như vậy thô bạo, hắn đều chỉ có thể bảo vệ tính mạng, muốn ở chỗ này giết người, tuyệt đối không thể."),t("br"),t("br"),n._v("“Ừm?”"),t("br"),t("br"),n._v("Phỏng đoán khả năng cũng không phải là Thạch Diệp Vương trở về, Trương Huyền thở phào nhẹ nhõm, hướng trước mắt phế tích nhìn lại."),t("br"),t("br"),n._v("Đã không phải gặp phải vây công, cái kia chính là giấu rồi."),t("br"),t("br"),n._v("Nhìn kỹ, quả nhiên để hắn phát hiện không đúng, nhịn không được nhẹ nhàng cười một tiếng."),t("br"),t("br"),n._v("Mặt đất bùn đất đều là mới, trước đó thành phố mặc dù toàn bộ đổ sụp, nhưng lại đều tích tụ tại mới quê mùa phía trên, nếu như đoán không sai, Ngô sư đám người, hẳn là móc lừa bịp giấu ở lòng đất."),t("br"),t("br"),n._v("Mặt đất trở lên, nhân loại bởi vì thể chất đặc thù sẽ thu hút lôi điện, mà chui vào mặt đất, liền sẽ an toàn không ít, quả thực thực tế rất tốt phương pháp."),t("br"),t("br"),n._v("Vù vù!"),t("br"),t("br"),n._v("Đem thân thể theo trữ vật giới chỉ thả ra, Vu hồn tiến vào bên trong, trên thân chân khí chấn động, đẩy ra bùn đất, trực tiếp chui vào."),t("br"),t("br"),n._v("Đi vào bùn đất, lôi điện công kích xác thực giảm bớt không ít, một mực trầm xuống bảy, tám mét, quả nhiên thấy một cái cực lớn lô đỉnh, giấu ở trong đó."),t("br"),t("br"),n._v("Chính là Kim Nguyên đỉnh!"),t("br"),t("br"),n._v("Lúc này Kim Nguyên đỉnh thể tích to lớn, đường kính chừng hai, ba mươi mét, bên ngoài một người không có, xem ra đều bị nó giấu ở trong đó."),t("br"),t("br"),n._v("“Ngươi bị thương?”"),t("br"),t("br"),n._v("Đang muốn vén lên nắp đỉnh, nhìn mọi người một cái như thế nào, Trương Huyền liền phát hiện trước mắt Kim Nguyên đỉnh, khí tức yếu ớt, hiển nhiên bị thương không nhẹ."),t("br"),t("br"),n._v("“Thiếu chủ, ngươi đã đến...”"),t("br"),t("br"),n._v("Thấy hắn đi tới, Kim Nguyên đỉnh trả lời, thanh âm có vẻ uể oải: “Bọn họ đều vô sự...”"),t("br"),t("br"),n._v("“Ngươi chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Trương Huyền cau mày."),t("br"),t("br"),n._v("“Lôi đình trở nên hung mãnh, ta muốn cứu người, hình thể trở nên quá lớn, bị lôi điện công kích... Hiện tại chỉ muốn nghỉ ngơi...”"),t("br"),t("br"),n._v("Kim Nguyên đỉnh quơ quơ."),t("br"),t("br"),n._v("Trương Huyền nắm đấm xiết chặt."),t("br"),t("br"),n._v("Lôi đình như vậy dày đặc, Kim Nguyên đỉnh lại biến thành đường kính hai, ba mươi mét, tự nhiên nhận lấy vô cùng mãnh liệt công kích."),t("br"),t("br"),n._v("Làm trung phẩm Thánh khí, lôi đình công kích sẽ không tổn hại bao nhiêu, nhưng... Trong đó linh tính, cũng có chút chống lại không được."),t("br"),t("br"),n._v("Lôi điện vốn đối linh hồn liền có cực lớn năng lực công kích."),t("br"),t("br"),n._v("Bởi vậy, Kim Nguyên đỉnh mặc dù bảo vệ mọi người, bản thân linh tính nhưng nhận lấy xao động, xem ra bất cứ lúc nào cũng sẽ rơi vào hôn mê."),t("br"),t("br"),n._v("“Ta truyền thụ cho ngươi một bộ pháp quyết, ngươi trước tu luyện... Tuyệt đối không nên ngủ mê mệt!”"),t("br"),t("br"),n._v("Biết Kim Nguyên đỉnh linh tính, một khi ngủ mê mệt vô cùng có khả năng tử vong, Trương Huyền tràn đầy cuống cuồng, tinh thần khẽ động, một bộ công pháp truyền lại đi qua."),t("br"),t("br"),n._v("Chính là linh hồn rèn luyện pháp quyết, mặc dù không biết đối linh tính có hữu dụng hay không, giờ phút này nhưng cũng không có biện pháp khác."),t("br"),t("br"),n._v("Cái tên này bình thường tùy tiện, độ trung tâm lại là không thể nghi ngờ, lần này càng là vì cứu người mới như vậy, tuyệt không thể để hắn xảy ra chuyện."),t("br"),t("br"),n._v("“Tốt... Ta lúc rảnh rỗi tu luyện... Thiếu chủ, ta mặc dù sợ chết, lại không phải kẻ hèn nhát...”"),t("br"),t("br"),n._v("Kim Nguyên đỉnh thanh âm càng ngày càng nhỏ, dần dần không có động tĩnh."),t("br"),t("br"),n._v("“Đỉnh Đỉnh...”"),t("br"),t("br"),n._v("Vội vàng hô một tiếng, phát hiện nó đã rơi vào trạng thái ngủ say."),t("br"),t("br"),n._v("Đối phương đã sớm không chịu nổi, chỉ là vì hướng mình khai báo, mới một mực kiên trì."),t("br"),t("br"),n._v("“Ngươi không phải kẻ hèn nhát, mà là anh hùng...” Mắt sáng lên, Trương Huyền răng cắn chặt: “Yên tâm đi, ta nhất định sẽ đưa ngươi cứu tỉnh...”"),t("br"),t("br"),n._v("Cứ việc cái tên này chỉ là cái vũ khí, hơn nữa một mực rất sợ chết, nhưng ở trái phải rõ ràng trước mặt, vẫn là dứt khoát quyết nhiên lựa chọn bảo hộ mọi người."),t("br"),t("br"),n._v("Bản thân đáp ứng nó, muốn cho hắn đạt tới Xuất Khiếu cảnh, lại thêm đáp ứng mang theo đối phương một đường tiến lên, hiện tại nó vì cứu người hôn mê, vô luận như thế nào đều muốn đem hắn cứu tỉnh, hoàn thành nó biến mạnh nguyện vọng."),t("br"),t("br"),n._v("Trong lòng có quyết định, vén lên nắp lò, nhảy vào trong đó."),t("br"),t("br"),n._v("“Trương sư, ngươi trở về...”"),t("br"),t("br"),n._v("Thấy hắn xuất hiện, ẩn thân trong đó Ngô sư đám người đồng thời đứng dậy."),t("br"),t("br"),n._v("Có Kim Nguyên đỉnh bảo hộ, bọn họ cũng không chịu đến bất cứ thương tổn gì, ngược lại trải qua cái này hồi lâu điều chỉnh, thương thế chuyển biến tốt đẹp, khôi phục thực lực không ít."),t("br"),t("br"),n._v("“Ừm, đây là có chuyện gì? Trước đó lôi điện không phải rất ổn định ư? Làm sao đột nhiên trở nên như vậy cuồng bạo?”"),t("br"),t("br"),n._v("Trương Huyền nhịn không được đến xem qua."),t("br"),t("br"),n._v("Hắn đi tìm Lạc Nhược Hi, không biết bên trong chuyện gì xảy ra, mọi người thân ở lôi đình trong hải dương, nên biết rõ ràng hơn một chút."),t("br"),t("br"),n._v("“Có lẽ cùng... Ngươi nói vị kia Thạch Diệp Vương liên quan đến! Hắn đi tìm cái gọi là bảo vật, có khả năng đụng vào cái gì, mới để cho lôi điện trở nên cường đại như vậy!”"),t("br"),t("br"),n._v("Ngô sư nói."),t("br"),t("br"),n._v("Vừa rồi bọn họ ở bên trong liền suy đoán, mọi người nhất trí cảm thấy, có thể là nguyên nhân này."),t("br"),n._v("Nếu không, khổng lồ như thế lôi đình chi hải, cũng không có khả năng thay đổi bất thường, thoáng cái hạ xuống nhiều như vậy lôi điện."),t("br"),t("br"),n._v("“Vô cùng có khả năng...” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Cũng đúng!"),t("br"),t("br"),n._v("Trước đó bọn họ cũng đã nói, vị kia Thánh vực tứ trọng Thạch Diệp Vương, đi tìm cái gì bảo vật, kết quả, thời gian không dài, liền xuất hiện loại tình huống này... Cả hai tất nhiên có liên hệ gì."),t("br"),t("br"),n._v("“Thạch Diệp Vương trước đó hướng phương hướng nào đi?”"),t("br"),t("br"),n._v("Trương Huyền híp mắt lại."),t("br"),t("br"),n._v("Nếu không phải cái tên này, Kim Nguyên đỉnh khả năng liền sẽ không ngủ say, vô luận như thế nào đều muốn báo thù!"),t("br"),t("br"),n._v("Lại nói, bất kể bảo vật gì, thật để cho đối phương nhận được, nhân tộc tất nhiên đối mặt cực lớn nguy hiểm, liều đi tính mạng đều muốn ngăn cản."),t("br"),t("br"),n._v("“Hướng tòa thành cổ này bên trong đi...”"),t("br"),t("br"),n._v("Ngô sư chỉ chỉ phương hướng."),t("br"),t("br"),n._v("Bọn họ vị trí, là lôi đình chi hải ở giữa nhất một tòa cũ nát cổ thành, Thạch Diệp Vương đi Phương Chính là cổ thành nơi trung tâm nhất."),t("br"),t("br"),n._v("“Ta đi qua nhìn một chút, các ngươi tiếp tục đợi ở chỗ này tu luyện dưỡng thương...” Trương Huyền khoát tay áo."),t("br"),t("br"),n._v("Thật ra thì phương pháp an toàn nhất chính là đem mọi người thu sạch tiến Thiên Nghĩ Phong sào."),t("br"),t("br"),n._v("Làm như vậy, mặc dù bảo vệ mọi người, nhưng cũng sẽ phơi bày tổ ong bí mật, lại nói, tất cả mọi người là tới thử luyện, một mực để hắn cất giấu, khẳng định cũng sẽ không bằng lòng."),t("br"),t("br"),n._v("Đối rất nhiều danh sư cũng là một loại không tôn trọng."),t("br"),t("br"),n._v("“Trương sư, vị kia Thạch Diệp Vương cực mạnh, ta và ngươi cùng đi, hơn nữa, ta có một loại thủ pháp đặc biệt, có thể theo dõi Dị Linh tộc nhân...” Phùng Huân đứng lên."),t("br"),t("br"),n._v("Hắn chịu đến ma âm xâm nhập, chỉ là lực lượng tiêu hao sạch sẽ, cùng Dị Linh tộc nhân thời điểm chiến đấu, bị Ngô sư đám người bảo hộ, cũng không chịu đến quá nghiêm trọng thương, lúc này điều chỉnh một đoạn thời gian, đã hoàn toàn khôi phục, khí tức như rồng, mang theo nồng đậm chiến ý."),t("br"),t("br"),n._v("“Tốt!” Thấy hắn thân thể hoàn hảo, Trương Huyền cũng không nhiều lời, nhẹ gật đầu."),t("br"),t("br"),n._v("Chiến sư, coi trọng chính là dũng cảm tiến tới, trăm chết dứt khoát, nếu để cho thứ nhất thẳng trốn tránh, đối với hắn lòng tin đều là một loại tiêu diệt."),t("br"),t("br"),n._v("Lại nói, Phùng Huân mặc dù cùng hắn giao đấu, một mực bị đánh, đó là bởi vì giảm thấp xuống tu vi, toàn bộ tu vi thi triển lời nói, Thánh vực tam trọng đỉnh phong, cho dù gặp được Thạch Diệp Vương cũng có thể một trận chiến!"),t("br"),t("br"),n._v("Chiến sư vượt cấp chiến đấu, tuyệt đối không phải giả."),t("br"),t("br"),n._v("Huống chi còn có theo dõi Dị Linh tộc nhân phương pháp."),t("br"),t("br"),n._v("Minh Lý Chi Nhãn cứ việc cũng được, nhưng ở lôi điện oanh kích bên dưới, rất nhiều dấu vết đã biến mất, cùng hắn tìm phiền phức, còn không bằng đem hắn mang theo, vừa vặn cũng mở mang kiến thức một chút, Chiến sư thủ đoạn."),t("br"),t("br"),n._v("Những người khác mọi người cũng có muốn tham dự, đều bị Trương Huyền từng cái cự tuyệt."),t("br"),t("br"),n._v("Vừa đến, thực lực của bọn hắn đều không có hoàn toàn khôi phục, thứ hai, Ngô sư mấy người cũng cần chú ý, không thể dễ dàng rời khỏi, nếu không, thật muốn bọn họ không tìm được Thạch Diệp Vương đám người, ngược lại bị người sau tìm được trước nơi này, không có Kim Nguyên đỉnh bảo hộ, vẫn là rất nguy hiểm."),t("br"),t("br"),n._v("Biết lo lắng của hắn, những người khác không nói thêm lời, đành phải vùi đầu tu luyện, mau chóng khôi phục."),t("br"),t("br"),n._v("Trương Huyền thì mang theo Phùng Huân theo trong đất bùn chui ra, lần nữa trở về mặt đất."),t("br"),t("br"),n._v("Lôi đình so vừa rồi dường như ít một chút, cho dù không cần mượn nhờ Vu hồn, cũng có thể tránh né, hai người thở phào nhẹ nhõm, trực tiếp hướng nội thành bay lượn mà đi."),t("br"),t("br"),n._v("Càng hướng phía trong đi, lôi điện càng ít, hình như cái này cổ xưa thành thị, dường như có một loại đặc thù lực lượng, che đậy lôi đình đồng dạng."),t("br"),t("br"),n._v("Mặc dù lôi đình ít, nhưng mà vị kia Thạch Diệp Vương không biết dùng thủ đoạn gì, đem dấu vết của mình lưu lại che đậy, đi một hồi, liền xem như Trương Huyền, đều có chút tìm không thấy phương hướng."),t("br"),t("br"),n._v("“Ta dò xét thoáng cái...”"),t("br"),t("br"),n._v("Phùng Huân ngừng lại, cổ tay khẽ đảo, một cái đặc thù pháp bảo xuất hiện tại lòng bàn tay, cùng la bàn có chút tương tự, có thể chỉ rõ phương hướng, cắn nát ngón tay ở phía trên một chút."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Pháp bảo một tiếng vang giòn, phía trên kim đồng hồ chỉ ra một cái phương hướng."),t("br"),t("br"),n._v("“Hướng cái hướng kia đi...”"),t("br"),t("br"),n._v("Phùng Huân nhẹ gật đầu."),t("br"),t("br"),n._v("“Đây là cái gì? Có thể nhận ra Dị Linh tộc nhân đi qua đường đi?”"),t("br"),t("br"),n._v("Trương Huyền nhìn kỳ quái, nhịn không được nói."),t("br"),t("br"),n._v("Thứ này hắn còn là lần đầu tiên thấy."),t("br"),t("br"),n._v("“Đây là Vô Cấu kính, đối máu tươi, chém giết vô cùng mẫn cảm, Dị Linh tộc nhân tu luyện chém giết chân khí, chỉ cần đi qua, tất nhiên lưu lại khí tức, dùng cái này liền có thể phân biệt! Đương nhiên, thời gian không thể quá dài, vượt qua hai canh giờ, hiệu quả còn kém...”"),t("br"),t("br"),n._v("Phùng Huân nhẹ gật đầu."),t("br"),t("br"),n._v("“Vô Cấu kính?” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Chiến sư tồn tại mục đích đúng là cùng Dị Linh tộc nhân chiến đấu, xem ra thứ này, chính là là bọn họ nghiên cứu luyện chế, chuyên môn dùng để tìm kiếm chạy trốn gia hỏa."),t("br"),t("br"),n._v("Theo phương hướng, hai người nhanh chóng hướng về phía trước."),t("br"),t("br"),n._v("Mỗi phi hành một hồi, Phùng Huân liền lấy ra Vô Cấu kính xác nhận phương vị, liên tục mấy lần qua đi, đi tới cổ thành ở giữa nhất một cái cũ nát phủ đệ."),t("br"),t("br"),n._v("“Cẩn thận một chút!”"),t("br"),t("br"),n._v("Biết Thạch Diệp Vương vô cùng có khả năng trong này, hai người liếc mắt nhìn nhau, từng người dặn dò một tiếng, lặng yên không tiếng động bay vào."),t("br"),t("br"),n._v("Phủ đệ sân nhỏ đã bị triệt để phá hư, một đầu đồ sộ Dị Linh tộc nhân, giờ phút này đang đứng tại ở giữa nhất vị trí, hai tay giơ lên, lực lượng điên cuồng tuôn ra, tựa hồ tại đối kháng cái gì."),t("br"),t("br"),n._v("Phía trước hắn, là một cái không lớn hồ nước, phía trên một cái lớn chừng quả đấm màu xanh đậm viên cầu, không ngừng xoay tròn."),t("br"),t("br"),n._v("Vị này Dị Linh tộc nhân tuôn ra lực lượng, chính là rơi vào phía trên, tựa hồ tại tận lực áp chế cái gì."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1054"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1056"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);