(window.webpackJsonp=window.webpackJsonp||[]).push([[771],{1126:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-1668-xuan-thu-đai-đien-lich-su"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1668-xuan-thu-đai-đien-lich-su"}},[n._v("#")]),n._v(" Chương 1668: Xuân Thu đại điển lịch sử")]),n._v(" "),i("hr"),n._v(" "),i("p",[n._v("Chương 1668: Xuân Thu đại điển lịch sử"),i("br"),i("br"),n._v("“Đây là Khổng miếu?”"),i("br"),i("br"),n._v("Trương Huyền sững sờ, vội vàng nhìn lại, chỉ thấy cái này mặt trời, trốn ở đặc thù không gian bên trong, không biết cách xa nhau bao xa, lại tốt lơ lửng tại dòng sông lịch sử, muốn qua, không chỉ cách khoảng cách, còn có thời gian."),i("br"),i("br"),n._v("Miếu thờ bên trong kiến trúc, như mặt trăng bên trên bóng mờ, tầng tầng lớp lớp, dùng tới Minh Lý Chi Nhãn, đều thấy không rõ lắm."),i("br"),i("br"),n._v("“Căn cứ sách sử ghi chép, Khổng sư bị Dị Linh tộc mạnh nhất Hoàng giả, khốn tại Trần Thái chỗ, không cách nào chạy ra, nhờ vào bản thân đối thời gian lĩnh ngộ, sáng chế Xuân Thu đại điển, món bảo vật này tạo ra thời điểm, thiên hạ vạn vật, nhất niệm vì xuân, nhất niệm vì thu, xuân thu luân phiên, nóng lạnh tướng xâm. Lúc ấy, bị đại điển ánh sáng chiếu xạ địa phương, xuất hiện đủ loại thần dị, vừa ra đời hài tử, còn đang bú sữa, thời gian nháy mắt biến thành hơn bốn mươi tuổi trung niên nhân. Tuổi già sức yếu lão nhân, nhoáng một cái thần biến thành anh hài, cùng cháu trai tuổi tác tương đương...”"),i("br"),i("br"),n._v("Nhớ tới trong cổ thư ghi lại nội dung, Nhậm Thanh Viễn nói."),i("br"),i("br"),n._v("Xuân Thu đại điển bị sáng lập ra thời điểm, nhân tộc còn không có đại thắng, đương nhiên, bộ này đại điển lúc đó uy lực, cũng không có về sau trong truyền thuyết khủng bố như vậy, Khổng sư về sau thoát vây về sau, từng chút một gia trì, dần dần rèn luyện, mới để cho kỳ danh động thiên bên dưới, Dị Linh tộc nhân đều sợ như sợ cọp."),i("br"),i("br"),n._v("“Xuân Thu đại điển, nhất niệm xuân thu... Chẳng lẽ, Khổng sư lúc ấy lĩnh ngộ cùng Trương gia tiên tổ lĩnh ngộ đồng dạng... Thời gian chân giải?”"),i("br"),i("br"),n._v("Sửng sốt một chút, Trương Huyền nhịn không được nói."),i("br"),i("br"),n._v("Trước đó liền nghe nói qua món pháp bảo này, chỉ bất quá biết không tỉ mỉ, Nhậm sư nói như vậy, cùng hắn lĩnh ngộ thời gian chân giải có chút tương tự."),i("br"),i("br"),n._v("“Ừm, hẳn là thời gian chân giải, bất quá... Khổng sư lĩnh ngộ, không chỉ có là tăng tốc, khả năng còn có giảm tốc, cùng đình chỉ!”"),i("br"),i("br"),n._v("Nhậm Thanh Viễn nói."),i("br"),i("br"),n._v("“Giảm tốc cùng đình chỉ?” Trương Huyền giật nảy mình."),i("br"),i("br"),n._v("Không gian chia làm rất nhiều chân giải, phong cấm chỉ là một loại trong đó, thời gian cũng giống vậy, Trương gia lĩnh ngộ tăng tốc, là để bản thân đối thời gian lực khống chế biến nhanh, người khác một giây đồng hồ thời gian, hắn biến thành mười giây đồng hồ, bởi vậy, thoạt nhìn những người khác hoạt động đều trở nên chậm."),i("br"),i("br"),n._v("Loại trừ loại này chân giải bên ngoài, còn có thời gian giảm tốc, cùng thời gian ngừng lại!"),i("br"),i("br"),n._v("Thời gian giảm tốc, đối với người bình thường tới nói, không có tác dụng gì, nhưng đối với Cổ Thánh tới nói, tác dụng cũng quá lớn, rơi vào trạng thái ngủ say, một khi chung quanh thời gian tốc độ chậm lại, tuổi thọ liền có thể rất kéo dài, vì nhân tộc, chiến đấu nhiều lần hơn."),i("br"),i("br"),n._v("Còn thời gian ngừng lại, là thời gian pháp tắc bên trong, khó khăn nhất lý giải, so không gian phong cấm càng thêm đáng sợ."),i("br"),i("br"),n._v("Người sau thi triển đi ra, nhiều nhất cùng trói tại dây thừng bên trong đồng dạng, không cách nào động đậy, có thể tư duy có ý thức, biết phát sinh trước mắt chuyện gì. Nhưng thời gian ngừng lại... Liền tư duy đều không thể vận chuyển, thật muốn bị giết, liền thật đã chết rồi, phục sinh cơ hội đều không có."),i("br"),i("br"),n._v("Năm đó Khổng sư, thật lĩnh ngộ những này, cái kia mạnh bao nhiêu?"),i("br"),i("br"),n._v("Ngoan Nhân coi như lợi hại hơn nữa, cũng chống lại không được đi!"),i("br"),i("br"),n._v("“Đúng vậy a, có điều, đây chỉ là hậu thế suy đoán, có thể khẳng định là... Khổng sư cả đời đối thời gian tất cả lý giải, đều lưu tại Xuân Thu đại điển bên trong, nếu như có thể đi vào Khổng miếu nhận được món bảo vật này, nhất định có thể có được truyền thừa của hắn, từ đó thực lực tăng nhiều! Dị Linh tộc tai hoạ ngầm lại lớn, cũng có thể giải quyết.”"),i("br"),i("br"),n._v("Nhậm Thanh Viễn nói."),i("br"),i("br"),n._v("Khổng sư lúc ấy, Danh Sư đường vừa mới thành hình, còn không có hiện tại như vậy rộng lớn, không cách nào giải quyết triệt để Dị Linh tộc nhân, chỉ có thể đem hắn trục xuất, mà bây giờ... Nhân tộc thực lực tổng hợp đại tăng, lại có Xuân Thu đại điển lời nói, đem hắn hoàn toàn đánh bại, liền sẽ dễ dàng rất nhiều."),i("br"),i("br"),n._v("“Đúng vậy a!”"),i("br"),i("br"),n._v("Trương Huyền tràn đầy rung động."),i("br"),i("br"),n._v("Không hổ là vạn thế chi sư, thiên hạ đệ nhất nhân, Khổng sư quả nhiên kinh tài tuyệt diễm."),i("br"),i("br"),n._v("“Danh Sư đường tổng bộ tất cả cửu tinh danh sư nghe lệnh, trừ bỏ trấn thủ Địa quật người, còn lại tất cả danh sư, lập tức lên đường, tiến về Khúc Phụ, Khổng miếu hiện thân, hẳn là không bao lâu liền sẽ hoàn toàn mở ra, có thể đi vào!”"),i("br"),i("br"),n._v("Nhậm Thanh Viễn xoay người, dặn dò một tiếng."),i("br"),i("br"),n._v("“Vâng!”"),i("br"),i("br"),n._v("Rất nhiều cửu tinh danh sư đồng thời gật đầu, từng cái vẻ mặt nghiêm túc."),i("br"),i("br"),n._v("“Trương sư, Khổng miếu hiện thân, Dị Linh tộc nhân chắc chắn sẽ không từ bỏ ý đồ, người cùng chúng ta Danh Sư đường tổng bộ cùng một chỗ, vẫn là đơn độc hành động?”"),i("br"),i("br"),n._v("Nhậm Thanh Viễn nhìn qua."),i("br"),i("br"),n._v("“Ta...”"),i("br"),n._v("Trương Huyền đang muốn nói chuyện, liền thấy Lạc Nhược Hi khe khẽ lắc đầu, lúc này mở miệng: “Nhậm sư ưu ái Trương mỗ tâm lĩnh, ta muốn một mình đi tới!”"),i("br"),i("br"),n._v("“Cũng tốt!”"),i("br"),i("br"),n._v("Dường như đã sớm đoán ra hắn sẽ có loại này quyết định, Nhậm Thanh Viễn gật gật đầu: “Chúng ta Khúc Phụ gặp lại đi!”"),i("br"),i("br"),n._v("Nói xong, không tiếp tục để ý Trương Huyền, vẫy bàn tay lớn một cái, rất nhiều cửu tinh danh sư vội vã bay đi, hẳn là thảo luận Khổng miếu mở ra, cùng đối kháng Dị Linh tộc nhân cụ thể chi tiết."),i("br"),i("br"),n._v("“Chúng ta trở về đi!”"),i("br"),i("br"),n._v("Thấy bọn họ rời khỏi, Trương Huyền cũng không nhiều đợi, gọi Lạc Nhược Hi một tiếng, hai người thẳng tắp hướng ra phía ngoài bay đi, thời gian nháy mắt liền rời đi Danh Sư đường phạm vi."),i("br"),i("br"),n._v("Nhớ tới vừa rồi Lạc Nhược Hi thần thái, Trương Huyền quay đầu nhìn sang."),i("br"),i("br"),n._v("“Khổng miếu mở ra, Dị Linh tộc nhân tất nhiên sẽ quy mô xâm phạm, thậm chí cũng có thể Cổ Thánh tọa trấn... Cùng Danh Sư đường tổng bộ cùng một chỗ, tuyệt đối là an toàn nhất, vì sao cự tuyệt?”"),i("br"),i("br"),n._v("Cổ Thánh coi như bị hạn chế, không cách nào đi vào, nhưng một khi Xuân Thu đại điển rời khỏi Khổng miếu, chân chính giao thủ tranh đoạt nhất định là những người mạnh nhất này."),i("br"),i("br"),n._v("  Danh Sư đường, thoạt nhìn chỉ có những này Đại Thánh cấp bậc cửu tinh danh sư, nhưng chân chính nội tình, không biết sâu bao nhiêu."),i("br"),i("br"),n._v("Trương gia, đều có một vị Cổ Thánh cấp bậc lão tổ sống, Danh Sư đường, chẳng phải càng nhiều?"),i("br"),i("br"),n._v("Cùng bọn hắn cùng một chỗ, nhất định càng thêm an toàn."),i("br"),i("br"),n._v("Dù sao, hắn tuy là tam đại gia tộc tộc trưởng, nhưng cũng là Danh Sư đường một thành viên, một vị cửu tinh cấp bậc danh sư."),i("br"),i("br"),n._v("“Khổng miếu xuất hiện, đến hoàn toàn mở ra, hẳn là còn có một đoạn thời gian, không cần thiết cùng bọn hắn cùng một chỗ, hiện tại toàn bộ đại lục rung chuyển, tiến về Khúc Phụ cường giả vô số, lo lắng đi qua, chẳng những không có chỗ tốt, sẽ chỉ tăng thêm phiền phức!”"),i("br"),i("br"),n._v("Lạc Nhược Hi nói."),i("br"),i("br"),n._v("“Còn có một đoạn thời gian?” Trương Huyền sững sờ."),i("br"),i("br"),n._v("Còn tưởng rằng Khổng miếu xuất hiện, liền có thể mang theo truyền thế thiên phù đi vào, không nghĩ tới còn muốn một đoạn thời gian, mới có thể mở ra."),i("br"),i("br"),n._v("“Không đúng, không phải nói... Khổng miếu chỉ có truyền thế thiên phù mới có thể đi vào ư? Nhiều người như vậy tiến đến cũng vô dụng thôi...”"),i("br"),i("br"),n._v("Nhớ tới một việc, nhịn không được nói."),i("br"),i("br"),n._v("Nắm giữ truyền thế thiên phù, mới có thể đi vào Khổng miếu, đây là đã sớm biết sự thật, không phải, Chư Tử bách gia cường giả, cũng không đến mức chạy đến Lạc gia, Trương gia tỷ thí."),i("br"),i("br"),n._v("Vào cũng không vào được, nhiều người như vậy xông qua làm gì?"),i("br"),i("br"),n._v("“Khổng miếu đại điện, quả thực chỉ có truyền thế thiên phù mới có thể đi vào, nhưng... Bên ngoài đâu?”"),i("br"),i("br"),n._v("Lạc Nhược Hi mỉm cười."),i("br"),i("br"),n._v("“Bên ngoài?”"),i("br"),i("br"),n._v("“Liền cùng chúng ta lần trước đi Khưu Ngô cung đồng dạng, Khưu Ngô cung là cái độc lập cá thể, muốn đi vào tương đối khó khăn, nhưng mà bên ngoài, bất kỳ người nào cũng có thể vào!” Lạc Nhược Hi gật đầu."),i("br"),i("br"),n._v("Trương Huyền sững sờ."),i("br"),i("br"),n._v("Khưu Ngô ngoài cung vây, đủ loại trận pháp, gặp phải không ít, nếu không phải hắn, rất khó đi vào cung điện."),i("br"),i("br"),n._v("Nói như vậy nổi dậy, cái gọi là Khổng miếu, cũng không vẻn vẹn là một chút kiến trúc, còn có vòng ngoài trận pháp các loại đồ vật."),i("br"),i("br"),n._v("“Khổng sư từ trước tới nay chú ý hữu giáo vô loại, hắn lưu lại di tích, loại trừ chủ điện bên ngoài, cái khác rất nhiều nơi cũng đều có bảo vật cùng cơ duyên, những người này không có truyền thế thiên phù, liều chính là những vật này, tự nhiên muốn đi đến...”"),i("br"),i("br"),n._v("Lạc Nhược Hi giải thích nói."),i("br"),i("br"),n._v("Người đăng: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-1679"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-1681"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);