(window.webpackJsonp=window.webpackJsonp||[]).push([[772],{1127:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1669-long-ho-son-đia-quat"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1669-long-ho-son-đia-quat"}},[n._v("#")]),n._v(" Chương 1669: Long Hổ Sơn Địa quật")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1669: Long Hổ Sơn Địa quật"),t("br"),t("br"),n._v("Trương Huyền giật mình."),t("br"),t("br"),n._v("Trước đó còn tưởng rằng, cái gọi là Khổng miếu, chỉ có 《 Xuân Thu đại điển 》 cùng Khổng sư truyền thừa, hiện tại xem ra có lẽ cùng di tích đồng dạng, có vô số bảo vật, cùng vô số cơ duyên."),t("br"),t("br"),n._v("“Không chỉ có như vậy, Khổng miếu ẩn chứa Khổng sư lưu truyền văn khí cùng văn vận, coi như tìm không thấy bảo vật, ở trong đó tu hành, cũng sẽ một ngày ngàn dặm, tiến bộ cực nhanh, đặc biệt là chạm đến tu luyện bình chướng, nghe nói ẩn chứa trong đó sư ngôn Thiên Âm, chỉ cần chăm chú lắng nghe, rất dễ dàng đột phá.”"),t("br"),t("br"),n._v("Nhìn lấy trên không cái thứ hai mặt trời, Lạc Nhược Hi vẻ mặt nghiêm túc: “Tóm lại, Khổng miếu mở ra, đối với toàn bộ đại lục tu luyện giả, đều là việc lớn, đến lúc đó, không chỉ nhân loại, Dị Linh tộc nhân, ẩn nấp Thánh thú, cũng sẽ không từ bỏ cơ hội... Vì lẽ đó, muốn đi vào trong đó, có thu hoạch, nhất định phải có chỗ chuẩn bị, không thể mù quáng mà đi!”"),t("br"),t("br"),n._v("Trương Huyền nghiêm nghị."),t("br"),t("br"),n._v("Dị Linh tộc cường giả vô số, không ít Thánh thú, cũng mười phần kinh khủng, Khổng miếu như vậy lực hấp dẫn, một khi mở ra, Danh Sư đường cho dù có năng lực ngăn cản, cũng sẽ không đi làm."),t("br"),t("br"),n._v("Dính dáng quá lớn, thật muốn ngăn cản, tương đương với đứt người tu luyện tiền đồ, đại chiến sẽ trực tiếp dẫn phát, vô số sinh linh đồ thán."),t("br"),t("br"),n._v("Đến lúc đó, Dị Linh tộc nhân lại nhân cơ hội xuất hiện, thật sự phiền phức lớn rồi."),t("br"),t("br"),n._v("Biện pháp duy nhất chính là, cướp được phía trước nhận được 《 Xuân Thu đại điển 》, khống chế Khổng miếu, khống chế đại thế."),t("br"),t("br"),n._v("Hắn tuy là có truyền thế thiên phù, nhưng khi đó Khổng sư hết thảy luyện chế sáu cái, hơn nữa còn có một cái mẫu phù, cộng lại cũng chính là bảy viên, hiện nay biết, Danh Sư đường tổng bộ, Trương gia, Lạc gia các một cái, còn lại ba cái, cụ thể ở đâu, cũng không rõ ràng."),t("br"),t("br"),n._v("Không có gì bất ngờ xảy ra, Dị Linh tộc, Chư Tử bách gia, nhất định cũng có."),t("br"),t("br"),n._v("Hơn nữa thể chất đặc thù cũng có thể tiến vào bên trong, bởi như vậy, không tính bên ngoài, có thể đi vào Khổng miếu, không biết bao nhiêu, trước thời hạn không có chuẩn bị, quả thực rất bị động."),t("br"),t("br"),n._v("Không nói cái khác, Chư Tử bách gia những cường giả kia, đủ loại bảo vật liên tiếp xuất hiện, thật muốn gặp lên, coi như hắn tu vi tiến nhanh, cũng khẳng định không chiếm được chỗ tốt."),t("br"),t("br"),n._v("Hắn thực lực tăng thêm, nhưng còn không làm được, xem thường thiên hạ cường giả tình trạng."),t("br"),t("br"),n._v("“Trước về Trương gia, xem bọn hắn như thế nào chuẩn bị!”"),t("br"),t("br"),n._v("Như thế nào đi vào Khổng miếu, thì sao mới có thể tìm kiếm Xuân Thu đại điển, hắn biết không nhiều, nhưng Trương gia loại này đại gia tộc, nhất định sớm đã chuẩn bị rất lâu."),t("br"),t("br"),n._v("Rõ ràng những này, Trương Huyền cùng Lạc Nhược Hi không có quá nhiều chần chừ, phá vỡ không gian, hướng về bay đi."),t("br"),t("br"),n._v("Tu vi bạo tăng, phá vỡ không gian càng thêm ổn định, thời gian không dài, liền thấy Trương gia to lớn phủ đệ xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Trở lại trong tộc, đi không xa, Trương Huyền nhất thời cảm thấy không đúng."),t("br"),t("br"),n._v("Trước kia Trương gia đều là người đến người đi, giờ phút này to lớn phủ đệ, trước cửa có thể giăng lưới bắt chim, không nhìn thấy vài bóng người."),t("br"),t("br"),n._v("“Chuyện gì xảy ra?”"),t("br"),t("br"),n._v("đọctruyện với http://truyen"),t("br"),n._v("cuａtui.net Thật vất vả tìm tới một vị trưởng lão, Trương Huyền vội vàng hỏi."),t("br"),t("br"),n._v("“Trong tộc hầu như Thánh vực trở lên cường giả, đều đã đi tới các nơi Địa quật, ngăn cản Dị Linh tộc nhân thế công, lão gia chủ cùng chủ mẫu, cũng đã xuất phát!”"),t("br"),t("br"),n._v("Thấy là tộc trưởng, vị trưởng lão này vội vàng khom người trả lời."),t("br"),t("br"),n._v("“Đều đi Địa quật?”"),t("br"),t("br"),n._v("Trương Huyền sững sờ."),t("br"),t("br"),n._v("Tại Danh Sư đường, hắn là đưa tin trở về, để tam đại gia tộc chuẩn bị trợ giúp Danh Sư đường, không nghĩ tới, lớn như vậy gia tộc, hầu như đi tay không, thậm chí liền Hưng, Mộng kiếm thánh đều đã đi tới."),t("br"),t("br"),n._v("“Đi đâu cái Địa quật? Mang ta tới!”"),t("br"),t("br"),n._v("Nhíu nhíu mày, Trương Huyền nói."),t("br"),t("br"),n._v("Khổng miếu mở ra, Dị Linh tộc nhân khẳng định không muốn bỏ qua cơ hội, lần này đại quy mô tấn công, nếu như ngăn cản không nổi, cho dù nhận được Xuân Thu đại điển thì có ích lợi gì?"),t("br"),t("br"),n._v("Thứ này cứ việc có thể khiến người ta thực lực tăng nhiều, thế nhưng là đối kháng một cái khác chủng tộc, cần tuyệt đối ngàn ngàn danh sư, lúc này nếu như bị đột phá phòng ngự, rơi vào nguy cơ, thật sự phiền toái."),t("br"),n._v("Cũng khó trách Danh Sư đường lo lắng, nhìn thấy bản thân, cái gì đều không có chuẩn bị, liền mở rộng đèn xanh, trực tiếp thông qua cửu tinh danh sư khảo hạch."),t("br"),t("br"),n._v("Nếu không, đơn nhất cái mới giáo dục lý niệm, liền cần vô số học sinh, nghiệm chứng thời gian mấy năm."),t("br"),t("br"),n._v("“108 chỗ Địa quật, tuy là đều xuất hiện xao động, nhưng đi qua dò xét, phần lớn đều là đánh nghi binh, địa phương Danh Sư đường liền có thể trấn áp, chân chính quy mô xâm phạm hết thảy sáu cái! Thứ nhất, Danh Sư đường tổng bộ Địa quật, Long Môn Địa quật, Băng Hải Địa quật, Vân Hải Địa quật cùng Trương gia trấn thủ Long Hổ Sơn Địa quật, cùng Lạc gia trấn thủ Tinh Thần Địa quật!”"),t("br"),t("br"),n._v("“Danh Sư đường tổng bộ bên kia, rất nhiều cửu tinh danh sư tọa trấn, không cần hỗ trợ, Long Môn Địa quật cùng Vân Hải Địa quật, Trịnh Dương chiến tử, nhận nhiệm vụ lúc lâm nguy, kế vị Chiến Sư đường đường chủ, dẫn dắt vô số chiến sư, đi tới trấn áp! Băng Hải Địa quật, Băng Nguyên cung, Viên gia, Thánh tử điện, cùng khải linh sư công hộ người, đi tới ngăn cản... Cái khác Thánh Nhân quý tộc cùng một chút mạnh mẽ công hội cùng thế lực, du tẩu tại cái khác Địa quật, phòng ngừa xuất hiện biến cố, Tưởng gia thì cùng chúng ta Trương, Lạc hai nhà, cùng nhau trấn áp Long Hổ Sơn cùng tinh thần hai đại Địa quật!”"),t("br"),t("br"),n._v("“Cái này hai đại Địa quật Dị Linh tộc nhân khí thế hung hung, Khưu Ngô Cổ Thánh lưu lại phong ấn, đã ngăn cản không nổi, chỉ cần là Trương gia vượt qua lục tinh danh sư, cũng đã đi tới!”"),t("br"),t("br"),n._v("Vị trưởng lão này giải thích nói: “Ta chịu trách nhiệm ở lại trong tộc chi viện, một khi xuất hiện nguy cơ, chuyển di còn lại tộc nhân, bảo tồn hương hỏa!”"),t("br"),t("br"),n._v("“Long Hổ Sơn Địa quật? Chẳng lẽ ngay tại phía dưới này?”"),t("br"),t("br"),n._v("Trương Huyền lông mày nhảy một cái."),t("br"),t("br"),n._v("Sớm biết, các nơi Danh Sư đường, không chỉ hội tụ nhân tộc số mệnh, càng quan trọng hơn là, trấn áp phân tán Địa quật, không nghĩ tới Trương gia cũng thế."),t("br"),t("br"),n._v("Long Hổ Sơn chính là gần sát ở Trương gia sơn mạch, cái này Địa quật lấy đó làm tên, chỉ sợ khoảng cách không xa."),t("br"),t("br"),n._v("“Ừm, cửa vào ngay tại phía dưới, tộc trưởng nếu đi qua, ta hiện tại liền dẫn ngươi đi!”"),t("br"),t("br"),n._v("Vị trưởng lão này vội vàng gật đầu."),t("br"),t("br"),n._v("“Tốt, ta đi qua nhìn một chút!”"),t("br"),t("br"),n._v("Trương Huyền lên tiếng."),t("br"),t("br"),n._v("Khổng miếu không nói trước còn không có mở ra, coi như mở ra, cũng không kịp cái này quan trọng."),t("br"),t("br"),n._v("“Tộc trưởng, mời đi theo ta!”"),t("br"),t("br"),n._v("Vị trưởng lão này không do dự, phía trước dẫn đường, rất mau tới đến một nơi, ngừng lại."),t("br"),t("br"),n._v("“Đây là Trương gia mộ tổ?”"),t("br"),t("br"),n._v("Trương Huyền sửng sốt."),t("br"),t("br"),n._v("“Đúng vậy a, Địa quật ngay tại phía dưới này!” Vị trưởng lão này gật đầu."),t("br"),t("br"),n._v("“Dùng tiền bối anh linh trấn áp...” Trương Huyền lặng im."),t("br"),t("br"),n._v("Cái này cùng lúc trước Hồng Viễn danh sư học viện Địa quật có chút tương tự, vô số tiền bối, cả đời cùng Dị Linh tộc nhân chống lại, chết rồi, anh linh trấn thủ Địa quật vào miệng, để hắn không cách nào phá tan phong ấn, đi tới đại lục."),t("br"),t("br"),n._v("“Tiền bối anh linh, mới có thể để cho hắn kiêng kị, không dám chạy trốn đi ra! Tộc trưởng, Địa quật ngay tại phía trước, ta còn muốn chuyển di tộc nhân, liền không đi qua!”"),t("br"),t("br"),n._v("Vị trưởng lão này chỉ về phía trước, xoay người rời khỏi."),t("br"),t("br"),n._v("Một khi Dị Linh tộc nhân công phá bọn họ thủ vững cái này, Trương gia nhất định gặp tai hoạ ngập đầu, nhất định phải trước thời hạn chuyển di, nếu không, toàn bộ diệt vong, về sau còn chúng ta cùng đối phương chống lại?"),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Không để ý đối phương, Trương Huyền nhấc chân đi vào trước mắt Trương gia tổ địa."),t("br"),t("br"),n._v("Vô số bia đá đứng sừng sững, phía trên ghi chép từng cái danh tự, mỗi một vị đều là do lúc cao nhất nhân vật... Đáng tiếc, đều không có tránh được thời gian làm hao mòn, biến thành một nắm cát vàng."),t("br"),t("br"),n._v("Dù là thiên phú có một không hai, dù là thiên tư mạnh mẽ, ngàn năm, vạn năm về sau, cũng cùng những người này đồng dạng, biến thành trên bia mộ lẻ loi trơ trọi tên."),t("br"),t("br"),n._v("Không có sinh mệnh, không có ý thức, hoàn toàn biến mất tại dòng sông lịch sử, chỉ còn lại có anh linh trường tồn."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1680"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1682"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);