(window.webpackJsonp=window.webpackJsonp||[]).push([[777],{1132:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1674-sup-đo-thong-soai"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1674-sup-đo-thong-soai"}},[n._v("#")]),n._v(" Chương 1674: Sụp đổ thống soái")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1674: Sụp đổ thống soái"),t("br"),t("br"),n._v("“Nửa bước Cổ Thánh thần binh?”"),t("br"),t("br"),n._v("“Không sai, ta là Cổ Thánh thần binh, chỉ cần chủ nhân đạt tới Cổ Thánh cấp bậc, liền có thể mở ra phong ấn, nắm giữ cùng Cổ Thánh sánh ngang sức chiến đấu... Tại Đại Thánh cùng Cổ Thánh ở giữa, còn có một cái nửa bước Cổ Thánh, kiện binh khí này, chính là loại này cấp bậc, một khi ra tay, Bất Hủ cảnh cường giả, đều không thể chống lại!”"),t("br"),t("br"),n._v("Long cốt thần thương giải thích."),t("br"),t("br"),n._v("Trương Huyền vẻ mặt nghiêm túc."),t("br"),t("br"),n._v("Nhìn kiện binh khí này uy lực, nếu quả thật để hắn dùng để đánh lén, Hưng, Mộng kiếm thánh cho dù liên hợp lại, cũng có không kém vũ khí, khẳng định cũng sẽ trúng chiêu."),t("br"),t("br"),n._v("“Đáng ghét!”"),t("br"),t("br"),n._v("Còn tưởng rằng bọn họ là không dám công kích, không nghĩ tới, ẩn giấu đi thủ đoạn lợi hại như vậy."),t("br"),t("br"),n._v("“Không thể để cho hắn được như ý!”"),t("br"),t("br"),n._v("Kiếp trước là cô nhi, không có hưởng thụ qua cha mẹ tình cảm, kiếp này nhận thân về sau, Hưng, Mộng hai vị, đối với hắn yêu chiều, lộ rõ trên mặt, đã sớm đem hắn cho rằng là người thân, cái tên này vậy mà muốn mượn thiên cơ sư bảo vật, tiến hành đánh lén, đã nhìn thấy, sao có thể để hắn toại nguyện?"),t("br"),t("br"),n._v("Chỉ là... Thực lực của hắn bây giờ, trừ phi vận dụng Cổ Thánh thi thể, muốn vượt qua trước mắt vị này, gần như không có khả năng!"),t("br"),t("br"),n._v("Có thể di động dùng Cổ Thánh thi thể, hồn lực sẽ trong nháy mắt tiêu hao sạch sẽ, đâu đâu cũng có Dị Linh tộc nhân, đến lúc đó chạy trốn đều không làm được."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Trong lòng đang tính toán, như thế nào đối phó cái tên này, chỉ thấy vị này thống soái, hít sâu một hơi, lực lượng trong cơ thể sôi trào, cắn răng một cái, hướng yêu đao bắt tới."),t("br"),t("br"),n._v("Xì xì xì!"),t("br"),t("br"),n._v("Yêu đao trên chuôi đao bộ xương, lóe ra sáng tối chập chờn ánh sáng, thân đao chấn động mạnh một cái, đem hắn bắn ra, mạnh mẽ sát khí, thẳng tắp lao đến."),t("br"),t("br"),n._v("Thống lĩnh vẻ mặt trong nháy mắt phát xanh, lui về sau hai bước, chân khí trong cơ thể giang hải giống như khuấy động, qua thật lâu, mới giảm bớt tới."),t("br"),t("br"),n._v("“Ta cũng không ý định đưa ngươi luyện hóa, chỉ là muốn mượn ngươi tay chém giết hai người!”"),t("br"),t("br"),n._v("Dừng lại một chút, thống soái mở miệng."),t("br"),t("br"),n._v("Loại cấp bậc này vũ khí, đã sớm có khí linh, có thể tới thương nghị."),t("br"),t("br"),n._v("Ong ong ong!"),t("br"),t("br"),n._v("Bộ xương dường như không để ý tới lời của hắn, vặn vẹo một chút, cũng không nói chuyện, hình như là đang bày tỏ, muốn để cho ta phối hợp ngươi, đầu tiên muốn thực lực qua ải, không được... Không bàn nữa!"),t("br"),t("br"),n._v("“Đã không phối hợp, cũng đừng trách ta dùng sức mạnh...”"),t("br"),t("br"),n._v("Thấy chuôi này đao, như vậy ngạo khí, thống soái trên mặt không dễ nhìn, híp mắt lại, không biết thi triển cái gì võ kỹ, trên bàn tay ánh sáng lóe lên, tạo thành một cái trong suốt màng mỏng, lần nữa bắt tới."),t("br"),t("br"),n._v("Xì xì xì xì...!"),t("br"),t("br"),n._v("Yêu đao cũng không khuất phục, hai cỗ lực lượng giao hòa, không gian bốn phía, xuất hiện vết rách."),t("br"),t("br"),n._v("Bởi vì có thiên cơ bảo vật cùng đặc thù trận pháp, một người một đao lực lượng tuy là mạnh mẽ, nhưng lại chưa tiết lộ ra mảy may."),t("br"),t("br"),n._v("“Cái tên này cũng không đem yêu đao luyện hóa...”"),t("br"),t("br"),n._v("Trương Huyền giật mình."),t("br"),t("br"),n._v("Nhìn tới những này kính, vừa đến, có thể giúp người đánh lén, che đậy thiên cơ, thứ hai, là che lấp cái này nửa bước Cổ Thánh thần binh lực lượng, làm cho không người nào có thể phát giác."),t("br"),t("br"),n._v("Vũ khí nếu như bị luyện hóa, trực tiếp thu vào trữ vật giới chỉ là được, căn bản không cần phiền toái như vậy."),t("br"),t("br"),n._v("Vị này thống soái, muốn đem thuần phục, một khi thành công, thực lực bản thân mạnh như vậy, lại phối hợp lợi hại như thế vũ khí, coi như không cần đánh lén, Hưng, Mộng kiếm thánh cũng khó có thể chống lại."),t("br"),t("br"),n._v("“Ngăn cản hắn luyện hóa là được...”"),t("br"),t("br"),n._v("Còn muốn lấy, như thế nào ngăn cản đối phương, đã không có luyện hóa, liền đơn giản không ít."),t("br"),t("br"),n._v("Thấy một người một đao đang tại giằng co, thống lĩnh sắc mặt biến ảo không ngừng, dường như đã dùng hết toàn lực, Trương Huyền ánh mắt sáng lên, thân ảnh “Kẽo kẹt! Kẽo kẹt!” Xuất hiện biến hóa, thời gian nháy mắt thành mặt khác một bức bộ dáng."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Kính bao phủ hoàn cảnh bên trong."),t("br"),t("br"),n._v("Biết yêu đao đáng sợ, thống soái không dám khinh thường, lực lượng hùng hồn, điên cuồng truyền vào, chống lại, không dám thất thần, đang cảm thấy đã dần dần chiếm được thượng phong, có thể để cho đối phương thừa nhận bản thân thời điểm, đột nhiên một hồi tiếng bước chân dồn dập truyền đến."),t("br"),n._v("Ngay sau đó một bóng người xuất hiện tại cách đó không xa, chính là vừa rồi rời đi “Ngả Hà tướng quân”, vừa đến trước mặt, lập tức ôm quyền khom người: “Thống soái, ta đã dựa theo ngươi dặn dò truyền lệnh xuống, không khiến người ta tới quấy rầy, ngươi có thể an tâm tu luyện!”"),t("br"),t("br"),n._v("“...” Thống soái thân thể nhoáng một cái."),t("br"),t("br"),n._v("Cùng yêu đao đối kháng, không riêng gì lực lượng tranh đấu, càng có phương diện tinh thần đối công, lúc này cần nhất chính là yên tĩnh, cho nên mới xuống trước đó mệnh lệnh, ai biết... Cái này hai hàng, chạy tới nói cho hắn biết, đã tuân thủ..."),t("br"),t("br"),n._v("Tuân thủ mệnh lệnh, ngươi còn tới?"),t("br"),t("br"),n._v("Ngươi ở đây nói nhảm, ta làm sao an tâm?"),t("br"),t("br"),n._v("“Đi xuống đi!”"),t("br"),t("br"),n._v("Cố nén một bàn tay đem hắn quất chết xúc động, thống lĩnh khẽ nói."),t("br"),t("br"),n._v("Hô hô!"),t("br"),t("br"),n._v("Hắn bên này tinh thần ba động biến lớn, yêu đao sát khí lập tức xâm nhập trong óc, để hắn lắc lư một cái, suýt chút nữa hộc máu."),t("br"),t("br"),n._v("“Vâng...” “Ngả Hà tướng quân” gật gật đầu, dừng lại một chút, lần nữa ôm quyền: “Thống lĩnh, lập tức liền muốn ăn cơm, ngươi là ăn... Mì thịt bò, vẫn là mì chua cay? Ta cũng tốt để nhà bếp chuẩn bị!”"),t("br"),t("br"),n._v("“Mì thịt bò? Mì chua cay?”"),t("br"),t("br"),n._v("Thống soái ngẩn ngơ, chúng ta nơi này có hai thứ đồ này ư?"),t("br"),t("br"),n._v("Lại nói, tu vi đạt tới bọn họ loại cảnh giới này, chỉ cần linh khí đầy đủ, mười năm tám năm không ăn không uống đều không quan trọng... Đang đánh trận, còn có người nào công phu, ăn cái gì mì thịt bò?"),t("br"),t("br"),n._v("“Không ăn, lui ra!”"),t("br"),t("br"),n._v("Vốn là cần yên tĩnh, không thể bị người ầm ĩ, cái tên này lại tại nơi này lải nhải, thống soái sắp nổ."),t("br"),t("br"),n._v("Hắn bên này tinh thần chuyển di, vốn ở vào hạ phong yêu đao, sát khí lao qua, để đầu chóng mặt hoa mắt, ngực sắp nổ tung, trên đầu mồ hôi lạnh phả ra."),t("br"),t("br"),n._v("“Không ăn? Vậy ngươi... Uống nước ư?”"),t("br"),t("br"),n._v("“Ngả Hà tướng quân” mặt mũi ân cần nhìn qua: “Ngươi nhìn, chảy nhiều như vậy mồ hôi...”"),t("br"),t("br"),n._v("“...” Thống soái."),t("br"),t("br"),n._v("Mmp ah! Trước đó, làm sao không cảm thấy cái này hai hàng, như vậy phí miệng?"),t("br"),t("br"),n._v("Ngươi là tướng quân, không phải đầu bếp... Lại muốn làm cơm, lại muốn đưa nước, làm gì? Không thấy được ta đang bận ư?"),t("br"),t("br"),n._v("“Cút...” Cũng nhịn không được nữa, quát to một tiếng, lời còn chưa dứt, đối diện vị này gia hỏa thanh âm vang lên lần nữa: “Nước cũng không uống a? Cái kia... Canh đây? Nghe nói mới vừa làm lớn canh xương mùi vị không tệ, nếu không, ta mang tới một chén...”"),t("br"),t("br"),n._v("“Phốc!”"),t("br"),t("br"),n._v("Trước mắt biến thành màu đen, yêu đao sát khí, xông vào trong cơ thể, thống soái sắc mặt trắng nhợt, một ngụm máu tươi phun ra."),t("br"),t("br"),n._v("Mẹ nó, xong chưa?"),t("br"),t("br"),n._v("Mắt mù ah, không thấy được ta đang bận thuần phục yêu đao ư?"),t("br"),t("br"),n._v("Làm sao một điểm nhãn lực sức lực hay không?"),t("br"),t("br"),n._v("Cố nén ngực sôi trào, hàm răng cắn chặt, trong mắt mang theo muốn giết người xúc động: “Ta không ăn cơm, không uống nước, càng không ăn canh... Bây giờ lập tức cút, không được qua đây quấy rầy...”"),t("br"),t("br"),n._v("“Thống soái, ngươi yên tâm, ta đã thông tri một chút đi, tuyệt đối sẽ không có người tới quấy rầy, nếu như bọn họ dám chống lại mệnh lệnh, coi như không cần ngươi mở miệng, ta cũng sẽ tự tay đem hắn chém giết...”"),t("br"),t("br"),n._v("Vung tay lên, “Ngả Hà tướng quân” một mặt nghĩa chính ngôn từ."),t("br"),t("br"),n._v("Thống soái ngực đau nhức."),t("br"),t("br"),n._v("Ngươi mẹ nó có bị bệnh không? Ta nói chính là người khác quấy rầy ư? Nói chính là ngươi ah..."),t("br"),t("br"),n._v("Vừa định mắng ra, chỉ thấy trước mắt “Ngả Hà” lần nữa nhìn qua: “Thống soái, ngươi không ăn không uống, vậy ngươi... Muốn hát ư? Nếu không, ta hát cho ngươi nghe cũng được?”"),t("br"),t("br"),n._v("“Phốc!”"),t("br"),t("br"),n._v("Thống soái lần nữa máu tươi phun ra."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1685"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1687"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);