(window.webpackJsonp=window.webpackJsonp||[]).push([[78],{432:function(n,h,t){"use strict";t.r(h);var g=t(44),c=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1047-đang-so-thach-diep-vuong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1047-đang-so-thach-diep-vuong"}},[n._v("#")]),n._v(" Chương 1047: Đáng sợ Thạch Diệp Vương")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1047: Đáng sợ Thạch Diệp Vương"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Đầu này Dị Linh tộc nhân lực lượng mặc dù hung mãnh, nhưng viên châu cũng không yếu, dường như muốn tránh thoát khống chế, bỗng nhiên chuyển động thoáng cái, hai người lập tức nhìn thấy phía trên hiện lên một đạo hào quang màu lam đậm, trực tiếp bắn vào bầu trời, lập tức kịch liệt nổ vang, bốn phía lôi điện mãnh liệt."),t("br"),t("br"),n._v("Thật giống như trên bầu trời lôi điện, chịu đến cái này viên cầu khống chế đồng dạng."),t("br"),t("br"),n._v("“Điều khiển lôi điện... Chẳng lẽ đây là Lôi Nguyên châu?”"),t("br"),t("br"),n._v("Con ngươi co rụt lại, Phùng Huân nhịn không được truyền âm."),t("br"),t("br"),n._v("“Lôi Nguyên châu?”"),t("br"),t("br"),n._v("Trương Huyền cau mày."),t("br"),t("br"),n._v("Đem danh sư học viện thư tịch nhìn xong, biết vô số bảo vật danh xưng, chân chính được cho kiến thức rộng rãi, nhưng mà cái này Lôi Nguyên châu nhưng lại chưa bao giờ nghe qua."),t("br"),t("br"),n._v("“Đúng một loại lôi thuộc tính pháp bảo, cùng linh thạch ẩn chứa linh khí đồng dạng, mang theo đại lượng lôi nguyên, một khi phóng thích, có lực lượng hủy thiên diệt địa, mười điểm đáng sợ.”"),t("br"),t("br"),n._v("Trên mặt lộ ra khó tin vẻ, Phùng Huân nói tiếp: “Thứ này chỉ có tại lôi điện nhất chỗ tụ tập mới có thể sinh ra, trình độ hiếm hoi, có thể so với cửu tinh danh sư, thuộc về trong truyền thuyết bảo vật, làm sao lại xuất hiện tại đây?”"),t("br"),t("br"),n._v("“Trân quý như vậy?”"),t("br"),t("br"),n._v("Trương Huyền líu lưỡi."),t("br"),t("br"),n._v("Mặc dù chưa nghe nói qua cái này lôi nguyên thạch, nhưng mà cửu tinh danh sư trân quý mức độ, thế nhưng là biết đến, toàn bộ đại lục cũng chưa chắc có thể tìm ra mấy vị..."),t("br"),t("br"),n._v("Như vậy hiếm có, có phần quá thưa thớt."),t("br"),t("br"),n._v("“Ẩn chứa lôi điện, ngược lại cũng thôi, mấu chốt nhất là, có thể coi như lôi thuộc tính trận pháp hạch tâm!” Phùng Huân nói tiếp: “Cùng trận bàn công hiệu tương tự, nếu luyện hóa, chẳng khác nào nắm giữ đại trận, có thể phóng xuất ra lực lượng kinh người!”"),t("br"),t("br"),n._v("“Chẳng lẽ cái này lôi đình chi hải là một cái đại trận?” Trương Huyền biến sắc."),t("br"),t("br"),n._v("“Khẳng định là, hơn nữa ít nhất đạt đến cấp tám, không có người khống chế, cứ như vậy lợi hại, một khi luyện hóa, hoàn toàn khống chế... Cho dù chúng ta đều có Ngô sư thực lực, cũng không cách nào đối kháng!” Phùng Huân khuôn mặt trắng bệch."),t("br"),t("br"),n._v("“Cái này...” Trương Huyền thân thể xiết chặt."),t("br"),t("br"),n._v("Theo lôi đình chi hải một đường đi tới, hắn biết trận pháp này đáng sợ, nếu như không phải Vu hồn, căn bản không thể tiến vào được."),t("br"),t("br"),n._v("Đây là không có bị người khống chế tình huống, một khi bị người nắm giữ, lôi đình lực lượng cùng công kích phương vị, lại không sơ hở... Chỉ sợ không ai cản nổi, bị nhốt trong đó, ra đều ra không được."),t("br"),t("br"),n._v("“Trước đó lôi điện đột nhiên trở nên nhiều như vậy, khẳng định là cái tên này muốn luyện hóa Lôi Nguyên châu, đưa tới trận pháp bạo động...”"),t("br"),t("br"),n._v("Biết thứ này đáng sợ, Trương Huyền hiểu được."),t("br"),t("br"),n._v("Lôi Nguyên châu có điều khiển lôi điện trận pháp năng lực, cái tên này dùng chân khí đụng vào, lúc này mới dẫn đến lôi đình chi hải, trở nên càng thêm nóng nảy, suýt chút nữa hại chết mọi người."),t("br"),t("br"),n._v("Chỉ là đụng vào cứ như vậy đáng sợ, thật điều khiển, có thể nói khó có thể tưởng tượng."),t("br"),t("br"),n._v("Khuôn mặt khó coi, trong lòng lặng lẽ câu thông Thiên Nghĩ Phong sào bên trong Tử Diệp Vương, rất nhanh đến mức đến đáp án rõ ràng, trước mắt vị này thân hình cao lớn Dị Linh tộc nhân, quả thực chính là thượng ngũ vương bên trong, xếp hạng thứ tư Thạch Diệp Vương."),t("br"),t("br"),n._v("Khó trách liền Lôi Nguyên châu lợi hại như thế bảo vật, cũng dám đụng vào, muốn luyện hóa."),t("br"),t("br"),n._v("“Nhất định phải ngăn cản, nếu không, lại nghĩ đối phó, thì càng khó khăn...”"),t("br"),t("br"),n._v("Phùng Huân nắm đấm xiết chặt."),t("br"),t("br"),n._v("Vị này Thạch Diệp Vương vừa rồi cùng Ngô sư đối chiến tình huống, hắn nhìn ở trong mắt, thực lực cực mạnh, cho dù đỉnh phong nhất thời điểm cũng không là đối thủ, một khi lại để cho hắn luyện hóa Lôi Nguyên châu thì càng chống lại không được."),t("br"),t("br"),n._v("Đến lúc đó, gặp nhau lần nữa, tất cả mọi người có khả năng sẽ chết ở đây."),t("br"),t("br"),n._v("“Không sai, hắn còn không có thành công, nghĩ một chút biện pháp có lẽ kịp!” Hiểu lo lắng của hắn, Trương Huyền truyền âm."),t("br"),t("br"),n._v("“Ừm!” Phùng Huân vẻ mặt nghiêm túc, đang muốn nói chuyện, liền thấy phía trước Lôi Nguyên châu, lần nữa xoay một cái, tản mát ra ôn nhuận ánh sáng, cách đó không xa Thạch Diệp Vương, ánh mắt sáng lên, hướng về phía trước duỗi ra bàn tay, vồ mạnh một cái."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Tản ra lam quang viên cầu, chậm rãi hướng hắn bay tới."),t("br"),t("br"),n._v("“Không còn kịp rồi, mau ra tay!”"),t("br"),t("br"),n._v("Thấy cảnh này, Phùng Huân cũng nhịn không được nữa, cổ tay khẽ đảo, một thanh trường kiếm xuất hiện tại lòng bàn tay, cả người hóa thành một đạo kiếm mang trực tiếp đối với Thạch Diệp Vương giữa lưng đâm tới."),t("br"),t("br"),n._v("Nhìn tình huống, Lôi Nguyên châu đã bị luyện hóa xấp xỉ, một khi rơi vào lòng bàn tay, chẳng khác nào đối phương nắm giữ vô cùng lôi điện lực lượng, lại không lực xoay chuyển trời đất."),t("br"),t("br"),n._v("“Không vội...” Không nghĩ tới đối phương còn không có cùng mình thương nghị chiến lược, liền vọt tới, Trương Huyền gấp gáp hô một tiếng, cũng theo sát tới."),t("br"),t("br"),n._v("Đối phương luyện hóa Lôi Nguyên châu, mặc dù đáng sợ, nhưng mà chỉ cần cẩn thận tìm kiếm, khẳng định còn có sơ hở mà theo, tiến hành lợi dụng được, chưa hẳn không thể thu được thắng, mà bây giờ vọt thẳng đi qua... Sẽ chỉ càng thêm nguy hiểm."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Phùng Huân không hổ là Chiến sư đường thiên phu trưởng, một kiếm đâm ra, nhân kiếm hợp nhất, tựa như một đạo phá không mà đến sao băng, liền xem như Thánh vực tứ trọng cường giả đỉnh phong gặp gỡ, đều muốn tạm tránh mũi nhọn, không dám trực tiếp ngăn cản."),t("br"),t("br"),n._v("“Ừm? Thế mà còn chưa có chết?”"),t("br"),t("br"),n._v("Đang tại luyện hóa Lôi Nguyên châu, lập tức cảm nhận được nồng đậm sát khí, Thạch Diệp Vương chợt xoay người, thấy rõ Phùng Huân dung mạo, lông mày giương lên: “Thiên đường có đường ngươi không đi, địa ngục không cửa ngươi xông tới. Cho ta xuống đây đi!”"),t("br"),t("br"),n._v("Nói xong cũng không tránh né, một bàn tay tiếp tục luyện hóa Lôi Nguyên châu, cái tay còn lại chưởng bổ về đằng trước."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Hùng hậu sóng khí cuốn tới, cùng Phùng Huân kiếm mang đụng một cái, người sau lập tức giống chuỳ sắt đụng vào bát sứ, vỡ vụn thành từng mảnh."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Còn lại lực lượng đánh vào Phùng Huân ngực, để bay ngược ra ngoài, ngã rầm trên mặt đất, một ngụm máu tươi phun mạnh mà ra."),t("br"),t("br"),n._v("Thực lực chênh lệch thực sự quá lớn, chỉ một chiêu liền bị đánh bay."),t("br"),n._v("“Thật mạnh!” Trương Huyền con ngươi co rụt lại."),t("br"),t("br"),n._v("Thập đại vương giả, hắn đã gặp sáu cái, hơn nữa hầu như đều chết ở trên tay hắn, mặc dù đều là Thánh vực tứ trọng cường giả, nhưng cùng trước mắt vị này so sánh, căn bản không tại một cái cấp bậc."),t("br"),t("br"),n._v("Khó trách thượng ngũ vương bài danh thứ tư, để tím dạ vương vừa nghe đến tên đều không ngừng run rẩy, quả nhiên cường đại đáng sợ."),t("br"),t("br"),n._v("Bất quá, mạnh hơn cũng muốn chiến đấu."),t("br"),t("br"),n._v("Băng Vũ kiếm giữ tại lòng bàn tay, ở giữa không trung quẹt một nửa hình tròn, cùng Phùng Huân đồng dạng, đâm tới."),t("br"),t("br"),n._v("Chỉ là... Hắn công kích không phải cách đó không xa Thạch Diệp Vương, mà là trước mặt đối phương Lôi Nguyên châu."),t("br"),t("br"),n._v("Hiện tại chủ yếu nhất là ngăn cản đối phương luyện hóa món bảo vật này, công kích đối phương, hoàn toàn có thời gian hóa giải, thừa dịp bất ngờ lời nói, có lẽ có thể thành công."),t("br"),t("br"),n._v("Ô ô!"),t("br"),t("br"),n._v("Một kiếm này, hắn đã dùng hết toàn lực, chân khí trong cơ thể như sôi, phối hợp thân thể cùng linh hồn lực lượng, cắt ra không khí, phát ra nghẹn ngào thanh âm, tựa như muốn đem không gian đều xé rách."),t("br"),t("br"),n._v("Đây cũng là hắn thi triển kiếm pháp đến nay công kích cường đại nhất chiêu số, so với vừa rồi Phùng Huân công kích, đều không hề yếu."),t("br"),t("br"),n._v("“Không biết sống chết!”"),t("br"),t("br"),n._v("Nhìn ra mục đích của hắn, Thạch Diệp Vương híp mắt lại, cũng không thấy có dư thừa động tác, duỗi ra hai ngón tay, nhẹ nhàng bắn ra."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Chỉ cảm thấy gan bàn tay chấn động, Trương Huyền trong tay Băng Vũ kiếm cũng lại cầm không được, bay ngược ra ngoài, cắm ở cách đó không xa trên mặt tường, mà chính hắn cùng Phùng Huân đồng dạng, máu tươi phun ra, người trên không trung cưỡng ép biến hướng, bàn tay trên mặt đất vỗ, thật vất vả ổn định thân hình."),t("br"),t("br"),n._v("Trong mắt tràn đầy kinh hãi."),t("br"),t("br"),n._v("Thực lực của người này so với trong tưởng tượng còn muốn đáng sợ, hắn đã dùng hết toàn lực, không nghĩ tới liền đối phương một ngón tay đều không có chặn lại."),t("br"),t("br"),n._v("Không có tiếp tục hướng phía trước, vội vàng đem trong đầu thư tịch lật đi ra."),t("br"),t("br"),n._v("Vừa rồi chiến đấu, ép đối phương thi triển ra võ kỹ, đã có liên quan tới hắn giới thiệu."),t("br"),t("br"),n._v("“Thạch Diệp Vương, Thanh Điền thập đại vương giả, thượng ngũ vương một trong, tu vi Thánh vực tứ trọng đỉnh phong, một thân phòng ngự vô địch... Thiếu sót: Thứ nhất, vận chuyển công pháp lúc, mi tâm chân khí không đủ; Thứ hai, dưới nách mềm mại, lực lượng không cách nào khắp...”"),t("br"),t("br"),n._v("Rất mau đem thư tịch nhìn một lần, Trương Huyền chẳng những không có cao hứng trở lại, ngược lại khuôn mặt càng thêm khó coi."),t("br"),t("br"),n._v("Thiên Đạo thư viện hình thành thư tịch, quả thực đem hắn thiếu sót ghi xuống, chỉ là mi tâm, dưới nách... Những địa phương này, đều là phòng ngự đất nghiêm mật nhất, hơn nữa còn nhất định phải cận thân mới có thể công kích, hiện tại liền đối phương hai trượng phạm vi bên trong cũng chưa tới, liền bị nhẹ nhõm đánh lui, biết những này cũng vô ích ah!"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Đang tại quan sát, liền nghe đến một tiếng nổ vang, cách đó không xa Thạch Diệp Vương lại một chưởng vỗ đi."),t("br"),t("br"),n._v("Hùng hậu chưởng ấn, bao trùm khoảng cách mấy chục mét, đem hắn cùng Phùng Huân toàn bộ bao phủ ở bên trong, còn chưa tới đến trước mặt, cảm giác áp bách mạnh mẽ cũng làm người ta cảm thấy hô hấp dồn dập, khó mà chống lại."),t("br"),t("br"),n._v("“Nguy rồi!”"),t("br"),t("br"),n._v("Biết chống lại không được, bàn chân đạp mạnh, Thiên Đạo thân pháp thi triển, trực tiếp hướng ra phía ngoài bay đi."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Còn không có bay ra chưởng ấn phạm vi, liền cảm thấy lực lượng khổng lồ xốc tới, mạnh mẽ đánh vào trên lưng, lần nữa máu tươi phun ra, liên tục va sụp bảy, tám mặt bức tường, lúc này mới ngừng lại."),t("br"),t("br"),n._v("Giãy dụa lấy đứng lên, Trương Huyền trong mắt tràn đầy hoảng sợ."),t("br"),t("br"),n._v("May mắn thân thể vừa mới tiến bộ, nếu không lần này, không chết cũng muốn ninh một lớp da!"),t("br"),t("br"),n._v("Hắn mặc dù tu luyện Phong Thánh giải, gần nhất lại tiến bộ rất lớn, nhưng là cùng chân chính Thánh vực tứ trọng đỉnh phong so, vẫn là kém thực sự quá nhiều, căn bản không tại cùng một cái cấp độ, hoàn toàn không khả năng so sánh."),t("br"),t("br"),n._v("“Phùng Huân đâu?”"),t("br"),t("br"),n._v("Vội vàng nhìn lại, chỉ thấy Phùng Huân cũng đầy là chật vật nằm ở cách đó không xa, vẻ mặt trở nên trắng, máu tươi nhuộm đỏ trước ngực vạt áo."),t("br"),t("br"),n._v("Rất rõ ràng, thương thế so với chính mình còn nặng hơn."),t("br"),t("br"),n._v("Thực lực của hắn mặc dù cao hơn chính mình, nhưng chạy trốn bản lĩnh rõ ràng không bằng, vừa rồi một chưởng, đại bộ phận đều bị đón đỡ, nếu không phải thân là Chiến sư, thân thể, chân khí, linh hồn đều vượt xa bình thường danh sư, chỉ sợ hiện tại khả năng đã chết rồi."),t("br"),t("br"),n._v("Dù vậy, cũng toàn thân như nhũn ra, khung xương không biết đứt gãy mấy cái, muốn động đều không có phương pháp nhúc nhích."),t("br"),t("br"),n._v("“Chết hết đi!”"),t("br"),t("br"),n._v("Tiếp tục luyện hóa Lôi Nguyên châu, Thạch Diệp Vương cười lạnh, cái tay còn lại, lần nữa đập tới."),t("br"),t("br"),n._v("Động tác của hắn nhẹ nhàng, nhìn xem không có gì lực lượng, nhưng mà Trương Huyền biết, một khi bị đánh trúng, khẳng định sẽ hẳn phải chết không nghi ngờ, sợ tới mức biến sắc, dưới chân xoay một cái, bỗng nhiên hướng Phùng Huân lao đến."),t("br"),t("br"),n._v("Đối phương cùng mình cùng một chỗ tới, tuyệt không thể trơ mắt nhìn xem chết tại trước mặt."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Thiên Đạo thân pháp tốc độ cực nhanh, thời gian nháy mắt đi tới Phùng Huân trước mặt, bàn tay một trảo đem hắn kéo tại lòng bàn tay, đang muốn nhanh chóng lui về phía sau, liền phát hiện đã không còn kịp rồi, Thạch Diệp Vương chưởng ấn đã rơi vào đỉnh đầu."),t("br"),t("br"),n._v("“Ngoan Nhân!”"),t("br"),t("br"),n._v("Trong lòng la hét, một cỗ nồng đậm tinh thuần sát lục chi khí trong nháy mắt xông về trước tới."),t("br"),t("br"),n._v("Mặc dù cùng là sát lục khí tức, đối Dị Linh tộc nhân ảnh hưởng không lớn, nhưng đột nhiên bị quấy rầy, cũng có thể trì hoãn một lát."),t("br"),t("br"),n._v("Quả nhiên, cảm nhận được thanh niên trước mắt, phóng xuất ra cường đại như thế sát lục chi khí, Thạch Diệp Vương một mặt khiếp sợ, lòng bàn tay lực lượng chậm một tia."),t("br"),t("br"),n._v("Vù vù!"),t("br"),t("br"),n._v("Thừa dịp một tia thời gian rảnh rỗi, hắn đã bắt lấy Phùng Huân, trốn ra chưởng ấn chỗ công kích phạm vi."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1055"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1057"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);