(window.webpackJsonp=window.webpackJsonp||[]).push([[781],{1136:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-160-huynh-đe-giao-đau"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-160-huynh-đe-giao-đau"}},[n._v("#")]),n._v(" Chương 160: Huynh đệ giao đấu")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v(" Trong lớp những người khác nghe được luận võ, tất cả đều đứng ở góc tường một bên."),t("br"),t("br"),n._v(" Nhìn thấy trên người của hai người sinh ra khí tức, Triệu Nhã sắc mặt nghiêm túc."),t("br"),t("br"),n._v(" Trịnh Dương thực lực trước kém xa nàng, học tập Trương lão sư thương pháp phía sau, khí thế càng ngày càng mạnh, thật muốn đánh lên, nàng đã hoàn toàn không phải là đối thủ."),t("br"),t("br"),n._v(" “Ngươi nói hai người bọn họ ai có thể thắng?” "),t("br"),t("br"),n._v(" Lưu Dương hỏi."),t("br"),t("br"),n._v(" “Bọn họ từ nhỏ cùng nhau lớn lên, Mạc Hiểu đối với thương pháp lĩnh ngộ cùng thiên phú, đều không phải Trịnh Dương có thể so với, nghe nói giao thủ vô số lần, luôn luôn đều là người sau bị thua mà kết thúc, ta sợ lần này cũng rất khó vượt qua!” "),t("br"),t("br"),n._v(" Triệu Nhã chần chờ một chút, nói."),t("br"),t("br"),n._v(" Tuy rằng không muốn thừa nhận, Mạc Hiểu vẫn là cực kỳ đáng sợ."),t("br"),t("br"),n._v(" Liền ngay cả Trịnh Dương chính mình cũng biết, hoàn toàn không phải người bạn thân này đối thủ."),t("br"),t("br"),n._v(" Khả năng chính là bởi vì đã biết chuyện này, Lục Tầm mới cố ý theo Vương Siêu lão sư trong tay đem hắn đào được bản thân môn hạ, dùng để đối chiến, không thể không nói, thủ đoạn đủ tàn nhẫn."),t("br"),t("br"),n._v(" “Ta nghe nói vì ứng đối lần này sư giả bình trắc, Vương Siêu lão sư còn đem chính mình gia truyền thương pháp cho Mạc Hiểu truyền thụ vài chiêu!” Viên Đào tu vi không được, tin tức luôn luôn không kém, chần chờ một chút nói."),t("br"),t("br"),n._v(" “Gia truyền thương pháp? Ngươi nói Vương gia thương?” "),t("br"),t("br"),n._v(" Triệu Nhã tràn đầy khó mà tin nổi."),t("br"),t("br"),n._v(" “Vương gia thương rất nổi danh?” Thấy nàng dáng dấp này, Lưu Dương nhìn sang."),t("br"),t("br"),n._v(" “Thiên Huyền Vương thành có hai cái Vương gia nổi tiếng, một cái chính là chúng ta Vương gia, tứ đại gia tộc một trong, một cái khác, nhưng là thương pháp đại sư Vương Sùng gia tộc!” Lần này không phải Triệu Nhã trả lời, mà là Vương Dĩnh."),t("br"),t("br"),n._v(" Làm tứ đại gia tộc Vương gia con cháu, một cái khác Vương gia cùng bọn họ tiếng tăm gần như, tự nhiên biết đến rất rõ ràng."),t("br"),t("br"),n._v(" “Vương Sùng?” "),t("br"),t("br"),n._v(" “Không sai, Vương Sùng được gọi là Thiên Huyền Vương thành thương pháp đệ nhất nhân, liền ngay cả Thẩm Truy bệ hạ đều khen không dứt miệng, một mình sáng lập Vương gia thương, càng là nhất tuyệt, là vô số học tập thương pháp giả tha thiết ước mơ tuyệt chiêu!” Vương Dĩnh mang theo sùng bái nói."),t("br"),t("br"),n._v(" “Lợi hại như vậy?” "),t("br"),t("br"),n._v(" Lưu Dương líu lưỡi, không nhịn được nói: “Vậy vị này Vương Sùng cùng Vương Siêu lão sư quan hệ gì?” "),t("br"),t("br"),n._v(" “Vương Siêu là vị này thương pháp đại sư con trai độc nhất, chính vì như thế, mới có thể dẫn được vô số thương pháp người tu luyện, tranh nhau chen lấn bái vào môn hạ!” Vương Dĩnh nói."),t("br"),t("br"),n._v(" “Thì ra là như vậy!” Lần thứ hai nhìn về phía giữa trường, Lưu Dương trên mặt lộ ra vẻ lo âu: “Nói như vậy, Trịnh Dương chẳng phải rất nguy hiểm?” "),t("br"),t("br"),n._v(" “Đúng đấy, Vương gia thương rất lợi hại, vốn là thuộc về bí mật bất truyền, không nghĩ tới Vương Siêu lão sư đánh vỡ quy củ, truyền hắn mấy chiêu, thật là hào phóng, xem ra vì lần này sư giả bình trắc, thực sự là bỏ ra vốn lớn.” Triệu Nhã hừ lạnh."),t("br"),t("br"),n._v(" Vương gia thương thuộc về gia truyền thương pháp, truyền con bất truyền nữ, chưa bao giờ truyền người ngoài, theo Viên Đào trong miệng, Vương Siêu lão sư dĩ nhiên truyền cho Mạc Hiểu mấy chiêu, bởi vậy có thể thấy được đối với sư giả bình trắc coi trọng."),t("br"),t("br"),n._v(" Vốn là Trịnh Dương liền đánh không lại vị này Mạc Hiểu, người sau vừa học tập Vương gia thương pháp, thế nào có thể là đối thủ?"),t("br"),t("br"),n._v(" Trong lúc nhất thời, mọi người tràn đầy lo lắng."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v(" Giữa trường."),t("br"),t("br"),n._v(" “Vương lão sư truyền thụ ta Vương gia thương, thức thứ ba, thức thứ bảy cùng thức thứ mười tám ba chiêu, ngươi phải cẩn thận!” Mũi thương xoay một cái, thẳng tắp chỉa sang, Mạc Hiểu nói."),t("br"),t("br"),n._v(" “Thức thứ ba, Hàn Mang Sạ Khởi, thức thứ bảy, Tịch Dương Dư Huy, thức thứ mười tám, Quy Điểu Nhập Lâm? Tốt!” Trịnh Dương vẻ mặt nghiêm túc."),t("br"),t("br"),n._v(" Vương gia thương tiếng tăm rất lớn, chỉ cần học thương cơ bản đều có nghiên cứu, bất quá, chỉ biết là tên cùng chiêu số, không biết tu luyện tâm pháp, chỉ biết hắn hình, không biết ý nghĩa thôi."),t("br"),t("br"),n._v(" “Vậy ta bắt đầu rồi, thức thứ ba, Hàn Mang Sạ Khởi!” "),t("br"),t("br"),n._v(" Một tiếng thở phào, Mạc Hiểu trường thương run rẩy, đâm thẳng mà ra."),t("br"),t("br"),n._v("  Thương chưa tới, kình khí liền theo thương thẳng tắp lan tràn tới, hàn mang hiện ra, đoạt mệnh truy hồn, để người toàn thân băng hàn."),t("br"),t("br"),n._v(" Chiêu này đem toàn thân tinh khí thần đều tập trung tại thương mang lên, lực thấu cán thương, mấy cái phía sau tấm thép e sợ đều có thể một đòn mà truyền."),t("br"),t("br"),n._v(" Mạc Hiểu không hổ là thương pháp thiên tài, Vương gia thương khó có thể luyện thành, hắn đều có thể phát huy ra mạnh mẽ như vậy sức mạnh."),t("br"),t("br"),n._v(" “Đến hay lắm!” "),t("br"),t("br"),n._v(" Nhìn thấy đối phương thương pháp lợi hại như vậy, Trịnh Dương con ngươi co rụt lại, một thương cũng tiến lên nghênh tiếp."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v(" “Thật là lợi hại thương pháp...” "),t("br"),t("br"),n._v(" Nhìn thấy Mạc Hiểu kình khí bắn ra bốn phía, trường thương khác nào sét đánh, mọi người tất cả đều sắc mặt ngưng lại."),t("br"),t("br"),n._v(" “Triệu Nhã, Mạc Hiểu nếu học được như thế lợi hại thương pháp, vì sao không chờ tỷ thí thời điểm tại cùng Trịnh Dương luận bàn, mà muốn lúc này chạy tới?” Vương Dĩnh có chút không hiểu."),t("br"),t("br"),n._v(" Học như thế lợi hại chiêu số, chắc là lặng lẽ luyện tập, tỷ thí thời điểm triển khai ra, mới có thể một tiếng hót lên làm kinh người, để Trịnh Dương không ứng phó kịp, hiện tại chạy tới tỷ thí, Trịnh Dương sớm đã biết, có phòng bị, lần thứ hai luận võ, liền e sợ không thuận lợi như vậy."),t("br"),t("br"),n._v(" “Là tình nghĩa huynh đệ!” Triệu Nhã cũng không làm rõ ràng được, một bên Lưu Dương mở miệng."),t("br"),t("br"),n._v(" “Tình nghĩa huynh đệ?” "),t("br"),t("br"),n._v(" “Ân, Mạc Hiểu cùng Trịnh Dương từ nhỏ cùng nhau lớn lên, để hắn cùng với tỷ thí, hắn khẳng định từ nội tâm có chút chống cự, bởi vậy chuyên môn đến tìm bạn tốt, nói là tỷ thí, trên thực tế là muốn nói cho bạn tốt, học tập cái gì, để hắn có cái phòng bị! Ngươi không thấy tỷ thí trước, hắn trước đem học được chiêu số nói rồi sao? Bình thường luận võ, nào có đem mình muốn dùng chiêu số gì cũng nói một lần?” "),t("br"),t("br"),n._v(" Chỉ có nam nhân mới có thể hiểu được tình nghĩa huynh đệ, Mạc Hiểu làm được tuy rằng mịt mờ, Triệu Nhã, Vương Dĩnh không thể nào hiểu được, lại làm sao có khả năng giấu giếm được Lưu Dương!"),t("br"),t("br"),n._v(" “Thì ra là như vậy!” "),t("br"),t("br"),n._v(" Mọi người lúc này mới chợt hiểu ra, lần thứ hai nhìn lại."),t("br"),t("br"),n._v(" Leng keng Keng!"),t("br"),t("br"),n._v(" Mấy người nói chuyện ở giữa, giữa trường dĩ nhiên giao thủ nhiều lần, tiếng thương vang lên giòn giã, kình khí phân tán bốn phía, Trịnh Dương quả nhiên có chút luống cuống tay chân, không chống đỡ được, liên tiếp lui về phía sau."),t("br"),t("br"),n._v(" “Thương pháp tốt, ngươi có học tập, ta cũng không nhàn rỗi, Mạc Hiểu, hiện tại liền để ngươi xem một chút Trương lão sư dạy ta thương pháp!” "),t("br"),n._v("  Ở hạ phong Trịnh Dương vẫn chưa căng thẳng, trái lại nhẹ nhàng nở nụ cười."),t("br"),t("br"),n._v(" Hắn vừa nãy vẫn chưa triển khai Trương Huyền dạy thương pháp, chỉ là dùng bản thân đối với thương pháp lý giải cùng cơ sở, tự nhiên không cách nào ngăn cản đối phương Vương gia thương."),t("br"),t("br"),n._v(" Ầm ầm!"),t("br"),t("br"),n._v(" Tiếng nói kết thúc, trường thương cuốn một cái, giống như Cự Long tung bay, sông dài chạy gấp sông, tuy rằng chỉ là rất phổ thông một chiêu, Mạc Hiểu lại cảm thấy cự núi đè xuống, cảm giác áp bách mạnh mẽ, để hắn không thở nổi."),t("br"),t("br"),n._v(" Oành!"),t("br"),t("br"),n._v(" Còn không phản ứng lại, Mạc Hiểu liền cảm thấy ngực một khó chịu, triển khai Vương gia thương trong nháy mắt phá nát, cả người lập tức bay ngược ra ngoài, sát mặt đất trượt bảy, tám mét này mới ngừng lại."),t("br"),t("br"),n._v(" “A?” "),t("br"),t("br"),n._v(" Trịnh Dương hiển nhiên cũng không nghĩ tới chiêu này lại có thể có uy lực này, sợ hết hồn."),t("br"),t("br"),n._v(" Từ khi học được chiêu này, hắn vẫn tu luyện, nằm mơ đều không nghĩ tới lợi hại như vậy."),t("br"),t("br"),n._v(" Lúc trước đánh bại Chu Thiên lão sư, hắn vẫn cho rằng là Trương lão sư đạo kia chân khí duyên cớ, bây giờ mới biết, cùng cấp bậc bên dưới, coi như không có đạo kia chân khí, chiêu này thương pháp, cũng tuyệt đối là sự tồn tại vô địch."),t("br"),t("br"),n._v(" Mạc Hiểu, từ nhỏ đánh tới lớn, bất luận thương pháp vẫn là thực lực, đều xa không phải là đối thủ, hiện tại càng là học tập Vương gia thương, vốn là có thể chính diện đối chiến là tốt lắm rồi, nằm mơ đều không nghĩ tới, một chiêu liền để hắn bay ngược mà ra, nếu không là đúng lúc thu hồi sức mạnh, e sợ đều sẽ bị thương."),t("br"),t("br"),n._v(" “Không có sao chứ!” "),t("br"),t("br"),n._v(" Hai bước đi tới trước mặt, đem bạn tốt nâng dậy."),t("br"),t("br"),n._v(" “Ngươi... Ngươi đây là chiêu số gì?” "),t("br"),t("br"),n._v(" Giẫy giụa đứng dậy, Mạc Hiểu con mắt trợn tròn, đầy mặt không thể tin được."),t("br"),t("br"),n._v(" Hắn dùng thế nhưng Vương gia thương, được gọi là Thiên Huyền Vương quốc phát súng đầu tiên pháp, kết quả... Tại đối phương thương pháp trước mặt cùng trò trẻ con bình thường buồn cười, một đòn bại trận."),t("br"),t("br"),n._v(" Không phải tận mắt nhìn thấy, tuyệt không thể tin được."),t("br"),t("br"),n._v(" “Đây là Trương lão sư truyền thụ cho ta thương pháp!” Trịnh Dương tràn đầy kiêu ngạo nói."),t("br"),t("br"),n._v(" Lúc trước hắn mơ ước lớn nhất chính là đi Vương Siêu lớp học, hi vọng có cơ hội có thể nhìn thấy Vương gia thương, học được một, hai, hiện tại mới rõ ràng, nhờ có lúc trước Vương lão sư không muốn hắn, bằng không, lại tại sao có thể có như vậy kỳ ngộ, trở thành Trương lão sư học sinh!"),t("br"),t("br"),n._v(" “Trương lão sư truyền thụ? Chuyện này... Như thế lợi hại thương pháp, hắn tựu cái này dạng truyền cho ngươi?” "),t("br"),t("br"),n._v(" Mạc Hiểu vẫn như cũ khó có thể tin tưởng được."),t("br"),t("br"),n._v(" Vương gia thương, Vương Siêu lão sư coi là trân bảo, nếu không là Lục Tầm lão sư thỉnh cầu, đồng thời muốn tiến hành sư giả bình trắc, ba chiêu này khẳng định đều không biết truyền thụ."),t("br"),t("br"),n._v(" Chiêu này so Vương gia thương cao minh gấp đôi không ngừng, đơn giản truyền thụ cho Trịnh Dương, thật hay giả?"),t("br"),t("br"),n._v(" “Trương lão sư truyền thụ chưa bao giờ giấu làm của riêng, là một vị chân chính sư giả!” Trịnh Dương kiên định nói."),t("br"),t("br"),n._v(" “Chuyện này...” "),t("br"),t("br"),n._v(" Thấy bạn tốt lời nói xuất phát từ nội tâm, là thành thật thực lòng đối với vị này Trương lão sư sùng bái, Mạc Hiểu nhẹ nhàng nở nụ cười."),t("br"),t("br"),n._v(" Vốn tưởng rằng không bái vào Vương Siêu môn hạ, làm bạn tốt cảm thấy đáng tiếc, không nghĩ tới đối phương lạy một vị càng tốt hơn lão sư, học tập càng cao thâm thương pháp."),t("br"),t("br"),n._v(" Này một chiêu dùng ra, hắn liền biết, đừng nói chỉ học được ba chiêu Vương gia thương, coi như tất cả học được, cũng xa không phải là đối thủ."),t("br"),t("br"),n._v(" “Thương pháp này lợi hại như vậy, ta bị bại tâm phục khẩu phục, có thể hay không nói cho tên ta?” "),t("br"),t("br"),n._v(" Một lát sau, Mạc Hiểu không nhịn được nói."),t("br"),t("br"),n._v(" Thua ở súng gì pháp bên dưới, hắn cũng phải biết tên mới được."),t("br"),t("br"),n._v(" “Tên?” Trịnh Dương gãi đầu một cái một mặt lúng túng: “Ta cũng không biết gọi tên gì, là Trương lão sư mới vừa sáng tạo ra, còn chưa kịp lấy...” "),t("br"),t("br"),n._v(" “Sáng tạo ra? Chưa kịp lấy?” Mạc Hiểu một cái lảo đảo, suýt chút nữa liền không tại chỗ ngất đi."),t("br"),t("br"),n._v(" Có thể sáng chế thương pháp, người nào không phải chân chính thương đạo đại sư, có vô số kinh người từng trải, thông thường thương pháp không thành hình phía trước, thì có tên, còn có như vậy... Đều truyền thụ học sinh, liền cái tên đều chưa nghĩ ra?"),t("br"),t("br"),n._v(" Hơn nữa, bộ này thương pháp so Vương gia thương còn lợi hại hơn, xưng là Thiên Huyền Vương quốc phát súng đầu tiên pháp đều không quá đáng... Không đặt tên..."),t("br"),t("br"),n._v(" Ngươi không biết lừa ta chơi đùa đi!"),t("br"),t("br"),n._v(" “Nếu không như vậy đi, ngươi giúp ta ngẫm lại, chắc là lấy cái cái gì tốt...” "),t("br"),t("br"),n._v(" Chính đang khiếp sợ, liền nghe đến bạn tốt âm thanh tiếp tục vang lên."),t("br"),t("br"),n._v(" “Ngươi...” "),t("br"),t("br"),n._v(" Nghe nói như thế, Mạc Hiểu sầm mặt lại, không nhịn được nói: “Trịnh Dương, nói cẩn thận! Mỗi một cái người tu luyện sáng chế võ kỹ, đều là tâm huyết tác phẩm, định tên cũng có thể lưu truyền Thiên Cổ, nếu chiêu này là Trương lão sư sáng chế, nhất định phải hắn đến đặt tên, ngươi lấy... Còn thể thống gì? Vạn nhất được biết, chẳng phải nổi giận, nói ngươi đang làm nhục tâm huyết của hắn?” "),t("br"),t("br"),n._v(" Ngươi học người khác chiêu số, còn muốn cho người khác chiêu số đặt tên, đùa gì thế?"),t("br"),t("br"),n._v(" “A...” "),t("br"),t("br"),n._v(" Thấy bạn tốt ngữ khí nghiêm trọng, không giống đùa giỡn, Trịnh Dương sững sờ."),t("br"),t("br"),n._v(" Sỉ nhục tâm huyết?"),t("br"),t("br"),n._v(" Trương lão sư liền sáng chế công pháp, đều để nhóm người mình tùy tiện đặt tên..."),t("br"),t("br"),n._v(" Nghĩ tới đây không nhịn được, nói: “Không có chuyện gì, Trương lão sư đã nói, bảo ta tùy tiện lấy, thực không dám giấu giếm, mấy người chúng ta công pháp tu luyện đều là Trương lão sư sáng lập, tên cũng đều do tự chúng ta nghĩ...” "),t("br"),t("br"),n._v(" “Cái gì? Sáng chế công pháp, võ kỹ để cho các ngươi mệnh danh?” "),t("br"),t("br"),n._v(" Mạc Hiểu thân thể loáng một cái, suýt chút nữa không tại chỗ ngất đi."),t("br"),t("br"),n._v(" Lão sư khác, truyền thụ một cái võ kỹ, chỉ lo bản thân truyền ra ngoài, cái này Trương lão sư ngược lại tốt, chuyên môn cho học sinh sáng tạo công pháp, võ kỹ, còn để bọn họ lấy tên..."),t("br"),t("br"),n._v(" Trương Huyền... Đây rốt cuộc là cái ra sao lão sư?"),t("br"),t("br"),n._v(" (Chưa xong còn tiếp.) Điện thoại di động người sử dụng mời xem lướt qua xem, càng chất lượng tốt xem trải nghiệm."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-168"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-170"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);