(window.webpackJsonp=window.webpackJsonp||[]).push([[787],{1142:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1683-đai-thong-linh"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1683-đai-thong-linh"}},[n._v("#")]),n._v(" Chương 1683: Đại thống lĩnh")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1683: Đại thống lĩnh"),t("br"),t("br"),n._v("“Mưu Vu đại nhân, quả thực hẳn phải biết, nhưng ta không biết...”"),t("br"),t("br"),n._v("Thấy bị nhận ra, Trương Huyền chẳng muốn tiếp tục ngụy trang, cười ha ha một tiếng."),t("br"),t("br"),n._v("“Hắn không phải Mưu Vu đại nhân, động thủ...”"),t("br"),t("br"),n._v("Ba đầu Dị Linh tộc nhân, đồng tử co rụt lại, đang muốn động thủ, trên không một đạo to lớn thân ảnh nhanh chóng hạ xuống."),t("br"),t("br"),n._v("Lạch cạch!"),t("br"),t("br"),n._v("Chủ trì trận pháp gia hỏa, lúc này bị đập thành bánh thịt."),t("br"),t("br"),n._v("Ngay sau đó một cái cốt long bay ra, há miệng cắn mất một người khác đầu, một chuôi yêu đao, ung dung đem người cuối cùng chém thành hai nửa."),t("br"),t("br"),n._v("Ba đầu Kiến Thần Bất Hoại cấp bậc Dị Linh tộc nhân mà thôi, đã bị nhận ra, giết chính là."),t("br"),t("br"),n._v("Xử lý xong thi thể, Trương Huyền chân khí trong cơ thể, rót vào trước mắt truyền tống trận, ánh sáng chợt lóe, từ biến mất tại chỗ."),t("br"),t("br"),n._v("Bản thân hắn chính là trận pháp đại tông sư, hơn nữa đối với không gian lĩnh ngộ, không người có thể địch, kích hoạt trận pháp, mười phần đơn giản."),t("br"),t("br"),n._v("Thân ảnh nhoáng một cái, xuất hiện tại một cái khác trên truyền tống trận, giương mắt nhìn lại, là cái to lớn sơn cốc, bốn phía dãy núi cao thấp nhấp nhô, đem nơi này hoàn toàn bao vây."),t("br"),t("br"),n._v("“Gặp qua thống lĩnh!”"),t("br"),t("br"),n._v("Mới vừa đứng vững thân hình, một bên liền vang lên thanh âm."),t("br"),t("br"),n._v("  Là mấy cái trận pháp bảo vệ binh sĩ, cấp bậc đều không cao, hẳn là nhìn ra trên người hắn khôi giáp đẳng cấp, cũng không phải là nhận ra hắn là ai."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Cũng không trả lời, Trương Huyền sải bước đi thẳng về phía trước."),t("br"),t("br"),n._v("Sơn cốc bao la, Huyết Nguyệt treo chếch tại núi xa bên trên, từ xa nhìn lại, tựa như một bức tranh thuỷ mặc."),t("br"),t("br"),n._v("Vô số binh sĩ, tạo thành từng cái phương trận, phân tán toàn bộ sơn cốc, từ xa nhìn lại, đều nhịp, không có một chút tạp tiếng, chỉ bằng vào kỷ luật, liền có thể biết, tuyệt đối là nghiêm chỉnh huấn luyện mạnh mẽ chi sư."),t("br"),t("br"),n._v("Bởi vì là mấy cái Địa quật đội ngũ hòa vào nhau, Mưu Vu tuy là lạ mặt, lại không người nghi ngờ, một đường chậm rãi tiến lên, càng đi Trương Huyền càng là hoảng sợ."),t("br"),t("br"),n._v("Những binh sĩ này, cùng Long Hổ Sơn Địa quật tương tự, thấp nhất đều đạt đến Thánh vực tam trọng."),t("br"),t("br"),n._v("Mười vạn loại cường giả cấp bậc này đồng thời ra tay, Cổ Thánh đều muốn tạm tránh mũi nhọn!"),t("br"),t("br"),n._v("“Nhất định phải ngăn cản, không phải... Khẳng định muốn rơi vào khổ chiến!”"),t("br"),t("br"),n._v("Lông mày nhăn lại."),t("br"),t("br"),n._v("Danh Sư đường đi qua nhiều năm như vậy phát triển, toàn bộ cộng lại, gom góp mười vạn Thánh vực tam trọng, còn có thể làm được, nhưng trấn thủ các nơi, lại thêm 108 chỗ Địa quật, đồng thời có người tấn công, đã sớm phân tán ra."),t("br"),t("br"),n._v("Một khi cái đội ngũ này lao ra, đề phòng không bằng phía dưới, không biết phải chết bao nhiêu."),t("br"),t("br"),n._v("Chỉ là... Làm sao ngăn cản?"),t("br"),t("br"),n._v("Coi như đạt tới Cổ Thánh cấp bậc, cũng không dám mạnh mẽ chống đỡ, chớ nói chi là hiện tại."),t("br"),t("br"),n._v("Thật muốn xông tới, chỉ sợ còn không có giết mấy người, liền bị vây công, chết cũng không biết chết như thế nào."),t("br"),t("br"),n._v("Dùng Thiên đạo chân khí hạ độc... Chân khí trong cơ thể toàn bộ tiêu hao sạch sẽ cũng không đủ... Còn muốn cùng trước đó đồng dạng, mượn lôi đình, không có thiên cơ sư pháp bảo, cũng vô dụng ah!"),t("br"),t("br"),n._v("Lại nói, tất cả binh sĩ cũng phải nghe lời nói mới được."),t("br"),t("br"),n._v("“Tìm được trước bọn họ thống lĩnh tối cao nhất... Nhìn một chút, có thể hay không đánh giết!”"),t("br"),t("br"),n._v("Suy tư phút chốc, nghĩ không ra biện pháp tốt hơn, mắt sáng lên."),t("br"),t("br"),n._v("Bắt người trước hết phải bắt ngựa, bắt giặc trước bắt vua."),t("br"),t("br"),n._v("Đã không thể đem những này Dị Linh tộc nhân một mẻ hốt gọn, trước hết nghĩ phương pháp, tìm tới thống lĩnh tối cao nhất lại nói, chỉ cần có thể giải quyết người này, còn lại hẳn là sẽ đơn giản không ít."),t("br"),t("br"),n._v("Minh Lý Chi Nhãn vận chuyển, bốn phía nhìn một vòng."),t("br"),t("br"),n._v("Rất nhanh, một đám binh sĩ vây quanh to lớn lều vải, xuất hiện tại tầm mắt."),t("br"),t("br"),n._v("Chậm rãi tới gần, tìm cái che đậy địa phương, ngón tay một điểm, trước mắt không gian lần nữa bóp méo, cả người bất ngờ biến mất không thấy gì nữa."),t("br"),n._v(" Che giấu tốt thân hình, hướng lều vải bay lượn mà đi, chỉ chốc lát đi tới trước mặt."),t("br"),t("br"),n._v("Sột soạt!"),t("br"),t("br"),n._v("Ở bên ngoài đợi không lâu sau, một cái giống như hắn, người mặc màu đen khôi giáp thống lĩnh, vén rèm cửa lên đi vào, theo sát ở phía sau, Trương Huyền nhẹ nhàng nhoáng một cái, cũng tiến vào bên trong."),t("br"),t("br"),n._v("Gian phòng bên trong, bếp lò bùng cháy, nhiệt độ hơi ấm, nhìn về phía trước, ngồi mười bảy, tám người, mỗi một cái đều người mặc màu đen khôi giáp, vừa nhìn liền biết cùng Mưu Vu địa vị tương đương, thống lĩnh cấp bậc."),t("br"),t("br"),n._v("“Đều là Bất Hủ cảnh cường giả?”"),t("br"),t("br"),n._v("Lông mày nhảy một cái."),t("br"),t("br"),n._v("Trước mắt những này, vậy mà đều là Đại Thánh tứ trọng Bất Hủ cảnh cường giả, nhìn thực lực, không thể so với Mưu Vu thấp, đặc biệt là ở chính giữa cầm trong tay kim đao ngồi ngay ngắn trung niên nhân, khí tức mơ hồ, so với Dương sư, đều không kém chút nào!"),t("br"),t("br"),n._v("Thân thể cứng ngắc, Trương Huyền động cũng không dám động."),t("br"),t("br"),n._v("May mắn, không có lỗ mãng, mà là đi theo một cái hắc giáp người đi vào, nếu không, cho dù phong cấm thân thể không gian bốn phía, cũng rất dễ dàng sẽ bị phát giác."),t("br"),t("br"),n._v("Trong khoảng thời gian này, tiến bộ rất lớn, có thể... Đối mặt nhiều cường giả như vậy, khẳng định là trốn không thoát."),t("br"),t("br"),n._v("“Bẩm báo đại thống lĩnh, bột cực nhọc bên kia, không đồng ý chúng ta yêu cầu...”"),t("br"),t("br"),n._v("Vừa mới tiến tới hắc giáp người ôm quyền."),t("br"),t("br"),n._v("“Không đồng ý?”"),t("br"),t("br"),n._v("Kim đao trung niên nhân mặt không hề cảm xúc."),t("br"),t("br"),n._v("“Hắn nói, vật liệu của bọn họ, cũng có hạn, không có khả năng cho chúng ta phân nhiều, mọi người đều vì mình chủ, cũng đừng có đối với chuyện này so đo!” Hắc giáp nhân đạo."),t("br"),t("br"),n._v("“Không so đo? Hắn đến đánh ý kiến hay, để chúng ta xung phong, mượn một nhóm khôi giáp cũng không cho, rõ ràng lấy để chúng ta tự tìm đường chết, tốt nhân cơ hội cướp chỗ tốt!”"),t("br"),t("br"),n._v("“Không sai, Thần tinh Hoàng từ trước tới nay hai không tham ăn hòa, không nghĩ tới vị này bột cực nhọc cũng như vậy gian xảo, tất cả mọi người liên minh, còn muốn xuất công không xuất lực, nào có chuyện tốt như vậy!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nghe được hắc giáp người, gian phòng bên trong lập tức vỡ tổ."),t("br"),t("br"),n._v("Kim đao trung niên nhân bàn tay nâng lên, cắt đứt thảo luận: “Theo chân bọn họ nói, không bỏ vốn cũng được, hai chúng ta nhóm người đội ngũ, kề vai sát cánh, ai cũng không thể lùi bước, nếu không, liền đem Thần tinh Hoàng luyện chế tinh thần khôi giáp lấy ra, không cần nhiều, một trăm chụp. Nếu không... Đánh rồi mới biết! Ta cùng hắn tỷ thí một trận, thắng, theo hắn làm, thua, nghe ta!”"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Hắc giáp người gật đầu, lần nữa lui ra ngoài."),t("br"),t("br"),n._v("Trương Huyền dừng lại một chút, nhẹ nhàng chợt lóe, theo ở phía sau đi ra lều vải."),t("br"),t("br"),n._v("Theo sát tại hắc giáp thân người về sau, đi một hồi, xa xa nhìn thấy lại một cái to lớn lều vải, xuất hiện tại tầm mắt."),t("br"),t("br"),n._v("“Nghe bọn hắn đối thoại, nơi này tuy là có mười vạn người, lại chia làm hai nhóm, một cái thuộc về Thần tinh Hoàng, một cái thuộc về Thần Linh Hoàng!”"),t("br"),t("br"),n._v("Trương Huyền suy tư."),t("br"),t("br"),n._v("Tuy là không biết, cái này hai đại Hoàng giả, đến cùng khác nhau ở chỗ nào, nhưng nghe đến lời nói, kết hợp trước đó biết tin tức, hai thế lực lớn, cũng không như trong tưởng tượng như vậy hòa thuận."),t("br"),t("br"),n._v("“Có khoảng cách tốt nhất, chỉ cần nghĩ biện pháp khiêu khích, để cho bọn họ đánh trước lên... Địa quật bên kia tai hoạ ngầm liền giải trừ...”"),t("br"),t("br"),n._v("Ánh mắt sáng lên."),t("br"),t("br"),n._v("Mười vạn người, nếu là trên dưới một lòng, thật đúng là hết cách rồi, chỉ có thể nghĩ biện pháp báo tin nhân tộc, trước thời hạn làm ra chuẩn bị, đã chia làm hai cái đội ngũ, thoạt nhìn lại có chút không quá hòa thuận... Vậy thì có máy thừa dịp!"),t("br"),t("br"),n._v("Thân ảnh nhoáng một cái, Trương Huyền thay đổi dung mạo, sau một khắc, lấy rơi ẩn giấu ở không gian bốn phía phong cấm, thân hình hiển lộ ra."),t("br"),t("br"),n._v("“Khục!”"),t("br"),t("br"),n._v("Lộ ra thân hình, cố ý ho khan một tiếng."),t("br"),t("br"),n._v("“Người nào!”"),t("br"),t("br"),n._v("Đang hướng đối diện trước lều vào hắc giáp người, nghe được thanh âm, vội vàng xoay người, thấy rõ dung mạo, giật nảy mình, vội vàng khom người đến cùng: “Đại thống lĩnh, ngươi... Sao lại tới đây?”"),t("br"),t("br"),n._v("Trương Huyền ngụy trang, chính là vừa rồi trong phòng, cầm trong tay kim đao đại thống lĩnh."),t("br"),t("br"),n._v("(Vì đường thanh Thư minh chủ tăng thêm, tham gia bớt tác hợp nhà văn đại biểu đại hội, dùng di động gõ một chương.)"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1694"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1696"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);