(window.webpackJsonp=window.webpackJsonp||[]).push([[788],{1143:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1684-khieu-khich-ly-gian-【-canh-thu-nhat-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1684-khieu-khich-ly-gian-【-canh-thu-nhat-】"}},[n._v("#")]),n._v(" Chương 1684: Khiêu khích ly gián 【 canh thứ nhất 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1684: Khiêu khích ly gián "),t("br"),t("br"),n._v("“Ta đột nhiên nhớ tới một việc, ngươi chuyển cáo cho Bột Tân!”"),t("br"),t("br"),n._v("Bắt chước thanh âm của đối phương và khí chất, Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Đại thống lĩnh dặn dò chính là!” Hắc giáp người vội vàng khom người."),t("br"),t("br"),n._v("“Đưa lỗ tai tới, chuyện này không thể để cho người biết được...” Mí mắt vừa nhấc, Trương Huyền nói."),t("br"),t("br"),n._v("“Vâng...”"),t("br"),t("br"),n._v("Tràn đầy nghi ngờ, đối phương vì sao không truyền âm, hắc giáp người suy nghĩ một chút, vẫn là đi tới trước mặt, đầu lâu sát lại, không dám nâng lên."),t("br"),t("br"),n._v("Nơi này là bọn họ đại bản doanh, nằm mơ cũng không dám nghĩ, trước mắt vị này là giả."),t("br"),t("br"),n._v("“Là như vậy...”"),t("br"),t("br"),n._v("Hạ giọng, Trương Huyền mới nói ba chữ, trước mắt một đạo hàn mang hiện lên."),t("br"),t("br"),n._v("Hắc giáp người mới vừa cảm thấy gặp nguy hiểm, liền cảm thấy tinh thần một cái rời đi thân thể, đầu lâu rơi trên mặt đất."),t("br"),t("br"),n._v("Hữu tâm tính vô tâm, lại thêm Yêu Dị huyền đao, cái tên này, cho dù đạt đến Bất Hủ cảnh, cũng bị tại chỗ thuấn sát."),t("br"),t("br"),n._v("Đương nhiên, cũng là bởi vì đối phương chỉ có Bất Hủ cảnh sơ kỳ, mạnh hơn một chút, liền không dễ dàng như vậy."),t("br"),t("br"),n._v("Đem cái tên này thi thể thu vào trữ vật giới chỉ, loại bỏ tất cả dấu vết, biến thành bộ dáng của đối phương."),t("br"),t("br"),n._v("Vừa rồi thời điểm chiến đấu, hắn liền phong cấm không gian bốn phía, cho dù có người dò xét, cũng khó có thể phát giác."),t("br"),t("br"),n._v("Ngụy trang thành cái này hắc giáp người, Trương Huyền sải bước hướng về phía trước rộng lớn doanh trướng đi đến."),t("br"),t("br"),n._v("Vừa tới đến trước cửa, hai vị binh sĩ, giơ lên trường thương, chặn lại đường đi."),t("br"),t("br"),n._v("“Láo xược! Ta đại biểu chúng ta đại thống lĩnh mà đến, còn không mau đi bẩm báo!” Hai tay chắp sau lưng, Trương Huyền trong mắt mang theo uy nghiêm."),t("br"),t("br"),n._v("Một cái hộ vệ, chần chờ một chút, xoay người đi vào lều vải, một lát sau, vẫy tay một cái: “Mời!”"),t("br"),t("br"),n._v("Hừ một tiếng, Trương Huyền theo ở phía sau đi vào."),t("br"),t("br"),n._v("Cái này doanh trướng cùng vừa rồi cái kia đồng dạng, bên trong cũng ngồi ngay thẳng mười cái Bất Hủ cảnh cường giả, ở giữa Dị Linh tộc nhân, khí tức so với đại thống lĩnh đều không kém chút nào, hẳn là đối phương trong miệng nói vị kia Bột Tân."),t("br"),t("br"),n._v("“Ô Mộc đồng ý điều kiện của ta?”"),t("br"),t("br"),n._v("Bột Tân thấy hắn lần nữa đi vào, mí mắt vừa nhấc."),t("br"),t("br"),n._v("“Đại thống lĩnh đồng ý, có điều, có chút chi tiết, muốn cùng ngươi ngay mặt hiệp thương!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Kẻ thức thời mới là tuấn kiệt, nhìn tới Ô Mộc, cũng không có trong truyền thuyết quật cường như vậy!” Nghe được đồng ý, Bột Tân dường như cũng không ngờ tới, ngay sau đó cười ha ha một tiếng: “Ở đâu hiệp thương?”"),t("br"),t("br"),n._v("“Việc quan hệ hai nhà liên minh, cần cẩn thận, ngay tại hai cái đại trướng ở giữa cái kia mảnh trong khe núi, nếu như ngươi không có ý kiến, mời tại sau nửa canh giờ đi tới, nhà ta đại thống lĩnh, sẽ tại nơi đó đợi chờ!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Vừa rồi tới thời điểm, nhìn thấy hai cái này trong lều vải gian, có một chỗ khe núi, địa phương không lớn."),t("br"),t("br"),n._v("“Tốt!” Bột Tân gật đầu."),t("br"),t("br"),n._v("“Cáo từ!”"),t("br"),t("br"),n._v("Trương Huyền lui ra ngoài, vừa mới rời khỏi, trong lều vải hắc giáp người, đứng dậy, vội vàng ôm quyền."),t("br"),t("br"),n._v("“Đại thống lĩnh, Ô Mộc từ trước tới nay gian xảo, lần này đột nhiên nói như vậy, ta sợ có bẫy!”"),t("br"),t("br"),n._v("“Đúng vậy a, cái tên này, hẹn ngươi ra ngoài, sợ có mai phục!”"),t("br"),t("br"),n._v("Lại có người nói."),t("br"),t("br"),n._v("“Muốn mai phục ta, không dễ dàng như vậy! Lại nói, cứ như vậy gần khoảng cách, các ngươi đều tại, chắc hẳn hắn cũng không dám phá hủy vừa mới liên minh tốt đẹp tình hình!”"),t("br"),t("br"),n._v("Hừ lạnh một tiếng, Bột Tân lòng bàn tay một chuôi trường thương xuất hiện, quay tròn xoay tròn, đem không gian bốn phía xé rách."),t("br"),t("br"),n._v("“Bất quá, tâm phòng bị người không thể không, các ngươi một lát nữa, phân tán tại bốn phía, một khi phát hiện có vấn đề, liền lập tức xông lại!”"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Mọi người đồng thời gật đầu."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Rời khỏi đối phương doanh trướng, Trương Huyền quay trở về, chỉ chốc lát trở lại trước đó đại trướng."),t("br"),t("br"),n._v("“Bẩm báo đại thống lĩnh, ta đưa ngươi muốn cùng Bột Tân tỷ thí sự tình nói, hắn, hắn...” Vẻ mặt xấu hổ, Trương Huyền dường như không tiện nói ra."),t("br"),t("br"),n._v("“Cứ nói đừng ngại!”"),t("br"),t("br"),n._v("Đại thống lĩnh nói."),t("br"),n._v("“Vâng... Hắn nói, thực lực của ngươi, không xứng tới động thủ, thật muốn dám đi qua, biết làm ngươi... Quỳ xuống cầu xin tha thứ!” Trương Huyền bờ môi run rẩy."),t("br"),t("br"),n._v("“Quỳ xuống cầu xin tha thứ? Khẩu khí thật lớn! Vậy liền để ta xem một chút, đến cùng có dạng gì thực lực, để cho ta cầu xin tha thứ!”"),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Đại thống lĩnh Ô Mộc đứng dậy, thân thể bốn phía, tựa như có gió lốc vây quanh."),t("br"),t("br"),n._v("Lập loè đôi mắt ở giữa, mang theo ngọn lửa tức giận."),t("br"),t("br"),n._v("“Hắn hẹn đại thống lĩnh sau nửa canh giờ, tại trong đại trướng gian cái kia mảnh trong khe núi quyết đấu...” Trương Huyền tiếp tục nói."),t("br"),t("br"),n._v("“Tốt, ta sẽ như ước nguyện của hắn!”"),t("br"),t("br"),n._v("Ô Mộc trường đao trong tay run lên, phát ra nghẹn ngào thanh âm."),t("br"),t("br"),n._v("“Đại thống lĩnh, Bột Tân dám như thế ăn nói ngông cuồng, lại không kiêng nể gì cả, ta sợ có bẫy!”"),t("br"),t("br"),n._v("Một cái hắc giáp người đứng dậy."),t("br"),t("br"),n._v("“Đúng vậy a, đại nhân nhất định phải cẩn thận!”"),t("br"),t("br"),n._v("Đồng dạng có người đứng lên."),t("br"),t("br"),n._v("“Ừm, cái tên này ta trước đó hẹn qua, phòng thủ mà không chiến, lần này dám như thế có gan, chỉ sợ không có đơn giản như vậy, Hòa Mộc, Khuê Ly, các ngươi hai cái đi xem một chút cái kia mảnh khe núi, nghiêm ngặt theo dõi, có cái gì động tĩnh, lập tức trở về tới bẩm báo!”"),t("br"),t("br"),n._v("Ô Mộc khoát tay áo."),t("br"),t("br"),n._v("“Vâng!” “Vâng!”"),t("br"),t("br"),n._v("Hai vị Dị Linh tộc nhân đi ra, vội vã đi ra ngoài."),t("br"),t("br"),n._v("“Ta đối cái kia mảnh khe núi quen thuộc, ta cùng bọn họ cùng đi!” Trương Huyền vội vàng ôm quyền."),t("br"),t("br"),n._v("“Đi đi!” Ô Mộc xua tay."),t("br"),t("br"),n._v("Trương Huyền xoay người đi ra, vội vã đuổi theo."),t("br"),t("br"),n._v("“Hai vị, chờ ta một chút, ta đối cái kia mảnh khe núi quen thuộc, mang các ngươi đi tới...”"),t("br"),t("br"),n._v("Hai vị hắc giáp người gật gật đầu, ba người che lấp khí tức, lặng lẽ tiến lên."),t("br"),t("br"),n._v("Chỉ chốc lát đi tới khe núi chỗ."),t("br"),t("br"),n._v("“Hai vị tốt nhất phân tán giấu kỹ, nếu như bọn họ có hoạt động lời nói, cũng có thể có chỗ đề phòng...” Trương Huyền dặn dò."),t("br"),t("br"),n._v("Hai người cảm thấy có lý, vừa mới chuyển thân tìm kiếm thích hợp chỗ ẩn thân, thân thể đồng thời nhoáng một cái, ngã trên mặt đất."),t("br"),t("br"),n._v("Một cái bị Long Cốt thần thương đâm thủng trái tim, một cái bị Yêu Dị huyền đao chém xuống đầu lâu."),t("br"),t("br"),n._v("Chém giết xong hai người, Trương Huyền lấy đi thi thể, thân thể nhoáng một cái, biến thành Bột Tân bộ dáng, thẳng tắp hướng trước đó doanh trướng bay đi."),t("br"),t("br"),n._v("Còn chưa tới đến trước mặt, tiếng cười liền vang lên."),t("br"),t("br"),n._v("“Ô Mộc, không dám nghênh chiến cứ việc nói thẳng, phái người đánh lén ta, đây chính là bọn họ kết quả...”"),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Tiếng nói kết thúc, vẫy tay một cái, ba cái thi thể thẳng tắp hướng doanh trướng bay đi, mà bản thân, xoay người rời đi."),t("br"),t("br"),n._v("Ô Mộc đám người lao ra lều vải, nhìn lấy nằm dưới đất ba cái thi thể, chính là vừa rồi phái đi ra Hòa Mộc, Khuê Ly cùng theo tới hắc giáp người, tức giận đến không có tại chỗ nổ tung."),t("br"),t("br"),n._v("Đều liên minh, cái tên này, vậy mà trắng trợn tàn sát thuộc hạ của hắn, quả thực khinh người quá đáng."),t("br"),t("br"),n._v("“Bột Tân, không giết ngươi, liền không gọi Ô Mộc!”"),t("br"),t("br"),n._v("Quát to một tiếng, cũng nhịn không được nữa, Ô Mộc thẳng tắp hướng đối phương doanh trướng vọt tới."),t("br"),t("br"),n._v("Phía sau hắn rất nhiều hắc giáp người, nhìn thấy đồng bạn bị đánh giết, cũng từng cái lên cơn giận dữ, cũng lại kìm nén không được, đi theo sát."),t("br"),t("br"),n._v("Đối diện doanh trướng, đang tại phỏng đoán, Ô Mộc hẹn nói mục đích đến cùng là cái gì, liền cảm thấy một cỗ to lớn khí tức hạ xuống, ngay sau đó, huy hoàng đao khí, lăng không mà đến, đem lều vải chém thành hai khúc."),t("br"),t("br"),n._v("Hô hô hô!"),t("br"),t("br"),n._v("Bay lên, nhìn thấy Ô Mộc cầm đao đứng lơ lửng trên không, Bột Tân lông mày nhảy một cái: “Ô Mộc, ngươi muốn làm gì? Nổi điên làm gì!”"),t("br"),t("br"),n._v("Không phải đồng ý điều kiện của mình sao? Tại sao lại đột nhiên xông lại, một bộ giết người biểu lộ?"),t("br"),t("br"),n._v("“Ít cho ta giả ngu, có bản lĩnh, liền để ta xem một chút, đến cùng có dạng gì thực lực...”"),t("br"),t("br"),n._v("Chẳng muốn nói nhiều, Ô Mộc trường đao run lên, thẳng tắp bổ tới."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1695"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1697"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);