(window.webpackJsonp=window.webpackJsonp||[]).push([[799],{1152:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1693-khuc-phu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1693-khuc-phu"}},[n._v("#")]),n._v(" Chương 1693: Khúc Phụ")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1693: Khúc Phụ"),t("br"),t("br"),n._v("“Không hổ là vạn thế chi sư...”"),t("br"),t("br"),n._v("Ngột Thần cảm xúc."),t("br"),t("br"),n._v("“Khổng sư không chỉ thực lực có một không hai thiên hạ, vạn cổ không ai bằng, đối đủ loại chức nghiệp, học thuật lưu truyền, cũng có không thể xóa nhòa cống hiến, theo ta được biết, thượng, trung, hạ ba loại chức nghiệp bên trong, hắn hỗ trợ tu sửa, xác định cấp bậc, liền có không dưới hai mươi loại!”"),t("br"),t("br"),n._v("Hưng kiếm thánh trong mắt phóng ra quang mang."),t("br"),t("br"),n._v("Danh sư truyền thừa tại Khổng sư, chỉ cần thân là danh sư, không có chỗ nào không đối hắn bội phục cực kỳ."),t("br"),t("br"),n._v("Trương Huyền không có cùng mọi người mảnh trò chuyện, mà là nhìn về phía “Mặt trời” bên trong nguy nga miếu thờ, trong mắt mang theo một tia nghi ngờ, loáng thoáng, hình như có đồ vật gì đó, tại đối với hắn tiến hành triệu hoán."),t("br"),t("br"),n._v("Thiên đạo thư viện dường như nhận lấy cảm ứng, nhẹ nhàng lay động, vô số thư tịch hoa hoa tác hưởng."),t("br"),t("br"),n._v("“Chẳng lẽ... Cái này Khổng miếu phía trong, có có thể để cho thư viện tấn cấp đồ vật?”"),t("br"),t("br"),n._v("Nhíu nhíu mày."),t("br"),t("br"),n._v("Thư viện từ trước tới nay an phận, hầu như không có bất cứ động tĩnh gì, mà bây giờ, cũng không ngừng run rẩy, không nói ra được kỳ lạ."),t("br"),t("br"),n._v("Coi như lần trước đi thiên cơ sư công hội, cũng không có hưng phấn như vậy."),t("br"),t("br"),n._v("Chẳng lẽ, Khổng sư lưu lại cái này di tích bên trong, có thư viện, khát vọng bảo vật?"),t("br"),t("br"),n._v("Nếu thật là như vậy, bất kể như thế nào, cũng muốn nhận được."),t("br"),t("br"),n._v("Cùng nhau đi tới, có thể từ bừa bãi vô danh, đến bây giờ danh chấn thiên hạ, hắn chỗ ỷ lại, không phải thiên phú kinh người, cao thượng phẩm cách, cùng không tính toán tin cậy chăm chỉ... Mà là thư viện!"),t("br"),t("br"),n._v("Không có thứ này, khả năng bây giờ còn tại Thiên huyền vương quốc, gắng sức giãy dụa lấy, xung kích nhất tinh danh sư."),t("br"),t("br"),n._v("Đây là hắn chỗ dựa lớn nhất cùng át chủ bài, nếu như có thể lần nữa tấn cấp, coi như tại nguy hiểm, cũng sẽ không lùi bước."),t("br"),t("br"),n._v("Minh Lý Chi Nhãn lấp lánh, hướng ánh sáng phía trong nhìn lại, tuy là đến Khúc Phụ, bay vùn vụt mấy trăm vạn cây số, nhưng vẫn như cũ thấy không rõ miếu thờ đến cùng sâu bao nhiêu, cho người ta một loại ngắm hoa trong màn sương ảo giác."),t("br"),t("br"),n._v("“Khổng miếu nằm ở một chỗ không gian gấp bên trong, không có hoàn toàn mở ra, liền xem như Cổ Thánh, bay lên mười năm, cũng chưa chắc có thể bay đến!”"),t("br"),t("br"),n._v("Lạc Nhược Hi nói."),t("br"),t("br"),n._v("Trương Huyền líu lưỡi."),t("br"),t("br"),n._v("Loại cảm giác này cùng kiếp trước mặt trời đồng dạng, treo ở đỉnh đầu, đưa tay có thể đụng, trên thực tế, liền xem như hết, đều cần tám phút mới có thể đến đạt."),t("br"),t("br"),n._v("“Đã xa như vậy, còn có thể nhìn thấy huy hoàng như vậy... Cái này di tích cái kia lớn bao nhiêu?” Nhịn không được nói."),t("br"),t("br"),n._v("Mặt trời sở dĩ như vậy, là bởi vì thể tích thực tế quá lớn."),t("br"),t("br"),n._v("Trước mắt cái này Khổng miếu, có thể hay không đồng dạng?"),t("br"),t("br"),n._v("“Di tích cũng không lớn, chỉ là một loại đối không gian pháp tắc vận dụng, cũng không phải là khoảng cách rất xa!” Lạc Nhược Hi cười cười."),t("br"),t("br"),n._v("Nàng tựa hồ đối với trước mắt ngôi miếu này, biết rất nhiều."),t("br"),t("br"),n._v("Nói chuyện phiếm bên trong, một tòa cổ xưa thành thị, xuất hiện ở trước mắt, mọi người thẳng tắp bay đi."),t("br"),t("br"),n._v("Khổng miếu vị trí địa phương, Khúc Phụ!"),t("br"),t("br"),n._v("Tòa thành này, không giống liên minh đế quốc phồn hoa như vậy cùng hùng vĩ, trong đó kiến trúc, càng là đứng sừng sững không biết bao nhiêu năm, có vẻ hơi cũ kỹ cùng bị thua, có điều, vừa tiến vào trong, liền cho người ta một loại yên tĩnh an lành cảm giác, tựa như hài nhi trở lại mẫu thân trước ngực."),t("br"),t("br"),n._v("“Đây là... Trời nhận danh sư, tản ra đặc thù khí tức?”"),t("br"),t("br"),t("br"),n._v("Trong thành thị toả ra khí tức, cùng trời nhận danh sư lúc, tràn vào khí tức trong người, mười phần gần, chỉ là mỏng manh vô số lần."),t("br"),n._v(" “Văn có văn khí, võ có võ vận, đây là danh sư đặc thù sư đạo văn hóa, cũng được xưng là ‘Sư phong’, thời gian dài nhận này hun đúc, tâm cảnh tiến bộ cực nhanh, suy nghĩ càng thêm thông suốt, từ đó, tâm ma không còn, tạp niệm không sinh!”"),t("br"),t("br"),n._v("Gặp hắn bộ dáng này, Hưng kiếm thánh cười nói."),t("br"),t("br"),n._v("“Sư phong?” Trương Huyền giật mình."),t("br"),t("br"),n._v("Thật ra thì loại này đặc thù khí tức, không chỉ trời nhận danh sư thời điểm sẽ có, trở thành danh sư về sau, tế bái rất nhiều tiên tổ thời điểm, đồng dạng sẽ có được khí tức phản hồi, là một loại văn hóa biểu tượng."),t("br"),t("br"),n._v("Danh sư, chính là dựa vào ở đây, mới có thể để cho vạn dân tin tưởng và nghe theo, cam tâm nghe lệnh, mà không bạo lực trấn áp."),t("br"),t("br"),n._v("“Nơi này... Vẫn luôn là như vậy?”"),t("br"),t("br"),n._v("Cảm thụ một cái, quả nhiên cùng đối phương nói, những khí tức này, có thể khiến người ta tâm cảnh không minh, suy nghĩ thông suốt, Trương Huyền nghi ngờ nhìn qua."),t("br"),t("br"),n._v("Nếu như cái này một mực như vậy, đối với danh sư tới nói hiệu quả, Danh Sư đường tổng bộ, cũng xa xa không bằng đi!"),t("br"),t("br"),n._v("“Khổng miếu không có mở ra phía trước, tuy có loại này ‘Sư phong’ tồn tại, nhưng mười phần mỏng manh, hiệu quả không lớn, có thể quanh năm sinh hoạt trong đó, tiến bộ cũng là mười phần rõ rệt! Nguyên nhân chính là như vậy, nơi này được xưng là danh sư tổ địa! Vô số đạt tới cửu tinh cấp bậc danh sư, đều sẽ đến đây ở đây, không tu luyện, không bế quan, chỉ là an ổn trải qua người bình thường sinh hoạt, rèn luyện tâm cảnh!”"),t("br"),t("br"),n._v("Giải thích một câu, Hưng kiếm thánh tiện tay một ngón tay: “Ngươi nhìn cái kia thợ mộc, cảm nhận được đến có cái gì không đúng?”"),t("br"),t("br"),n._v("Trương Huyền nhìn lại."),t("br"),t("br"),n._v("Quả nhiên tại bên đường, thấy được một cái đang tại làm đồ dùng trong nhà thợ mộc, trên tay tràn đầy vết chai, nhất cử nhất động như là cây thước đo đạc đồng dạng chính xác, mảnh gỗ vụn tung bay, rất nhanh một cái thành phẩm liền xuất hiện tại phía trước."),t("br"),t("br"),n._v("“Hắn là... Cửu tinh danh sư?”"),t("br"),t("br"),n._v("Trương Huyền một mặt ngạc nhiên."),t("br"),t("br"),n._v("Cái này thợ mộc, hoạt động bên trên không có chút nào lực lượng, trong cơ thể cũng cảm giác không thấy chân khí chảy xuôi, nhưng Minh Lý Chi Nhãn cẩn thận quan sát, có thể tuỳ tiện nhìn ra, trong đan điền khí tức, giống biển cả mạnh mẽ, liếc mắt không nhìn thấy phần cuối, chỉ sợ không riêng gì cửu tinh danh sư, thực lực so với hắn, đều chỉ mạnh không yếu!"),t("br"),t("br"),n._v("“Đúng vậy a, đây là ba trăm năm trước, Danh Sư đường mười phần có tiếng một vị danh sư, năm đó liền đạt đến Đại Thánh nhị trọng Kim Thân cảnh, hiện tại khẳng định càng thêm cường đại!”"),t("br"),t("br"),n._v("Hưng kiếm thánh gật đầu."),t("br"),t("br"),n._v("“Kim Thân cảnh cường giả, làm nghề mộc?”"),t("br"),t("br"),n._v("Trương Huyền khó tin."),t("br"),t("br"),n._v("Vốn hắn vẫn cho là, loại cảnh giới này cường giả, tại nhân tộc, mười phần thưa thớt, không nghĩ tới, cái này Khúc Phụ nội thành, tùy tiện nhìn thấy một cái thợ mộc, liền đạt đến loại này cấp bậc."),t("br"),t("br"),n._v("“Đúng vậy a, ngươi nhìn bên kia cái kia bán cá, giết gà... Năm đó đều là tên tuổi lẫy lừng cửu tinh danh sư, khi đó thực lực cũng không cao, mà bây giờ kém cỏi nhất đều giống như chúng ta, đạt đến Đại Thánh tam trọng, Tâm Huyết Lai Triều!”"),t("br"),t("br"),n._v("Hưng kiếm thánh tiếp lấy ngón tay đi, giải thích nói: “Nếu như không phải ngươi xảy ra chuyện, hai người chúng ta vốn cũng tính toán tới đây, qua cuộc sống của người bình thường, trải nghiệm củi gạo dầu muối tương dấm trà gian khổ, tăng thêm tâm cảnh khắc độ, để xung kích cảnh giới cao hơn!”"),t("br"),t("br"),n._v("Trương Huyền nhìn quanh."),t("br"),t("br"),n._v("Cùng hắn nói đồng dạng, bốn phía không ít thoạt nhìn bình thường gia hỏa, đều đạt đến Đại Thánh cấp bậc."),t("br"),t("br"),n._v("Trước đó, rất nhiều gia tộc, khó gặp siêu cấp cao thủ, ở đây, cùng rau cải trắng đồng dạng tùy tiện liền có thể đụng phải."),t("br"),t("br"),n._v("Khó trách mệnh danh danh sư tổ địa, quả nhiên không tầm thường."),t("br"),t("br"),n._v("“Đúng rồi, Huyền nhi, tâm cảnh của ngươi khắc độ hiện tại bao nhiêu? Danh sư muốn tiến bộ, trọng yếu nhất chính là cái này, tâm cảnh khắc độ không đến, tâm không tĩnh, ý không thuần, xung kích cảnh giới cao hơn, liền sẽ càng ngày càng khó!”"),t("br"),t("br"),n._v("Hưng kiếm thánh đột nhiên nhìn qua, một mặt tò mò."),t("br"),t("br"),n._v("Tu vi, thiên phú cao, tài nguyên đủ, có cái tốt lão sư, tiến bộ sẽ rất nhanh, vừa ý cảnh... Lại chỉ có thể từng bước một, dần dần từng bước tăng thêm, không có khả năng nóng vội hỏng việc, bản thân vị thiên tài này nhi tử, chẳng qua hai mươi tuổi, liền trở thành cửu tinh danh sư, tâm cảnh khắc độ bao nhiêu, lại là tu luyện như thế nào?"),t("br"),t("br"),n._v("“Ta sao?”"),t("br"),t("br"),n._v("Trương Huyền trầm ngâm một chút, không có giấu diếm: “29. 1!”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1704"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1706"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);