(window.webpackJsonp=window.webpackJsonp||[]).push([[804],{1159:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-162-thu-hai-canh"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-162-thu-hai-canh"}},[n._v("#")]),n._v(" Chương 162: Thứ hai cảnh?")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v(" “Hơn nữa, vẽ tranh coi trọng chính là ý cảnh, không có ý cảnh, lại giống y như thật đều vô dụng, Tả Hữu Du Long, tương đương với đem hoàn chỉnh hình ảnh mạnh mẽ phân cách thành hai phần, ý cảnh có vết rách... Vì lẽ đó, bức họa này, mặc dù hoàn mỹ kết lên, cũng e sợ rất khó đạt đến cảnh giới thứ ba.” "),t("br"),t("br"),n._v(" Tay trái tay phải phối hợp cho dù tốt, cũng là đem hình ảnh phân cách ra, toàn thể ý cảnh nhất định sẽ kém hơn không ít."),t("br"),t("br"),n._v(" “Quá độ khoe khoang vẽ bản lĩnh, mà mất đi vốn là vẽ tranh mục đích, xốc nổi!” Giải thích xong, Nguyên Ngữ đại sư lắc lắc đầu."),t("br"),t("br"),n._v(" Sau khi hết khiếp sợ, hắn đối với vị này Trương Huyền ấn tượng phi phàm không có chuyển biến tốt, trở nên càng tệ hơn."),t("br"),t("br"),n._v(" Ngạo mạn vô lễ, để bọn họ chờ đến buổi trưa không đến; Vẽ tranh phía trước muốn điều chỉnh trạng thái, làm lỡ hai, ba canh giờ, không có thời gian khái niệm; Hiện tại càng là khoe khoang vẽ bản lĩnh, mất đi vốn là vẽ tranh ý nghĩa, quả thực chính là tâm thái bất ổn, xốc nổi đến cực điểm."),t("br"),t("br"),n._v(" Vẽ tranh không có ôn hòa tâm, chỉ muốn để cho người khác khiếp sợ, cho người khác xem, coi như tài nghệ cao đến đâu, thì có ích lợi gì? Khó thành một đời đại sư."),t("br"),t("br"),n._v(" “Trương tiểu huynh đệ còn trẻ, ưa thích làm náo động cũng là bình thường...” "),t("br"),t("br"),n._v(" Lục Trầm đại sư biết lão hữu phát cáu, có lời gì chưa bao giờ giấu giấu diếm diếm, trực tiếp liền nói, cười khổ một tiếng nói."),t("br"),t("br"),n._v(" Người trẻ tuổi mà, ưa thích khoe khoang bản lĩnh, thuộc về bình thường, cũng không thể với bọn hắn những cái này muốn xuống mồ người như thế đi."),t("br"),t("br"),n._v(" “Làm ra tốt nhất vẽ, chính là làm náo động, Tả Hữu Du Long chỉ là đầu đường những cái kia thợ thủ công, dùng để lượng lớn sinh sản tác phẩm hội họa dùng, không thay đổi thói quen này, e sợ cả đời cũng khó khăn có thành tựu!” "),t("br"),t("br"),n._v(" Nguyên Ngữ đại sư ngữ khí không có một chút nào khách khí."),t("br"),t("br"),n._v(" “Ây...” "),t("br"),t("br"),n._v(" Nghe hắn đánh giá như thế, Hoàng Ngữ hai người từng cái từng cái trầm mặc không nói."),t("br"),t("br"),n._v(" Vẽ tranh lên, bọn họ khẳng định không trước mắt vị lão giả này có quyền lên tiếng, cũng không cách nào phản bác."),t("br"),t("br"),n._v(" Lục Trầm đang muốn lại nói hai câu, liền nghe Bạch Tốn âm thanh âm vang lên."),t("br"),t("br"),n._v(" “Mau nhìn, đã bắt đầu kết nối...” "),t("br"),t("br"),n._v(" Mọi người thấy đi, quả nhiên thấy Trương Huyền hai tay bút vẽ, đã tụ hợp lại một nơi, trên giấy hình ảnh, cũng bắt đầu kết hợp."),t("br"),t("br"),n._v(" “Cái này... Tiếp không lên đi!” "),t("br"),t("br"),n._v(" Liếc mắt nhìn, Hoàng Ngữ không nhịn được nói."),t("br"),t("br"),n._v(" Mắt thấy hai bên hai bức tranh lập tức tiếp cùng nhau, có thể hai bên trên tờ giấy màu mực sâu cạn, họa phong, ý vị, không hề có một chút tương đồng, thậm chí sắc tốc độ đều không giống nhau, tình huống như thế coi như thành công tụ hội cùng nhau, cũng là hai loại phong cách, không phải một bức họa..."),t("br"),t("br"),n._v(" Một nửa độ sáng cao, một nửa độ sáng thấp... Tiếp cùng nhau, cũng là phế phẩm."),t("br"),t("br"),n._v(" Lẽ nào, thất bại?"),t("br"),t("br"),n._v(" “Là tiếp không lên...” "),t("br"),t("br"),n._v(" Vốn còn muốn thay Trương Huyền nói hai câu lời hay Lục Trầm đại sư, giờ khắc này cũng khàn giọng đi, sắc mặt cũng khó coi, ngón tay dùng sức, đem râu nặn gãy tận mấy cái đều không tự hiểu."),t("br"),t("br"),n._v(" Tuy rằng không muốn thừa nhận, nhưng dựa vào hắn đối với vẽ tranh nghiên cứu, có thể rõ ràng nhìn ra hai bên trái phải hình ảnh hoàn toàn khác nhau, như là hai cái thế giới giống như vậy, căn bản không thể dung hợp, lẽ nào vị này Trương tiểu hữu, thật cùng Nguyên huynh nói như thế, quá mức xốc nổi?"),t("br"),t("br"),n._v(" Trước tuy rằng chỉ tiếp xúc một lần, nhưng khiêm tốn cẩn thận, nho nhã lễ độ, không giống người như thế a..."),t("br"),t("br"),n._v(" Nếu như đúng là loại tính cách này, sau đó tại thư họa lên e sợ thật sự rất khó có thành tựu cao hơn..."),t("br"),t("br"),n._v(" “Tiếp không lên chính là phế phẩm, đợi một buổi trưa, không nghĩ tới cùng đến vật này...” Nguyên Ngữ đại sư lắc đầu, tràn đầy thất vọng."),t("br"),t("br"),n._v(" Lục Trầm nói ra Trương Huyền sự tích thời điểm, hắn vẫn ôm rất lớn hi vọng, vốn tưởng rằng thật là một thiếu niên thiên tài, có thể đem thư họa phát dương quang đại, tận mắt thấy mới biết, là cái xốc nổi hạng người."),t("br"),t("br"),n._v(" Rõ ràng không bản lĩnh, an tâm vẽ tranh liền có thể, còn càng muốn trang cái gì cao thủ, dùng Tả Hữu Du Long, lần này được rồi, hai bức vẽ phong cách không giống nhau, tiếp đều tiếp không tới cùng một chỗ, ta xem ngươi làm sao xuống đài."),t("br"),t("br"),n._v(" Trang bức đầu tiên muốn trước có bản lãnh này mới được, bằng không, chỉ có thể đem mình giả dạng làm ngu ngốc."),t("br"),t("br"),n._v(" Rất rõ ràng, vị này Trương Huyền chính là người sau."),t("br"),t("br"),n._v(" Thở dài một tiếng, chậm rãi nhắm hai mắt lại, không tiếp tục đến xem."),t("br"),t("br"),n._v(" Không phải là không muốn xem, mà là sống sợ nhìn đến đối phương tiếp không lên xấu mặt dáng vẻ, sẽ càng thêm tức giận."),t("br"),t("br"),n._v(" “Hả?” "),t("br"),t("br"),n._v(" “Không đúng, chuyện này...” "),t("br"),t("br"),n._v(" “Sao có thể có chuyện đó?” "),t("br"),t("br"),n._v(" Vừa đem con mắt nhắm lại, tiếp theo bên tai liền vang lên thán phục âm thanh, tựa hồ nhìn thấy một số khó có thể tin cảnh tượng."),t("br"),t("br"),n._v(" Lông mày vừa nhíu, lần thứ hai đem con mắt mở, lập tức nhìn thấy Lục Trầm đại sư, Hoàng Ngữ, Bạch Tốn ba người từng cái từng cái trợn mắt ngoác mồm nhìn chằm chằm phía trước, quái đản."),t("br"),t("br"),n._v(" “Làm sao? Coi như tiếp không lên, cũng không đến mức như thế kinh ngạc đi...” "),t("br"),t("br"),n._v(" Mang theo nghi hoặc, Nguyên Ngữ đại sư cũng không nhịn được nhìn sang, chỉ một chút, con ngươi đột nhiên co rút lại, thân thể loáng một cái, ngốc tại chỗ: “Chuyện này... Chuyện này... Làm sao cái tình huống?” "),t("br"),t("br"),n._v(" Hắn cũng choáng váng."),t("br"),t("br"),n._v(" Chỉ thấy ngay phía trước, hai bức vẽ kết hợp địa phương, Trương Huyền bút vẽ nhẹ nhàng một câu, một đạo tường viện xuất hiện, giống như một cái bình phong đem hai bức vẽ chia làm hai cái bộ phận."),t("br"),t("br"),n._v(" Trước không cách nào nối liền vi cùng cảm giác, tại vách tường phân cách bên dưới, không có một chút nào không đúng, trái lại làm cho người ta một thế giới khác cảm giác."),t("br"),t("br"),n._v(" “Là khu nhà nhỏ này...” "),t("br"),t("br"),n._v(" Bạch Tốn không nhịn được hô."),t("br"),t("br"),n._v(" Lúc này hắn nhìn ra, Trương Huyền tay trái vẽ đúng là bọn họ vị trí sân, trong đó có người vẽ tranh, có người bình chút, bầu không khí an lành, tay phải vẽ nhưng là một cái khác sân, trong đó sắc màu rực rỡ, chim tước bay lượn, trong đó còn có đủ loại động vật qua lại cùng ở giữa, thiền âm thanh to rõ, làm cho người ta một loại huyên náo động đến thế ngoại đào nguyên cảm giác."),t("br"),t("br"),n._v(" Một bên là người, một bên là đồ vật, một bên yên tĩnh, một bên huyên náo, vốn là hai cái hoàn cảnh, hai loại trạng thái, ý vị không giống, trước hai bên trái phải vi cùng, lại có thể cái này vách tường bên dưới, trong nháy mắt hòa làm một thể, làm cho người ta một loại hoàn mỹ cảm giác."),t("br"),t("br"),n._v(" “Thần lai chi bút (tác phẩm của thần), tuyệt đối là thần lai chi bút...” "),t("br"),n._v(" Lục Trầm đại sư liên tục run cầm cập."),t("br"),t("br"),n._v(" Trước hắn cùng Nguyên Ngữ đại sư ý nghĩ như thế, cũng cảm thấy Trương Huyền bức họa này khẳng định phế bỏ, nằm mơ đều không nghĩ tới, rõ ràng hai cái ý vị không giống cảnh tượng, một đạo tường viện bên dưới, trở nên không gì sánh được hài hòa, động tĩnh có độ, làm cho người ta một kiểu khác vẻ đẹp cùng hưởng thụ."),t("br"),t("br"),n._v(" Trong tĩnh có động, trong động có tĩnh."),t("br"),t("br"),n._v(" Có thể nói, này vách tường, tuyệt đối là thần lai chi bút."),t("br"),t("br"),n._v(" Cách nhau một bức tường, “Chỉ xích thiên nhai”, chỉnh bức hoạ chất lượng, lập tức lên một đoạn dài không ngừng."),t("br"),t("br"),n._v(" “Lợi hại...” "),t("br"),t("br"),n._v(" Nguyên Ngữ đại sư cũng nín nửa ngày, phun ra hai chữ."),t("br"),t("br"),n._v(" Rất hiển nhiên, này một đạo tường viện, là hắn không nghĩ tới."),t("br"),t("br"),n._v(" Nếu như tường viện hai bên ý cảnh đều giống nhau, ngược lại sẽ biến thành nét bút hỏng."),t("br"),t("br"),n._v(" “Bêu xấu!” "),t("br"),t("br"),n._v(" Tường viện vẽ xong, chỉnh bức tác phẩm cũng hoàn thành, Trương Huyền thả xuống bút lông, nhẹ nhàng nở nụ cười: “Kính xin mấy vị lại đây giám định đi!” "),t("br"),t("br"),n._v(" Hắn mới vừa học được vẽ tranh, không biết vẽ cái gì, liền đem mọi người vị trí sân cùng bên cạnh viện vẽ ra."),t("br"),t("br"),n._v(" “Ta tới xem một chút!” "),t("br"),t("br"),n._v(" Lục Trầm đại sư đi đầu đi tới, cúi đầu nhìn về phía trước mắt hình ảnh."),t("br"),t("br"),n._v(" Hoàng Ngữ, Bạch Tốn mấy người cũng theo sát đi tới trước mặt."),t("br"),t("br"),n._v(" Trước bọn họ ở cách xa, thấy không rõ lắm, vì lẽ đó vừa bắt đầu không nhìn ra là nơi này, lúc này khoảng cách gần nhìn chút, tất cả đều không khỏi thán phục."),t("br"),t("br"),n._v(" Sân cảnh vật, giống y như thật, trông rất sống động, từng cái từng cái phảng phất sống giống như vậy, tại hắn bút vẽ diễn dịch bên dưới, nổi tại giấy mặt, đẹp cực kỳ."),t("br"),t("br"),n._v(" “Thật là đẹp...” "),t("br"),t("br"),n._v(" Không nhịn được tán dương, Hoàng Ngữ đôi mắt đẹp tỏa ánh sáng."),t("br"),t("br"),n._v(" Tuy rằng tay trái tay phải đồng thời vẽ tranh, nhưng chi tiết không thể xoi mói, liền ngay cả một cái cái bàn một cái băng, đều có thể thấy rõ ràng, hình ảnh lên vẻ mặt của mọi người đều tại, thật giống in ở phía trên như thế, chỉnh bức họa đẹp đẽ kỳ cục."),t("br"),t("br"),n._v(" “Đáng tiếc...” "),t("br"),t("br"),n._v(" Nhìn một hồi, Nguyên Ngữ đại sư không nhịn được lắc đầu, liền ngay cả một bên Lục Trầm đại sư cũng trên mặt lộ ra tiếc hận tâm ý."),t("br"),t("br"),n._v(" “Bức họa này rất đẹp a, còn có cái gì không đúng?” Không biết bọn họ thở dài cái gì, Bạch Tốn không nhịn được mở miệng."),t("br"),t("br"),n._v(" Nói thật, hắn không nhìn ra bức họa này đến cùng có vấn đề gì, cảm giác so với trước hai bức, cũng không kém bao nhiêu."),t("br"),t("br"),n._v(" “Bức họa này, hoạ sĩ không chê vào đâu được, không có một chút nào sai lầm, sắc thái, phối hợp... Cũng tất cả đều là tốt nhất tác phẩm, chỉ là cùng ta trước nói như thế, xung quanh dung hợp, quá mức chú trọng chi tiết, trái lại khuyết thiếu ý cảnh! Không có ý cảnh, nhiều nhất chỉ đạt đến cảnh giới thứ hai linh động, khoảng cách ý tồn, còn có rất lớn một đoạn chênh lệch!” "),t("br"),t("br"),n._v(" Lục Trầm đại sư lắc lắc đầu."),t("br"),t("br"),n._v(" Thư họa chia làm bốn cái cảnh giới, lục thực, linh động, ý tồn, kinh hồng."),t("br"),t("br"),n._v(" Trước Nguyên Ngữ, Lục Trầm hai vị đại sư vẽ ra đều là cảnh giới thứ ba, ẩn chứa ý cảnh, nhìn chút bên dưới làm cho người ta tâm thần thoải mái cảm giác."),t("br"),t("br"),n._v(" Trương Huyền bộ này, tuy rằng đầu bút lông nhẵn nhụi, bất luận kết cấu vẫn là bố cục, cũng không có vấn đề gì, nhưng đáng tiếc... Thiếu mất cái cảm giác này."),t("br"),t("br"),n._v(" Không có ý cảnh mà nói, nhiều nhất cũng chính là cảnh giới thứ hai Linh Động cảnh giới, không tính là cái gì trân phẩm."),t("br"),t("br"),n._v(" “Đáng tiếc, bất quá, Trương tiểu huynh đệ còn trẻ vô cùng, chờ đến chúng ta cái tuổi này, là có thể tùy tiện vẽ ra tam cảnh, thậm chí tứ cảnh tác phẩm hội họa!” "),t("br"),t("br"),n._v(" Lục Trầm đại sư an ủi."),t("br"),t("br"),n._v(" Tuy rằng không đạt đến cảnh giới thứ ba, hắn vẫn tương đối khiếp sợ."),t("br"),t("br"),n._v(" Chung quy Trương Huyền không tới hai mươi tuổi, từng tuổi này, liền có cao thâm như vậy vẽ kỹ xảo, mặc dù là hắn, đều tán dương không ngớt."),t("br"),t("br"),n._v(" “Tính có chút chân tài thực học!” Nguyên Ngữ đại sư cũng gật gù, nhìn về phía Trương Huyền, trong giọng nói mang theo răn dạy mùi vị: “Bất quá, người trẻ tuổi hay là muốn điệu thấp tốt hơn!” "),t("br"),t("br"),n._v(" Đi qua một loạt sự tình, đối với Trương Huyền ấn tượng chênh lệch chút, nhưng bức họa này, thật không tệ, ngoại trừ không ý cảnh, hắn cũng xoi mói không ra tật xấu, xem ra cái tên này, cũng không phải là tên lừa đảo, là thật có chút bản lĩnh."),t("br"),t("br"),n._v(" Bất quá, chút bản lãnh này, liền như vậy cậy tài khinh người, để bọn họ cùng lâu như vậy, sâu trong nội tâm, vẫn còn có chút không cao hứng."),t("br"),t("br"),n._v(" “Điệu thấp?” Nghe được trong lời nói của đối phương có chuyện, Trương Huyền biết chắc là kéo quá lâu, đối phương tức rồi, lúng túng nở nụ cười: “Vâng, là, là ta làm lỡ thời gian quá lâu, hết sức xin lỗi!” "),t("br"),t("br"),n._v(" “Này còn tạm được!” Thấy hắn thái độ rất tốt, Nguyên Ngữ đại sư lúc này mới thoả mãn gật gù: “Ngươi hiện tại cơ sở không sai, sau đó cố gắng bồi dưỡng, cố gắng du lịch, vẽ ra cảnh giới thứ ba tác phẩm hội họa, cũng ngay trong tầm tay, tuyệt đối không nên bởi vì ngông cuồng, lãng phí thiên phú!” "),t("br"),t("br"),n._v(" “Đúng đấy, muốn làm ra có ý cảnh tác phẩm hội họa, đầu tiên muốn nhiều quan sát, đối với thế giới có càng sâu hiểu rõ, đối với xung quanh có nhận thức sâu hơn!” "),t("br"),t("br"),n._v(" Lục Trầm đại sư cũng ân cần giáo dục."),t("br"),t("br"),n._v(" “Thụ giáo!” "),t("br"),t("br"),n._v(" Trương Huyền biết đối phương là hảo ý, không nhịn được gật gù."),t("br"),t("br"),n._v(" “Tốt lắm, chúng ta đều xong vẽ, hai người các ngươi bắt đầu giám thưởng đi...” Thấy hắn khiêm tốn tiếp thu, Lục Trầm không nói thêm nữa, đang muốn để Hoàng Ngữ hai người sát hạch tiếp tục, liền nghe đến Bạch Tốn thanh âm kinh ngạc lần thứ hai tiếng vang lên."),t("br"),t("br"),n._v(" “Không đúng.. Trương đại sư bức họa này người và chim muông, làm sao... Đều không có mắt?” "),t("br"),t("br"),n._v(" Trước tất cả mọi người chỉ xem chỉnh bức họa bố cục cùng kết cấu, vẫn chưa chú ý những chi tiết này, giờ khắc này nhìn chút, quả nhiên phát giác chỉnh bức họa lên người và chim muông, đều còn không vẽ con mắt."),t("br"),t("br"),n._v(" (Chưa xong còn tiếp.) Điện thoại di động người sử dụng mời xem lướt qua xem, càng chất lượng tốt xem trải nghiệm."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-170"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-172"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);