(window.webpackJsonp=window.webpackJsonp||[]).push([[81],{433:function(n,h,g){"use strict";g.r(h);var t=g(44),i=Object(t.a)({},(function(){var n=this,h=n.$createElement,g=n._self._c||h;return g("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[g("h1",{attrs:{id:"chuong-1050-ngoc-diep-vuong"}},[g("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1050-ngoc-diep-vuong"}},[n._v("#")]),n._v(" Chương 1050: Ngọc Diệp Vương")]),n._v(" "),g("hr"),n._v(" "),g("p",[n._v("Chương 1050: Ngọc Diệp Vương"),g("br"),g("br"),n._v("Cái này Dị Linh tộc nhân cái đầu không cao, khí tức trên thân cũng không mãnh liệt, cùng cách đó không xa Thạch Diệp Vương so sánh, rõ ràng không bằng."),g("br"),g("br"),n._v("Nhưng mà ánh mắt bên trong, cho người ta một loại lạnh lùng đến cực điểm cảm giác, nhất là làn da, trong suốt như ngọc, phun ra nuốt vào ở giữa, hô hấp khoảng cách thật dài, vừa nhìn liền biết tu vi hùng hậu."),g("br"),g("br"),n._v("“Thiếu gia, hắn chính là Ngọc Diệp Vương!” Tử Diệp Vương truyền âm ở bên tai lặng lẽ vang lên, nghe ngữ khí tựa hồ có chút phát run: “Hắn tu luyện chính là Tàn Ngọc công, không chỉ thân thể mạnh mẽ, nguyên thần cũng mười phần đáng sợ, đã đạt đến nửa bước Xuất Khiếu cảnh...”"),g("br"),g("br"),n._v("“Nửa bước xuất khiếu?” Trương Huyền sững sờ, lần nữa nhìn lại, quả nhiên phát hiện trước mắt vị này Dị Linh tộc nhân, mi tâm lấp lánh, dường như nguyên thần bất cứ lúc nào cũng sẽ bước ra một bước cuối cùng, nhảy ra, ngao du bầu trời."),g("br"),g("br"),n._v("Thánh vực ngũ trọng Xuất Khiếu cảnh, là chỉ nguyên thần xuất khiếu, có thể tự do bay lượn, loại người này lại càng dễ dung nhập thiên địa, mượn nhờ tự nhiên lực lượng, phát huy ra lực chiến đấu mạnh hơn."),g("br"),g("br"),n._v("Xuất Khiếu cảnh cùng Nguyên Thần cảnh là một cái cực lớn điểm phân định, tựa như hiện tại Trương Huyền, có thể đánh lén, chém giết Nguyên Thần cảnh cường giả, nhưng mà gặp được Xuất Khiếu cảnh, có thể trốn bao xa liền chạy bao xa, căn bản không có cách nào chiến thắng."),g("br"),g("br"),n._v("Cho dù đột phá Thánh vực cũng làm không được!"),g("br"),g("br"),n._v("Nửa bước xuất khiếu mặc dù không có đáng sợ như thế, nhưng cũng không phải bình thường vật lý công kích, có thể đem hắn chém giết."),g("br"),g("br"),n._v("Khó trách có thể để cho Tử Diệp Vương kiêng kỵ như vậy, loại thực lực này so với trước mắt Thạch Diệp Vương, càng thêm lợi hại."),g("br"),g("br"),n._v("“Tính sai, hẳn là đem Thanh Điền thập đại vương giả tin tức, nói cho Thanh Nguyên Danh Sư đường phân bộ, để hắn phái thêm một chút lợi hại danh sư tới...”"),g("br"),g("br"),n._v("Trương Huyền có chút bất đắc dĩ."),g("br"),g("br"),n._v("Lúc đó liên tục giết chết mấy vị vương giả, lại thêm phong ấn có danh sư đường bảo vệ, cho rằng Dị Linh tộc nhân không có khả năng đi vào, cũng không có quá mức để ý."),g("br"),g("br"),n._v("Hiện tại bọn hắn bên này, Thất Tinh cấp bậc danh sư, chỉ có Ngô sư, Mộc sư cùng ba đại viện trưởng, Chiến sư đường Phùng Huân mấy người cũng đạt đến Thất Tinh, chẳng qua thực lực chân chính khoảng cách loại này nửa bước xuất khiếu, còn kém rất lớn một đoạn."),g("br"),g("br"),n._v("Có thể nói bọn họ bên này tổng thể thực lực, cùng thập đại vương giả so sánh... Căn bản không tại cùng một cái cấp bậc bên trên."),g("br"),g("br"),n._v("Biết sớm như vậy bị động, liền trước thời hạn bẩm báo, để hắn phái thêm một chút lợi hại hơn danh sư đến đây."),g("br"),g("br"),n._v("Thanh Nguyên đế quốc Danh Sư đường, mặc dù chỉ là cái phân bộ, nhưng mà đạt tới Thánh vực tứ trọng đỉnh phong danh sư, cũng không tại số ít."),g("br"),g("br"),n._v("“Quên đi, nói cái gì đều không dùng...” Ý nghĩ này trong đầu chợt lóe qua, lại không xoắn xuýt."),g("br"),g("br"),n._v("Dù sao xoắn xuýt có hữu dụng hay không, lại nói, mười cái đã bị hại chết sáu cái, lại cố gắng một chút, còn lại bốn cái hẳn là cũng không khó đi!"),g("br"),g("br"),n._v("“Gấp gáp như vậy bảo ngươi tới, quả thực có một việc, cần hỗ trợ...”"),g("br"),g("br"),n._v("Thạch Diệp Vương tiến về phía trước một bước."),g("br"),g("br"),n._v("“Ừm?” Ngọc Diệp Vương nhìn qua."),g("br"),g("br"),n._v("“Là như vậy, nằm dưới đất vị này, nghe nói là trong nhân tộc thân phận mười phần cao quý tồn tại, hy vọng có thể mượn nhờ tay của ngươi, nghiệm minh chân thân!”"),g("br"),g("br"),n._v("Một ngón tay nằm dưới đất Phùng Huân, Thạch Diệp Vương nói."),g("br"),g("br"),n._v("Trời nhận danh sư trước đó không nói, chờ đối phương nghiệm chứng qua đi, nếu như tương đồng, liền không hoài nghi nữa."),g("br"),g("br"),n._v("“Nghiệm minh chân thân?” Ngọc Diệp Vương nhìn sang: “Nhìn quần áo hẳn là Chiến sư, chẳng lẽ còn có cái gì cái khác thân phận? Mặt khác... Tử Diệp Vương, ngươi tại sao lại ở chỗ này?”"),g("br"),g("br"),n._v("Nói xong, nhìn chằm chằm Trương Huyền ánh mắt sáng ngời."),g("br"),g("br"),n._v("“Là như vậy...” Trương Huyền đem trước cùng đối Thạch Diệp Vương nói nói láo, lần nữa nói một lần, chỉ bất quá... Trời nhận danh sư, đồng dạng không có nói ra."),g("br"),g("br"),n._v("“Ngươi nói là theo dõi vị này có thân phận nhân tộc, mai danh ẩn tích, ngụy trang nhân loại, cố ý đem hắn dẫn tới nơi này?”"),g("br"),g("br"),n._v("Ngọc Diệp Vương có chút kỳ quái, có điều, vẫn gật đầu: “Tốt, ta xem một chút hắn đến cùng thân phận gì, có thể để ngươi làm như vậy, nếu quả như thật rất kính trọng, sẽ giúp ngươi hướng Thanh Điền Hoàng, xin công lao!”"),g("br"),g("br"),n._v("Nói xong cổ tay khẽ đảo, lấy ra một cái tế đàn, thứ này cùng Trương Huyền theo Tử Diệp Vương trong tay lấy được cũng không tương đồng, toàn thân do Thanh Đồng đúc thành, cái đầu càng lớn, phía trên điêu khắc rất nhiều kỳ quái phù văn, để cho người ta nhìn lên một cái, liền cảm thấy choáng đầu hoa mắt, dường như linh hồn đều sẽ bị nuốt chửng."),g("br"),g("br"),n._v("“Đây là Ngọc Diệp Vương đặc thù Ngọc Thanh tế đàn, thập đại vương giả bên trong Thanh Điền Hoàng liền ban thưởng một mình hắn... Dùng cái tế đàn này cúng tế Linh Thần lời nói, có thể nghiệm minh chân thân, phân biệt thật giả!” Tử Diệp Vương lặng lẽ truyền âm tới."),g("br"),g("br"),n._v("“Chỉ cho hắn một cái?” Trương Huyền kỳ quái."),g("br"),g("br"),n._v("Trước mắt vị này không phải là thập đại vương giả bên trong thực lực mạnh nhất cũng không phải sau cùng, vì cái gì chỉ ban cho một mình hắn?"),g("br"),g("br"),n._v("“Ngọc Diệp Vương tại Thanh Điền nhất mạch, nếu không hình pháp cùng thẩm vấn, chưởng quản hình sự đường...”"),g("br"),g("br"),n._v("Trương Huyền nhẹ gật đầu."),g("br"),g("br"),n._v("Phụ trách hình sự tự nhiên phải có phân biệt thật giả năng lực, nếu không lung tung xét xử, không cần công kích, đều muốn lộn xộn."),g("br"),g("br"),n._v("Hướng tế đàn nhìn lại, nhịn không được trong lòng kỳ quái, xét xử phân biệt hẳn là một loại năng lực đặc thù, cùng thứ này có quan hệ gì?"),g("br"),g("br"),n._v("Nghi ngờ không có duy trì liên tục quá lâu, chỉ thấy Ngọc Diệp Vương, vẻ mặt nghiêm túc lấy ra một đống cúng tế đồ vật."),g("br"),g("br"),n._v("Tế đàn phát ra ánh sáng yếu ớt, đem cúng tế đồ vật thiêu đốt, một đạo trắng tinh ánh sáng, đem hắn cùng bên người Phùng Huân bao phủ ở bên trong."),g("br"),g("br"),n._v("Thấy ánh sáng bao phủ, vị này Tử Diệp Vương đều không có đi ra, Ngọc Diệp Vương nhướng mày."),g("br"),g("br"),n._v("Thạch Diệp Vương sắc mặt cũng khó coi, nhịn không được hô lên: “Tử Diệp Vương, hiện tại đang tại nghiệm minh thân phận của người này, ngươi rời đi trước...”"),g("br"),g("br"),n._v("“Không cần... Cái tên này là ta tự tay chỗ bắt, muốn tận mắt nhìn xem...” Trương Huyền lắc đầu."),g("br"),g("br"),n._v("Đối phương kiểm nghiệm nguyên lý, hắn không rõ ràng, chẳng qua Phùng Huân khẳng định không phải trời nhận danh sư, bản thân là nếu như rời đi, khẳng định đo lường không đến thứ gì."),g("br"),g("br"),n._v("Bị bao phủ, có khả năng sẽ rò rỉ thân phận, nhưng hắn có tự tin tại đối phương nhìn ra ngụy trang trước đó, để hắn phát hiện trời nhận danh sư thân phận."),g("br"),g("br"),n._v("Đương nhiên, cái này trời nhận danh sư, là muốn giá họa tại Phùng Huân trên người."),g("br"),g("br"),n._v("“Tùy ngươi đi!” Thấy hắn bất luận thế nào không ra, trong mắt tràn đầy vẻ cảnh giác, Thạch Diệp Vương còn tưởng rằng lo lắng cho mình an nguy, nhịn không được lắc đầu."),g("br"),g("br"),n._v("“Tiếp tục!”"),g("br"),g("br"),n._v("Ngọc Diệp Vương cũng không nhiều lời, tiếp tục hướng tế đàn để vào cúng tế vật phẩm cần thiết."),g("br"),g("br"),n._v("Trương Huyền thở phào nhẹ nhõm, bàn chân nhẹ nhàng điểm tại Phùng Huân sau lưng, vận chuyển trong cơ thể trời nhận danh sư đặc thù khí tức, nhẹ nhàng đưa đến người sau trong cơ thể."),g("br"),n._v("Loại khí tức này, có thể làm cho danh sư dễ dàng phân biệt ra thân phận, nhưng mà không vận chuyển lời nói, sẽ ẩn núp, để cho người ta không thể nào phát hiện."),g("br"),g("br"),n._v("Chính là có này dựa dẫm, mới cố ý nói Phùng Huân là trời nhận danh sư, không có chút nào sợ hãi."),g("br"),g("br"),n._v("Ông!"),g("br"),g("br"),n._v("Bao phủ hai người ánh sáng càng lúc càng nồng nặc, Trương Huyền cũng không nhìn thấy bất luận cái gì đặc thù, nhưng đối diện Ngọc Diệp Vương lông mày lại là kìm lòng không được nhăn lại."),g("br"),g("br"),n._v("“Thân phận của người này quả nhiên không giống bình thường...”"),g("br"),g("br"),n._v("Nói xong, tiếp tục hướng tế đàn ném ra bảo vật."),g("br"),g("br"),n._v("Phía trên đồ vật, đã tiêu hao hầu như không còn."),g("br"),g("br"),n._v("Thạch Diệp Vương cũng kìm lòng không được nắm đấm xiết chặt."),g("br"),g("br"),n._v("Trương Huyền đối loại này cúng tế không quá lý giải, hắn lại là vô cùng hiểu, cúng tế cần có vật phẩm càng nhiều, nói rõ đối diện thân phận càng cao."),g("br"),g("br"),n._v("Không biết thả xuống mấy lần về sau, tế đàn bên trên ánh sáng, rốt cục chậm rãi ngừng lại, cho thấy bốn chữ lớn —— thiên địa công nhận!"),g("br"),g("br"),n._v("“Quả nhiên là!”"),g("br"),g("br"),n._v("Thấy rõ ràng phía trên chữ, Thạch Diệp Vương con mắt lập tức đỏ lên, hô hấp đều trở nên có chút gấp rút."),g("br"),g("br"),n._v("Xem ra đối diện Tử Diệp Vương cũng không nói dối, cách đó không xa nằm vị này, chính là cùng Khổng sư đồng dạng trời nhận danh sư."),g("br"),g("br"),n._v("Chỉ cần đem người này nộp lên, có thể đoán được, địa vị của hắn cùng thân phận, đều sẽ phát sinh biến hóa nghiêng trời lệch đất."),g("br"),g("br"),n._v("“Thiên địa công nhận... Trời nhận danh sư?”"),g("br"),g("br"),n._v("Ngọc Diệp Vương trong đôi mắt cũng lộ ra hừng hực."),g("br"),g("br"),n._v("Thân là thượng ngũ vương xếp hạng thứ ba tồn tại, tự nhiên biết bốn chữ này đại biểu cái gì."),g("br"),g("br"),n._v("“Khó trách hai người này, gấp gáp như vậy tìm ta tới... Nguyên lai bắt lấy như vậy một con cá lớn...”"),g("br"),g("br"),n._v("Ngọc Diệp Vương nắm đấm xiết chặt."),g("br"),g("br"),n._v("“Ngọc Diệp Vương, cái tên này là ta cùng Thạch Diệp Vương bắt, như là đã nghiệm chứng xong, xác nhận thân phận... Ta hy vọng có thể được phê chuẩn, để chúng ta hai người lập tức đem hắn đưa về trong tộc!”"),g("br"),g("br"),n._v("Thấy mình phương pháp thành công, Trương Huyền thở phào nhẹ nhõm, nói."),g("br"),g("br"),n._v("Câu nói này nói đến rất đơn giản, trên thực tế mang theo khiêu khích hương vị."),g("br"),g("br"),n._v("Cố ý nói là hắn cùng Thạch Diệp Vương bắt lấy, lại xin rời khỏi, chẳng khác nào đem công lao cùng vị này Ngọc Diệp Vương che đậy."),g("br"),g("br"),n._v("“Cái này...”"),g("br"),g("br"),n._v("Nghe nói như thế, Thạch Diệp Vương lập tức hiểu được, biến sắc."),g("br"),g("br"),n._v("Bọn họ về trong tộc, là tất nhiên, nhưng... Đừng hiện tại nói ngay, nếu không, đối phương khẳng định sẽ cảm thấy, muốn đem công lao độc chiếm."),g("br"),g("br"),n._v("Quả nhiên, hắn còn chưa kịp giải thích, liền thấy Ngọc Diệp Vương mày nhăn lại, vẻ mặt khó coi."),g("br"),g("br"),n._v("Ngay sau đó khóe miệng nâng lên, lộ ra giống như cười mà không phải cười biểu lộ: “Hoàng giao phó nhiệm vụ, còn chưa hoàn thành, như thế đi, ta sợ sẽ rất phiền phức đi!”"),g("br"),g("br"),n._v("“Không phiền phức, dù sao cái tên này, là hai người chúng ta bắt, cùng cái khác vương giả không có quan hệ, đến lúc đó thật muốn trừng phạt, hai người chúng ta bằng lòng gánh chịu...”"),g("br"),g("br"),n._v("Trương Huyền gật đầu."),g("br"),g("br"),n._v("Nghe nói như thế, Thạch Diệp Vương suýt chút nữa không hộc máu ngất đi."),g("br"),g("br"),n._v("Ngọc Diệp Vương bản thân liền mất hứng, cùng lắm thì lừa gạt thoáng cái, nói công lao là mọi người... Hắn ngược lại tốt, nói thẳng cùng cái khác vương giả không quan hệ..."),g("br"),g("br"),n._v("Rõ ràng không đem đối phương để vào mắt."),g("br"),g("br"),n._v("“Ngọc Diệp Vương, hắn ý tứ không phải cái này...”"),g("br"),g("br"),n._v("Vội vàng nhảy lên, đang muốn giải thích, chỉ thấy trước mắt Ngọc Diệp Vương vẻ mặt trầm thấp như máu, sắp bùng nổ: “Ngươi ý tứ, không có quan hệ gì với ta phải không?”"),g("br"),g("br"),n._v("“Đương nhiên! Ta cùng Thạch Diệp Vương đã thương nghị qua, nếu như ngươi thức thời không nói cái gì ngược lại cũng thôi, thật muốn tìm phiền toái, muốn tham ô công lao của chúng ta, không ngại đưa ngươi chém giết!”"),g("br"),g("br"),n._v("Trương Huyền gật đầu, một mặt không quan trọng nhìn qua."),g("br"),g("br"),n._v("Thân thể nhoáng một cái, Thạch Diệp Vương mắt tối sầm lại."),g("br"),g("br"),n._v("Con mẹ nó chứ lúc nào nói qua lời này?"),g("br"),g("br"),n._v("Ta bảo ngươi thăm dò, không phải khiêu chiến..."),g("br"),g("br"),n._v("Cho dù trong lòng thật nghĩ như vậy, cũng muốn thầm tiến hành mới được... Nói thẳng ra, mất đi tiên cơ, không phải muốn chết sao?"),g("br"),g("br"),n._v("“Rất tốt, rất tốt, ta Ngọc Diệp Vương, rong ruổi nhiều năm như vậy, đã rất lâu không bị đến uy hiếp như vậy...”"),g("br"),g("br"),n._v("Răng cắn chặt, Ngọc Diệp Vương trong mắt hung quang lóe lên, lạnh lùng nhìn về phía Thạch Diệp Vương hai người, tràn đầy sát cơ."),g("br"),g("br"),n._v("“Ngọc Diệp Vương, không phải ngươi nghĩ như vậy, bắt cái này trời nhận danh sư...”"),g("br"),g("br"),n._v("Âm thầm mắng một tiếng “Heo đồng đội”, Thạch Diệp Vương vội vàng giải thích, đang muốn để quan hệ đừng khẩn trương như vậy, liền nghe đến cách đó không xa “A!” Một tiếng."),g("br"),g("br"),n._v("Nằm dưới đất Phùng Huân xoa xoa đầu, dường như tính toán đứng lên."),g("br"),g("br"),n._v("“Làm sao vậy? Cái gì trời nhận danh sư?”"),g("br"),g("br"),n._v("Trong mơ hồ, Phùng Huân nghe được một cái từ ngữ, nhịn không được mở miệng."),g("br"),g("br"),g("br"),g("br")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("a",{attrs:{href:"./chap-1058"}},[n._v("Chương trước")]),n._v("  |  "),g("a",{attrs:{href:"./chap-1060"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);