(window.webpackJsonp=window.webpackJsonp||[]).push([[823],{1176:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1715-đai-thu-than-phuc-【-canh-3-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1715-đai-thu-than-phuc-【-canh-3-】"}},[n._v("#")]),n._v(" Chương 1715: Đại thụ thần phục 【 Canh [3] 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1715: Đại thụ thần phục "),t("br"),t("br"),n._v("Huyền đao sắc bén có một không hai, rất mau đem quất tới cành cây chặt đứt, có điều, sức mạnh mạnh mẽ, để cánh tay hắn run lên, nhịn không được lui về phía sau."),t("br"),t("br"),n._v("Trương Huyền thực lực bây giờ, có thể cùng Bất Hủ cảnh đại viên mãn chống lại, dựa vào là rất nhiều pháp bảo, đơn thuần lực lượng lời nói, vẫn là kém loại này cường giả không ít."),t("br"),t("br"),n._v("Sưu sưu sưu sưu!"),t("br"),t("br"),n._v("Đứt mất một cái nhánh cây, đại thụ dường như cũng không đau đớn, rất nhanh, càng nhiều cành cây lao qua, tựa như bạch tuộc, mỗi một đầu, đều mang Đại Thánh tứ trọng Bất Hủ cảnh đại viên mãn lực lượng, không gian chung quanh, bị ép ô ô vang vọng, dường như không chịu nổi."),t("br"),t("br"),n._v("“Tiền bối...”"),t("br"),t("br"),n._v("Không nghĩ tới, vị tiền bối này kiểm tra lấy kiểm tra lấy, cùng một cái cây đánh nhau, mọi người tất cả đều tràn đầy lo lắng, liền muốn tới hỗ trợ."),t("br"),t("br"),n._v("“Không được qua đây!”"),t("br"),t("br"),n._v("Trương Huyền thanh âm vang lên."),t("br"),t("br"),n._v("Hắn đều chống lại không được, những người này xông lại, chỉ có bị giết phần."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Lời còn chưa dứt, cát phía dưới, vô số rễ cây, chui ra, đối mọi người quét sạch."),t("br"),t("br"),n._v("“Sợi rễ... Ta hiểu, không có gì bất ngờ xảy ra, ngươi sợi rễ, lan tràn toàn bộ sa mạc, ngươi là dùng sợi rễ, đang tiến hành khải linh!”"),t("br"),t("br"),n._v("Nhìn thấy thứ này, Trương Huyền chợt."),t("br"),t("br"),n._v("Trước mắt cây này, thoạt nhìn cũng không đồ sộ, nhưng có thể nắm giữ, Đại Thánh tứ trọng đại viên mãn thực lực, sợi rễ nhất định lan ra cực xa, mấy chục cây số, mấy trăm cây số bên ngoài khải linh, vô cùng có khả năng chính là thứ này làm."),t("br"),t("br"),n._v("Nếu không, hắn đều không thể làm được, đối phương lại như thế nào hoàn thành?"),t("br"),t("br"),n._v("“Ngươi sợi rễ nhiều, thủ đoạn của ta cũng không ít...”"),t("br"),t("br"),n._v("Hiểu rõ chuyện gì xảy ra, Trương Huyền chẳng muốn tiếp tục vết mực, cổ tay khẽ đảo."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Năm đầu vương giả, xuất hiện ở trước mắt, từng cái tản mát ra khí tức cường đại, nhận được mệnh lệnh về sau, đồng thời hướng trước mắt đại thụ vọt tới."),t("br"),t("br"),n._v("“Chuyện này...”"),t("br"),t("br"),n._v("“Tiền bối lại có nhiều như vậy Thánh thú?”"),t("br"),t("br"),n._v("“Hình như, mỗi một cái, đều là Bất Hủ cảnh đại viên mãn...”"),t("br"),t("br"),n._v("“Tùy tiện một cái, chúng ta cũng không là đối thủ...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Ngay tại chiến đấu mọi người, nhìn thấy thanh niên tiện tay thả ra nhiều như vậy thú sủng, từng cái nuốt nước bọt, bờ môi run rẩy."),t("br"),t("br"),n._v("Bình thường tu luyện giả, có một đầu thú sủng là tốt lắm rồi, một cái nhiều như vậy, hơn nữa từng cái mạnh mẽ... Vốn cảm thấy đã đánh giá cao vị này trẻ tuổi tiền bối, không nghĩ tới, còn đánh giá thấp!"),t("br"),t("br"),n._v("Cho người đánh vào thị giác lực, thực tế quá lớn."),t("br"),t("br"),n._v("“Chủ nhân không cần lo lắng, ta tới xử lý...”"),t("br"),t("br"),n._v("Năm đại vương giả xuất hiện, đại thụ quả nhiên tại không còn lúc trước uy phong, lực lượng bị toàn diện áp chế, chiến đấu phút chốc, Thiên Thương Hỏa Phượng một tiếng kêu to, tiếng vang chín tầng trời."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Hỏa diễm theo nó trong miệng phun ra, đem nửa bầu trời đều chiếu rọi thấu hồng, đại thụ vừa gặp phải hỏa diễm, lập tức run lẩy bẩy, tại không còn vừa rồi uy thế."),t("br"),t("br"),n._v("Tuy là cái tên này, có thể thả ra đem người đốt thành hạt cát lông tơ, nhưng dù sao cũng là cây cối, đồng dạng sợ hãi minh hỏa."),t("br"),t("br"),n._v("Hừng hực!"),t("br"),t("br"),n._v("Không để ý tới nó sợ hãi, Thiên Thương Hỏa Phượng tiếp tục phun ra hỏa diễm, rất nhanh, cây cối bắt đầu phát khô, xanh biếc cành lá trở nên khô vàng, lại không còn lúc trước lực lượng."),t("br"),t("br"),n._v("“Ta biết ngươi có ý thức, thần phục với ta, có thể tha cho ngươi khỏi chết, nếu không, ngày hôm nay tai kiếp khó thoát!”"),t("br"),t("br"),n._v("Hai tay chắp sau lưng, Trương Huyền thản nhiên nói."),t("br"),t("br"),n._v("Thuần phục năm đại thú sủng, Cổ Thánh phía dưới, hắn xuất thủ hay không, đều không quan trọng."),t("br"),t("br"),n._v("Trước mắt cây đại thụ này, có thể thiết kế sát hại tu luyện giả, hơn nữa cho hạt cát khải linh, chỉ bằng vào điểm ấy liền có thể biết, khẳng định có thuộc về mình trí tuệ."),t("br"),t("br"),n._v("Đã như vậy, đương nhiên là biến thành của mình."),t("br"),t("br"),n._v("“Để cho ta thần phục, nằm mơ!”"),t("br"),t("br"),n._v("Vừa dứt lời, một đạo ý niệm truyền tới."),t("br"),t("br"),n._v("“Vậy thì thiêu chết...”"),t("br"),t("br"),n._v("Trương Huyền bàn tay lớn vẫy một cái."),t("br"),t("br"),n._v("Hừng hực!"),t("br"),n._v(" Thiên Thương Hỏa Phượng biến thành hỏa hồng chi sắc, hướng về phía trước mắt đại thụ bay đi."),t("br"),t("br"),n._v("Hỏa diễm đầy trời, nướng bốn phía cực nóng không gì sánh được, đại thụ dần dần khô héo, triệt để thiêu đốt."),t("br"),t("br"),n._v("Chỉ chốc lát, liền biến thành tro tàn."),t("br"),t("br"),n._v("“Ừm?”"),t("br"),t("br"),n._v("Thấy triệt để thiêu đốt, đều không thần phục, Trương Huyền nhíu nhíu mày, nhìn về phía mặt đất, khóe miệng nâng lên: “Hậu Thổ Kỳ Lân, đem lòng đất rễ cây đều bị ta cuốn lại, Hỏa Phượng phối hợp, ta không tin đốt không chết!”"),t("br"),t("br"),n._v("Hậu Thổ thân Kỳ Lân thể nhoáng một cái, chui vào sa mạc, ngay sau đó, mọi người liền thấy từng đầu to lớn rễ cây, bị lật lên."),t("br"),t("br"),n._v("Thiên Thương Hỏa Phượng tiếp tục vọt tới."),t("br"),t("br"),n._v("Hỏa diễm tràn ngập, xuất hiện một mảng lớn biển lửa."),t("br"),t("br"),n._v("Thiên Thương Hỏa Phượng toả ra hỏa diễm, tuy là thua kém hơn thiên hỏa, nhưng cũng so với địa hỏa không kém chút nào."),t("br"),t("br"),n._v("Thiêu đốt huyết mạch lời nói, hỏa diễm càng thêm cực nóng, Đại Thánh thần binh, đều có thể hòa tan!"),t("br"),t("br"),n._v("“Ta sẽ không khuất phục nhân loại, bớt làm mộng...”"),t("br"),t("br"),n._v("Căn bị đốt, đại thụ vẫn như cũ không có khuất phục, nhỏ yếu ý niệm truyền lại mà đến, mang theo quật cường."),t("br"),t("br"),n._v("“Như vậy đều không phục?”"),t("br"),t("br"),n._v("Trương Huyền sửng sốt."),t("br"),t("br"),n._v("Một gốc cây mà thôi, có muốn hay không như thế có cốt khí?"),t("br"),t("br"),n._v("Cây khô không còn, rễ cây tại, có thể phục sinh, nhưng rễ cây cũng mất, hẳn phải chết không nghi ngờ ah..."),t("br"),t("br"),n._v("“Không đúng, khẳng định thiêu hủy rễ cây, cũng không cách nào đem hắn chém giết, nếu không, không có khả năng ngoan cường như vậy!”"),t("br"),t("br"),n._v("Mắt sáng lên."),t("br"),t("br"),n._v("Chỉ là một cái cây, cho dù có linh trí, cũng không có khả năng cùng danh sư đồng dạng, tuân thủ quy củ, thà chết chứ không chịu khuất phục."),t("br"),t("br"),n._v("Sở dĩ như vậy, hẳn là có cái gì dựa dẫm cùng át chủ bài, biết, chỉ bằng vào đốt, là đem hắn chém giết không xong."),t("br"),t("br"),n._v("“Hồ nước!”"),t("br"),t("br"),n._v("Cúi đầu nhìn về phía cách đó không xa ao nước."),t("br"),t("br"),n._v("Hỏa Phượng hỏa diễm, như vậy cực nóng, trong hồ nước thủy, thế mà không có tổn thất mảy may."),t("br"),t("br"),n._v("“Minh U Huyền Quy, ngươi nắm giữ thủy thuộc tính lực lượng, đem cái này trong hồ nước nước, tất cả đều cho ta lấy đi, ta ngược lại muốn xem xem, cái tên này có thể kiên trì bao lâu thời gian!”"),t("br"),t("br"),n._v("Chẳng muốn nói nhảm, Trương Huyền lần nữa nói."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Huyền Quy chậm rãi đi tới, há to miệng rộng."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Hồ nước lập tức thủy long đồng dạng, hướng trong miệng nó chảy ngược mà đi."),t("br"),t("br"),n._v("Rất nhanh, bị nuốt sạch sẽ, lộ ra bên trong một đoạn thật nhỏ rễ cây, toàn thân trắng bệch, cho người ta một loại ôn nhuận như ngọc cảm giác."),t("br"),t("br"),n._v("Lăng không một trảo, cầm tới, ngọc chất giống như rễ cây, vừa đến lòng bàn tay của hắn, Thiên Thương Hỏa Phượng ngay tại thiêu đốt cây cối, giống như là mất đi cơ sở, lắc lư một cái, thời gian nháy mắt, đồng thời biến thành cát vàng."),t("br"),t("br"),n._v("“Quả nhiên là bản thể...”"),t("br"),t("br"),n._v("Thấy cảnh này, Trương Huyền nơi nào vẫn không rõ."),t("br"),t("br"),n._v("Lúc trước đại thụ, rễ cây, đều là giả, chỉ có cái này mới là bản thể, khó trách không thần phục... Chỉ cần tìm không thấy cái này, nó sẽ không phải chết."),t("br"),t("br"),n._v("“Hỏa Phượng, đem cái này rễ cây cho ta đốt... Kim Nguyên đỉnh, nếu như đốt không xong, giúp ta luyện chế thành xỉ than!”"),t("br"),t("br"),n._v("Bàn tay run lên, đem rễ cây ném ra ngoài, Kim Nguyên đỉnh, Thiên Thương Hỏa Phượng cùng với khác rất nhiều Thánh thú, vũ khí tất cả đều bay tới."),t("br"),t("br"),n._v("“Đừng có giết ta...”"),t("br"),t("br"),n._v("Thấy nhiều cường giả như vậy khí thế hùng hổ, rễ cây không ngừng run rẩy, nhúc nhích một chút: “Ta thần phục, nguyện ý thần phục...”"),t("br"),t("br"),n._v("Không bị lấy ra đến, ngược lại cũng thôi, bản thể chỉ cần tại, cây cối, rễ cây cũng có thể lần nữa mọc ra, nhưng bây giờ bị chộp vào trong tay, liền không dám tiếp tục nhiều lời."),t("br"),t("br"),n._v("“Cái này còn tạm được!”"),t("br"),t("br"),n._v("Thấy cái tên này bị bắt lại bản thể, lại không còn lúc trước kiên cường, Trương Huyền lúc này mới gật gật đầu."),t("br"),t("br"),n._v("Rất nhanh ký kết khế ước."),t("br"),t("br"),n._v("Làm cho cả sa mạc tất cả cường giả, đều kinh hoàng không dứt kẻ cầm đầu, sau một khắc, liền biến thành của hắn thú sủng."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1726"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1728"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);