(window.webpackJsonp=window.webpackJsonp||[]).push([[827],{1181:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-1718-truong-huyen-bi-bat"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1718-truong-huyen-bi-bat"}},[n._v("#")]),n._v(" Chương 1718: Trương Huyền bị bắt")]),n._v(" "),i("hr"),n._v(" "),i("p",[n._v("Chương 1718: Trương Huyền bị bắt"),i("br"),i("br"),n._v("“Thật mạnh!”"),i("br"),i("br"),n._v("Trương Huyền ngụy trang qua dung mạo, biến sắc, trường kiếm trong tay bỗng nhiên ném đi, vội vàng lui về phía sau."),i("br"),i("br"),n._v("Hắn hiện tại chỉ dùng ra Kim Thân cảnh đỉnh phong thực lực, đối phương không kiêng kị, cũng sẽ không đối mọi người ra tay."),i("br"),i("br"),n._v("“Trốn được ư?”"),i("br"),i("br"),n._v("Thấy cái tên này như vậy quả quyết, một đòn không thành lập khắc chạy trốn, hắc giáp người hừ lạnh, lòng bàn tay lực lượng, cuồng bạo như sóng, cho người ta một loại nghiền ép cảm giác."),i("br"),i("br"),n._v("“Phá!”"),i("br"),i("br"),n._v("Trương Huyền ngón tay một điểm."),i("br"),i("br"),n._v("Ầm ầm!"),i("br"),i("br"),n._v("Tuyệt phẩm Thánh khí trường kiếm, lập tức ở trên không nổ tung, tựa như một vị Thánh vực cửu trọng cường giả tự bạo sinh ra lực lượng, đem hắc giáp người công kích, ngăn tại bên ngoài."),i("br"),i("br"),n._v("Làm xong những này, trên không trung đạp mạnh, thân hình hóa thành một hàng dài, nhanh chóng hướng nơi xa thoát ra."),i("br"),i("br"),n._v("“Dừng lại...”"),i("br"),i("br"),n._v("Rầm rầm!"),i("br"),i("br"),n._v("Thấy hắn hoạt động, bốn phía Dị Linh tộc nhân, nhao nhao lấy ra vũ khí, liền muốn ra tay."),i("br"),i("br"),n._v("“Không cần các ngươi động thủ, cái tên này có ý tứ, để lại cho ta!”"),i("br"),i("br"),n._v("Cười nhạt một chút, hắc giáp người hướng về phía trước một bước, lăng không bóp một cái."),i("br"),i("br"),n._v("Tuyệt phẩm Thánh khí uy lực nổ tung, mặc dù không yếu, nhưng đối Bất Hủ cảnh cường giả tới nói, không tính là gì."),i("br"),i("br"),n._v("Phần phật!"),i("br"),i("br"),n._v("Trên không lập tức xuất hiện một cái bàn tay khổng lồ, đem Trương Huyền bọc tại. Phía trong, bốn phía đều bị phong cấm."),i("br"),i("br"),n._v("“Đáng ghét...”"),i("br"),i("br"),n._v("Một mặt dữ tợn, Trương Huyền máu tươi phun ra, giống như là sử dụng một loại nào đó huyết mạch lực lượng, khí tức lập tức chợt tăng một đoạn, đối phong cấm không gian đâm đến."),i("br"),i("br"),n._v("“Ngươi tại Kim Thân cảnh xem như không yếu, nhưng cùng Bất Hủ cảnh, vẫn là kém quá nhiều... Cho ta xuống đây đi!”"),i("br"),i("br"),n._v("  Rầm!"),i("br"),i("br"),n._v("Hắc giáp người hai tay hợp lại, không khí phát ra liên tục âm bạo, Trương Huyền lực lượng cuối cùng không có đột phá, bị vây ở ở giữa, giống như là bị dây thừng trói lại con cua, bất kể như thế nào giãy dụa, đều chạy trốn không xong."),i("br"),i("br"),n._v("“Tới!”"),i("br"),i("br"),n._v("Lại vồ một cái, Trương Huyền bị khống chế bay đến trước mặt, thân bất do kỷ."),i("br"),i("br"),n._v("Hắn giờ phút này, vẻ mặt trắng bệch, hình như là huyết mạch thiêu đốt sau đó, không còn sức phản kháng."),i("br"),i("br"),n._v("“Muốn giết cứ giết, ta Huyền Chương chắc chắn sẽ không một chút nhíu mày!” Biết chạy trốn không xong, tôi một cái, Trương Huyền một tiếng gầm thét."),i("br"),i("br"),n._v("“Giết ngươi? Yên tâm, ngươi có thể sống lâu một đoạn thời gian. Càng lợi hại danh sư, chân khí càng tinh khiết hơn, tế đàn lực lượng liền càng mạnh... Vốn, còn sợ uy lực không đủ, ngươi xuất hiện, đầy đủ...”"),i("br"),i("br"),n._v("Nhẹ nhàng cười một tiếng, hắc giáp người cong ngón tay búng một cái, Trương Huyền lập tức bay đến Hồ Yêu Yêu đám người trước mặt: “Đem hắn khóa lại, cùng một chỗ tế tự!”"),i("br"),i("br"),n._v("“Vâng!”"),i("br"),i("br"),n._v("Hai đầu Dị Linh tộc nhân nhẹ nhàng vồ một cái, Trương Huyền lập tức cảm thấy một cái chỉ khóa lại bản thân mạch môn, lực lượng toàn thân bị phong cấm trong cơ thể, cũng lại không thi triển ra được."),i("br"),i("br"),n._v("Làm xong những này, không để ý hắn, tiếp tục sắp đặt tế đàn."),i("br"),i("br"),n._v("“Ngươi không nên đi ra, đây là hi sinh vô ích...”"),i("br"),i("br"),n._v("Thấy quả nhiên cùng đoán đồng dạng, bị bắt được nơi này, Trương Huyền thở phào nhẹ nhõm, đang định nghiên cứu một chút cái này chỉ đến cùng là cái gì, liền nghe đến một cái than thở ở bên tai vang lên."),i("br"),i("br"),n._v("Nói chuyện chính là cách đó không xa một vị lão giả, Kiến Thần Bất Hoại cảnh giới."),i("br"),n._v("“Vị tiền bối này, có biết bọn họ chơi cái tế đàn này, vì cái gì?”"),i("br"),i("br"),n._v("Thấy người trước mắt này, thần thái tỉnh táo, không giống những người khác như thế uể oải, Trương Huyền nhịn không được hỏi."),i("br"),i("br"),n._v("“Bọn họ là muốn thông qua tế tự, phá vỡ không gian, tạo dựng cầu nối, đi vào Khổng miếu!” Lão giả hạ giọng."),i("br"),i("br"),n._v("Tu vi bị phong, không cách nào truyền âm."),i("br"),i("br"),n._v("“Phá vỡ không gian? Đi vào Khổng miếu?” Trương Huyền sửng sốt một chút: “Cái này thế giới băng tuyết không phải có lối ra ư? Chỉ cần tìm được lối ra, chẳng phải có thể thuận lợi đi vào Khổng miếu phạm vi, cần phải phiền toái như vậy?”"),i("br"),i("br"),n._v("“Lối ra?” Lão giả một mặt cười khổ: “Là có lối ra, có điều, cái cửa ra này, là vào không được Khổng miếu!”"),i("br"),i("br"),n._v("“Vào không được?” Trương Huyền nghi ngờ nhìn qua."),i("br"),i("br"),n._v("Hắn từ rừng rậm thế giới, đến sa mạc thế giới, sau đó lại đi tới cái này thế giới băng tuyết... Trong bóng tối suy tính, có lẽ cùng lầu các đồng dạng, chỉ cần đi qua đối ứng số tầng, liền nhất định có thể đạt tới Khổng miếu vị trí."),i("br"),i("br"),n._v("“Khổng miếu hết thảy có sáu cái phân điện cùng một cái chủ điện, mỗi cái phân điện, đều đối ứng một cái thế giới khác nhau, những thế giới này, có lối ra cùng cửa vào nối liền cùng một chỗ, chỉ án chiếu cái này đi, sẽ đến về tại đây sáu cái thế giới xuyên qua, vĩnh viễn tìm không thấy vị trí cụ thể!”"),i("br"),i("br"),n._v("Lão giả nói: “Lấy cái này thế giới băng tuyết làm thí dụ, dựa theo lối ra, cửa vào xuyên qua lời nói, thông qua năm cái thế giới về sau, sẽ lần nữa trở lại cái này...”"),i("br"),i("br"),n._v("“Ây... Là như thế này?” Trương Huyền sửng sốt."),i("br"),i("br"),n._v("Vốn cho rằng, chỉ cần tốc độ nhanh, đi hết tất cả thế giới, liền nhất định có thể tìm tới Khổng miếu phạm vi, nằm mơ đều không có nghĩ đến, lại là nối liền cùng một chỗ."),i("br"),i("br"),n._v("Nói như vậy lên, chẳng phải tương đương tại chỗ xoay quanh?"),i("br"),i("br"),n._v("“Đây cũng là bí mật, Danh Sư đường tổng bộ, cũng không biết được a, ngươi như thế nào biết được?”"),i("br"),i("br"),n._v("Nghi ngờ nhìn lại."),i("br"),i("br"),n._v("Hắn thân là tam đại gia tộc tộc trưởng, cửu tinh danh sư, Thánh tử điện điện chủ... Cũng không biết những tin tức này, Danh Sư đường Nhậm đường chủ, nếu là biết, khẳng định cũng trước thời hạn nói... Trước mắt lão giả này, như thế nào biết được cặn kẽ như vậy?"),i("br"),i("br"),n._v("“Là vừa rồi vị kia Dị Linh tộc nhân chính miệng nói... Muốn đi vào Khổng miếu, dựa theo lối ra đi, không thể nào làm được, duy nhất phương pháp chính là thông qua hiến tế, phá vỡ không gian, như vậy, có thể trực tiếp đi vào phân điện vị trí!”"),i("br"),i("br"),n._v("Lão giả nói."),i("br"),i("br"),n._v("“Đi vào phân điện? Không phải nói, không có truyền thế thiên phù, không cách nào đi vào ư?”"),i("br"),i("br"),n._v("Nếu như người người đều có thể phá vỡ không gian, đi vào Khổng miếu phân điện, cái gọi là truyền thế thiên phù, còn có cái gì ý nghĩa?"),i("br"),i("br"),n._v("Cũng không đến mức để vô số người tranh đoạt."),i("br"),i("br"),n._v("“Không có truyền thế thiên phù, đương nhiên vào không được phân điện, nhưng đi tới phân điện bên ngoài, còn có thể làm được, nơi này là Khổng miếu chân chính bên ngoài, linh khí càng thêm nồng đậm, tu luyện càng gia sự hơn gấp rưỡi... Hơn nữa, đi vào phân điện, cướp được bảo vật, liền đại biểu thứ này là của ngươi ư? Thực lực không đủ, vừa rời đi phân điện, liền biết bị cướp đi... Vì lẽ đó, không ít cường giả, canh giữ ở bên ngoài, nghe nói... Còn có Cổ Thánh!”"),i("br"),i("br"),n._v("Lão giả ánh mắt lộ ra kinh hoàng."),i("br"),i("br"),n._v("“Cổ Thánh? Bọn họ canh giữ ở bên ngoài?”"),i("br"),i("br"),n._v("Trương Huyền đồng tử co rút lại."),i("br"),i("br"),n._v("“Đúng vậy a, bọn họ bị hạn chế, vào không được Khổng miếu, nhưng có thể ôm cây đợi thỏ ah! Có thể vào, sớm muộn đều sẽ đi ra, một khi rời đi, liền vô cùng có khả năng gặp phải phục kích... Nói thật, đây mới thực sự là địa phương nguy hiểm, chúng ta những này bình thường tu luyện giả, ở đây quanh quẩn một chỗ là được, chỗ kia, thật muốn đi, chết chỉ có thể càng nhanh!”"),i("br"),i("br"),n._v("Lão giả mặt mũi cười khổ: “Quên đi, một hồi liền bị xem như tế phẩm, nói đến nhiều hơn nữa cũng vô dụng, vẫn là tiết kiệm chút khí lực đi!”"),i("br"),i("br"),n._v("“Ừm!”"),i("br"),i("br"),n._v("Trương Huyền gật đầu, trong lòng tràn đầy khiếp sợ."),i("br"),i("br"),n._v("Còn tưởng rằng, Cổ Thánh cường giả liền nơi này đều tiến bộ đến, không nghĩ tới, có thể đến Khổng miếu bên ngoài... Nếu thực như thế, quả thực sẽ nguy hiểm rất nhiều."),i("br"),i("br"),n._v("“Dị Linh tộc nhân biết những này, có thể hay không đã dò xét xong sáu cái thế giới, hoặc là... Đã có cường giả, đến phân điện bên ngoài?”"),i("br"),i("br"),n._v("Một cái ý nghĩ xông ra."),i("br"),i("br"),n._v("Liên tục đi ba cái thế giới, không có gặp được Triệu Nhã, Ngụy Như Yên, cũng không có gặp được Chư Tử bách gia, thậm chí Lạc Nhược Hi đám người, bọn họ đi nơi nào?"),i("br"),i("br"),n._v("Có thể hay không, đã đến rất nhiều phân điện bên ngoài?"),i("br"),i("br"),n._v("Người đăng: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-1729"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-1731"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);