(window.webpackJsonp=window.webpackJsonp||[]).push([[834],{1189:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1725-re-cay-lap-cong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1725-re-cay-lap-cong"}},[n._v("#")]),n._v(" Chương 1725: Rễ cây lập công")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1725: Rễ cây lập công"),t("br"),t("br"),n._v("“Ngươi láo xược!”"),t("br"),t("br"),n._v("“Thật sự là ăn nói ngông cuồng, không biết trời cao đất rộng!”"),t("br"),t("br"),n._v("“Vô tri cực kỳ, chẳng lẽ còn thật sự cho rằng, thuần phục vài đầu Thánh thú liền vô địch thiên hạ?”"),t("br"),t("br"),n._v("Hai người lúc chiến đấu, từ hạ táo lâu đi ra không ít Dị Linh tộc nhân, giờ phút này nghe nói như thế, tất cả đều khí khuôn mặt phát xanh, lên tiếng hét lớn."),t("br"),t("br"),n._v("Đám người này, chừng hơn ba mươi vị, cùng Bột Nguyên thực lực tương đương, cũng có sáu cái nhiều, trước mắt vị này Trương Huyền, coi như thuần phục năm vị Bất Hủ cảnh đại viên mãn Thánh thú, muốn một người, vượt qua bọn họ những này, cũng không thể nghi ngờ nằm mơ."),t("br"),t("br"),n._v("“Cãi lộn vô dụng, dám ứng chiến, liền lấy ra bảo vật khi tiền đặt cược, ta cũng lấy thiên phù xem như cá cược... Thực lực, là so với tới, không phải mắng ra!”"),t("br"),t("br"),n._v("Trương Huyền thản nhiên nói."),t("br"),t("br"),n._v("Rất nhiều Dị Linh tộc nhân, tất cả đều khí sắc mặt phát xanh."),t("br"),t("br"),n._v("Đối phương nói không sai, thực lực, là so với tới, ở đây nói nhiều hơn nữa cũng vô dụng."),t("br"),t("br"),n._v("“Cho các ngươi mười phút đồng hồ thời gian cân nhắc, ta trước đi ngủ một hồi, so vẫn là không thể so với, tùy thời phụng bồi!”"),t("br"),t("br"),n._v("Không thèm để ý đối phương, Trương Huyền lần nữa duỗi lưng một cái, quay trở về."),t("br"),t("br"),n._v("“Tộc trưởng quá lợi hại...”"),t("br"),t("br"),n._v("“Một người đè nhiều như vậy Dị Linh tộc nhân nói không ra lời, đây mới là danh sư khí độ nên có!”"),t("br"),t("br"),n._v("Xuân Noãn lâu bên trong rất nhiều tu luyện giả, lần nữa nhìn tới, từng cái tràn đầy bội phục."),t("br"),t("br"),n._v("Tuy là có Cổ Thánh tọa trấn, đối phương không dám đem nhóm người mình như thế nào, nhưng Dị Linh tộc nhân ở đây thế lực, rõ ràng so với nhân tộc mạnh hơn, vì lẽ đó, bọn họ đều là bị động tiếp nhận khiêu chiến, nghĩ không ra, vị này tấm tộc trưởng, vừa xuất hiện, liền biểu hiện ra nghiền ép cùng nhau quyết đoán."),t("br"),t("br"),n._v("“Trương Huyền đúng không, tại hạ Bột Phong, nguyện ý đánh với ngươi một trận!”"),t("br"),t("br"),n._v("Còn không có đi trở về Xuân Noãn lâu, một đầu đồ sộ Dị Linh tộc nhân, đi ra, chính là còn lại sáu vị Bất Hủ cảnh đại viên mãn một trong."),t("br"),t("br"),n._v("“Tiền đánh cược là cái gì?” Thấy có người đưa đồ ăn, Trương Huyền một mặt vui vẻ."),t("br"),t("br"),n._v("“Ta chỉ có một kiện Đại Thánh chí bảo! Thua có thể cho ngươi, thắng, ta cũng không cần truyền thế thiên phù, chỉ cần ba cái theo ở phía sau đi vào Đại Thành điện danh ngạch!” Bột Phong nói."),t("br"),t("br"),n._v("“Danh ngạch?” Trương Huyền hơi nghi hoặc một chút."),t("br"),t("br"),n._v("“Một cái truyền thế thiên phù, có thể kéo mười người đi vào Đại Thành điện, đây cũng là ta đi tới nơi này mới biết...” Hưng kiếm thánh nói."),t("br"),t("br"),n._v("“Ah!” Trương Huyền gật gật đầu: “Phân ngươi ba cái cũng không sao, ta đồng ý!”"),t("br"),t("br"),n._v("Phân cho đối phương ba cái không có gì, cùng lắm thì đi vào Đại Thành điện về sau, nghĩ biện pháp giết chính là."),t("br"),t("br"),n._v("ngantruyen.com"),t("br"),n._v("Cổ Thánh vào không được Khổng miếu, cũng liền không có năng lực can thiệp tình huống bên trong, chết mấy người, mười phần bình thường."),t("br"),t("br"),n._v("“Cái này năm đầu Thánh thú, mặc dù là ngươi thú sủng, nhưng để bọn chúng vây công, không thể hiển lộ rõ ràng cá nhân thực lực, ta khiêu chiến là ngươi, không phải Thánh thú, thân là danh sư, có dám hay không, vứt bỏ thú sủng, đánh với ta một trận?”"),t("br"),t("br"),n._v("Thấy hắn đồng ý, Bột Phong khẽ nói."),t("br"),t("br"),n._v("“Ngươi ý tứ, không cho ta sử dụng thú sủng?” Mỉm cười, Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Không sai, có dám hay không không dựa vào Thánh thú, chỉ bằng vào thực lực của mình, chân ướt chân ráo cùng ta chiến đấu một hồi, thật muốn như vậy, thua, ta cũng vui lòng phục tùng!” Bột Phong nói."),t("br"),t("br"),n._v("“Để cho ta không cần thú sủng cũng được, nhưng... Lại muốn thêm một cái Đại Thánh chí bảo!” Trương Huyền nói."),t("br"),t("br"),n._v("“Lại thêm một cái?”"),t("br"),t("br"),n._v("Nhíu nhíu mày, Bột Phong chần chờ một chút, cổ tay khẽ đảo, một cái huyết hồng sắc linh thạch xuất hiện tại phía trước: “Đại Thánh chí bảo, ta đã không còn, có điều, khối này Thánh huyết linh thạch, là Bất Hủ cảnh đại viên mãn Thánh thú tinh huyết tẩm bổ mà thành, ẩn chứa loại này cường giả toàn bộ lực lượng, dùng để tu luyện, linh khí mười phần, mặc dù so ra kém Đại Thánh chí bảo, nhưng cũng không kém quá nhiều, ngươi nhìn có thể hay không thay thế?”"),t("br"),t("br"),n._v("Trương Huyền nhìn sang, quả nhiên tuỳ tiện cảm nhận được trên tảng đá tản ra linh khí nồng nặc, giống như thực chất."),t("br"),t("br"),n._v("Dùng để tu luyện, quả thực có thể nhanh chóng tiến bộ, đối với hắn, cũng không nhỏ trợ giúp."),t("br"),t("br"),n._v("“Thành giao!” Trương Huyền liền vội vàng gật đầu."),t("br"),t("br"),n._v("Đại Thánh chí bảo, nói thật, hắn đều có rất nhiều, nhiều hơn nữa cũng vô dụng, thật muốn nói tác dụng, khả năng còn không bằng khối linh thạch này."),t("br"),t("br"),n._v("“Vậy thì bắt đầu a, các ngươi nhân tộc Cổ Thánh, cùng tộc ta Cổ Thánh, cũng có thể làm chứng, lần tỷ đấu này, lại không có thể sử dụng thú sủng!” Còn có chút không yên lòng, Bột Phong cất cao giọng nói."),t("br"),t("br"),n._v("Tuy là không biết Cổ Thánh trốn ở nơi nào, nhưng nói như vậy đi ra, tương đương với lập xuống quy củ, bất kỳ người nào cũng không dám vi phạm."),t("br"),t("br"),n._v("“Yên tâm, đã đồng ý ngươi, tự nhiên sẽ không lại dùng!”"),t("br"),n._v("Bàn tay một trảo, đem rất nhiều Thánh thú, thu vào không gian gấp, Trương Huyền một mặt bình tĩnh."),t("br"),t("br"),n._v("“Ta tin tưởng danh sư, sẽ nhất ngôn cửu đỉnh!”"),t("br"),t("br"),n._v("Bột Phong gật đầu, tiến về phía trước một bước, hít sâu một hơi, binh khí trong tay thả ra hào quang chói sáng."),t("br"),t("br"),n._v("Là một chuôi Hàn Nguyệt câu, đạt đến Đại Thánh cấp bậc, cá cược bên trong vũ khí, chính là cái này."),t("br"),t("br"),n._v("“Tộc trưởng, cẩn thận...”"),t("br"),t("br"),n._v("Thấy Bất Hủ cảnh viên mãn cường giả, lấy ra Đại Thánh cấp bậc vũ khí, một vị thái thượng trưởng lão, có chút lo lắng hô lên."),t("br"),t("br"),n._v("Vị tộc trưởng này, không sử dụng Thánh thú lời nói, cũng liền Kim Thân cảnh đỉnh phong, cùng đối phương chênh lệch hai cái đại cấp bậc."),t("br"),t("br"),n._v("Thực lực chênh lệch như thế lớn, thật sự có thể chiến thắng?"),t("br"),t("br"),n._v("“Ha ha!”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Trương Huyền đi tới, một cái ngọc chất giống như rễ cây xuất hiện tại lòng bàn tay: “Yên tâm, ta nói qua không cần Thánh thú, liền sẽ không dùng, bất quá... Ta không có quá lợi hại Đại Thánh chí bảo, cái này rễ cây, là trong lúc vô tình lấy được, trình độ cứng cáp không sai, hẳn là có thể sử dụng đi!”"),t("br"),t("br"),n._v("“Rễ cây?”"),t("br"),t("br"),n._v("Xác định trong tay đối phương đích thật là rễ cây, Bột Phong lúc này mới gật gật đầu: “Đương nhiên có thể!”"),t("br"),t("br"),n._v("Chỉ cần không sử dụng thú sủng, đối phương coi như sử dụng Đại Thánh chí bảo, đối với hắn mà nói ảnh hưởng cũng không lớn, huống chi, chỉ là cái thoạt nhìn không có gì đặc thù rễ cây."),t("br"),t("br"),n._v("“Vậy là tốt rồi... Tiểu Căn Căn, phía dưới liền dựa vào ngươi!”"),t("br"),t("br"),n._v("Gật gật đầu, Trương Huyền tiện tay ném đi."),t("br"),t("br"),n._v("“Được rồi!”"),t("br"),t("br"),n._v("Rễ cây bên trong lập tức tản mát ra một đạo ý niệm, đi tới Bột Phong bầu trời."),t("br"),t("br"),n._v("“Giả thần giả quỷ!”"),t("br"),t("br"),n._v("Không nghĩ tới một cái rễ cây còn có thể nói chuyện, con mắt lập tức híp lại, Bột Phong trong tay Hàn Nguyệt câu nhẹ nhàng run lên, nhất thời tìm tới."),t("br"),t("br"),n._v("Tê lạp!"),t("br"),t("br"),n._v("Không gian một hồi rung chuyển."),t("br"),t("br"),n._v("Bất Hủ cảnh đại viên mãn phối hợp lợi hại Đại Thánh chí bảo, thi triển ra khiến người ta run sợ lực lượng."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Hàn Nguyệt câu còn chưa tới đến rễ cây trước mặt, liền cảm thấy trước mắt không gian một hồi lay động, sau một khắc, giống như là rơi vào một mảnh sa mạc bên trong, ngay sau đó một gốc đại thụ che trời, xuất hiện ở trước mắt, gió nhẹ phủ động, từng mảnh từng mảnh lông tơ, như là hoa tuyết, đem hắn bao phủ ở bên trong."),t("br"),t("br"),n._v("“Đây là cái gì?”"),t("br"),t("br"),n._v("Bột Phong đồng tử co rụt lại, la lên, ngay sau đó liền cảm thấy mấy mảnh lông tơ bay vào cổ họng."),t("br"),t("br"),n._v("Ngay sau đó, thân thể cứng đờ, giống như là bị giam cầm một nửa, cũng lại không thể động đậy."),t("br"),t("br"),n._v("“Chuyện gì xảy ra?”"),t("br"),t("br"),n._v("“Đây là cái gì vũ khí?”"),t("br"),t("br"),n._v("Không nghĩ tới cái này rễ cây, trực tiếp làm ra một mảng lớn sa mạc, tất cả mọi người đều có chút choáng váng, phản ứng không kịp."),t("br"),t("br"),n._v("“Bột Phong, hẳn là ảo giác, nghĩ biện pháp phá vỡ là được...”"),t("br"),t("br"),n._v("Dị Linh tộc bên trong, một vị cường giả hô."),t("br"),t("br"),n._v("“Được...”"),t("br"),t("br"),n._v("Giãy dụa lấy hô một câu, Bột Phong thanh âm còn không có kết thúc, toàn thân đột nhiên co lại."),t("br"),t("br"),n._v("Rầm rầm!"),t("br"),t("br"),n._v("Trong nháy mắt biến thành một đống cát vàng, vương xuống đến, không có lưu lại nửa điểm dấu vết."),t("br"),t("br"),n._v("Bốn phía, lặng ngắt như tờ. U"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1736"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1738"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);