(window.webpackJsonp=window.webpackJsonp||[]).push([[841],{1198:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1731-đai-thanh-đien"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1731-đai-thanh-đien"}},[n._v("#")]),n._v(" Chương 1731: Đại Thành điện")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1731: Đại Thành điện"),t("br"),t("br"),n._v("“Đi vào đi!”"),t("br"),t("br"),n._v("Tất cả Thánh thú, tất cả đều nhận chủ, Trương Huyền lúc này mới thoả mãn gật gật đầu, đem rất nhiều Thánh thú thu vào không gian gấp, lần nữa hướng bên trong xông tới."),t("br"),t("br"),n._v("Bất quá, lần này không đồng dạng, tới đụng một cái, bị ngăn tại bên ngoài."),t("br"),t("br"),n._v("“Chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Khóe miệng giật một cái."),t("br"),t("br"),n._v("Chẳng lẽ, trở ngại điểm ấy thời gian, lại không đi vào a?"),t("br"),t("br"),n._v("Thật muốn như vậy, chỉ có thể khóc."),t("br"),t("br"),n._v("“Chủ nhân, ngươi mang Thánh thú quá nhiều...”"),t("br"),t("br"),n._v("Tiểu Phù Phù thanh âm vang lên: “Bình thường truyền thế thiên phù, là có thể mang mười người, ta làm mẫu phù, nhiều nhất có thể mang mười lăm cái, ngươi mới vừa thuần phục, đều vượt qua hai mươi...”"),t("br"),t("br"),n._v("Nghe được cũng không phải là không cách nào đi vào, Trương Huyền thở phào nhẹ nhõm, nhịn không được hỏi: “Đặt ở không gian gấp cũng không được?”"),t("br"),t("br"),n._v("“Không được! Nếu như vậy nếu có thể, vừa rồi Hưng, Mộng kiếm thánh bọn họ cũng có thể đem tất cả mọi người mang đi!”"),t("br"),t("br"),n._v("Tiểu Phù Phù giải thích."),t("br"),t("br"),n._v("“Cũng đúng!” Trương Huyền chợt."),t("br"),t("br"),n._v("Khổng sư tự thân vật lưu lại, sao có thể đơn giản như vậy?"),t("br"),t("br"),n._v("Đã nói hạn chế, vậy thì có hạn chế, nếu không, người người đều lợi dụng sơ hở, chưa đủ lớn **?"),t("br"),t("br"),n._v("Đem tu vi thấp Thánh thú, lần nữa thả ra, khống chế số lượng tại mười bốn đầu khoảng chừng, lần nữa hướng phía trong chui vào, quả nhiên thuận lợi thông qua, không còn ràng buộc."),t("br"),t("br"),n._v("“Không đúng, Khung Mộc Thanh giao bọn họ cũng tại không gian gấp, như vậy cộng lại, đã vượt qua mười lăm?”"),t("br"),t("br"),n._v("Đi vào màng ánh sáng, mới vừa đi không xa, Trương Huyền nói."),t("br"),t("br"),n._v("Hắn lưu lại mười bốn đầu, đều là vừa mới thuần phục, không có tính cả cùng Mộc Thanh giao, Kim Linh Vân hổ mấy người thú, thậm chí lúc trước thu phục xích ngạch Bạch Hổ cũng ở trong đó, tính toán ra, chân chính số lượng, đã sớm vượt qua hai mươi đại quan."),t("br"),t("br"),n._v("“Bọn chúng bản thân liền là Khổng miếu trong di tích sinh mệnh, có thể tùy ý đi vào, không giữ lấy danh ngạch!” Tiểu Phù Phù nói."),t("br"),t("br"),n._v("“Còn có loại thuyết pháp này?”"),t("br"),t("br"),n._v("Không nghĩ tới Khổng miếu trong di tích Thánh thú, có loại đãi ngộ này, Trương Huyền cười khổ một tiếng, không tại nhiều nói, tăng thêm tốc độ, hướng trong phong ấn bay đi."),t("br"),t("br"),n._v("Mọi người đã đi vào vượt qua một phút đồng hồ, hắn cũng muốn nhanh lên một chút."),t("br"),t("br"),n._v("Rộng lớn sau đại môn, là cái to lớn cung điện, hai bên bày đặt hai hàng tượng, có suy nghĩ, có nhìn, có đọc sách, có tu hành... Sinh động như thật, tựa như bất cứ lúc nào cũng sẽ sống lại đồng dạng, hình thái khác nhau, thần thái không giống nhau."),t("br"),t("br"),n._v("“Đây là... Bảy mươi hai Thánh?”"),t("br"),t("br"),n._v("Rất nhanh, Trương Huyền nhận ra được những này tượng, không phải cái khác, chính là Khổng môn bảy mươi hai Thánh."),t("br"),t("br"),n._v("Khổng sư đệ tử ba ngàn, thân truyền bảy mươi hai vị, người xưng bảy mươi hai Thánh, còn Nhiễm Cầu Cổ Thánh, Bốc Thương Cổ Thánh, thì là thực lực mạnh hơn mười đại hiền giả."),t("br"),t("br"),n._v("Bảy mươi hai vị thân truyền, mỗi một cái đều có truyện ký, đều có kinh người quá khứ, từ đó bị Danh Sư đường, vô số người nhớ rõ, Trương Huyền từng nhìn qua một ít, giờ phút này nhìn thấy tượng, lập tức nhận ra được."),t("br"),t("br"),n._v("Tử Uyên Cổ Thánh, Tử Khiên Cổ Thánh, Tử Liễu Cổ Thánh, Nhiễm Canh Cổ Thánh.."),t("br"),t("br"),n._v("Từng cái nghe nhiều nên thuộc tên, hiện lên ở trong óc, cùng trước mắt rất nhiều tượng hòa làm một thể, phảng phất năm đó, chinh chiến sa trường, cùng Dị Linh tộc nhân dục huyết phấn chiến."),t("br"),t("br"),n._v("Có thể nói, không có bọn họ, Khổng sư một người, không có khả năng bình định Dị Linh tộc nhân chi mắc!"),t("br"),t("br"),n._v("Nhân loại tiến lên phong bi bên trên, điêu khắc mỗi một người bọn hắn tên, sáng loà, đời đời bất hủ."),t("br"),t("br"),n._v("Vẻ mặt nghiêm túc, Trương Huyền khom người được rồi sùng cao nhất lễ tiết."),t("br"),t("br"),n._v("Bảy mươi hai Thánh, tản mát ra nhàn nhạt uy áp, trong lúc đi lại, đối tâm cảnh cùng tinh thần, có tăng lên cực lớn cùng cảm ngộ."),t("br"),t("br"),n._v("Lại đi vài bước, nhìn thấy có mấy vị danh sư, khoanh chân ngồi dưới đất, không cách nào tiếp tục đi tới."),t("br"),t("br"),n._v("Tâm cảnh của bọn hắn không đủ, tiếp tục đi tới, đối thân thể tổn thương cực lớn, thua kém hơn mượn loại này áp bức, nhanh chóng tăng lên."),t("br"),n._v("Chính là Trương gia mấy vị thái thượng trưởng lão, Trương Huyền không có nói nhiều, tiếp tục hướng phía trước."),t("br"),t("br"),n._v("Triệu Nhã mang theo một đám người, đến cùng là ai, cũng không rõ ràng, có điều, có thể xuyên qua bảy mươi hai Thánh tượng tạo thành thông đạo, tâm cảnh chỉ sợ so với hắn, đều không kém quá nhiều."),t("br"),t("br"),n._v("Nắm giữ như vậy tâm cảnh, tu vi phỏng đoán cũng đạt tới Bất Hủ cảnh cảnh giới đại viên mãn, đối với cha mẹ đám người, tuyệt đối là uy hiếp rất lớn."),t("br"),t("br"),n._v("Thông qua bảy mươi hai hiền tượng thông đạo, đi tới một cái khác đại điện."),t("br"),t("br"),n._v("Cha mẹ đám người, ngay tại trong đó, cũng không đi xa, mà đối diện bọn họ, thì là tám vị thanh niên, mỗi cái trên người đều tản mát ra ngạo nhân khí tức., cùng đoán đồng dạng, quả nhiên đều là đạt tới Bất Hủ cảnh đại viên mãn nhân vật tuyệt đỉnh."),t("br"),t("br"),n._v("Thấy cha mẹ đám người không có việc gì, Trương Huyền thở phào nhẹ nhõm, hướng đối diện mọi người nhìn lại."),t("br"),t("br"),n._v("Một cái nữ hài, đang đứng trong đám người, hai mắt nhắm nghiền, trong cơ thể từng đạo băng hàn lực lượng, không ngừng toả ra, dường như muốn kích hoạt trên vách tường cái nào đó phong ấn."),t("br"),t("br"),n._v("“Triệu Nhã!”"),t("br"),t("br"),n._v("Lông mày nhíu một cái, vội vàng đi tới."),t("br"),t("br"),n._v("“Huyền nhi, nàng hình như không nghe được tiếng la!”"),t("br"),t("br"),n._v("Còn chưa tới đến trước mặt, Hưng, Mộng kiếm thánh hai người liền tới đón, lặng lẽ truyền âm."),t("br"),t("br"),n._v("“Không nghe được thanh âm?”"),t("br"),t("br"),n._v("Trương Huyền sững sờ."),t("br"),t("br"),n._v("“Ừm, vừa rồi chúng ta đã hô, có điều, không có bất kỳ cái gì phản ứng, thậm chí liền đầu đều không có xoay, hình như nghe không được la lên đồng dạng...”"),t("br"),t("br"),n._v("Hưng kiếm thánh nói."),t("br"),t("br"),n._v("“Nàng hẳn là hết sức chăm chú tại phá giải, trên mặt tường bức kia đồ, mục đích làm như vậy là cái gì, chúng ta cũng không rõ ràng...”"),t("br"),t("br"),n._v("Mộng kiếm thánh cũng lặng lẽ một chỉ."),t("br"),t("br"),n._v("“Đồ?” Trương Huyền lúc này mới hướng về phía trước bức tường nhìn lại, quả nhiên thấy trên tường, vẽ lấy một bộ to lớn đan thanh, sinh động như thật, hình như một phương thế giới, bị phong ấn ở phía trong."),t("br"),t("br"),n._v("Triệu Nhã trong cơ thể không ngừng tản mát ra băng lãnh khí tức, dường như muốn đem gia trì tại đan thanh bên trên phong ấn tháo ra."),t("br"),t("br"),n._v("Dựa theo tình huống bình thường, bản thân đi vào đại điện, lại hô một tiếng, đối phương khẳng định sẽ phát hiện, có thể giờ phút này một điểm phản ứng đều không có, chỉ sợ thật cùng cha mẹ nói đồng dạng, không nghe được thanh âm."),t("br"),t("br"),n._v("“Bọn họ là ai?”"),t("br"),t("br"),n._v("Đã nhìn thấy Triệu Nhã, cũng không nhận tổn thương, Trương Huyền lại không lo lắng, mà là híp mắt lại, nhìn về phía tám vị thanh niên."),t("br"),t("br"),n._v("“Vừa rồi ta hỏi, bọn họ là... Chư Tử bách gia cường giả!”"),t("br"),t("br"),n._v("Hưng kiếm thánh nói."),t("br"),t("br"),n._v("“Chư Tử bách gia?” Trương Huyền cau mày: “Bọn họ không phải bảy mươi hai Thánh Nhân đời sau ư? Vì sao muốn bắt Triệu Nhã? Trên bức họa này phong ấn lại là cái gì?”"),t("br"),t("br"),n._v("Lúc trước vẫn cho là, là Dị Linh tộc nhân bắt đi Triệu Nhã đám người."),t("br"),t("br"),n._v("Đối phương vô cùng hung ác, làm ra loại chuyện này, mười phần bình thường, có thể... Chư Tử bách gia, truyền thừa tại Khổng sư, lấy lễ lấy xưng, vì sao làm ra loại này vi phạm đạo đức, không báo tin người khác, liền trực tiếp bắt người hoạt động?"),t("br"),t("br"),n._v("Không phù hợp đạo lý ah!"),t("br"),t("br"),n._v("Hơn nữa mấu chốt chính là, phía ngoài đại điện phía trong, nắm giữ bảy mươi hai Thánh Nhân tượng, thân là hậu bối, không những không tế bái, ngược lại đối một bức tranh để tâm, chẳng lẽ bức họa này bên trong ẩn giấu đi, một loại nào đó cơ mật?"),t("br"),t("br"),n._v("Nghĩ đến cái này, cũng nhịn không được nữa, hướng trước mắt đan thanh nhìn lại."),t("br"),t("br"),n._v("Bức tranh ở trên vách tường bày ra, từ xa nhìn lại, mang theo xuyên thấu lịch sử tang thương, thời gian, không gian hoàn mỹ giao hòa, lấy hắn Minh Lý Chi Nhãn, đều thấy không rõ lắm."),t("br"),t("br"),n._v("Chỉ cảm thấy trong hình vẽ, truyền đến từng cơn cuồn cuộn chi khí, hào hùng khí thế, để cho người ta liếc mắt liền rơi vào trong đó, không cách nào tự kềm chế."),t("br"),t("br"),n._v("“Ngươi nói... Có thể hay không đây là 《 Xuân Thu đại điển 》?”"),t("br"),t("br"),n._v("Đột nhiên, Hưng kiếm thánh thanh âm ở bên tai vang lên."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1742"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1744"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);