(window.webpackJsonp=window.webpackJsonp||[]).push([[842],{1196:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1732-tan-thang-co-thanh-co-duyen"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1732-tan-thang-co-thanh-co-duyen"}},[n._v("#")]),n._v(" Chương 1732: Tấn thăng Cổ Thánh cơ duyên")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1732: Tấn thăng Cổ Thánh cơ duyên"),t("br"),t("br"),n._v("“Xuân Thu đại điển?” Trương Huyền sửng sốt."),t("br"),t("br"),n._v("“Xuân Thu đại điển theo như truyền thuyết là Khổng sư mạnh nhất pháp bảo, có người nói là thư tịch, có người nói là vũ khí, cũng có người nói, là một loại không gian đồ vật... Cụ thể là cái gì, không ai thấy qua, không thể làm thống nhất kết luận, có phải hay không là một bức họa?”"),t("br"),t("br"),n._v("Hưng kiếm thánh phân tích nói: “Nếu không, chư tử bách gia cường giả, vì sao vừa đến liền phá giải nơi này phong ấn? Hơn nữa, biết Triệu Nhã có cái này năng lực đặc thù, trước thời hạn liền đem nó bắt đi?”"),t("br"),t("br"),n._v("Trương Huyền suy nghĩ."),t("br"),t("br"),n._v("Hắn nói không phải không có lý."),t("br"),t("br"),n._v("Ngoại giới truyền tới tin tức, cũng không nhất định chính xác, thật giống như, cầm trong tay mẫu phù, liền có thể đi vào chủ điện, mẫu phù trong tay hắn, nhưng chủ điện ở đâu... Đến bây giờ đều không có nhìn thấy tung tích."),t("br"),t("br"),n._v("Xuân Thu đại điển, trong truyền thuyết là thư tịch, chưa hẳn không thể là một bức họa."),t("br"),t("br"),n._v("Tiếp tục hướng bức tranh nhìn, phong ấn che lấp lại, nội dung mặc dù thấy không rõ lắm, lại có thể nhìn ra trong đó tuyết trắng bao phủ, thoáng có chút hoang vu, một bộ trời đông giá rét quá cảnh bộ dáng."),t("br"),t("br"),n._v("“Quả nhiên có thời gian pháp tắc...”"),t("br"),t("br"),n._v("Cẩn thận cảm thụ một chút, bức tranh này bên trong, không chỉ có không gian lực lượng, còn có thời gian lan tràn, nói cách khác, tranh bên trong thời gian, cùng ngoại giới tốc độ thời gian trôi qua, cũng không tương đồng."),t("br"),t("br"),n._v("Ngoại giới chẳng qua cuối thu, mà trong bức tranh, đã trời đông giá rét."),t("br"),t("br"),n._v("Trong bức họa, có thời gian tồn tại... Chẳng lẽ, thật sự là Xuân Thu đại điển?"),t("br"),t("br"),n._v("Nếu thật là, vô luận như thế nào cũng muốn cướp đoạt tới tay, mặc kệ đối phương có phải hay không chư tử bách gia, cũng sẽ không lưu tình."),t("br"),t("br"),n._v("“Đúng, ta không nhận ra, Tiểu Phù Phù khẳng định có thể nhận ra!”"),t("br"),t("br"),n._v("Trong lòng hơi động."),t("br"),t("br"),n._v("Hắn đối Thượng Cổ tin tức, hiểu rõ không nhiều, nhưng Tiểu Phù Phù khác biệt, là Khổng sư chuyên môn luyện chế ra tới, đi vào Khổng miếu chìa khoá, khẳng định có thể nhận ra Xuân Thu đại điển, đến cùng loại nào bộ dáng."),t("br"),t("br"),n._v("Nghĩ đến cái này, tinh thần khẽ động truyền âm qua."),t("br"),t("br"),n._v("“Xuân Thu đại điển? Nhờ có ngươi nghĩ ra, đây là Khổng sư thân vẽ 【 Tứ Quý Đồ】!”"),t("br"),t("br"),n._v("Tiểu Phù Phù tại không gian gấp duỗi lưng một cái, lá bùa tại gió thổi bên dưới, lá phong hoa hoa tác hưởng."),t("br"),t("br"),n._v("“Tứ Quý Đồ?”"),t("br"),t("br"),n._v("“Tới thời điểm, ngươi không có phát hiện phía ngoài lầu các à? Xuân hạ thu đông, đại biểu bốn mùa!” Tiểu Phù Phù giải thích."),t("br"),t("br"),n._v("Trương Huyền sững sờ, nhẹ gật đầu."),t("br"),t("br"),n._v("Lúc ấy, nhìn thấy cái gọi là xuân noãn lâu, hạ nóng nảy lâu, vẫn chưa để ý, giờ phút này nói chuyện, quả là thế."),t("br"),t("br"),n._v("Hơn nữa, Khổng sư đột phá đại thánh bí thuật, là lấy bản thân vì thiên địa, diễn biến xuân hạ thu đông, một năm bốn mùa... Sẽ có hay không có liên hệ nhất định?"),t("br"),t("br"),n._v("“Cái này Tứ Quý Đồ, có tác dụng gì, để chư tử bách gia cường giả như thế dụng tâm?” Hiểu không là Xuân Thu đại điển, trong lòng hơi có chút thất lạc, Trương Huyền đồng thời tò mò."),t("br"),t("br"),n._v("Coi như không phải đại điển, đoán chừng cũng là không kém bảo vật, nếu không, không có khả năng để nhiều cường giả như vậy, như vậy để ý."),t("br"),t("br"),n._v("“Tứ Quý Đồ, thoạt nhìn là một bức tranh, trên thực tế ẩn chứa xuân hạ thu đông bốn mùa, hơn nữa tốc độ thời gian trôi qua, so bên ngoài phải nhanh... Có lẽ là chừng gấp hai, nói cách khác, ở bên trong tu luyện hai năm, bên ngoài mới đi qua một năm... Đối với bồi dưỡng nhân tài tới nói, tuyệt đối là rất tốt bảo vật!”"),t("br"),t("br"),n._v("Tiểu Phù Phù nói."),t("br"),t("br"),n._v("Trương Huyền gật đầu, rất tán thành."),t("br"),t("br"),n._v("Trương gia sở dĩ trở thành đệ nhất gia tộc, lâu dài không suy, chủ yếu nhất liền là nắm giữ qua huyết trì, linh hồn có thể có thời gian dài hơn, cảm ngộ công pháp tu luyện võ kỹ."),t("br"),t("br"),n._v("Danh Sư đường tổng bộ, thiên tài vô số, cửu tinh danh sư chỉ điểm, ít đi đường quanh co, là một cái phương diện, càng quan trọng hơn nắm giữ Xuân Thu điện, cùng ngoại giới có thời gian kém."),t("br"),t("br"),n._v("Trịnh Dương tiến bộ nhanh như vậy, so với hắn đều muốn nhanh chóng, Chiến Sư đường truyền thừa, chỉ là thứ nhất, một nguyên nhân khác, liền là đi vào tại đây tu luyện."),t("br"),n._v("Cái này Tứ Quý Đồ, một năm làm hai năm dùng, quả thực xem như không kém bảo vật."),t("br"),t("br"),n._v("“Chẳng qua... Theo ta được biết, bản vẽ này, trọng yếu nhất cũng không phải là chênh lệch thời gian, mà là... Khổng sư phong cấm, một bộ phận thiên địa!”"),t("br"),t("br"),n._v("Dừng lại một chút, nhớ ra cái gì đó, Tiểu Phù Phù tiếp tục nói."),t("br"),t("br"),n._v("“Phong ấn thiên địa?” Sửng sốt một chút, Trương Huyền ngay sau đó cười cười: “Bức họa này bên trong, ẩn chứa không gian chi lực, phong cấm một bộ phận thiên địa, là bình thường!”"),t("br"),t("br"),n._v("Bình thường không gian gấp, là mở ra một phương thiên địa, có thể dung nạp đồ vật, lại không cách nào thích ứng sinh mệnh trữ hàng, tỷ như: Trữ vật giới chỉ."),t("br"),t("br"),n._v("Cao cấp một chút, cùng nhà ấm đồng dạng, có thể sinh tồn sinh mệnh, sinh trưởng thực vật, nhưng thời gian dài không cùng ngoại giới câu thông, sinh thái môi trường sẽ gặp phải phá hư, dần dà, linh khí suy yếu, sinh mệnh khó mà tiếp tục."),t("br"),t("br"),n._v("Hắn hiện tại không gian gấp liền là loại cấp bậc này, cần linh khí cung cấp, mới có thể tốt hơn tốt phát triển, mà không giống danh sư đại lục, mình có thể sinh ra linh khí, sinh ra mới sinh mệnh, tạo thành một cái động thái cân bằng."),t("br"),t("br"),n._v("Bức họa này, có thời gian pháp tắc, có thể diễn biến xuân hạ thu đông, có lẽ cùng trước đó rừng rậm thế giới đồng dạng, có thể thai nghén sinh mệnh, tự động sinh ra linh khí, tự nhiên muốn so với hắn không gian gấp cao cấp hơn."),t("br"),t("br"),n._v("Khổng sư chế tạo ra, phong cấm một phương thiên địa ở bên trong, cũng không kỳ lạ."),t("br"),t("br"),n._v("“Là rất bình thường... Nhưng ngươi cần nghĩ kĩ, đây chính là Thượng Cổ thời kỳ một phương thiên địa!” Tiểu Phù Phù nói."),t("br"),t("br"),n._v("“Thượng Cổ thời kỳ thiên địa? Vậy thì sao? Còn có thể cùng hiện tại thiên địa khác biệt? Nhiều nhất linh khí dồi dào một chút thôi...”"),t("br"),t("br"),n._v("Trương Huyền không để ý lắm."),t("br"),t("br"),n._v("Thượng Cổ thời kì, có thể sinh ra Khổng sư cùng bảy mươi hai hiền nhiều như vậy đại năng, linh khí nhất định so hiện tại càng thêm dồi dào, nhưng này lại như thế nào? Phong cấm một phương thiên địa, dù sao cũng là tiểu thiên địa, không có khả năng cùng danh sư đại lục loại này bát ngát địa phương so."),t("br"),t("br"),n._v("“Bất thường...”"),t("br"),t("br"),n._v("Lời còn chưa dứt, Trương Huyền con ngươi co rụt lại: “Ngươi nói là, phong cấm lúc ấy một phương thiên địa? Chẳng phải chứng tỏ... Ở đây, có cơ hội đột phá đến Cổ Thánh?”"),t("br"),t("br"),n._v("Dương sư cùng Hưng kiếm thánh, từng cùng hắn đàm phán qua liên quan tới Cổ Thánh phương diện không ít chuyện, gần một vạn năm tới, vùng thế giới này, tựa như là mất đi cái gì, không ít tu luyện giả, rõ ràng đạt đến bất hủ cảnh đại viên mãn, liền là không cách nào đột phá cuối cùng ràng buộc, thành tựu Cổ Thánh."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, Cổ Thánh mới tự nguyện ngủ say, lúc chiến đấu tỉnh nữa tới, chỉ vì kéo dài sinh mệnh."),t("br"),t("br"),n._v("Gần một vạn năm trước, mới ít đồ vật, nếu như phương thiên địa này, là tại Khổng sư lúc, bị phong cấm, chẳng phải đại biểu, loại đồ vật này vẫn tồn tại, đủ có thể khiến người đột phá đến loại cảnh giới này?"),t("br"),t("br"),n._v("“Đúng vậy a, nếu không ngươi cho rằng, những người này vì cái gì như vậy dùng sức?”"),t("br"),t("br"),n._v("Tiểu Phù Phù tiếp tục nói."),t("br"),t("br"),n._v("“Thì ra là thế... Bức họa này, dính dáng đột phá Cổ Thánh cơ duyên... Khó trách, liều mạng như vậy!”"),t("br"),t("br"),n._v("Trương Huyền bừng tỉnh hiểu ra."),t("br"),t("br"),n._v("Hắn hiện tại tuy là kim thân cảnh đỉnh phong, nhưng khoảng cách Cổ Thánh, cũng tới gần, tìm không thấy loại cơ duyên này, một mực vây ở đại thánh cảnh giới, tuyệt đối là một loại dằn vặt."),t("br"),t("br"),n._v("Hiện tại, cơ duyên đang ở trước mắt, vô luận như thế nào, đều muốn đạt được!"),t("br"),t("br"),n._v("“Cảm ơn nhắc nhở, Tiểu Phù Phù, đột nhiên phát hiện, ngươi vẫn có chút dùng...”"),t("br"),t("br"),n._v("Con mắt sáng lên, Trương Huyền cười ha ha một tiếng."),t("br"),t("br"),n._v("Nếu không phải người này, quen biết thứ này, biết những bí ẩn này, làm không cẩn thận sẽ bỏ lỡ món bảo vật này."),t("br"),t("br"),n._v("“...” Tiểu Phù Phù."),t("br"),t("br"),n._v("Nhìn thấy con trai một mặt cười ngây ngô, Hưng kiếm thánh sửng sốt một chút, không hiểu ra sao."),t("br"),t("br"),n._v("Đây là... Cao hứng điên rồi? D."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1743"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1745"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);