(window.webpackJsonp=window.webpackJsonp||[]).push([[847],{1201:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1737-trieu-nha-luyen-hoa-co-thanh-chi-huyet-【-canh-thu-nhat-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1737-trieu-nha-luyen-hoa-co-thanh-chi-huyet-【-canh-thu-nhat-】"}},[n._v("#")]),n._v(" Chương 1737: Triệu Nhã luyện hóa Cổ Thánh chi huyết 【 canh thứ nhất 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1737: Triệu Nhã luyện hóa Cổ Thánh chi huyết "),t("br"),t("br"),n._v("“Đứng lên đi!”"),t("br"),t("br"),n._v("Trương Huyền vẫy tay một cái, huyền đao rơi vào lòng bàn tay, lập tức cảm thấy trong đó lực lượng hủy thiên diệt địa."),t("br"),t("br"),n._v("Lúc trước, Bất Hủ cảnh đại viên mãn cường giả, hắn muốn vượt qua, cần đủ loại thủ đoạn toàn bộ thi triển, mà bây giờ nhẹ nhàng một đao... Mười mấy cái cũng có thể ung dung diệt sát!"),t("br"),t("br"),n._v("Cổ Thánh cùng Bất Hủ cảnh, mặc dù chỉ thua kém một cái cấp bậc, nhưng lại có thiên địa khác biệt, số lượng nhiều hơn nữa, cũng không cách nào bù đắp."),t("br"),t("br"),n._v("“Rốt cục có năng lực tự vệ...”"),t("br"),t("br"),n._v("Thở ra một hơi, Trương Huyền con mắt sáng lên."),t("br"),t("br"),n._v("Bị Dị Linh tộc vị kia Cổ Thánh áp bức, đối phương nếu như vẫn ẩn núp lên, không xuất hiện, cho dù có Thiên đạo chi sách, cũng không cách nào làm bị thương mảy may, giờ phút này huyền đao tấn cấp, hoàn toàn khác biệt."),t("br"),t("br"),n._v("Đều là Cổ Thánh cấp bậc, chỉ cần hắn dám động thủ, liền có thể truy tìm đến khí tức cội nguồn, đem hắn kích thương!"),t("br"),t("br"),n._v("Trương gia lão tổ, chính là như vậy, đem hắn đẩy lùi."),t("br"),t("br"),n._v("Có thể nói, huyền đao tấn cấp, thủ đoạn bảo mệnh lần nữa tăng thêm, phối hợp lúc trước Cổ Thánh thi thể, không có chút nào đề phòng phía dưới, cùng cấp bậc Cổ Thánh, cũng có thể tuỳ tiện chém giết!"),t("br"),t("br"),n._v("Lại không là Thiên đạo chi sách, cái loại này làm một cú."),t("br"),t("br"),n._v("“Trước củng cố tu vi đi!”"),t("br"),t("br"),n._v("Cảm thụ một chút uy lực, Trương Huyền không nói thêm lời, để Yêu Dị huyền đao, đi một bên củng cố tu vi, quen thuộc lực lượng."),t("br"),t("br"),n._v("Huyền đao gật đầu lên tiếng, nhẹ nhàng run rẩy, hướng nơi xa bay đi, còn chưa đi xa, liền gặp được bay tới Long Cốt thần thương, lần nữa cung kính quỳ gối."),t("br"),t("br"),n._v("Đột phá Cổ Thánh, nó mới phát hiện, Long Cốt thần thương bên trong phong ấn lực lượng rốt cuộc mạnh cỡ nào, một khi phá giải, cho dù nó đột phá, cũng hoàn toàn không phải là đối thủ."),t("br"),t("br"),n._v("“Huyền nhi, ngươi chuôi này thương...”"),t("br"),t("br"),n._v("Thấy cảnh này, Hưng kiếm thánh trợn mắt lên."),t("br"),t("br"),n._v("Vũ khí chỉ hướng cường giả thần phục, chẳng phải biểu thị, chuôi này thương càng thêm cường đại?"),t("br"),t("br"),n._v("Hắn biết, đây là Cổ Thánh lưu lại vũ khí, không còn trước kia vinh quang, cụ thể bao nhiêu lợi hại, cũng không rõ ràng."),t("br"),t("br"),n._v("“Không có gì, một ít tiểu cơ duyên mà thôi...” Trương Huyền cười cười."),t("br"),t("br"),n._v("“Đây là tiểu cơ duyên?”"),t("br"),t("br"),n._v("Hưng kiếm thánh run rẩy, suýt chút nữa không có sụp đổ."),t("br"),t("br"),n._v("Ngươi có phải hay không đối tiểu cơ duyên, có cái gì hiểu lầm?"),t("br"),t("br"),n._v("Lợi hại như thế trường thương, đột phá Cổ Thánh huyền đao, cùng rất nhiều Đại Thánh chí bảo... Những này là tiểu cơ duyên lời nói, cái gì gọi lớn?"),t("br"),t("br"),n._v("“Các ngươi ở đây sau đó, ta đi luyện hóa cái này bản vẽ liền trở lại...”"),t("br"),t("br"),n._v("Không nhiều làm giải thích, Trương Huyền thẳng tắp hướng một cái phương hướng bay đi."),t("br"),t("br"),n._v("Thời gian không dài, đi tới một chỗ, nhẹ nhàng vồ một cái, một cái quyển trục xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Chính là bức họa này bản thể."),t("br"),t("br"),n._v("Vừa rồi thần thức ngưng tụ thành tơ, tiến hành lan ra thời điểm, liền phát hiện."),t("br"),t("br"),n._v("Rất nhanh luyện hóa."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Cả cuốn tranh, thu vào trong đó, Hưng kiếm thánh đám người, thân thể run rẩy, xuất hiện lần nữa trong đại sảnh, trước mắt bức tường giờ phút này nhẵn bóng như mới, không còn nửa điểm dấu vết."),t("br"),t("br"),n._v("“Các ngươi là ở đây tu luyện, vẫn là cùng ta cùng rời đi?”"),t("br"),t("br"),n._v("Xử lý xong những này, Trương Huyền nhìn về phía cha mẹ."),t("br"),t("br"),n._v("Toà này Đại Thành điện, trọng yếu nhất bảo vật chính là bức họa này, có điều, bảy mươi hai Thánh tượng, linh khí nồng nặc cùng trên vách tường điêu khắc văn tự, phù văn, đối tu luyện giả đồng dạng có tác dụng cực lớn."),t("br"),t("br"),n._v("Ở đây tu luyện, so phía ngoài tốc độ, ít nhất phải tốt hơn gấp mười lần."),t("br"),t("br"),n._v("“Không cùng ngươi cùng nhau, nơi này yên tĩnh, vừa vặn tu luyện linh hồn cùng tâm cảnh, luyện hóa Cổ Thánh chi huyết, thực hiện sớm ngày đột phá!”"),t("br"),t("br"),n._v("Hai vị kiếm thánh liếc mắt nhìn nhau, đồng thời lắc đầu."),t("br"),t("br"),n._v("Thực lực của bọn hắn, đi theo nhi tử sau lưng, chỉ có thể cản trở, cùng hắn như vậy, còn không bằng ở đây, gắng sức tu luyện, thực hiện sớm ngày đột phá."),t("br"),t("br"),n._v("“Ta cũng ở lại chỗ này!”"),t("br"),t("br"),n._v("Hồ Yêu Yêu cũng gật gật đầu."),t("br"),t("br"),n._v("Nàng vừa mới đột phá Kiến Thần Bất Hoại cảnh, ra ngoài khẳng định càng là vướng víu."),t("br"),n._v(" “Lão sư, ta cùng ngươi cùng một chỗ, ta biết bọn họ đem Ngụy Như Yên đưa đến đi nơi nào, có thể mang ngươi tìm!” Triệu Nhã đi tới trước mặt."),t("br"),t("br"),n._v("“Ừm!” Trương Huyền gật gật đầu, chần chờ một chút, ngón tay búng một cái, lúc trước Cổ Thánh chi huyết, bay tới."),t("br"),t("br"),n._v("“Trước đem giọt máu này hấp thu!”"),t("br"),t("br"),n._v("Ra ngoài nguy hiểm không ít, giọt máu này tuy là trân quý, nhưng cũng không bằng học sinh tính mạng quan trọng."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Khoanh chân ngồi xuống, Triệu Nhã đem huyết dịch hấp thu vào trong cơ thể, lực lượng toàn thân cuồn cuộn, không đến mười phút đồng hồ, đứng dậy, trong mắt tinh quang bắn ra tứ phía, chiếu rọi bát phương."),t("br"),t("br"),n._v("“Ngươi... Luyện hóa?” Trương Huyền sững sờ."),t("br"),t("br"),n._v("Hắn luyện hóa dễ dàng, là bởi vì, vị này tiên tổ huyết dịch, đến từ hắn bản thân, đồng căn đồng nguyên, Triệu Nhã làm sao có thể cũng nhanh như vậy?"),t("br"),t("br"),n._v("“Luyện hóa!” Triệu Nhã gật đầu: “Lão sư cho ta sửa đổi kinh mạch về sau, đối năng lượng chuyển hóa tốc độ, mười phần nhanh chóng, giọt máu này mặc dù lợi hại, cũng sẽ không tiêu phí quá nhiều thời gian!”"),t("br"),t("br"),n._v("“Kinh mạch?”"),t("br"),t("br"),n._v("Trương Huyền ngón tay một điểm, một đạo chân khí tràn vào đối phương trong cơ thể, quả nhiên phát hiện, sửa đổi kinh mạch sau Triệu Nhã, đối năng lượng vận chuyển, so với người bình thường nhanh không biết bao nhiêu."),t("br"),t("br"),n._v("Thậm chí liền xem như hắn, đều theo không kịp."),t("br"),t("br"),n._v("Khó trách có thể nhanh như vậy luyện hóa Cổ Thánh chi huyết, chỉ sợ nàng kinh mạch đồ, so Cổ Thánh đều muốn hợp lý!"),t("br"),t("br"),n._v("“Đi thôi!”"),t("br"),t("br"),n._v("Đã nàng luyện hóa huyết dịch, nói rõ nắm giữ năng lực bảo vệ tính mạng, Trương Huyền cũng không nhiều lời, thẳng tắp hướng ngoài điện bay đi."),t("br"),t("br"),n._v("Nhìn lấy hai người rời đi, Hưng kiếm thánh mặt mũi cười khổ, nhịn không được cảm xúc: “Cái này sư đồ, đều là biến thái...”"),t("br"),t("br"),n._v("Bọn họ hấp thu một giọt máu, cần đặt ở trong đan điền, dùng chân khí ngày đêm rèn luyện, đối phương hấp thu liền luyện hóa... Thật là biến thái."),t("br"),t("br"),n._v("“Chúng ta cũng muốn tăng thêm tốc độ, nếu không, thật bị nhi tử, đồ tôn vượt qua, tôn nghiêm ở đâu?”"),t("br"),t("br"),n._v("Mộng kiếm thánh nói."),t("br"),t("br"),n._v("“Cũng là!”"),t("br"),t("br"),n._v("Gật gật đầu, hai người không nói thêm lời, đồng thời ngồi dưới đất, bắt đầu tu luyện."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Lão tổ, ngươi nhất định phải làm chủ cho chúng ta!”"),t("br"),t("br"),n._v("Đại Thành điện bên ngoài, rất nhiều thanh niên khóc ròng ròng."),t("br"),t("br"),n._v("Chuẩn bị thời gian dài như vậy, hao tốn vô số tâm huyết, mới lấy đi vào cái này Đại Thành điện, nằm mơ đều không có nghĩ đến, cái gì đều không có đạt được, liền bị người đánh một trận tơi bời, ném đi đi ra."),t("br"),t("br"),n._v("“Tài nghệ không bằng người, chỉ có thể trách bản thân, không trách được người khác!”"),t("br"),t("br"),n._v("Ẩn giấu ở một bên Cổ Thánh, trầm tĩnh phút chốc, thanh âm nhàn nhạt vang lên."),t("br"),t("br"),n._v("“Không phải chúng ta tài nghệ không bằng người, mà là cái tên này, thuần phục nhiều như vậy Thánh thú, luyện hóa nhiều như vậy lợi hại vũ khí, nếu như không dùng những vật này, chỉ bằng vào thực lực, khẳng định không phải chúng ta đối thủ!”"),t("br"),t("br"),n._v("Đế giày thanh niên cắn răng."),t("br"),t("br"),n._v("Tên kia có gì đặc biệt hơn người?"),t("br"),t("br"),n._v("Cũng chỉ là dựa dẫm thú sủng cùng vũ khí thôi, không có hai thứ này, không tin còn có thể theo kịp thực lực của bọn hắn?"),t("br"),t("br"),n._v("“Đúng vậy a, mong rằng lão tổ làm chủ cho chúng ta, cùng hắn công bằng quyết đấu!”"),t("br"),t("br"),n._v("“Không dựa vào những này bàng môn tà đạo, nếu như hắn có thể thắng, chúng ta tâm phục khẩu phục!”"),t("br"),t("br"),n._v("Cái khác mấy cái thanh niên cũng cắn răng."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Lão tổ lặng im, một lát sau, thanh âm vang lên: “Hoằng Thiên huynh, ta những vãn bối này, muốn cùng ngươi hậu bối, tiến hành tỷ thí công bằng, không biết có thể thưởng cái tình mọn?”"),t("br"),t("br"),n._v("“Ngươi muốn cùng vãn bối của ta thảo luận, ta không sẽ thay hắn làm chủ, yên tâm, chỉ cần các ngươi không làm ra vi phạm đạo nghĩa sự tình, ta sẽ không nhúng tay!”"),t("br"),t("br"),n._v("Trương gia lão tổ thanh âm vang lên."),t("br"),t("br"),n._v("Trương Huyền đám người đi vào Đại Thành điện, hắn cũng không rời đi, vẫn ẩn núp tại bốn phía."),t("br"),t("br"),n._v("“Đã Hoằng Thiên huynh không nhúng tay vào, ta an tâm...”"),t("br"),t("br"),n._v("Chư Tử bách gia vị lão tổ này, lên tiếng."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1748"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1750"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);